INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Magical Tinkering', 2, 9, 'Artificer', 'TCE', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magical Tinkering' AND class_name = 'Artificer' AND level = 1), 0, '{@i 1st-level artificer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magical Tinkering' AND class_name = 'Artificer' AND level = 1), 1, 'You''ve learned how to invest a spark of magic into mundane objects. To use this ability, you must have {@item thieves'' tools|PHB} or {@item artisan''s tools|PHB} in hand. You then touch a Tiny nonmagical object as an action and give it one of the following magical properties of your choice:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Magical Tinkering' AND class_name = 'Artificer' AND level = 1), 2, 'list', NULL, '["The object sheds bright light in a 5-foot radius and dim light for an additional 5 feet.","Whenever tapped by a creature, the object emits a recorded message that can be heard up to 10 feet away. You utter the message when you bestow this property on the object, and the recording can be no more than 6 seconds long.","The object continuously emits your choice of an odor or a nonverbal sound (wind, waves, chirping, or the like). The chosen phenomenon is perceivable up to 10 feet away.","A static visual effect appears on one of the object''s surfaces. This effect can be a picture, up to 25 words of text, lines and shapes, or a mixture of these elements, as you like."]', '["The object sheds bright light in a 5-foot radius and dim light for an additional 5 feet.","Whenever tapped by a creature, the object emits a recorded message that can be heard up to 10 feet away. You utter the message when you bestow this property on the object, and the recording can be no more than 6 seconds long.","The object continuously emits your choice of an odor or a nonverbal sound (wind, waves, chirping, or the like). The chosen phenomenon is perceivable up to 10 feet away.","A static visual effect appears on one of the object''s surfaces. This effect can be a picture, up to 25 words of text, lines and shapes, or a mixture of these elements, as you like."]', NULL, NULL, NULL, '{"type":"list","items":["The object sheds bright light in a 5-foot radius and dim light for an additional 5 feet.","Whenever tapped by a creature, the object emits a recorded message that can be heard up to 10 feet away. You utter the message when you bestow this property on the object, and the recording can be no more than 6 seconds long.","The object continuously emits your choice of an odor or a nonverbal sound (wind, waves, chirping, or the like). The chosen phenomenon is perceivable up to 10 feet away.","A static visual effect appears on one of the object''s surfaces. This effect can be a picture, up to 25 words of text, lines and shapes, or a mixture of these elements, as you like."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magical Tinkering' AND class_name = 'Artificer' AND level = 1), 3, 'The chosen property lasts indefinitely. As an action, you can touch the object and end the property early.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magical Tinkering' AND class_name = 'Artificer' AND level = 1), 4, 'You can bestow magic on multiple objects, touching one object each time you use this feature, though a single object can only bear one property at a time. The maximum number of objects you can affect with this feature at one time is equal to your Intelligence modifier (minimum of one object). If you try to exceed your maximum, the oldest property immediately ends, and then the new property applies.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Magical Tinkering' AND class_name = 'Artificer' AND level = 1), 'ERLW', 54);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Optional Rule: Firearm Proficiency', 2, 9, 'Artificer', 'TCE', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Optional Rule: Firearm Proficiency' AND class_name = 'Artificer' AND level = 1), 0, 'The secrets of creating and operating gunpowder weapons have been discovered in various corners of the D&D multiverse. If your Dungeon Master uses the rules on firearms in {@book chapter 9|DMG|9|Firearms} of the {@book Dungeon Master''s Guide|DMG} and your artificer has been exposed to the operation of such weapons, your artificer is proficient with them.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Optional Rule: Firearm Proficiency' AND class_name = 'Artificer' AND level = 1), 'ERLW', 54);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spellcasting', 2, 9, 'Artificer', 'TCE', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Artificer' AND level = 1), 0, '{@i 1st-level artificer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Artificer' AND level = 1), 1, 'You''ve studied the workings of magic and how to cast spells, channeling the magic through objects. To observers, you don''t appear to be casting spells in a conventional way; you appear to produce wonders from mundane items and outlandish inventions.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Artificer' AND level = 1), 2, 'entries', 'Tools Required', '["You produce your artificer spell effects through your tools. You must have a spellcasting focus—specifically {@item thieves'' tools|phb} or some kind of {@item artisan''s tools|phb|artisan''s tool}—in hand when you cast any spell with this Spellcasting feature (meaning the spell has an ''M'' component when you cast it). You must be proficient with the tool to use it in this way. See {@book chapter 5|PHB|4}, \"Equipment,\" in the {@book Player''s Handbook|PHB} for descriptions of these tools.","After you gain the Infuse Item feature at 2nd level, you can also use any item bearing one of your infusions as a spellcasting focus.",{"type":"inset","name":"The Magic of Artifice","entries":["As an artificer, you use tools when you cast your spells. When describing your spellcasting, think about how you''re using a tool to perform the spell effect. If you cast {@spell cure wounds} using {@item alchemist''s supplies|PHB}, you could be quickly producing a salve. If you cast it using {@item tinker''s tools|PHB}, you might have a miniature mechanical spider that binds wounds. When you cast {@spell poison spray}, you could fling foul chemicals or use a wand that spits venom. The effect of the spell is the same as for a spellcaster of any other class, but your method of spellcasting is special.","The same principle applies when you prepare your spells. As an artificer, you don''t study a spellbook or pray to prepare your spells. Instead, you work with your tools and create the specialized items you''ll use to produce your effects. If you replace {@spell cure wounds} with {@spell heat metal}, you might be altering the device you use to heal—perhaps modifying a tool so that it channels heat instead of healing energy.","Such details don''t limit you in any way or provide you with any benefit beyond the spell''s effects. You don''t have to justify how you''re using tools to cast a spell. But describing your spellcasting creatively is a fun way to distinguish yourself from other spellcasters."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Tools Required","entries":["You produce your artificer spell effects through your tools. You must have a spellcasting focus—specifically {@item thieves'' tools|phb} or some kind of {@item artisan''s tools|phb|artisan''s tool}—in hand when you cast any spell with this Spellcasting feature (meaning the spell has an ''M'' component when you cast it). You must be proficient with the tool to use it in this way. See {@book chapter 5|PHB|4}, \"Equipment,\" in the {@book Player''s Handbook|PHB} for descriptions of these tools.","After you gain the Infuse Item feature at 2nd level, you can also use any item bearing one of your infusions as a spellcasting focus.",{"type":"inset","name":"The Magic of Artifice","entries":["As an artificer, you use tools when you cast your spells. When describing your spellcasting, think about how you''re using a tool to perform the spell effect. If you cast {@spell cure wounds} using {@item alchemist''s supplies|PHB}, you could be quickly producing a salve. If you cast it using {@item tinker''s tools|PHB}, you might have a miniature mechanical spider that binds wounds. When you cast {@spell poison spray}, you could fling foul chemicals or use a wand that spits venom. The effect of the spell is the same as for a spellcaster of any other class, but your method of spellcasting is special.","The same principle applies when you prepare your spells. As an artificer, you don''t study a spellbook or pray to prepare your spells. Instead, you work with your tools and create the specialized items you''ll use to produce your effects. If you replace {@spell cure wounds} with {@spell heat metal}, you might be altering the device you use to heal—perhaps modifying a tool so that it channels heat instead of healing energy.","Such details don''t limit you in any way or provide you with any benefit beyond the spell''s effects. You don''t have to justify how you''re using tools to cast a spell. But describing your spellcasting creatively is a fun way to distinguish yourself from other spellcasters."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Artificer' AND level = 1), 3, 'entries', 'Cantrips (0-Level Spells)', '["At 1st level, you know two cantrips of your choice from the {@filter artificer spell list|spells|level=0|class=Artificer}. At higher levels, you learn additional artificer cantrips of your choice, as shown in the Cantrips Known column of the Artificer table.","When you gain a level in this class, you can replace one of the artificer cantrips you know with another cantrip from the artificer spell list."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Cantrips (0-Level Spells)","entries":["At 1st level, you know two cantrips of your choice from the {@filter artificer spell list|spells|level=0|class=Artificer}. At higher levels, you learn additional artificer cantrips of your choice, as shown in the Cantrips Known column of the Artificer table.","When you gain a level in this class, you can replace one of the artificer cantrips you know with another cantrip from the artificer spell list."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Artificer' AND level = 1), 4, 'entries', 'Preparing and Casting Spells', '["The Artificer table shows how many spell slots you have to cast your artificer spells. To cast one of your artificer spells of 1st level or higher, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","You prepare the list of artificer spells that are available for you to cast, choosing from the {@filter artificer spell list|spells|class=Artificer}. When you do so, choose a number of artificer spells equal to your Intelligence modifier + half your artificer level, rounded down (minimum of one spell). The spells must be of a level for which you have spell slots.","For example, if you are a 5th-level artificer, you have four 1st-level and two 2nd-level spell slots. With an Intelligence of 14, your list of prepared spells can include four spells of 1st or 2nd level, in any combination. If you prepare the 1st-level spell {@spell cure wounds}, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.","You can change your list of prepared spells when you finish a long rest. Preparing a new list of artificer spells requires time spent tinkering with your spellcasting focuses: at least 1 minute per spell level for each spell on your list."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Preparing and Casting Spells","entries":["The Artificer table shows how many spell slots you have to cast your artificer spells. To cast one of your artificer spells of 1st level or higher, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","You prepare the list of artificer spells that are available for you to cast, choosing from the {@filter artificer spell list|spells|class=Artificer}. When you do so, choose a number of artificer spells equal to your Intelligence modifier + half your artificer level, rounded down (minimum of one spell). The spells must be of a level for which you have spell slots.","For example, if you are a 5th-level artificer, you have four 1st-level and two 2nd-level spell slots. With an Intelligence of 14, your list of prepared spells can include four spells of 1st or 2nd level, in any combination. If you prepare the 1st-level spell {@spell cure wounds}, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.","You can change your list of prepared spells when you finish a long rest. Preparing a new list of artificer spells requires time spent tinkering with your spellcasting focuses: at least 1 minute per spell level for each spell on your list."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Artificer' AND level = 1), 5, 'entries', 'Spellcasting Ability', '["Intelligence is your spellcasting ability for your artificer spells; your understanding of the theory behind magic allows you to wield these spells with superior skill. You use your Intelligence whenever an artificer spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for an artificer spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["int"]},{"type":"abilityAttackMod","name":"Spell","attributes":["int"]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Ability","entries":["Intelligence is your spellcasting ability for your artificer spells; your understanding of the theory behind magic allows you to wield these spells with superior skill. You use your Intelligence whenever an artificer spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for an artificer spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["int"]},{"type":"abilityAttackMod","name":"Spell","attributes":["int"]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Artificer' AND level = 1), 6, 'entries', 'Ritual Casting', '["You can cast an artificer spell as a ritual if that spell has the ritual tag and you have the spell prepared."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Ritual Casting","entries":["You can cast an artificer spell as a ritual if that spell has the ritual tag and you have the spell prepared."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Artificer' AND level = 1), 'ERLW', 54);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Infuse Item', 2, 9, 'Artificer', 'TCE', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Infuse Item' AND class_name = 'Artificer' AND level = 2), 0, '{@i 2nd-level artificer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Infuse Item' AND class_name = 'Artificer' AND level = 2), 1, 'You''ve gained the ability to imbue mundane items with certain magical infusions, turning those objects into magic items.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Infuse Item' AND class_name = 'Artificer' AND level = 2), 2, 'refClassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refClassFeature","classFeature":"Infusions Known|Artificer|TCE|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Infuse Item' AND class_name = 'Artificer' AND level = 2), 3, 'inset', 'Artificer Infusions', '["Artificers have invented numerous magical infusions, extraordinary processes that rapidly create magic items. To many, artificers seem like wonderworkers, accomplishing in hours what others need weeks to complete.","The description of each of the following infusions details the type of item that can receive it, along with whether the resulting magic item requires attunement.","Some infusions specify a minimum artificer level. You can''t learn such an infusion until you are at least that level.","Unless an infusion''s description says otherwise, you can''t learn an infusion more than once."]', NULL, NULL, NULL, NULL, '{"type":"inset","name":"Artificer Infusions","entries":["Artificers have invented numerous magical infusions, extraordinary processes that rapidly create magic items. To many, artificers seem like wonderworkers, accomplishing in hours what others need weeks to complete.","The description of each of the following infusions details the type of item that can receive it, along with whether the resulting magic item requires attunement.","Some infusions specify a minimum artificer level. You can''t learn such an infusion until you are at least that level.","Unless an infusion''s description says otherwise, you can''t learn an infusion more than once."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Infuse Item' AND class_name = 'Artificer' AND level = 2), 4, 'entries', 'Infusing an Item', '["Whenever you finish a long rest, you can touch a nonmagical object and imbue it with one of your artificer infusions, turning it into a magic item. An infusion works on only certain kinds of objects, as specified in the infusion''s description. If the item requires attunement, you can attune yourself to it the instant you infuse the item. If you decide to attune to the item later, you must do so using the normal process for attunement (see \"{@book Attunement|DMG|7|Attunement}\" in chapter 7 of the {@book Dungeon Master''s Guide|DMG}).","Your infusion remains in an item indefinitely, but when you die, the infusion vanishes after a number of days have passed equal to your Intelligence modifier (minimum of 1 day). The infusion also vanishes if you give up your knowledge of the infusion for another one.","You can infuse more than one nonmagical object at the end of a long rest; the maximum number of objects appears in the Infused Items column of the Artificer table. You must touch each of the objects, and each of your infusions can be in only one object at a time. Moreover, no object can bear more than one of your infusions at a time. If you try to exceed your maximum number of infusions, the oldest infusion immediately ends, and then the new infusion applies.","If an infusion ends on an item that contains other things, like a {@item bag of holding}, its contents harmlessly appear in and around its space."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Infusing an Item","entries":["Whenever you finish a long rest, you can touch a nonmagical object and imbue it with one of your artificer infusions, turning it into a magic item. An infusion works on only certain kinds of objects, as specified in the infusion''s description. If the item requires attunement, you can attune yourself to it the instant you infuse the item. If you decide to attune to the item later, you must do so using the normal process for attunement (see \"{@book Attunement|DMG|7|Attunement}\" in chapter 7 of the {@book Dungeon Master''s Guide|DMG}).","Your infusion remains in an item indefinitely, but when you die, the infusion vanishes after a number of days have passed equal to your Intelligence modifier (minimum of 1 day). The infusion also vanishes if you give up your knowledge of the infusion for another one.","You can infuse more than one nonmagical object at the end of a long rest; the maximum number of objects appears in the Infused Items column of the Artificer table. You must touch each of the objects, and each of your infusions can be in only one object at a time. Moreover, no object can bear more than one of your infusions at a time. If you try to exceed your maximum number of infusions, the oldest infusion immediately ends, and then the new infusion applies.","If an infusion ends on an item that contains other things, like a {@item bag of holding}, its contents harmlessly appear in and around its space."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Infuse Item' AND class_name = 'Artificer' AND level = 2), 'ERLW', 54);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Infusions Known', 2, 9, 'Artificer', 'TCE', NULL, NULL, 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Infusions Known' AND class_name = 'Artificer' AND level = 2), 0, 'When you gain this feature, pick four artificer infusions to learn, choosing from the "{@filter Artificer Infusions|optionalfeatures|feature type=ai|source=TCE}" section at the end of the class''s description. You learn additional infusions of your choice when you reach certain levels in this class, as shown in the Infusions Known column of the Artificer table.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Infusions Known' AND class_name = 'Artificer' AND level = 2), 1, 'Whenever you gain a level in this class, you can replace one of the artificer infusions you learned with a new one.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Infusions Known' AND class_name = 'Artificer' AND level = 2), 'ERLW', 54);
