INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Right Tool for the Job', 2, 9, 'Artificer', 'TCE', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Right Tool for the Job' AND class_name = 'Artificer' AND level = 3), 0, '{@i 3rd-level artificer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Right Tool for the Job' AND class_name = 'Artificer' AND level = 3), 1, 'You''ve learned how to produce exactly the tool you need: with {@item thieves'' tools|PHB} or {@item artisan''s tools|PHB} in hand, you can magically create one set of {@item artisan''s tools|PHB} in an unoccupied space within 5 feet of you. This creation requires 1 hour of uninterrupted work, which can coincide with a short or long rest. Though the product of magic, the tools are nonmagical, and they vanish when you use this feature again.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'The Right Tool for the Job' AND class_name = 'Artificer' AND level = 3), 'ERLW', 54);
