INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Magic Item Adept', 2, 9, 'Artificer', 'TCE', NULL, NULL, 10, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic Item Adept' AND class_name = 'Artificer' AND level = 10), 0, '{@i 10th-level artificer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic Item Adept' AND class_name = 'Artificer' AND level = 10), 1, 'You''ve achieved a profound understanding of how to use and make magic items:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Magic Item Adept' AND class_name = 'Artificer' AND level = 10), 2, 'list', NULL, '["You can attune to up to four magic items at once.","If you craft a magic item with a rarity of common or uncommon, it takes you a quarter of the normal time, and it costs you half as much of the usual gold."]', '["You can attune to up to four magic items at once.","If you craft a magic item with a rarity of common or uncommon, it takes you a quarter of the normal time, and it costs you half as much of the usual gold."]', NULL, NULL, NULL, '{"type":"list","items":["You can attune to up to four magic items at once.","If you craft a magic item with a rarity of common or uncommon, it takes you a quarter of the normal time, and it costs you half as much of the usual gold."]}');
