INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic Item Savant' AND class_name = 'Artificer' AND level = 14), 0, '{@i 14th-level artificer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic Item Savant' AND class_name = 'Artificer' AND level = 14), 1, 'Your skill with magic items deepens:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Magic Item Savant' AND class_name = 'Artificer' AND level = 14), 2, 'list', NULL, '["You can attune to up to five magic items at once.","You ignore all class, race, spell, and level requirements on attuning to or using a magic item."]', '["You can attune to up to five magic items at once.","You ignore all class, race, spell, and level requirements on attuning to or using a magic item."]', NULL, NULL, NULL, '{"type":"list","items":["You can attune to up to five magic items at once.","You ignore all class, race, spell, and level requirements on attuning to or using a magic item."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Magic Item Savant' AND class_name = 'Artificer' AND level = 14), 'ERLW', 54);
