INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Artificer Specialist Feature' AND class_name = 'Artificer' AND level = 15), 1, 'You gain a feature granted by your Artificer Specialist choice.');
