INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Soul of Artifice', 2, 9, 'Artificer', 'TCE', NULL, NULL, 20, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Soul of Artifice' AND class_name = 'Artificer' AND level = 20), 0, '{@i 20th-level artificer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Soul of Artifice' AND class_name = 'Artificer' AND level = 20), 1, 'You have developed a mystical connection to your magic items, which you can draw on for protection:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Soul of Artifice' AND class_name = 'Artificer' AND level = 20), 2, 'list', NULL, '["You gain a +1 bonus to all saving throws per magic item you are currently attuned to.","If you''re reduced to 0 hit points but not killed outright, you can use your reaction to end one of your artificer infusions, causing you to drop to 1 hit point instead of 0."]', '["You gain a +1 bonus to all saving throws per magic item you are currently attuned to.","If you''re reduced to 0 hit points but not killed outright, you can use your reaction to end one of your artificer infusions, causing you to drop to 1 hit point instead of 0."]', NULL, NULL, NULL, '{"type":"list","items":["You gain a +1 bonus to all saving throws per magic item you are currently attuned to.","If you''re reduced to 0 hit points but not killed outright, you can use your reaction to end one of your artificer infusions, causing you to drop to 1 hit point instead of 0."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Soul of Artifice' AND class_name = 'Artificer' AND level = 20), 'ERLW', 54);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Alchemist', 2, 14, 'Artificer', 'TCE', 'Alchemist', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Alchemist' AND class_name = 'Artificer' AND level = 3), 0, 'An Alchemist is an expert at combining reagents to produce mystical effects. Alchemists use their creations to give life and to leech it away. Alchemy is the oldest of artificer traditions, and its versatility has long been valued during times of war and peace.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Alchemist' AND class_name = 'Artificer' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tool Proficiency|Artificer|TCE|Alchemist|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Alchemist' AND class_name = 'Artificer' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Alchemist Spells|Artificer|TCE|Alchemist|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Alchemist' AND class_name = 'Artificer' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Experimental Elixir|Artificer|TCE|Alchemist|TCE|3"}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Alchemist' AND class_name = 'Artificer' AND level = 3), 'ERLW', 58);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Alchemist Spells', 2, 14, 'Artificer', 'TCE', 'Alchemist', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Alchemist Spells' AND class_name = 'Artificer' AND level = 3), 0, 'Starting at 3rd level, you always have certain spells prepared after you reach particular levels in this class, as shown in the Alchemist Spells table. These spells count as artificer spells for you, but they don''t count against the number of artificer spells you prepare.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Alchemist Spells' AND class_name = 'Artificer' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Alchemist Spells', '["Artificer Level","Spell"]', '[["3rd","{@spell healing word}, {@spell ray of sickness}"],["5th","{@spell flaming sphere}, {@spell Melf''s acid arrow}"],["9th","{@spell gaseous form}, {@spell mass healing word}"],["13th","{@spell blight}, {@spell death ward}"],["17th","{@spell cloudkill}, {@spell raise dead}"]]', '{"type":"table","caption":"Alchemist Spells","colLabels":["Artificer Level","Spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell healing word}, {@spell ray of sickness}"],["5th","{@spell flaming sphere}, {@spell Melf''s acid arrow}"],["9th","{@spell gaseous form}, {@spell mass healing word}"],["13th","{@spell blight}, {@spell death ward}"],["17th","{@spell cloudkill}, {@spell raise dead}"]]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Alchemist Spells' AND class_name = 'Artificer' AND level = 3), 'ERLW', 58);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Experimental Elixir', 2, 14, 'Artificer', 'TCE', 'Alchemist', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Experimental Elixir' AND class_name = 'Artificer' AND level = 3), 0, 'Beginning at 3rd level, whenever you finish a long rest, you can magically produce an {@i experimental elixir} in an empty flask you touch. Roll on the Experimental Elixir table for the elixir''s effect, which is triggered when someone drinks the elixir. As an action, a creature can drink the elixir or administer it to an {@condition incapacitated} creature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Experimental Elixir' AND class_name = 'Artificer' AND level = 3), 1, 'Creating an {@i experimental elixir} requires you to have {@item alchemist''s supplies|PHB} on your person, and any elixir you create with this feature lasts until it is drunk or until the end of your next long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Experimental Elixir' AND class_name = 'Artificer' AND level = 3), 2, 'When you reach certain levels in this class, you can make more elixirs at the end of a long rest: two at 6th level and three at 15th level. Roll for each elixir''s effect separately. Each elixir requires its own flask.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Experimental Elixir' AND class_name = 'Artificer' AND level = 3), 3, 'You can create additional {@i experimental elixirs} by expending a spell slot of 1st level or higher for each one. When you do so, you use your action to create the elixir in an empty flask you touch, and you choose the elixir''s effect from the Experimental Elixir table.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Experimental Elixir' AND class_name = 'Artificer' AND level = 3), 4, 'table', NULL, NULL, NULL, 'Experimental Elixir', '["d6","Effect"]', '[["1","{@b Healing}. The drinker regains a number of hit points equal to {@dice 2d4} + your Intelligence modifier."],["2","{@b Swiftness}. The drinker''s walking speed increases by 10 feet for 1 hour."],["3","{@b Resilience}. The drinker gains a +1 bonus to AC for 10 minutes."],["4","{@b Boldness}. The drinker can roll a {@dice d4} and add the number rolled to every attack roll and saving throw they make for the next minute."],["5","{@b Flight}. The drinker gains a flying speed of 10 feet for 10 minutes."],["6","{@b Transformation}. The drinker''s body is transformed as if by the {@spell alter self} spell. The drinker determines the transformation caused by the spell, the effects of which last for 10 minutes."]]', '{"type":"table","caption":"Experimental Elixir","colLabels":["d6","Effect"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","{@b Healing}. The drinker regains a number of hit points equal to {@dice 2d4} + your Intelligence modifier."],["2","{@b Swiftness}. The drinker''s walking speed increases by 10 feet for 1 hour."],["3","{@b Resilience}. The drinker gains a +1 bonus to AC for 10 minutes."],["4","{@b Boldness}. The drinker can roll a {@dice d4} and add the number rolled to every attack roll and saving throw they make for the next minute."],["5","{@b Flight}. The drinker gains a flying speed of 10 feet for 10 minutes."],["6","{@b Transformation}. The drinker''s body is transformed as if by the {@spell alter self} spell. The drinker determines the transformation caused by the spell, the effects of which last for 10 minutes."]]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Experimental Elixir' AND class_name = 'Artificer' AND level = 3), 'ERLW', 58);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tool Proficiency', 2, 14, 'Artificer', 'TCE', 'Alchemist', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tool Proficiency' AND class_name = 'Artificer' AND level = 3), 0, 'When you adopt this specialization at 3rd level, you gain proficiency with {@item alchemist''s supplies|PHB}. If you already have this proficiency, you gain proficiency with one other type of {@item artisan''s tools|PHB} of your choice.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Tool Proficiency' AND class_name = 'Artificer' AND level = 3), 'ERLW', 58);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Alchemical Savant', 2, 14, 'Artificer', 'TCE', 'Alchemist', 'TCE', 5, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Alchemical Savant' AND class_name = 'Artificer' AND level = 5), 0, 'At 5th level, you develop masterful command of magical chemicals, enhancing the healing and damage you create through them. Whenever you cast a spell using your {@item alchemist''s supplies|PHB} as the spellcasting focus, you gain a bonus to one roll of the spell. That roll must restore hit points or be a damage roll that deals acid, fire, necrotic, or poison damage, and the bonus equals your Intelligence modifier (minimum of +1).');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Alchemical Savant' AND class_name = 'Artificer' AND level = 5), 'ERLW', 58);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Restorative Reagents', 2, 14, 'Artificer', 'TCE', 'Alchemist', 'TCE', 9, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Restorative Reagents' AND class_name = 'Artificer' AND level = 9), 0, 'Starting at 9th level, you can incorporate restorative reagents into some of your works:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Restorative Reagents' AND class_name = 'Artificer' AND level = 9), 1, 'list', NULL, '["Whenever a creature drinks an {@i experimental elixir} you created, the creature gains temporary hit points equal to {@dice 2d6} + your Intelligence modifier (minimum of 1 temporary hit point).","You can cast {@spell lesser restoration} without expending a spell slot and without preparing the spell, provided you use {@item alchemist''s supplies|PHB} as the spellcasting focus. You can do so a number of times equal to your Intelligence modifier (minimum of once), and you regain all expended uses when you finish a long rest."]', '["Whenever a creature drinks an {@i experimental elixir} you created, the creature gains temporary hit points equal to {@dice 2d6} + your Intelligence modifier (minimum of 1 temporary hit point).","You can cast {@spell lesser restoration} without expending a spell slot and without preparing the spell, provided you use {@item alchemist''s supplies|PHB} as the spellcasting focus. You can do so a number of times equal to your Intelligence modifier (minimum of once), and you regain all expended uses when you finish a long rest."]', NULL, NULL, NULL, '{"type":"list","items":["Whenever a creature drinks an {@i experimental elixir} you created, the creature gains temporary hit points equal to {@dice 2d6} + your Intelligence modifier (minimum of 1 temporary hit point).","You can cast {@spell lesser restoration} without expending a spell slot and without preparing the spell, provided you use {@item alchemist''s supplies|PHB} as the spellcasting focus. You can do so a number of times equal to your Intelligence modifier (minimum of once), and you regain all expended uses when you finish a long rest."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Restorative Reagents' AND class_name = 'Artificer' AND level = 9), 'ERLW', 58);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Chemical Mastery', 2, 14, 'Artificer', 'TCE', 'Alchemist', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Chemical Mastery' AND class_name = 'Artificer' AND level = 15), 0, 'By 15th level, you have been exposed to so many chemicals that they pose little risk to you, and you can use them to quickly end certain ailments:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Chemical Mastery' AND class_name = 'Artificer' AND level = 15), 1, 'list', NULL, '["You gain resistance to acid damage and poison damage, and you are immune to the {@condition poisoned} condition.","You can cast {@spell greater restoration} and {@spell heal} without expending a spell slot, without preparing the spell, and without material components, provided you use {@item alchemist''s supplies|PHB} as the spellcasting focus. Once you cast either spell with this feature, you can''t cast that spell with it again until you finish a long rest."]', '["You gain resistance to acid damage and poison damage, and you are immune to the {@condition poisoned} condition.","You can cast {@spell greater restoration} and {@spell heal} without expending a spell slot, without preparing the spell, and without material components, provided you use {@item alchemist''s supplies|PHB} as the spellcasting focus. Once you cast either spell with this feature, you can''t cast that spell with it again until you finish a long rest."]', NULL, NULL, NULL, '{"type":"list","items":["You gain resistance to acid damage and poison damage, and you are immune to the {@condition poisoned} condition.","You can cast {@spell greater restoration} and {@spell heal} without expending a spell slot, without preparing the spell, and without material components, provided you use {@item alchemist''s supplies|PHB} as the spellcasting focus. Once you cast either spell with this feature, you can''t cast that spell with it again until you finish a long rest."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Chemical Mastery' AND class_name = 'Artificer' AND level = 15), 'ERLW', 58);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Armorer', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armorer' AND class_name = 'Artificer' AND level = 3), 0, 'An artificer who specializes as an Armorer modifies armor to function almost like a second skin. The armor is enhanced to hone the artificer''s magic, unleash potent attacks, and generate a formidable defense. The artificer bonds with this armor, becoming one with it even as they experiment with it and refine its magical capabilities.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Armorer' AND class_name = 'Artificer' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tools of the Trade|Artificer|TCE|Armorer|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Armorer' AND class_name = 'Artificer' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Armorer Spells|Artificer|TCE|Armorer|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Armorer' AND class_name = 'Artificer' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Arcane Armor|Artificer|TCE|Armorer|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Armorer' AND class_name = 'Artificer' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Armor Model|Artificer|TCE|Armorer|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dampening Field', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dampening Field' AND class_name = 'Artificer' AND level = 3), 0, 'You have advantage on Dexterity ({@skill Stealth}) checks. If the armor normally imposes disadvantage on such checks, the advantage and disadvantage cancel each other, as normal.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Defensive Field', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Defensive Field' AND class_name = 'Artificer' AND level = 3), 0, 'As a bonus action, you can gain temporary hit points equal to your level in this class, replacing any temporary hit points you already have. You lose these temporary hit points if you doff the armor. You can use this bonus action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Lightning Launcher', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Lightning Launcher' AND class_name = 'Artificer' AND level = 3), 0, 'A gemlike node appears on one of your armored fists or on the chest (your choice). It counts as a simple ranged weapon, with a normal range of 90 feet and a long range of 300 feet, and it deals {@damage 1d6} lightning damage on a hit. Once on each of your turns when you hit a creature with it, you can deal an extra {@damage 1d6} lightning damage to that target.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Powered Steps', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Powered Steps' AND class_name = 'Artificer' AND level = 3), 0, 'Your walking speed increases by 5 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Thunder Gauntlets', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Thunder Gauntlets' AND class_name = 'Artificer' AND level = 3), 0, 'Each of the armor''s gauntlets counts as a simple melee weapon while you aren''t holding anything in it, and it deals {@damage 1d8} thunder damage on a hit. A creature hit by the gauntlet has disadvantage on attack rolls against targets other than you until the start of your next turn, as the armor magically emits a distracting pulse when the creature attacks someone else.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Armor', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Armor' AND class_name = 'Artificer' AND level = 3), 0, '{@i 3rd-level Armorer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Armor' AND class_name = 'Artificer' AND level = 3), 1, 'Your metallurgical pursuits have led to you making armor a conduit for your magic. As an action, you can turn a suit of armor you are wearing into Arcane Armor, provided you have {@item smith''s tools|PHB} in hand.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Armor' AND class_name = 'Artificer' AND level = 3), 2, 'You gain the following benefits while wearing this armor:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcane Armor' AND class_name = 'Artificer' AND level = 3), 3, 'list', NULL, '["If the armor normally has a Strength requirement, the arcane armor lacks this requirement for you.","You can use the arcane armor as a spellcasting focus for your artificer spells.","The armor attaches to you and can''t be removed against your will. It also expands to cover your entire body, although you can retract or deploy the helmet as a bonus action. The armor replaces any missing limbs, functioning identically to a limb it replaces.","You can doff or don the armor as an action. The armor continues to be Arcane Armor until you don another suit of armor or you die."]', '["If the armor normally has a Strength requirement, the arcane armor lacks this requirement for you.","You can use the arcane armor as a spellcasting focus for your artificer spells.","The armor attaches to you and can''t be removed against your will. It also expands to cover your entire body, although you can retract or deploy the helmet as a bonus action. The armor replaces any missing limbs, functioning identically to a limb it replaces.","You can doff or don the armor as an action. The armor continues to be Arcane Armor until you don another suit of armor or you die."]', NULL, NULL, NULL, '{"type":"list","items":["If the armor normally has a Strength requirement, the arcane armor lacks this requirement for you.","You can use the arcane armor as a spellcasting focus for your artificer spells.","The armor attaches to you and can''t be removed against your will. It also expands to cover your entire body, although you can retract or deploy the helmet as a bonus action. The armor replaces any missing limbs, functioning identically to a limb it replaces.","You can doff or don the armor as an action. The armor continues to be Arcane Armor until you don another suit of armor or you die."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Armor Model', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armor Model' AND class_name = 'Artificer' AND level = 3), 0, '{@i 3rd-level Armorer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armor Model' AND class_name = 'Artificer' AND level = 3), 1, 'You can customize your Arcane Armor. When you do so, choose one of the following armor models: Guardian or Infiltrator. The model you choose gives you special benefits while you wear it.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armor Model' AND class_name = 'Artificer' AND level = 3), 2, 'Each model includes a special weapon. When you attack with that weapon, you can add your Intelligence modifier, instead of Strength or Dexterity, to the attack and damage rolls.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armor Model' AND class_name = 'Artificer' AND level = 3), 3, 'You can change the armor''s model whenever you finish a short or long rest, provided you have {@item smith''s tools|PHB} in hand.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Armor Model' AND class_name = 'Artificer' AND level = 3), 4, 'entries', NULL, '[{"type":"entries","name":"Guardian","entries":["You design your armor to be in the front line of conflict. It has the following features:",{"type":"list","style":"list-hang-notitle","items":[{"type":"refSubclassFeature","subclassFeature":"Thunder Gauntlets|Artificer|TCE|Armorer|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Defensive Field|Artificer|TCE|Armorer|TCE|3"}]}]},{"type":"entries","name":"Infiltrator","entries":["You customize your armor for subtle undertakings. It has the following features:",{"type":"list","style":"list-hang-notitle","items":[{"type":"refSubclassFeature","subclassFeature":"Lightning Launcher|Artificer|TCE|Armorer|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Powered Steps|Artificer|TCE|Armorer|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Dampening Field|Artificer|TCE|Armorer|TCE|3"}]}]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Guardian","entries":["You design your armor to be in the front line of conflict. It has the following features:",{"type":"list","style":"list-hang-notitle","items":[{"type":"refSubclassFeature","subclassFeature":"Thunder Gauntlets|Artificer|TCE|Armorer|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Defensive Field|Artificer|TCE|Armorer|TCE|3"}]}]},{"type":"entries","name":"Infiltrator","entries":["You customize your armor for subtle undertakings. It has the following features:",{"type":"list","style":"list-hang-notitle","items":[{"type":"refSubclassFeature","subclassFeature":"Lightning Launcher|Artificer|TCE|Armorer|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Powered Steps|Artificer|TCE|Armorer|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Dampening Field|Artificer|TCE|Armorer|TCE|3"}]}]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Armorer Spells', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armorer Spells' AND class_name = 'Artificer' AND level = 3), 0, '{@i 3rd-level Armorer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armorer Spells' AND class_name = 'Artificer' AND level = 3), 1, 'You always have certain spells prepared after you reach particular levels in this class, as shown in the Armorer Spells table. These spells count as artificer spells for you, but they don''t count against the number of artificer spells you prepare.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Armorer Spells' AND class_name = 'Artificer' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Armorer Spells', '["Artificer Level","Spell"]', '[["3rd","{@spell magic missile}, {@spell thunderwave}"],["5th","{@spell mirror image}, {@spell shatter}"],["9th","{@spell hypnotic pattern}, {@spell lightning bolt}"],["13th","{@spell fire shield}, {@spell greater invisibility}"],["17th","{@spell passwall}, {@spell wall of force}"]]', '{"type":"table","caption":"Armorer Spells","colLabels":["Artificer Level","Spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell magic missile}, {@spell thunderwave}"],["5th","{@spell mirror image}, {@spell shatter}"],["9th","{@spell hypnotic pattern}, {@spell lightning bolt}"],["13th","{@spell fire shield}, {@spell greater invisibility}"],["17th","{@spell passwall}, {@spell wall of force}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tools of the Trade', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tools of the Trade' AND class_name = 'Artificer' AND level = 3), 0, '{@i 3rd-level Armorer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tools of the Trade' AND class_name = 'Artificer' AND level = 3), 1, 'You gain proficiency with heavy armor. You also gain proficiency with {@item smith''s tools|PHB}. If you already have this tool proficiency, you gain proficiency with one other type of {@item artisan''s tools|PHB} of your choice.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extra Attack', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 5, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Attack' AND class_name = 'Artificer' AND level = 5), 0, '{@i 5th-level Armorer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Attack' AND class_name = 'Artificer' AND level = 5), 1, 'You can attack twice, rather than once, whenever you take the {@action Attack} action on your turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Armor Modifications', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 9, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armor Modifications' AND class_name = 'Artificer' AND level = 9), 0, '{@i 9th-level Armorer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armor Modifications' AND class_name = 'Artificer' AND level = 9), 1, 'You learn how to use your artificer infusions to specially modify your Arcane Armor. That armor now counts as separate items for the purposes of your Infuse Items feature: armor (the chest piece), boots, helmet, and the armor''s special weapon. Each of those items can bear one of your infusions, and the infusions transfer over if you change your armor''s model with the Armor Model feature. In addition, the maximum number of items you can infuse at once increases by 2, but those extra items must be part of your Arcane Armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Guardian', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Guardian' AND class_name = 'Artificer' AND level = 15), 0, 'When a Huge or smaller creature you can see ends its turn within 30 feet of you, you can use your reaction to magically force it to make a Strength saving throw against your spell save DC. On a failed save, you pull the creature up to 25 feet directly to an unoccupied space. If you pull the target to a space within 5 feet of you, you can make a melee weapon attack against it as part of this reaction.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Guardian' AND class_name = 'Artificer' AND level = 15), 1, 'You can use this reaction a number of times equal to your proficiency bonus, and you regain all expended uses of it when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Infiltrator', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Infiltrator' AND class_name = 'Artificer' AND level = 15), 0, 'Any creature that takes lightning damage from your Lightning Launcher glimmers with magical light until the start of your next turn. The glimmering creature sheds dim light in a 5-foot radius, and it has disadvantage on attack rolls against you, as the light jolts it if it attacks you. In addition, the next attack roll against it has advantage, and if that attack hits, the target takes an extra {@damage 1d6} lightning damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Perfected Armor', 2, 15, 'Artificer', 'TCE', 'Armorer', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Perfected Armor' AND class_name = 'Artificer' AND level = 15), 0, '{@i 15th-level Armorer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Perfected Armor' AND class_name = 'Artificer' AND level = 15), 1, 'Your Arcane Armor gains additional benefits based on its model, as shown below.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Perfected Armor' AND class_name = 'Artificer' AND level = 15), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Guardian|Artificer|TCE|Armorer|TCE|15"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Perfected Armor' AND class_name = 'Artificer' AND level = 15), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Infiltrator|Artificer|TCE|Armorer|TCE|15"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Artillerist', 2, 17, 'Artificer', 'TCE', 'Artillerist', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Artillerist' AND class_name = 'Artificer' AND level = 3), 0, 'An Artillerist specializes in using magic to hurl energy, projectiles, and explosions on a battlefield. This destructive power was valued by all the armies of the Last War. Now that the war is over, some members of this specialization have sought to build a more peaceful world by using their powers to fight the resurgence of strife in Khorvaire.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Artillerist' AND class_name = 'Artificer' AND level = 3), 1, 'The gnome artificer Vi, an unlikely yet key member of House Cannith''s warforged project, has been especially vocal about making things right: "It''s about time we fixed things instead of blowing them all to hell."');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Artillerist' AND class_name = 'Artificer' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tool Proficiency|Artificer|TCE|Artillerist|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Artillerist' AND class_name = 'Artificer' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Artillerist Spells|Artificer|TCE|Artillerist|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Artillerist' AND class_name = 'Artificer' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Eldritch Cannon|Artificer|TCE|Artillerist|TCE|3"}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Artillerist' AND class_name = 'Artificer' AND level = 3), 'ERLW', 59);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Artillerist Spells', 2, 17, 'Artificer', 'TCE', 'Artillerist', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Artillerist Spells' AND class_name = 'Artificer' AND level = 3), 0, 'Starting at 3rd level, you always have certain spells prepared after you reach particular levels in this class, as shown in the Artillerist Spells table. These spells count as artificer spells for you, but they don''t count against the number of artificer spells you prepare.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Artillerist Spells' AND class_name = 'Artificer' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Artillerist Spells', '["Artificer Level","Spell"]', '[["3rd","{@spell shield}, {@spell thunderwave}"],["5th","{@spell scorching ray}, {@spell shatter}"],["9th","{@spell fireball}, {@spell wind wall}"],["13th","{@spell ice storm}, {@spell wall of fire}"],["17th","{@spell cone of cold}, {@spell wall of force}"]]', '{"type":"table","caption":"Artillerist Spells","colLabels":["Artificer Level","Spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell shield}, {@spell thunderwave}"],["5th","{@spell scorching ray}, {@spell shatter}"],["9th","{@spell fireball}, {@spell wind wall}"],["13th","{@spell ice storm}, {@spell wall of fire}"],["17th","{@spell cone of cold}, {@spell wall of force}"]]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Artillerist Spells' AND class_name = 'Artificer' AND level = 3), 'ERLW', 59);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eldritch Cannon', 2, 17, 'Artificer', 'TCE', 'Artillerist', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Cannon' AND class_name = 'Artificer' AND level = 3), 0, 'At 3rd level, you learn how to create a magical cannon. Using {@item woodcarver''s tools|PHB} or {@item smith''s tools|PHB}, you can take an action to magically create a Small or Tiny eldritch cannon in an unoccupied space on a horizontal surface within 5 feet of you. A Small eldritch cannon occupies its space, and a Tiny one can be held in one hand.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Cannon' AND class_name = 'Artificer' AND level = 3), 1, 'Once you create a cannon, you can''t do so again until you finish a long rest or until you expend a spell slot of 1st level or higher. You can have only one cannon at a time and can''t create one while your cannon is present.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Cannon' AND class_name = 'Artificer' AND level = 3), 2, 'The cannon is a magical object. Regardless of size, the cannon has an AC of 18 and a number of hit points equal to five times your artificer level. It is immune to poison damage and psychic damage, and all conditions. If it is forced to make an ability check or a saving throw, treat all its ability scores as 10 (+0). If the {@spell mending} spell is cast on it, it regains {@dice 2d6} hit points. It disappears if it is reduced to 0 hit points or after 1 hour. You can dismiss it early as an action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Cannon' AND class_name = 'Artificer' AND level = 3), 3, 'When you create the cannon, you determine its appearance and whether it has legs. You also decide which type it is, choosing from the options on the Eldritch Cannons table. On each of your turns, you can take a bonus action to cause the cannon to activate if you are within 60 feet of it. As part of the same bonus action, you can direct the cannon to walk or climb up to 15 feet to an unoccupied space, provided it has legs.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Cannon' AND class_name = 'Artificer' AND level = 3), 4, 'table', NULL, NULL, NULL, 'Eldritch Cannons', '["Cannon","Activation"]', '[["{@object Eldritch Cannon, Flamethrower|TCE|Flamethrower}","The cannon exhales fire in an adjacent 15-foot cone that you designate. Each creature in that area must make a Dexterity saving throw against your spell save DC, taking {@damage 2d8} fire damage on a failed save or half as much damage on a successful one. The fire ignites any flammable objects in the area that aren''t being worn or carried."],["{@object Eldritch Cannon, Force Ballista|TCE|Force Ballista}","Make a ranged spell attack, originating from the cannon, at one creature or object within 120 feet of it. On a hit, the target takes {@damage 2d8} force damage, and if the target is a creature, it is pushed up to 5 feet away from the cannon."],["{@object Eldritch Cannon, Protector|TCE|Protector}","The cannon emits a burst of positive energy that grants itself and each creature of your choice within 10 feet of it a number of temporary hit points equal to {@damage 1d8} + your Intelligence modifier (minimum of +1)."]]', '{"type":"table","caption":"Eldritch Cannons","colLabels":["Cannon","Activation"],"colStyles":["col-2","col-10"],"rows":[["{@object Eldritch Cannon, Flamethrower|TCE|Flamethrower}","The cannon exhales fire in an adjacent 15-foot cone that you designate. Each creature in that area must make a Dexterity saving throw against your spell save DC, taking {@damage 2d8} fire damage on a failed save or half as much damage on a successful one. The fire ignites any flammable objects in the area that aren''t being worn or carried."],["{@object Eldritch Cannon, Force Ballista|TCE|Force Ballista}","Make a ranged spell attack, originating from the cannon, at one creature or object within 120 feet of it. On a hit, the target takes {@damage 2d8} force damage, and if the target is a creature, it is pushed up to 5 feet away from the cannon."],["{@object Eldritch Cannon, Protector|TCE|Protector}","The cannon emits a burst of positive energy that grants itself and each creature of your choice within 10 feet of it a number of temporary hit points equal to {@damage 1d8} + your Intelligence modifier (minimum of +1)."]]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Cannon' AND class_name = 'Artificer' AND level = 3), 'ERLW', 59);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tool Proficiency', 2, 17, 'Artificer', 'TCE', 'Artillerist', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tool Proficiency' AND class_name = 'Artificer' AND level = 3), 0, 'When you adopt this specialization at 3rd level, you gain proficiency with {@item woodcarver''s tools|PHB}. If you already have this proficiency, you gain proficiency with one other type of {@item artisan''s tools|PHB} of your choice.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Tool Proficiency' AND class_name = 'Artificer' AND level = 3), 'ERLW', 59);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Firearm', 2, 17, 'Artificer', 'TCE', 'Artillerist', 'TCE', 5, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Firearm' AND class_name = 'Artificer' AND level = 5), 0, 'At 5th level, you know how to turn a wand, staff, or rod into an arcane firearm, a conduit for your destructive spells. When you finish a long rest, you can use {@item woodcarver''s tools|PHB} to carve special sigils into a wand, staff, or rod and thereby turn it into your arcane firearm. The sigils disappear from the object if you later carve them on a different item. The sigils otherwise last indefinitely.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Firearm' AND class_name = 'Artificer' AND level = 5), 1, 'You can use your arcane firearm as a spellcasting focus for your artificer spells. When you cast an artificer spell through the firearm, roll a {@damage d8}, and you gain a bonus to one of the spell''s damage rolls equal to the number rolled.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Arcane Firearm' AND class_name = 'Artificer' AND level = 5), 'ERLW', 59);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Explosive Cannon', 2, 17, 'Artificer', 'TCE', 'Artillerist', 'TCE', 9, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Explosive Cannon' AND class_name = 'Artificer' AND level = 9), 0, 'Starting at 9th level, every eldritch cannon you create is more destructive:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Explosive Cannon' AND class_name = 'Artificer' AND level = 9), 1, 'list', NULL, '["The cannon''s damage rolls all increase by {@damage 1d8}.","As an action, you can command the cannon to detonate if you are within 60 feet of it. Doing so destroys the cannon and forces each creature within 20 feet of it to make a Dexterity saving throw against your spell save DC, taking {@damage 3d8} force damage on a failed save or half as much damage on a successful one."]', '["The cannon''s damage rolls all increase by {@damage 1d8}.","As an action, you can command the cannon to detonate if you are within 60 feet of it. Doing so destroys the cannon and forces each creature within 20 feet of it to make a Dexterity saving throw against your spell save DC, taking {@damage 3d8} force damage on a failed save or half as much damage on a successful one."]', NULL, NULL, NULL, '{"type":"list","items":["The cannon''s damage rolls all increase by {@damage 1d8}.","As an action, you can command the cannon to detonate if you are within 60 feet of it. Doing so destroys the cannon and forces each creature within 20 feet of it to make a Dexterity saving throw against your spell save DC, taking {@damage 3d8} force damage on a failed save or half as much damage on a successful one."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Explosive Cannon' AND class_name = 'Artificer' AND level = 9), 'ERLW', 59);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fortified Position', 2, 17, 'Artificer', 'TCE', 'Artillerist', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fortified Position' AND class_name = 'Artificer' AND level = 15), 0, 'Starting at 15th level, you''re a master at forming well-defended emplacements using Eldritch Cannon:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Fortified Position' AND class_name = 'Artificer' AND level = 15), 1, 'list', NULL, '["You and your allies have {@quickref Cover||3||half cover} while within 10 feet of a cannon you create with Eldritch Cannon, as a result of a shimmering field of magical protection that the cannon emits.","You can now have two cannons at the same time. You can create two with the same action (but not the same spell slot), and you can activate both of them with the same bonus action. You determine whether the cannons are identical to each other or different. You can''t create a third cannon while you have two."]', '["You and your allies have {@quickref Cover||3||half cover} while within 10 feet of a cannon you create with Eldritch Cannon, as a result of a shimmering field of magical protection that the cannon emits.","You can now have two cannons at the same time. You can create two with the same action (but not the same spell slot), and you can activate both of them with the same bonus action. You determine whether the cannons are identical to each other or different. You can''t create a third cannon while you have two."]', NULL, NULL, NULL, '{"type":"list","items":["You and your allies have {@quickref Cover||3||half cover} while within 10 feet of a cannon you create with Eldritch Cannon, as a result of a shimmering field of magical protection that the cannon emits.","You can now have two cannons at the same time. You can create two with the same action (but not the same spell slot), and you can activate both of them with the same bonus action. You determine whether the cannons are identical to each other or different. You can''t create a third cannon while you have two."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Fortified Position' AND class_name = 'Artificer' AND level = 15), 'ERLW', 59);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Battle Smith', 2, 18, 'Artificer', 'TCE', 'Battle Smith', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith' AND class_name = 'Artificer' AND level = 3), 0, 'Armies require protection, and someone has to put things back together if defenses fail. A combination of protector and medic, a Battle Smith is an expert at defending others and repairing both material and personnel. To aid in their work, Battle Smiths are usually accompanied by a steel defender, a protective companion of their own creation. Many soldiers tell stories of nearly dying before being saved by a Battle Smith and a steel defender.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith' AND class_name = 'Artificer' AND level = 3), 1, 'Battle Smiths played a key role in House Cannith''s work on battle constructs and the original warforged, and after the Last War, these artificers led efforts to aid those who were injured in the war''s horrific battles.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith' AND class_name = 'Artificer' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tool Proficiency|Artificer|TCE|Battle Smith|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith' AND class_name = 'Artificer' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Battle Smith Spells|Artificer|TCE|Battle Smith|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith' AND class_name = 'Artificer' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Battle Ready|Artificer|TCE|Battle Smith|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith' AND class_name = 'Artificer' AND level = 3), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Steel Defender|Artificer|TCE|Battle Smith|TCE|3"}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith' AND class_name = 'Artificer' AND level = 3), 'ERLW', 60);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Battle Ready', 2, 18, 'Artificer', 'TCE', 'Battle Smith', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battle Ready' AND class_name = 'Artificer' AND level = 3), 0, 'When you reach 3rd level, your combat training and your experiments with magic have paid off in two ways:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Ready' AND class_name = 'Artificer' AND level = 3), 1, 'list', NULL, '["You gain proficiency with {@filter martial weapons|items|type=martial weapon}.","When you attack with a magic weapon, you can use your Intelligence modifier, instead of Strength or Dexterity modifier, for the attack and damage rolls."]', '["You gain proficiency with {@filter martial weapons|items|type=martial weapon}.","When you attack with a magic weapon, you can use your Intelligence modifier, instead of Strength or Dexterity modifier, for the attack and damage rolls."]', NULL, NULL, NULL, '{"type":"list","items":["You gain proficiency with {@filter martial weapons|items|type=martial weapon}.","When you attack with a magic weapon, you can use your Intelligence modifier, instead of Strength or Dexterity modifier, for the attack and damage rolls."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Battle Ready' AND class_name = 'Artificer' AND level = 3), 'ERLW', 60);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Battle Smith Spells', 2, 18, 'Artificer', 'TCE', 'Battle Smith', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith Spells' AND class_name = 'Artificer' AND level = 3), 0, 'Starting at 3rd level, you always have certain spells prepared after you reach particular levels in this class, as shown in the Battle Smith Spells table. These spells count as artificer spells for you, but they don''t count against the number of artificer spells you prepare.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith Spells' AND class_name = 'Artificer' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Battle Smith Spells', '["Artificer Level","Spell"]', '[["3rd","{@spell heroism}, {@spell shield}"],["5th","{@spell branding smite}, {@spell warding bond}"],["9th","{@spell aura of vitality}, {@spell conjure barrage}"],["13th","{@spell aura of purity}, {@spell fire shield}"],["17th","{@spell banishing smite}, {@spell mass cure wounds}"]]', '{"type":"table","caption":"Battle Smith Spells","colLabels":["Artificer Level","Spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell heroism}, {@spell shield}"],["5th","{@spell branding smite}, {@spell warding bond}"],["9th","{@spell aura of vitality}, {@spell conjure barrage}"],["13th","{@spell aura of purity}, {@spell fire shield}"],["17th","{@spell banishing smite}, {@spell mass cure wounds}"]]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Battle Smith Spells' AND class_name = 'Artificer' AND level = 3), 'ERLW', 60);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Steel Defender', 2, 18, 'Artificer', 'TCE', 'Battle Smith', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steel Defender' AND class_name = 'Artificer' AND level = 3), 0, 'By 3rd level, your tinkering has borne you a faithful companion, a {@creature steel defender|tce}. It is friendly to you and your companions, and it obeys your commands. See this creature''s game statistics in the steel defender stat block, which uses your proficiency bonus (PB) in several places. You determine the creature''s appearance and whether it has two legs or four; your choice has no effect on its game statistics.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steel Defender' AND class_name = 'Artificer' AND level = 3), 1, 'In combat, the defender shares your initiative count, but it takes its turn immediately after yours. It can move and use its reaction on its own, but the only action it takes on its turn is the {@action Dodge} action, unless you take a bonus action on your turn to command it to take another action. That action can be one in its stat block or some other action. If you are {@condition incapacitated}, the defender can take any action of its choice, not just {@action Dodge}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steel Defender' AND class_name = 'Artificer' AND level = 3), 2, 'If the {@spell mending} spell is cast on it, it regains {@dice 2d6} hit points. If it has died within the last hour, you can use your {@item smith''s tools|PHB} as an action to revive it, provided you are within 5 feet of it and you expend a spell slot of 1st level or higher. The steel defender returns to life after 1 minute with all its hit points restored.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steel Defender' AND class_name = 'Artificer' AND level = 3), 3, 'At the end of a long rest, you can create a new steel defender if you have your {@item smith''s tools|PHB} with you. If you already have a steel defender from this feature, the first one immediately perishes. The defender also perishes if you die.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Steel Defender' AND class_name = 'Artificer' AND level = 3), 'ERLW', 60);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tool Proficiency', 2, 18, 'Artificer', 'TCE', 'Battle Smith', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tool Proficiency' AND class_name = 'Artificer' AND level = 3), 0, 'When you adopt this specialization at 3rd level, you gain proficiency with {@item smith''s tools|PHB}. If you already have this proficiency, you gain proficiency with one other type of {@item artisan''s tools|PHB} of your choice.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Tool Proficiency' AND class_name = 'Artificer' AND level = 3), 'ERLW', 60);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extra Attack', 2, 18, 'Artificer', 'TCE', 'Battle Smith', 'TCE', 5, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Attack' AND class_name = 'Artificer' AND level = 5), 0, 'Starting at 5th level, you can attack twice, rather than once, whenever you take the {@action Attack} action on your turn.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Extra Attack' AND class_name = 'Artificer' AND level = 5), 'ERLW', 60);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Jolt', 2, 18, 'Artificer', 'TCE', 'Battle Smith', 'TCE', 9, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Jolt' AND class_name = 'Artificer' AND level = 9), 0, 'At 9th level, you learn new ways to channel arcane energy to harm or heal. When either you hit a target with a magic weapon attack or your steel defender hits a target, you can channel magical energy through the strike to create one of the following effects:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcane Jolt' AND class_name = 'Artificer' AND level = 9), 1, 'list', NULL, '["The target takes an extra {@damage 2d6} force damage.","Choose one creature or object you can see within 30 feet of the target. Healing energy flows into the chosen recipient, restoring {@dice 2d6} hit points to it."]', '["The target takes an extra {@damage 2d6} force damage.","Choose one creature or object you can see within 30 feet of the target. Healing energy flows into the chosen recipient, restoring {@dice 2d6} hit points to it."]', NULL, NULL, NULL, '{"type":"list","items":["The target takes an extra {@damage 2d6} force damage.","Choose one creature or object you can see within 30 feet of the target. Healing energy flows into the chosen recipient, restoring {@dice 2d6} hit points to it."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Jolt' AND class_name = 'Artificer' AND level = 9), 2, 'You can use this energy a number of times equal to your Intelligence modifier (minimum of once), but you can do so no more than once on a turn. You regain all expended uses when you finish a long rest.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Arcane Jolt' AND class_name = 'Artificer' AND level = 9), 'ERLW', 60);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Improved Defender', 2, 18, 'Artificer', 'TCE', 'Battle Smith', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Improved Defender' AND class_name = 'Artificer' AND level = 15), 0, 'At 15th level, your Arcane Jolt and steel defender become more powerful:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Improved Defender' AND class_name = 'Artificer' AND level = 15), 1, 'list', NULL, '["The extra damage and the healing of your Arcane Jolt both increase to {@damage 4d6}.","Your steel defender gains a +2 bonus to Armor Class.","Whenever your steel defender uses its Deflect Attack, the attacker takes force damage equal to {@damage 1d4} + your Intelligence modifier."]', '["The extra damage and the healing of your Arcane Jolt both increase to {@damage 4d6}.","Your steel defender gains a +2 bonus to Armor Class.","Whenever your steel defender uses its Deflect Attack, the attacker takes force damage equal to {@damage 1d4} + your Intelligence modifier."]', NULL, NULL, NULL, '{"type":"list","items":["The extra damage and the healing of your Arcane Jolt both increase to {@damage 4d6}.","Your steel defender gains a +2 bonus to Armor Class.","Whenever your steel defender uses its Deflect Attack, the attacker takes force damage equal to {@damage 1d4} + your Intelligence modifier."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Improved Defender' AND class_name = 'Artificer' AND level = 15), 'ERLW', 60);
