INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Rage', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rage' AND class_name = 'Barbarian' AND level = 1), 0, 'In battle, you fight with primal ferocity. On your turn, you can enter a rage as a bonus action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rage' AND class_name = 'Barbarian' AND level = 1), 1, 'While raging, you gain the following benefits if you aren''t wearing heavy armor:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Rage' AND class_name = 'Barbarian' AND level = 1), 2, 'list', NULL, '["You have advantage on Strength checks and Strength saving throws.","When you make a melee weapon attack using Strength, you gain a +2 bonus to the damage roll. This bonus increases as you level.","You have resistance to bludgeoning, piercing, and slashing damage."]', '["You have advantage on Strength checks and Strength saving throws.","When you make a melee weapon attack using Strength, you gain a +2 bonus to the damage roll. This bonus increases as you level.","You have resistance to bludgeoning, piercing, and slashing damage."]', NULL, NULL, NULL, '{"type":"list","items":["You have advantage on Strength checks and Strength saving throws.","When you make a melee weapon attack using Strength, you gain a +2 bonus to the damage roll. This bonus increases as you level.","You have resistance to bludgeoning, piercing, and slashing damage."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rage' AND class_name = 'Barbarian' AND level = 1), 3, 'If you are able to cast spells, you can''t cast them or concentrate on them while raging.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rage' AND class_name = 'Barbarian' AND level = 1), 4, 'Your rage lasts for 1 minute. It ends early if you are knocked {@condition unconscious} or if your turn ends and you haven''t attacked a hostile creature since your last turn or taken damage since then. You can also end your rage on your turn as a bonus action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rage' AND class_name = 'Barbarian' AND level = 1), 5, 'Once you have raged the maximum number of times for your barbarian level, you must finish a long rest before you can rage again. You may rage 2 times at 1st level, 3 at 3rd, 4 at 6th, 5 at 12th, and 6 at 17th.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unarmored Defense', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unarmored Defense' AND class_name = 'Barbarian' AND level = 1), 0, 'While you are not wearing any armor, your Armor Class equals 10 + your Dexterity modifier + your Constitution modifier. You can use a shield and still gain this benefit.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Danger Sense', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Danger Sense' AND class_name = 'Barbarian' AND level = 2), 0, 'At 2nd level, you gain an uncanny sense of when things nearby aren''t as they should be, giving you an edge when you dodge away from danger. You have advantage on Dexterity saving throws against effects that you can see, such as traps and spells. To gain this benefit, you can''t be {@condition blinded}, {@condition deafened}, or {@condition incapacitated}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Reckless Attack', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Reckless Attack' AND class_name = 'Barbarian' AND level = 2), 0, 'Starting at 2nd level, you can throw aside all concern for defense to attack with fierce desperation. When you make your first attack on your turn, you can decide to attack recklessly. Doing so gives you advantage on melee weapon attack rolls using Strength during this turn, but attack rolls against you have advantage until your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Primal Knowledge', 2, 24, 'Barbarian', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Knowledge' AND class_name = 'Barbarian' AND level = 3), 0, '{@i 3rd-level barbarian {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Knowledge' AND class_name = 'Barbarian' AND level = 3), 1, 'When you reach 3rd level and again at 10th level, you gain proficiency in one skill of your choice from the list of skills available to barbarians at 1st level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Primal Path', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Path' AND class_name = 'Barbarian' AND level = 3), 0, 'At 3rd level, you choose a path that shapes the nature of your rage from the list of available paths. Your choice grants you features at 3rd level and again at 6th, 10th, and 14th levels.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Barbarian' AND level = 4), 0, 'When you reach 4th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Barbarian' AND level = 4), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extra Attack', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 5, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Attack' AND class_name = 'Barbarian' AND level = 5), 0, 'Beginning at 5th level, you can attack twice, instead of once, whenever you take the {@action Attack} action on your turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fast Movement', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 5, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fast Movement' AND class_name = 'Barbarian' AND level = 5), 0, 'Starting at 5th level, your speed increases by 10 feet while you aren''t wearing heavy armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path Feature', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path Feature' AND class_name = 'Barbarian' AND level = 6), 0, 'At 6th level, you gain a feature from your Primal Path.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Feral Instinct', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 7, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Feral Instinct' AND class_name = 'Barbarian' AND level = 7), 0, 'By 7th level, your instincts are so honed that you have advantage on initiative rolls.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Feral Instinct' AND class_name = 'Barbarian' AND level = 7), 1, 'Additionally, if you are {@status surprised} at the beginning of combat and aren''t {@condition incapacitated}, you can act normally on your first turn, but only if you enter your rage before doing anything else on that turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Instinctive Pounce', 2, 24, 'Barbarian', 'PHB', NULL, NULL, 7, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Instinctive Pounce' AND class_name = 'Barbarian' AND level = 7), 0, '{@i 7th-level barbarian {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Instinctive Pounce' AND class_name = 'Barbarian' AND level = 7), 1, 'As part of the bonus action you take to enter your rage, you can move up to half your speed.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 8, null, false, false) ON CONFLICT (name) DO NOTHING;
