INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Brutal Critical (1 die)', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 9, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Brutal Critical (1 die)' AND class_name = 'Barbarian' AND level = 9), 0, 'Beginning at 9th level, you can roll one additional weapon damage die when determining the extra damage for a critical hit with a melee attack.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Brutal Critical (1 die)' AND class_name = 'Barbarian' AND level = 9), 1, 'This increases to two additional dice at 13th level and three additional dice at 17th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path feature', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 10, null, false, false) ON CONFLICT (name) DO NOTHING;
