INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Relentless Rage', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 11, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Relentless Rage' AND class_name = 'Barbarian' AND level = 11), 0, 'Starting at 11th level, your rage can keep you fighting despite grievous wounds. If you drop to 0 hit points while you''re raging and don''t die outright, you can make a {@dc 10} Constitution saving throw. If you succeed, you drop to 1 hit point instead.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Relentless Rage' AND class_name = 'Barbarian' AND level = 11), 1, 'Each time you use this feature after the first, the DC increases by 5. When you finish a short or long rest, the DC resets to 10.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 46, 'Barbarian', 'PHB', NULL, NULL, 12, null, false, false) ON CONFLICT (name) DO NOTHING;
