INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Giant Power', null, 11, 'Barbarian', 'PHB', 'Giant', 'BGG', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Giant Power' AND class_name = 'Barbarian' AND level = 3), 0, '{@i 3rd-Level Path of the Giant Feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Giant Power' AND class_name = 'Barbarian' AND level = 3), 1, 'When you choose this path, you learn to speak, read, and write Giant or one other language of your choice if you already know Giant. Additionally, you learn a cantrip of your choice: either {@spell druidcraft} or {@spell thaumaturgy}. Wisdom is your spellcasting ability for this spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Giant''s Havoc', null, 11, 'Barbarian', 'PHB', 'Giant', 'BGG', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Giant''s Havoc' AND class_name = 'Barbarian' AND level = 3), 0, '{@i 3rd-Level Path of the Giant Feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Giant''s Havoc' AND class_name = 'Barbarian' AND level = 3), 1, 'Your rages pull strength from the primal might of giants, transforming you into a hulking force of destruction. While raging, you gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Giant''s Havoc' AND class_name = 'Barbarian' AND level = 3), 2, 'list', NULL, '[{"type":"item","name":"Crushing Throw","entry":"When you make a successful ranged attack with a thrown weapon using Strength, you can add your Rage Damage bonus to the attack''s damage roll."},{"type":"item","name":"Giant Stature","entry":"Your reach increases by 5 feet, and if you are smaller than Large, you become Large, along with anything you are wearing. If there isn''t enough room for you to increase your size, your size doesn''t change."}]', '[{"type":"item","name":"Crushing Throw","entry":"When you make a successful ranged attack with a thrown weapon using Strength, you can add your Rage Damage bonus to the attack''s damage roll."},{"type":"item","name":"Giant Stature","entry":"Your reach increases by 5 feet, and if you are smaller than Large, you become Large, along with anything you are wearing. If there isn''t enough room for you to increase your size, your size doesn''t change."}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Crushing Throw","entry":"When you make a successful ranged attack with a thrown weapon using Strength, you can add your Rage Damage bonus to the attack''s damage roll."},{"type":"item","name":"Giant Stature","entry":"Your reach increases by 5 feet, and if you are smaller than Large, you become Large, along with anything you are wearing. If there isn''t enough room for you to increase your size, your size doesn''t change."}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Giant', null, 11, 'Barbarian', 'PHB', 'Giant', 'BGG', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Giant' AND class_name = 'Barbarian' AND level = 3), 0, 'Barbarians who walk the Path of the Giant draw strength from the same primal forces as giants. As they rage, these barbarians surge with elemental power and grow in size, taking on forms that evoke the glory of giants. Some barbarians look like oversized versions of themselves, perhaps with a hint of elemental energy flaring in their eyes and around their weapons. Others transform more dramatically, taking on the appearance of an actual giant or a form similar to an Elemental, wreathed in fire, frost, or lightning.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Giant' AND class_name = 'Barbarian' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Giant Power|Barbarian||Giant|BGG|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Giant' AND class_name = 'Barbarian' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Giant''s Havoc|Barbarian||Giant|BGG|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elemental Cleaver', null, 11, 'Barbarian', 'PHB', 'Giant', 'BGG', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Cleaver' AND class_name = 'Barbarian' AND level = 6), 0, '{@i 6th-Level Path of the Giant Feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Cleaver' AND class_name = 'Barbarian' AND level = 6), 1, 'Your bond with the elemental might of giants grows, and you learn to infuse weapons with primordial energy.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Cleaver' AND class_name = 'Barbarian' AND level = 6), 2, 'When you enter your rage, you can choose one weapon that you are holding and infuse it with one of the following damage types: acid, cold, fire, thunder, or lightning. While you wield the infused weapon during your rage, the weapon''s damage type changes to the chosen type, it deals an extra {@damage 1d6} damage of the chosen type when it hits, and it gains the thrown property, with a normal range of 20 feet and a long range of 60 feet. If you throw the weapon, it reappears in your hand the instant after it hits or misses a target. The infused weapon''s benefits are suppressed while a creature other than you wields it.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Cleaver' AND class_name = 'Barbarian' AND level = 6), 3, 'While raging and holding the infused weapon, you can use a bonus action to change the infused weapon''s current damage type to another one from the damage type options above.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mighty Impel', null, 12, 'Barbarian', 'PHB', 'Giant', 'BGG', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mighty Impel' AND class_name = 'Barbarian' AND level = 10), 0, '{@i 10th-Level Path of the Giant Feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mighty Impel' AND class_name = 'Barbarian' AND level = 10), 1, 'Your connection to giant strength allows you to hurl both allies and enemies on the battlefield. As a bonus action while raging, you can choose one Medium or smaller creature within your reach and move it to an unoccupied space you can see within 30 feet of yourself. An unwilling creature must succeed on a Strength saving throw (DC equals 8 + your proficiency bonus + your Strength modifier) to avoid the effect.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mighty Impel' AND class_name = 'Barbarian' AND level = 10), 2, 'If, at the end of this movement, the thrown creature isn''t on a surface or liquid that can support it, the creature falls, taking damage as normal and landing {@condition prone}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Demiurgic Colossus', null, 12, 'Barbarian', 'PHB', 'Giant', 'BGG', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Demiurgic Colossus' AND class_name = 'Barbarian' AND level = 14), 0, '{@i 14th-Level Path of the Giant Feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Demiurgic Colossus' AND class_name = 'Barbarian' AND level = 14), 1, 'The primordial power of your rage intensifies. When you rage, your reach increases by 10 feet, your size can increase to Large or Huge (your choice), and you can use your Mighty Impel to move creatures that are Large or smaller.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Demiurgic Colossus' AND class_name = 'Barbarian' AND level = 14), 2, 'In addition, the extra damage dealt by your Elemental Cleaver feature increases to {@dice 2d6}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Berserker', 1, 49, 'Barbarian', 'PHB', 'Berserker', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Berserker' AND class_name = 'Barbarian' AND level = 3), 0, 'For some barbarians, rage is a means to an end—that end being violence. The Path of the Berserker is a path of untrammeled fury, slick with blood. As you enter the berserker''s rage, you thrill in the chaos of battle, heedless of your own health or well-being.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Berserker' AND class_name = 'Barbarian' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Frenzy|Barbarian||Berserker||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Frenzy', 1, 49, 'Barbarian', 'PHB', 'Berserker', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Frenzy' AND class_name = 'Barbarian' AND level = 3), 0, 'Starting when you choose this path at 3rd level, you can go into a frenzy when you rage. If you do so, for the duration of your rage you can make a single melee weapon attack as a bonus action on each of your turns after this one. When your rage ends, you suffer one level of {@condition exhaustion}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mindless Rage', 1, 49, 'Barbarian', 'PHB', 'Berserker', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mindless Rage' AND class_name = 'Barbarian' AND level = 6), 0, 'Beginning at 6th level, you can''t be {@condition charmed} or {@condition frightened} while raging. If you are {@condition charmed} or {@condition frightened} when you enter your rage, the effect is suspended for the duration of the rage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Intimidating Presence', 1, 49, 'Barbarian', 'PHB', 'Berserker', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Intimidating Presence' AND class_name = 'Barbarian' AND level = 10), 0, 'Beginning at 10th level, you can use your action to frighten someone with your menacing presence. When you do so, choose one creature that you can see within 30 feet of you. If the creature can see or hear you, it must succeed on a Wisdom saving throw (DC equal to 8 + your proficiency bonus + your Charisma modifier) or be {@condition frightened} of you until the end of your next turn. On subsequent turns, you can use your action to extend the duration of this effect on the {@condition frightened} creature until the end of your next turn. This effect ends if the creature ends its turn out of line of sight or more than 60 feet away from you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Intimidating Presence' AND class_name = 'Barbarian' AND level = 10), 1, 'If the creature succeeds on its saving throw, you can''t use this feature on that creature again for 24 hours.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Retaliation', 1, 49, 'Barbarian', 'PHB', 'Berserker', 'PHB', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Retaliation' AND class_name = 'Barbarian' AND level = 14), 0, 'Starting at 14th level, when you take damage from a creature that is within 5 feet of you, you can use your reaction to make a melee weapon attack against that creature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bear', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bear' AND class_name = 'Barbarian' AND level = 3), 0, 'While raging, you have resistance to all damage except psychic damage. The spirit of the bear makes you tough enough to stand up to any punishment.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eagle', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eagle' AND class_name = 'Barbarian' AND level = 3), 0, 'While you''re raging and aren''t wearing heavy armor, other creatures have disadvantage on opportunity attack rolls against you, and you can use the {@action Dash} action as a bonus action on your turn. The spirit of the eagle makes you into a predator who can weave through the fray with ease.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elk', null, 122, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elk' AND class_name = 'Barbarian' AND level = 3), 0, 'While you''re raging and aren''t wearing heavy armor, your walking speed increases by 15 feet. The spirit of the elk makes you extraordinarily swift.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Totem Warrior', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Totem Warrior' AND class_name = 'Barbarian' AND level = 3), 0, 'The Path of the Totem Warrior is a spiritual journey, as the barbarian accepts a spirit animal as guide, protector, and inspiration. In battle, your totem spirit fills you with supernatural might, adding magical fuel to your barbarian rage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Totem Warrior' AND class_name = 'Barbarian' AND level = 3), 1, 'Most barbarian tribes consider a totem animal to be kin to a particular clan. In such cases, it is unusual for an individual to have more than one totem animal spirit, though exceptions exist.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Totem Warrior' AND class_name = 'Barbarian' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Spirit Seeker|Barbarian||Totem Warrior||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Totem Warrior' AND class_name = 'Barbarian' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Totem Spirit|Barbarian||Totem Warrior||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tiger', null, 122, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tiger' AND class_name = 'Barbarian' AND level = 3), 0, 'While raging, you can add 10 feet to your long jump distance and 3 feet to your high jump distance. The spirit of the tiger empowers your leaps.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wolf', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wolf' AND class_name = 'Barbarian' AND level = 3), 0, 'While you''re raging, your friends have advantage on melee attack rolls against any creature within 5 feet of you that is hostile to you. The spirit of the wolf makes you a leader of hunters.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spirit Seeker', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Seeker' AND class_name = 'Barbarian' AND level = 3), 0, 'Yours is a path that seeks attunement with the natural world, giving you a kinship with beasts. At 3rd level when you adopt this path, you gain the ability to cast the {@spell beast sense} and {@spell speak with animals} spells, but only as rituals, as described in {@book chapter 10|PHB|10|rituals}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Totem Spirit', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Totem Spirit' AND class_name = 'Barbarian' AND level = 3), 0, 'At 3rd level, when you adopt this path, you choose a totem spirit and gain its feature. You must make or acquire a physical totem object—an amulet or similar adornment—that incorporates fur or feathers, claws, teeth, or bones of the totem animal. At your option, you also gain minor physical attributes that are reminiscent of your totem spirit. For example, if you have a bear totem spirit, you might be unusually hairy and thick-skinned, or if your totem is the eagle, your eyes turn bright yellow.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Totem Spirit' AND class_name = 'Barbarian' AND level = 3), 1, 'Your totem animal might be an animal related to those listed here but more appropriate to your homeland. For example, you could choose a hawk or vulture in place of an eagle.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Totem Spirit' AND class_name = 'Barbarian' AND level = 3), 2, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Bear|Barbarian||Totem Warrior||3"},{"type":"refSubclassFeature","subclassFeature":"Eagle|Barbarian||Totem Warrior||3"},{"type":"refSubclassFeature","subclassFeature":"Elk|Barbarian|PHB|Totem Warrior||3|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Tiger|Barbarian|PHB|Totem Warrior||3|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Wolf|Barbarian||Totem Warrior||3"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refSubclassFeature","subclassFeature":"Bear|Barbarian||Totem Warrior||3"},{"type":"refSubclassFeature","subclassFeature":"Eagle|Barbarian||Totem Warrior||3"},{"type":"refSubclassFeature","subclassFeature":"Elk|Barbarian|PHB|Totem Warrior||3|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Tiger|Barbarian|PHB|Totem Warrior||3|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Wolf|Barbarian||Totem Warrior||3"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bear', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bear' AND class_name = 'Barbarian' AND level = 6), 0, 'You gain the might of a bear. Your carrying capacity (including maximum load and maximum lift) is doubled, and you have advantage on Strength checks made to push, pull, lift, or break objects.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eagle', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eagle' AND class_name = 'Barbarian' AND level = 6), 0, 'You gain the eyesight of an eagle. You can see up to 1 mile away with no difficulty, able to discern even fine details as though looking at something no more than 100 feet away from you. Additionally, dim light doesn''t impose disadvantage on your Wisdom ({@skill Perception}) checks.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elk', null, 122, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elk' AND class_name = 'Barbarian' AND level = 6), 0, 'Whether mounted or on foot, your travel pace is doubled, as is the travel pace of up to ten companions while they''re within 60 feet of you and you''re not {@condition incapacitated}. The elk spirit helps you roam far and fast.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tiger', null, 122, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tiger' AND class_name = 'Barbarian' AND level = 6), 0, 'You gain proficiency in two skills from the following list: {@skill Athletics}, {@skill Acrobatics}, {@skill Stealth}, and {@skill Survival}. The cat spirit hones your survival instincts.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wolf', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wolf' AND class_name = 'Barbarian' AND level = 6), 0, 'You gain the hunting sensibilities of a wolf. You can track other creatures while traveling at a fast pace, and you can move stealthily while traveling at a normal pace.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aspect of the Beast', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aspect of the Beast' AND class_name = 'Barbarian' AND level = 6), 0, 'At 6th level, you gain a magical benefit based on the totem animal of your choice. You can choose the same animal you selected at 3rd level or a different one.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Aspect of the Beast' AND class_name = 'Barbarian' AND level = 6), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Bear|Barbarian||Totem Warrior||6"},{"type":"refSubclassFeature","subclassFeature":"Eagle|Barbarian||Totem Warrior||6"},{"type":"refSubclassFeature","subclassFeature":"Elk|Barbarian|PHB|Totem Warrior||6|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Tiger|Barbarian|PHB|Totem Warrior||6|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Wolf|Barbarian||Totem Warrior||6"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refSubclassFeature","subclassFeature":"Bear|Barbarian||Totem Warrior||6"},{"type":"refSubclassFeature","subclassFeature":"Eagle|Barbarian||Totem Warrior||6"},{"type":"refSubclassFeature","subclassFeature":"Elk|Barbarian|PHB|Totem Warrior||6|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Tiger|Barbarian|PHB|Totem Warrior||6|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Wolf|Barbarian||Totem Warrior||6"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spirit Walker', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Walker' AND class_name = 'Barbarian' AND level = 10), 0, 'At 10th level, you can cast the {@spell commune with nature} spell, but only as a ritual. When you do so, a spiritual version of one of the animals you chose for Totem Spirit or Aspect of the Beast appears to you to convey the information you seek.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bear', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bear' AND class_name = 'Barbarian' AND level = 14), 0, 'While you''re raging, any creature within 5 feet of you that''s hostile to you has disadvantage on attack rolls against targets other than you or another character with this feature. An enemy is immune to this effect if it can''t see or hear you or if it can''t be {@condition frightened}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eagle', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eagle' AND class_name = 'Barbarian' AND level = 14), 0, 'While raging, you have a flying speed equal to your current walking speed. This benefit works only in short bursts; you fall if you end your turn in the air and nothing else is holding you aloft.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elk', null, 122, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elk' AND class_name = 'Barbarian' AND level = 14), 0, 'While raging, you can use a bonus action during your move to pass through the space of a Large or smaller creature. That creature must succeed on a Strength saving throw ({@dc 8} + your Strength bonus + your proficiency bonus) or be knocked {@condition prone} and take bludgeoning damage equal to {@damage 1d12} + your Strength modifier.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tiger', null, 122, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tiger' AND class_name = 'Barbarian' AND level = 14), 0, 'While you''re raging, if you move at least 20 feet in a straight line toward a Large or smaller target right before making a melee weapon attack against it, you can use a bonus action to make an additional melee weapon attack against it.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wolf', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wolf' AND class_name = 'Barbarian' AND level = 14), 0, 'While you''re raging, you can use a bonus action on your turn to knock a Large or smaller creature {@condition prone} when you hit it with melee weapon attack.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Totemic Attunement', 1, 50, 'Barbarian', 'PHB', 'Totem Warrior', 'PHB', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Totemic Attunement' AND class_name = 'Barbarian' AND level = 14), 0, 'At 14th level, you gain a magical benefit based on a totem animal of your choice. You can choose the same animal you selected previously or a different one.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Totemic Attunement' AND class_name = 'Barbarian' AND level = 14), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Bear|Barbarian||Totem Warrior||14"},{"type":"refSubclassFeature","subclassFeature":"Eagle|Barbarian||Totem Warrior||14"},{"type":"refSubclassFeature","subclassFeature":"Elk|Barbarian|PHB|Totem Warrior||14|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Tiger|Barbarian|PHB|Totem Warrior||14|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Wolf|Barbarian||Totem Warrior||14"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refSubclassFeature","subclassFeature":"Bear|Barbarian||Totem Warrior||14"},{"type":"refSubclassFeature","subclassFeature":"Eagle|Barbarian||Totem Warrior||14"},{"type":"refSubclassFeature","subclassFeature":"Elk|Barbarian|PHB|Totem Warrior||14|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Tiger|Barbarian|PHB|Totem Warrior||14|SCAG"},{"type":"refSubclassFeature","subclassFeature":"Wolf|Barbarian||Totem Warrior||14"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Battlerager', null, 121, 'Barbarian', 'PHB', 'Battlerager', 'SCAG', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Battlerager' AND class_name = 'Barbarian' AND level = 3), 0, 'Known as Kuldjargh (literally "axe idiot") in Dwarvish, battleragers are dwarf followers of the gods of war and take the Path of the Battlerager. They specialize in wearing bulky, {@item spiked armor|scag} and throwing themselves into combat, striking with their body itself and giving themselves over to the fury of battle.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Battlerager' AND class_name = 'Barbarian' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Restriction—Dwarves Only|Barbarian||Battlerager|SCAG|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Battlerager' AND class_name = 'Barbarian' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Battlerager Armor|Barbarian||Battlerager|SCAG|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Battlerager Armor', null, 121, 'Barbarian', 'PHB', 'Battlerager', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battlerager Armor' AND class_name = 'Barbarian' AND level = 3), 0, 'When you choose this path at 3rd level, you gain the ability to use {@item spiked armor|scag} as a weapon.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battlerager Armor' AND class_name = 'Barbarian' AND level = 3), 1, 'While you are wearing {@item spiked armor|scag} and are raging, you can use a bonus action to make one melee weapon attack with your armor spikes at a target within 5 feet of you. If the attack hits, the spikes deal {@damage 1d4} piercing damage. You use your Strength modifier for the attack and damage rolls.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battlerager Armor' AND class_name = 'Barbarian' AND level = 3), 2, 'Additionally, when you use the {@action Attack} action to grapple a creature, the target takes 3 piercing damage if your grapple check succeeds.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Restriction—Dwarves Only', null, 121, 'Barbarian', 'PHB', 'Battlerager', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Restriction—Dwarves Only' AND class_name = 'Barbarian' AND level = 3), 0, 'Only dwarves can follow the Path of the Battlerager. The battlerager fills a particular niche in dwarven society and culture.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Restriction—Dwarves Only' AND class_name = 'Barbarian' AND level = 3), 1, 'Your DM can lift this restriction to better suit the campaign. The restriction exists for the Forgotten Realms. It might not apply to your DM''s setting or your DM''s version of the Realms.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Reckless Abandon', null, 121, 'Barbarian', 'PHB', 'Battlerager', 'SCAG', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Reckless Abandon' AND class_name = 'Barbarian' AND level = 6), 0, 'Beginning at 6th level, when you use Reckless Attack while raging, you also gain temporary hit points equal to your Constitution modifier (minimum of 1). They vanish if any of them are left when your rage ends.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Battlerager Charge', null, 121, 'Barbarian', 'PHB', 'Battlerager', 'SCAG', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battlerager Charge' AND class_name = 'Barbarian' AND level = 10), 0, 'Beginning at 10th level, you can take the {@action Dash} action as a bonus action while you are raging.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spiked Retribution', null, 121, 'Barbarian', 'PHB', 'Battlerager', 'SCAG', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spiked Retribution' AND class_name = 'Barbarian' AND level = 14), 0, 'Starting at 14th level, when a creature within 5 feet of you hits you with a melee attack, the attacker takes 3 piercing damage if you are raging, aren''t {@condition incapacitated}, and are wearing {@item spiked armor|scag}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Beast', 2, 24, 'Barbarian', 'PHB', 'Beast', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Beast' AND class_name = 'Barbarian' AND level = 3), 0, 'Barbarians who walk the Path of the Beast draw their rage from a bestial spark burning within their souls. That beast bursts forth in the throes of rage, physically transforming the barbarian.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Beast' AND class_name = 'Barbarian' AND level = 3), 1, 'Such a barbarian might be inhabited by a primal spirit or be descended from shape-shifters. You can choose the origin of your feral might or determine it by rolling on the Origin of the Beast table.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Beast' AND class_name = 'Barbarian' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Origin of the Beast', '["d4","Origin"]', '[["1","One of your parents is a lycanthrope, and you''ve inherited some of their curse."],["2","You are descended from an archdruid and inherited the ability to partially change shape."],["3","A fey spirit gifted you with the ability to adopt different bestial aspects."],["4","An ancient animal spirit dwells within you, allowing you to walk this path."]]', '{"type":"table","caption":"Origin of the Beast","colLabels":["d4","Origin"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","One of your parents is a lycanthrope, and you''ve inherited some of their curse."],["2","You are descended from an archdruid and inherited the ability to partially change shape."],["3","A fey spirit gifted you with the ability to adopt different bestial aspects."],["4","An ancient animal spirit dwells within you, allowing you to walk this path."]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Beast' AND class_name = 'Barbarian' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Form of the Beast|Barbarian||Beast|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Form of the Beast', 2, 24, 'Barbarian', 'PHB', 'Beast', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Form of the Beast' AND class_name = 'Barbarian' AND level = 3), 0, '{@i 3rd-level Path of the Beast feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Form of the Beast' AND class_name = 'Barbarian' AND level = 3), 1, 'When you enter your rage, you can transform, revealing the bestial power within you. Until the rage ends, you manifest a natural weapon. It counts as a simple melee weapon for you, and you add your Strength modifier to the attack and damage rolls when you attack with it, as normal.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Form of the Beast' AND class_name = 'Barbarian' AND level = 3), 2, 'You choose the weapon''s form each time you rage:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Form of the Beast' AND class_name = 'Barbarian' AND level = 3), 3, 'list', NULL, '[{"type":"item","name":"Bite","entry":"Your mouth transforms into a bestial muzzle or great mandibles (your choice). It deals {@damage 1d8} piercing damage on a hit. Once on each of your turns when you damage a creature with this bite, you regain a number of hit points equal to your proficiency bonus, provided you have less than half your hit points when you hit."},{"type":"item","name":"Claws","entry":"Each of your hands transforms into a claw, which you can use as a weapon if it''s empty. It deals {@damage 1d6} slashing damage on a hit. Once on each of your turns when you attack with a claw using the {@action Attack} action, you can make one additional claw attack as part of the same action."},{"type":"item","name":"Tail","entry":"You grow a lashing, spiny tail, which deals {@damage 1d8} piercing damage on a hit and has the reach property. If a creature you can see within 10 feet of you hits you with an attack roll, you can use your reaction to swipe your tail and roll a {@dice d8}, applying a bonus to your AC equal to the number rolled, potentially causing the attack to miss you."}]', '[{"type":"item","name":"Bite","entry":"Your mouth transforms into a bestial muzzle or great mandibles (your choice). It deals {@damage 1d8} piercing damage on a hit. Once on each of your turns when you damage a creature with this bite, you regain a number of hit points equal to your proficiency bonus, provided you have less than half your hit points when you hit."},{"type":"item","name":"Claws","entry":"Each of your hands transforms into a claw, which you can use as a weapon if it''s empty. It deals {@damage 1d6} slashing damage on a hit. Once on each of your turns when you attack with a claw using the {@action Attack} action, you can make one additional claw attack as part of the same action."},{"type":"item","name":"Tail","entry":"You grow a lashing, spiny tail, which deals {@damage 1d8} piercing damage on a hit and has the reach property. If a creature you can see within 10 feet of you hits you with an attack roll, you can use your reaction to swipe your tail and roll a {@dice d8}, applying a bonus to your AC equal to the number rolled, potentially causing the attack to miss you."}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Bite","entry":"Your mouth transforms into a bestial muzzle or great mandibles (your choice). It deals {@damage 1d8} piercing damage on a hit. Once on each of your turns when you damage a creature with this bite, you regain a number of hit points equal to your proficiency bonus, provided you have less than half your hit points when you hit."},{"type":"item","name":"Claws","entry":"Each of your hands transforms into a claw, which you can use as a weapon if it''s empty. It deals {@damage 1d6} slashing damage on a hit. Once on each of your turns when you attack with a claw using the {@action Attack} action, you can make one additional claw attack as part of the same action."},{"type":"item","name":"Tail","entry":"You grow a lashing, spiny tail, which deals {@damage 1d8} piercing damage on a hit and has the reach property. If a creature you can see within 10 feet of you hits you with an attack roll, you can use your reaction to swipe your tail and roll a {@dice d8}, applying a bonus to your AC equal to the number rolled, potentially causing the attack to miss you."}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bestial Soul', 2, 24, 'Barbarian', 'PHB', 'Beast', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bestial Soul' AND class_name = 'Barbarian' AND level = 6), 0, '{@i 6th-level Path of the Beast feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bestial Soul' AND class_name = 'Barbarian' AND level = 6), 1, 'The feral power within you increases, causing the natural weapons of your Form of the Beast to count as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bestial Soul' AND class_name = 'Barbarian' AND level = 6), 2, 'You can also alter your form to help you adapt to your surroundings. When you finish a short or long rest, choose one of the following benefits, which lasts until you finish your next short or long rest:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Bestial Soul' AND class_name = 'Barbarian' AND level = 6), 3, 'list', NULL, '["You gain a swimming speed equal to your walking speed, and you can breathe underwater.","You gain a climbing speed equal to your walking speed, and you can climb difficult surfaces, including upside down on ceilings, without needing to make an ability check.","When you jump, you can make a Strength ({@skill Athletics}) check and extend your jump by a number of feet equal to the check''s total. You can make this special check only once per turn."]', '["You gain a swimming speed equal to your walking speed, and you can breathe underwater.","You gain a climbing speed equal to your walking speed, and you can climb difficult surfaces, including upside down on ceilings, without needing to make an ability check.","When you jump, you can make a Strength ({@skill Athletics}) check and extend your jump by a number of feet equal to the check''s total. You can make this special check only once per turn."]', NULL, NULL, NULL, '{"type":"list","items":["You gain a swimming speed equal to your walking speed, and you can breathe underwater.","You gain a climbing speed equal to your walking speed, and you can climb difficult surfaces, including upside down on ceilings, without needing to make an ability check.","When you jump, you can make a Strength ({@skill Athletics}) check and extend your jump by a number of feet equal to the check''s total. You can make this special check only once per turn."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Infectious Fury', 2, 24, 'Barbarian', 'PHB', 'Beast', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Infectious Fury' AND class_name = 'Barbarian' AND level = 10), 0, '{@i 10th-level Path of the Beast feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Infectious Fury' AND class_name = 'Barbarian' AND level = 10), 1, 'When you hit a creature with your natural weapons while you are raging, the beast within you can curse your target with rabid fury. The target must succeed on a Wisdom saving throw (DC equal to 8 + your Constitution modifier + your proficiency bonus) or suffer one of the following effects (your choice):');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Infectious Fury' AND class_name = 'Barbarian' AND level = 10), 2, 'list', NULL, '["The target must use its reaction to make a melee attack against another creature of your choice that you can see.","The target takes {@damage 2d12} psychic damage."]', '["The target must use its reaction to make a melee attack against another creature of your choice that you can see.","The target takes {@damage 2d12} psychic damage."]', NULL, NULL, NULL, '{"type":"list","items":["The target must use its reaction to make a melee attack against another creature of your choice that you can see.","The target takes {@damage 2d12} psychic damage."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Infectious Fury' AND class_name = 'Barbarian' AND level = 10), 3, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Call the Hunt', 2, 24, 'Barbarian', 'PHB', 'Beast', 'TCE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Call the Hunt' AND class_name = 'Barbarian' AND level = 14), 0, '{@i 14th-level Path of the Beast feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Call the Hunt' AND class_name = 'Barbarian' AND level = 14), 1, 'The beast within you grows so powerful that you can spread its ferocity to others and gain resilience from them joining your hunt. When you enter your rage, you can choose a number of other willing creatures you can see within 30 feet of you equal to your Constitution modifier (minimum of one creature).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Call the Hunt' AND class_name = 'Barbarian' AND level = 14), 2, 'You gain 5 temporary hit points for each creature that accepts this feature. Until the rage ends, the chosen creatures can each use the following benefit once on each of their turns: when the creature hits a target with an attack roll and deals damage to it, the creature can roll a {@dice d6} and gain a bonus to the damage equal to the number rolled.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Call the Hunt' AND class_name = 'Barbarian' AND level = 14), 3, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of Wild Magic', 2, 25, 'Barbarian', 'PHB', 'Wild Magic', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of Wild Magic' AND class_name = 'Barbarian' AND level = 3), 0, 'Many places in the multiverse abound with beauty, intense emotion, and rampant magic; the Feywild, the Upper Planes, and other realms of supernatural power radiate with such forces and can profoundly influence people. As folk of deep feeling, barbarians are especially susceptible to these wild influences, with some barbarians being transformed by the magic. These magic-suffused barbarians walk the Path of Wild Magic. Elf, tiefling, aasimar, and genasi barbarians often seek this path, eager to manifest the otherworldly magic of their ancestors.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of Wild Magic' AND class_name = 'Barbarian' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Magic Awareness|Barbarian||Wild Magic|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of Wild Magic' AND class_name = 'Barbarian' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Wild Surge|Barbarian||Wild Magic|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Magic Awareness', 2, 25, 'Barbarian', 'PHB', 'Wild Magic', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic Awareness' AND class_name = 'Barbarian' AND level = 3), 0, '{@i 3rd-level Path of Wild Magic feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic Awareness' AND class_name = 'Barbarian' AND level = 3), 1, 'As an action, you can open your awareness to the presence of concentrated magic. Until the end of your next turn, you know the location of any spell or magic item within 60 feet of you that isn''t behind {@quickref Cover||3||total cover}. When you sense a spell, you learn which school of magic it belongs to.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic Awareness' AND class_name = 'Barbarian' AND level = 3), 2, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wild Surge', 2, 25, 'Barbarian', 'PHB', 'Wild Magic', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Surge' AND class_name = 'Barbarian' AND level = 3), 0, '{@i 3rd-level Path of Wild Magic feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Surge' AND class_name = 'Barbarian' AND level = 3), 1, 'The magical energy roiling inside you sometimes erupts from you. When you enter your rage, roll on the Wild Magic table to determine the magical effect produced.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Surge' AND class_name = 'Barbarian' AND level = 3), 2, 'If the effect requires a saving throw, the DC equals 8 + your proficiency bonus + your Constitution modifier.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Wild Surge' AND class_name = 'Barbarian' AND level = 3), 3, 'table', NULL, NULL, NULL, 'Wild Magic', '["d8","Magical Effect"]', '[["1","Shadowy tendrils lash around you. Each creature of your choice that you can see within 30 feet of you must succeed on a Constitution saving throw or take {@damage 1d12} necrotic damage. You also gain {@dice 1d12} temporary hit points."],["2","You teleport up to 30 feet to an unoccupied space you can see. Until your rage ends, you can use this effect again on each of your turns as a bonus action."],["3","An intangible spirit, which looks like a {@creature flumph} or a {@creature pixie} (your choice), appears within 5 feet of one creature of your choice that you can see within 30 feet of you. At the end of the current turn, the spirit explodes, and each creature within 5 feet of it must succeed on a Dexterity saving throw or take {@damage 1d6} force damage. Until your rage ends, you can use this effect again, summoning another spirit, on each of your turns as a bonus action."],["4","Magic infuses one weapon of your choice that you are holding. Until your rage ends, the weapon''s damage type changes to force, and it gains the light and thrown properties, with a normal range of 20 feet and a long range of 60 feet. If the weapon leaves your hand, the weapon reappears in your hand at the end of the current turn."],["5","Whenever a creature hits you with an attack roll before your rage ends, that creature takes {@damage 1d6} force damage, as magic lashes out in retribution."],["6","Until your rage ends, you are surrounded by multi colored, protective lights; you gain a +1 bonus to AC, and while within 10 feet of you, your allies gain the same bonus."],["7","Flowers and vines temporarily grow around you. Until your rage ends, the ground within 15 feet of you is {@quickref difficult terrain||3} for your enemies."],["8","A bolt of light shoots from your chest. Another creature of your choice that you can see within 30 feet of you must succeed on a Constitution saving throw or take {@damage 1d6} radiant damage and be {@condition blinded} until the start of your next turn. Until your rage ends, you can use this effect again on each of your turns as a bonus action."]]', '{"type":"table","caption":"Wild Magic","colLabels":["d8","Magical Effect"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","Shadowy tendrils lash around you. Each creature of your choice that you can see within 30 feet of you must succeed on a Constitution saving throw or take {@damage 1d12} necrotic damage. You also gain {@dice 1d12} temporary hit points."],["2","You teleport up to 30 feet to an unoccupied space you can see. Until your rage ends, you can use this effect again on each of your turns as a bonus action."],["3","An intangible spirit, which looks like a {@creature flumph} or a {@creature pixie} (your choice), appears within 5 feet of one creature of your choice that you can see within 30 feet of you. At the end of the current turn, the spirit explodes, and each creature within 5 feet of it must succeed on a Dexterity saving throw or take {@damage 1d6} force damage. Until your rage ends, you can use this effect again, summoning another spirit, on each of your turns as a bonus action."],["4","Magic infuses one weapon of your choice that you are holding. Until your rage ends, the weapon''s damage type changes to force, and it gains the light and thrown properties, with a normal range of 20 feet and a long range of 60 feet. If the weapon leaves your hand, the weapon reappears in your hand at the end of the current turn."],["5","Whenever a creature hits you with an attack roll before your rage ends, that creature takes {@damage 1d6} force damage, as magic lashes out in retribution."],["6","Until your rage ends, you are surrounded by multi colored, protective lights; you gain a +1 bonus to AC, and while within 10 feet of you, your allies gain the same bonus."],["7","Flowers and vines temporarily grow around you. Until your rage ends, the ground within 15 feet of you is {@quickref difficult terrain||3} for your enemies."],["8","A bolt of light shoots from your chest. Another creature of your choice that you can see within 30 feet of you must succeed on a Constitution saving throw or take {@damage 1d6} radiant damage and be {@condition blinded} until the start of your next turn. Until your rage ends, you can use this effect again on each of your turns as a bonus action."]],"data":{"genTables":{"tableInclude":true}}}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bolstering Magic', 2, 25, 'Barbarian', 'PHB', 'Wild Magic', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bolstering Magic' AND class_name = 'Barbarian' AND level = 6), 0, '{@i 6th-level Path of Wild Magic feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bolstering Magic' AND class_name = 'Barbarian' AND level = 6), 1, 'You can harness your wild magic to bolster yourself or a companion. As an action, you can touch one creature (which can be yourself) and confer one of the following benefits of your choice to that creature:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Bolstering Magic' AND class_name = 'Barbarian' AND level = 6), 2, 'list', NULL, '["For 10 minutes, the creature can roll a {@dice d3} whenever making an attack roll or an ability check and add the number rolled to the {@dice d20} roll.","Roll a {@dice d3}. The creature regains one expended spell slot, the level of which equals the number rolled or lower (the creature''s choice). Once a creature receives this benefit, that creature can''t receive it again until after a long rest."]', '["For 10 minutes, the creature can roll a {@dice d3} whenever making an attack roll or an ability check and add the number rolled to the {@dice d20} roll.","Roll a {@dice d3}. The creature regains one expended spell slot, the level of which equals the number rolled or lower (the creature''s choice). Once a creature receives this benefit, that creature can''t receive it again until after a long rest."]', NULL, NULL, NULL, '{"type":"list","items":["For 10 minutes, the creature can roll a {@dice d3} whenever making an attack roll or an ability check and add the number rolled to the {@dice d20} roll.","Roll a {@dice d3}. The creature regains one expended spell slot, the level of which equals the number rolled or lower (the creature''s choice). Once a creature receives this benefit, that creature can''t receive it again until after a long rest."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bolstering Magic' AND class_name = 'Barbarian' AND level = 6), 3, 'You can take this action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unstable Backlash', 2, 25, 'Barbarian', 'PHB', 'Wild Magic', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unstable Backlash' AND class_name = 'Barbarian' AND level = 10), 0, '{@i 10th-level Path of Wild Magic feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unstable Backlash' AND class_name = 'Barbarian' AND level = 10), 1, 'When you are imperiled during your rage, the magic within you can lash out; immediately after you take damage or fail a saving throw while raging, you can use your reaction to roll on the Wild Magic table and immediately produce the effect rolled. This effect replaces your current Wild Magic effect.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Controlled Surge', 2, 25, 'Barbarian', 'PHB', 'Wild Magic', 'TCE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Controlled Surge' AND class_name = 'Barbarian' AND level = 14), 0, '{@i 14th-level Path of Wild Magic feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Controlled Surge' AND class_name = 'Barbarian' AND level = 14), 1, 'Whenever you roll on the Wild Magic table, you can roll the die twice and choose which of the two effects to unleash. If you roll the same number on both dice, you can ignore the number and choose any effect on the table.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Juggernaut', null, 165, 'Barbarian', 'PHB', 'Juggernaut', 'TDCSR', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Juggernaut' AND class_name = 'Barbarian' AND level = 3), 0, 'Barbarians who follow the Path of the Juggernaut stand so resolutely that none can deter them, and they swing their weapons with such force that all who stand against them are flung aside. In might and in spirit, juggernauts are immovable object and unstoppable force all at once.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Juggernaut' AND class_name = 'Barbarian' AND level = 3), 1, 'Juggernaut barbarians can be found all over Tal''Dorei, and are common among the goliath warriors of the {@book Rivermaw herd|TDCSR|3|Rivermaw Herd} that wanders the {@book Dividing Plains|TDCSR|3|Dividing Plains}. Some {@book dwarves|TDCSR|4|dwarves} and humanoid survivalists of the {@book Cliffkeep Mountains|TDCSR|3|Cliffkeep Mountains} adopt this fighting style as an extension of their rugged determinism. And a number of stalwart juggernauts hail from the jungles of the {@book Rifenmist Peninsula|TDCSR|3|Rifenmist Peninsula}, having cast off the oppressive yoke of the {@book Iron Authority|TDCSR|3|Tz''Arrm, Helm of the Emperor}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Juggernaut' AND class_name = 'Barbarian' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Thunderous Blows|Barbarian|PHB|Juggernaut|TDCSR|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Juggernaut' AND class_name = 'Barbarian' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Spirit of the Mountain|Barbarian|PHB|Juggernaut|TDCSR|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spirit of the Mountain', null, 166, 'Barbarian', 'PHB', 'Juggernaut', 'TDCSR', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit of the Mountain' AND class_name = 'Barbarian' AND level = 3), 0, 'At 3rd level, you harness your fury to anchor your feet to the ground, becoming a bulwark of strength. While you are raging, you can''t be knocked {@condition prone} or moved along the ground against your will.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Thunderous Blows', null, 165, 'Barbarian', 'PHB', 'Juggernaut', 'TDCSR', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Thunderous Blows' AND class_name = 'Barbarian' AND level = 3), 0, 'Starting when you choose this path at 3rd level, your rage instills you with the strength to shove and smash your way through your foes, making any battlefield your domain. When you hit a creature with a melee attack while you''re raging, you can push that creature up to 5 feet away from you in a direction of your choice. A creature that is Huge or larger makes a Strength {@quickref saving throws|PHB|2|1|saving throw} with a DC equal to 8 + your proficiency bonus + your Strength modifier. On a success, the creature is not pushed.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Thunderous Blows' AND class_name = 'Barbarian' AND level = 3), 1, 'inset', 'Rules Tip: Forced Movement', '["Usually when one creature moves out of a hostile creature''s reach, the hostile creature can use its reaction to make an {@action opportunity attack}. However, forced movement—such as being pushed by a Path of the Juggernaut barbarian''s {@subclassFeature Thunderous Blows|Barbarian|PHB|Juggernaut|TDCSR|3} feature—doesn''t provoke {@action opportunity attack|PHB|opportunity attacks}.","Likewise, a juggernaut barbarian''s {@subclassFeature Hurricane Strike|Barbarian|PHB|Juggernaut|TDCSR|10} feature allows an ally to make a melee weapon attack as a reaction only if the foe ends its forced movement within 5 feet of the ally. If a foe is pushed through other spaces within 5 feet of your allies, those allies can''t make normal {@action opportunity attack|PHB|opportunity attacks} against the foe."]', NULL, NULL, NULL, NULL, '{"type":"inset","name":"Rules Tip: Forced Movement","page":166,"entries":["Usually when one creature moves out of a hostile creature''s reach, the hostile creature can use its reaction to make an {@action opportunity attack}. However, forced movement—such as being pushed by a Path of the Juggernaut barbarian''s {@subclassFeature Thunderous Blows|Barbarian|PHB|Juggernaut|TDCSR|3} feature—doesn''t provoke {@action opportunity attack|PHB|opportunity attacks}.","Likewise, a juggernaut barbarian''s {@subclassFeature Hurricane Strike|Barbarian|PHB|Juggernaut|TDCSR|10} feature allows an ally to make a melee weapon attack as a reaction only if the foe ends its forced movement within 5 feet of the ally. If a foe is pushed through other spaces within 5 feet of your allies, those allies can''t make normal {@action opportunity attack|PHB|opportunity attacks} against the foe."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Demolishing Might', null, 166, 'Barbarian', 'PHB', 'Juggernaut', 'TDCSR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Demolishing Might' AND class_name = 'Barbarian' AND level = 6), 0, 'Starting at 6th level, your melee weapon attacks deal an extra {@damage 1d8} damage to constructs, and deal double damage to objects and structures.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Resolute Stance', null, 166, 'Barbarian', 'PHB', 'Juggernaut', 'TDCSR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Resolute Stance' AND class_name = 'Barbarian' AND level = 6), 0, 'Also at 6th level, you can temporarily refocus your combat ability to make yourself a bulwark of defense. At the start of your turn (no action required), you can assume a defensive stance that lasts until the start of your next turn. While in this stance, you can''t be {@condition grappled}, attack rolls against you have {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage}, and your weapon attacks are made with {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hurricane Strike', null, 166, 'Barbarian', 'PHB', 'Juggernaut', 'TDCSR', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hurricane Strike' AND class_name = 'Barbarian' AND level = 10), 0, 'Starting at 10th level, your blows can hurl foes through the air and into the attacks of your allies. As a reaction when you push a creature at least 5 feet, you can then leap into an unoccupied space next to the creature. If you do so, the creature must succeed on a Strength {@quickref saving throws|PHB|2|1|saving throw} with a DC equal to 8 + your proficiency bonus + your Strength modifier or be knocked {@condition prone}. This leap costs no movement and does not provoke {@action opportunity attack|PHB|opportunity attacks}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hurricane Strike' AND class_name = 'Barbarian' AND level = 10), 1, 'Additionally, whenever you push a creature into a space within 5 feet of one of your allies, the ally can use its reaction to make a melee weapon attack against that creature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Thunderous Blows (10th Level)', null, 165, 'Barbarian', 'PHB', 'Juggernaut', 'TDCSR', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Thunderous Blows (10th Level)' AND class_name = 'Barbarian' AND level = 10), 0, 'Starting at 10th level, you can push a creature up to 10 feet when you hit it with a melee attack while you''re raging.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unstoppable', null, 166, 'Barbarian', 'PHB', 'Juggernaut', 'TDCSR', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unstoppable' AND class_name = 'Barbarian' AND level = 14), 0, 'At 14th level, your fury in battle makes you unstoppable. While you''re raging, your speed cannot be reduced, and you are immune to the {@condition frightened}, {@condition paralyzed}, {@condition prone}, and {@condition stunned} conditions.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unstoppable' AND class_name = 'Barbarian' AND level = 14), 1, 'If you are {@condition frightened}, {@condition paralyzed}, or {@condition stunned}, you can still use a bonus action to enter a rage (even if you can''t otherwise take actions). You aren''t affected by any of these conditions while you''re raging.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Ancestral Guardian', 3, 9, 'Barbarian', 'PHB', 'Ancestral Guardian', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Ancestral Guardian' AND class_name = 'Barbarian' AND level = 3), 0, 'Some barbarians hail from cultures that revere their ancestors. These tribes teach that the warriors of the past linger in the world as mighty spirits, who can guide and protect the living. When a barbarian who follows this path rages, the barbarian contacts the spirit world and calls on these guardian spirits for aid.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Ancestral Guardian' AND class_name = 'Barbarian' AND level = 3), 1, 'Barbarians who draw on their ancestral guardians can better fight to protect their tribes and their allies. In order to cement ties to their ancestral guardians, barbarians who follow this path cover themselves in elaborate tattoos that celebrate their ancestors'' deeds. These tattoos tell sagas of victories against terrible monsters and other fearsome rivals.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Ancestral Guardian' AND class_name = 'Barbarian' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Ancestral Protectors|Barbarian||Ancestral Guardian|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ancestral Protectors', 3, 9, 'Barbarian', 'PHB', 'Ancestral Guardian', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ancestral Protectors' AND class_name = 'Barbarian' AND level = 3), 0, 'Starting when you choose this path at 3rd level, spectral warriors appear when you enter your rage. While you''re raging, the first creature you hit with an attack on your turn becomes the target of the warriors, which hinder its attacks. Until the start of your next turn, that target has disadvantage on any attack roll that isn''t against you, and when the target hits a creature other than you with an attack, that creature has resistance to the damage dealt by the attack. The effect on the target ends early if your rage ends.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spirit Shield', 3, 9, 'Barbarian', 'PHB', 'Ancestral Guardian', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Shield' AND class_name = 'Barbarian' AND level = 6), 0, 'Beginning at 6th level, the guardian spirits that aid you can provide supernatural protection to those you defend. If you are raging and another creature you can see within 30 feet of you takes damage, you can use your reaction to reduce that damage by {@dice 2d6}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Shield' AND class_name = 'Barbarian' AND level = 6), 1, 'When you reach certain levels in this class, you can reduce the damage by more: by {@dice 3d6} at 10th level and by {@dice 4d6} at 14th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Consult the Spirits', 3, 9, 'Barbarian', 'PHB', 'Ancestral Guardian', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Consult the Spirits' AND class_name = 'Barbarian' AND level = 10), 0, 'At 10th level, you gain the ability to consult with your ancestral spirits. When you do so, you cast the {@spell augury} or {@spell clairvoyance} spell, without using a spell slot or material components. Rather than creating a spherical sensor, this use of {@spell clairvoyance} invisibly summons one of your ancestral spirits to the chosen location. Wisdom is your spellcasting ability for these spells.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Consult the Spirits' AND class_name = 'Barbarian' AND level = 10), 1, 'After you cast either spell in this way, you can''t use this feature again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Vengeful Ancestors', 3, 9, 'Barbarian', 'PHB', 'Ancestral Guardian', 'XGE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vengeful Ancestors' AND class_name = 'Barbarian' AND level = 14), 0, 'At 14th level, your ancestral spirits grow powerful enough to retaliate. When you use your Spirit Shield to reduce the damage of an attack, the attacker takes an amount of force damage equal to the damage that your Spirit Shield prevents.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Desert', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Desert' AND class_name = 'Barbarian' AND level = 3), 0, 'When this effect is activated, all other creatures in your aura take 2 fire damage each. The damage increases when you reach certain levels in this class, increasing to 3 at 5th level, 4 at 10th level, 5 at 15th level, and 6 at 20th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Storm Herald', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Storm Herald' AND class_name = 'Barbarian' AND level = 3), 0, 'All barbarians harbor a fury within. Their rage grants them superior strength, durability, and speed. Barbarians who follow the Path of the Storm Herald learn to transform that rage into a mantle of primal magic, which swirls around them. When in a fury, a barbarian of this path taps into the forces of nature to create powerful magical effects.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Storm Herald' AND class_name = 'Barbarian' AND level = 3), 1, 'Storm heralds are typically elite champions who train alongside druids, rangers, and others sworn to protect nature. Other storm heralds hone their craft in lodges in regions wracked by storms, in the frozen reaches at the world''s end, or deep in the hottest deserts.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Storm Herald' AND class_name = 'Barbarian' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Storm Aura|Barbarian||Storm Herald|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sea', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sea' AND class_name = 'Barbarian' AND level = 3), 0, 'When this effect is activated, you can choose one other creature you can see in your aura. The target must make a Dexterity saving throw. The target takes {@damage 1d6} lightning damage on a failed save, or half as much damage on a successful one. The damage increases when you reach certain levels in this class, increasing to {@damage 2d6} at 10th level, {@damage 3d6} at 15th level, and {@damage 4d6} at 20th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tundra', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tundra' AND class_name = 'Barbarian' AND level = 3), 0, 'When this effect is activated, each creature of your choice in your aura gains 2 temporary hit points, as icy spirits inure it to suffering. The temporary hit points increase when you reach certain levels in this class, increasing to 3 at 5th level, 4 at 10th level, 5 at 15th level, and 6 at 20th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Storm Aura', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Storm Aura' AND class_name = 'Barbarian' AND level = 3), 0, 'Starting at 3rd level, you emanate a stormy, magical aura while you rage. The aura extends 10 feet from you in every direction, but not through {@quickref Cover||3||total cover}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Storm Aura' AND class_name = 'Barbarian' AND level = 3), 1, 'Your aura has an effect that activates when you enter your rage, and you can activate the effect again on each of your turns as a bonus action. Choose desert, sea, or tundra. Your aura''s effect depends on that chosen environment, as detailed below. You can change your environment choice whenever you gain a level in this class.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Storm Aura' AND class_name = 'Barbarian' AND level = 3), 2, 'If your aura''s effects require a saving throw, the DC equals 8 + your proficiency bonus + your Constitution modifier.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Storm Aura' AND class_name = 'Barbarian' AND level = 3), 3, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Desert|Barbarian|PHB|Storm Herald|XGE|3"},{"type":"refSubclassFeature","subclassFeature":"Sea|Barbarian|PHB|Storm Herald|XGE|3"},{"type":"refSubclassFeature","subclassFeature":"Tundra|Barbarian|PHB|Storm Herald|XGE|3"}]', NULL, NULL, NULL, NULL, '{"type":"options","entries":[{"type":"refSubclassFeature","subclassFeature":"Desert|Barbarian|PHB|Storm Herald|XGE|3"},{"type":"refSubclassFeature","subclassFeature":"Sea|Barbarian|PHB|Storm Herald|XGE|3"},{"type":"refSubclassFeature","subclassFeature":"Tundra|Barbarian|PHB|Storm Herald|XGE|3"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Desert', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Desert' AND class_name = 'Barbarian' AND level = 6), 0, 'You gain resistance to fire damage, and you don''t suffer the effects of extreme heat, as described in the Dungeon Master''s Guide. Moreover, as an action, you can touch a flammable object that isn''t being worn or carried by anyone else and set it on fire.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sea', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sea' AND class_name = 'Barbarian' AND level = 6), 0, 'You gain resistance to lightning damage, and you can breathe underwater. You also gain a swimming speed of 30 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tundra', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tundra' AND class_name = 'Barbarian' AND level = 6), 0, 'You gain resistance to cold damage, and you don''t suffer the effects of extreme cold, as described in the Dungeon Master''s Guide. Moreover, as an action, you can touch water and turn a 5-foot cube of it into ice, which melts after 1 minute. This action fails if a creature is in the cube.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Storm Soul', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Storm Soul' AND class_name = 'Barbarian' AND level = 6), 0, 'At 6th level, the storm grants you benefits even when your aura isn''t active. The benefits are based on the environment you chose for your Storm Aura.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Storm Soul' AND class_name = 'Barbarian' AND level = 6), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Desert|Barbarian|PHB|Storm Herald|XGE|6"},{"type":"refSubclassFeature","subclassFeature":"Sea|Barbarian|PHB|Storm Herald|XGE|6"},{"type":"refSubclassFeature","subclassFeature":"Tundra|Barbarian|PHB|Storm Herald|XGE|6"}]', NULL, NULL, NULL, NULL, '{"type":"options","entries":[{"type":"refSubclassFeature","subclassFeature":"Desert|Barbarian|PHB|Storm Herald|XGE|6"},{"type":"refSubclassFeature","subclassFeature":"Sea|Barbarian|PHB|Storm Herald|XGE|6"},{"type":"refSubclassFeature","subclassFeature":"Tundra|Barbarian|PHB|Storm Herald|XGE|6"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Shielding Storm', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Shielding Storm' AND class_name = 'Barbarian' AND level = 10), 0, 'At 10th level, you learn to use your mastery of the storm to protect others. Each creature of your choice has the damage resistance you gained from the Storm Soul feature while the creature is in your Storm Aura.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Desert', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Desert' AND class_name = 'Barbarian' AND level = 14), 0, 'Immediately after a creature in your aura hits you with an attack, you can use your reaction to force that creature to make a Dexterity saving throw. On a failed save, the creature takes fire damage equal to half your barbarian level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sea', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sea' AND class_name = 'Barbarian' AND level = 14), 0, 'When you hit a creature in your aura with an attack, you can use your reaction to force that creature to make a Strength saving throw. On a failed save, the creature is knocked {@condition prone}, as if struck by a wave.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tundra', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tundra' AND class_name = 'Barbarian' AND level = 14), 0, 'Whenever the effect of your Storm Aura is activated, you can choose one creature you can see in the aura. That creature must succeed on a Strength saving throw, or its speed is reduced to 0 until the start of your next turn, as magical frost covers it.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Raging Storm', 3, 10, 'Barbarian', 'PHB', 'Storm Herald', 'XGE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Raging Storm' AND class_name = 'Barbarian' AND level = 14), 0, 'At 14th level, the power of the storm you channel grows mightier, lashing out at your foes. The effect is based on the environment you chose for your Storm Aura.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Raging Storm' AND class_name = 'Barbarian' AND level = 14), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Desert|Barbarian|PHB|Storm Herald|XGE|14"},{"type":"refSubclassFeature","subclassFeature":"Sea|Barbarian|PHB|Storm Herald|XGE|14"},{"type":"refSubclassFeature","subclassFeature":"Tundra|Barbarian|PHB|Storm Herald|XGE|14"}]', NULL, NULL, NULL, NULL, '{"type":"options","entries":[{"type":"refSubclassFeature","subclassFeature":"Desert|Barbarian|PHB|Storm Herald|XGE|14"},{"type":"refSubclassFeature","subclassFeature":"Sea|Barbarian|PHB|Storm Herald|XGE|14"},{"type":"refSubclassFeature","subclassFeature":"Tundra|Barbarian|PHB|Storm Herald|XGE|14"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Zealot', 3, 11, 'Barbarian', 'PHB', 'Zealot', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Zealot' AND class_name = 'Barbarian' AND level = 3), 0, 'Some deities inspire their followers to pitch themselves into a ferocious battle fury. These barbarians are zealots—warriors who channel their rage into powerful displays of divine power.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Zealot' AND class_name = 'Barbarian' AND level = 3), 1, 'A variety of gods across the worlds of D&D inspire their followers to embrace this path. Tempus from the Forgotten Realms and Hextor and Erythnul of Greyhawk are all prime examples. In general, the gods who inspire zealots are deities of combat, destruction, and violence. Not all are evil, but few are good.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Zealot' AND class_name = 'Barbarian' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Divine Fury|Barbarian||Zealot|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Zealot' AND class_name = 'Barbarian' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Warrior of the Gods|Barbarian||Zealot|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Fury', 3, 11, 'Barbarian', 'PHB', 'Zealot', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Fury' AND class_name = 'Barbarian' AND level = 3), 0, 'Starting when you choose this path at 3rd level, you can channel divine fury into your weapon strikes. While you''re raging, the first creature you hit on each of your turns with a weapon attack takes extra damage equal to {@damage 1d6} + half your barbarian level. The extra damage is necrotic or radiant; you choose the type of damage when you gain this feature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Warrior of the Gods', 3, 11, 'Barbarian', 'PHB', 'Zealot', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Warrior of the Gods' AND class_name = 'Barbarian' AND level = 3), 0, 'At 3rd level, your soul is marked for endless battle. If a spell, such as {@spell raise dead}, has the sole effect of restoring you to life (but not undeath), the caster doesn''t need material components to cast the spell on you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fanatical Focus', 3, 11, 'Barbarian', 'PHB', 'Zealot', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fanatical Focus' AND class_name = 'Barbarian' AND level = 6), 0, 'Starting at 6th level, the divine power that fuels your rage can protect you. If you fail a saving throw while you''re raging, you can reroll it, and you must use the new roll. You can use this ability only once per rage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Zealous Presence', 3, 11, 'Barbarian', 'PHB', 'Zealot', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Zealous Presence' AND class_name = 'Barbarian' AND level = 10), 0, 'At 10th level, you learn to channel divine power to inspire zealotry in others. As a bonus action, you unleash a battle cry infused with divine energy. Up to ten other creatures of your choice within 60 feet of you that can hear you gain advantage on attack rolls and saving throws until the start of your next turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Zealous Presence' AND class_name = 'Barbarian' AND level = 10), 1, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Rage beyond Death', 3, 11, 'Barbarian', 'PHB', 'Zealot', 'XGE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rage beyond Death' AND class_name = 'Barbarian' AND level = 14), 0, 'Beginning at 14th level, the divine power that fuels your rage allows you to shrug off fatal blows.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rage beyond Death' AND class_name = 'Barbarian' AND level = 14), 1, 'While you''re raging, having 0 hit points doesn''t knock you {@condition unconscious}. You still must make death saving throws, and you suffer the normal effects of taking damage while at 0 hit points. However, if you would die due to failing death saving throws, you don''t die until your rage ends, and you die then only if you still have 0 hit points.');
