INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Bard' AND level = 12), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Song of Rest (d10)', 1, 51, 'Bard', 'PHB', NULL, NULL, 13, null, false, false) ON CONFLICT (name) DO NOTHING;
