INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Intervention', 1, 56, 'Cleric', 'PHB', NULL, NULL, 10, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Intervention' AND class_name = 'Cleric' AND level = 10), 0, 'Beginning at 10th level, you can call on your deity to intervene on your behalf when your need is great.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Intervention' AND class_name = 'Cleric' AND level = 10), 1, 'Imploring your deity''s aid requires you to use your action. Describe the assistance you seek, and roll percentile dice. If you roll a number equal to or lower than your cleric level, your deity intervenes. The DM chooses the nature of the intervention; the effect of any cleric spell or cleric domain spell would be appropriate. If your deity intervenes, you can''t use this feature again for 7 days. Otherwise, you can use it again after you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Intervention' AND class_name = 'Cleric' AND level = 10), 2, 'At 20th level, your call for intervention succeeds automatically, no roll required.');
