INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Cleric' AND level = 19), 0, 'When you reach 19th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Cleric' AND level = 19), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Intervention Improvement', 1, 56, 'Cleric', 'PHB', NULL, NULL, 20, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Intervention Improvement' AND class_name = 'Cleric' AND level = 20), 0, 'At 20th level, your call for intervention succeeds automatically, no roll required.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Death Domain', 6, 96, 'Cleric', 'PHB', 'Death', 'DMG', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Death Domain' AND class_name = 'Cleric' AND level = 1), 0, 'The Death domain is concerned with the forces that cause death, as well as the negative energy that gives rise to undead creatures. Deities such as {@deity Chemosh|Dragonlance}, {@deity Myrkul}, and {@deity Wee Jas|Greyhawk} are patrons of necromancers, death knights, liches, mummy lords, and vampires. Gods of the Death domain also embody murder ({@deity Anubis|Egyptian}, {@deity Bhaal}, and Pyremius), pain ({@deity Iuz|Greyhawk} or {@deity Loviatar}), disease or poison ({@deity Incabulos|Greyhawk}, {@deity Talona}, or {@deity Morgion|Dragonlance}), and the underworld ({@deity Hades|Greek} and {@deity Hel|Norse}).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Death Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, add the listed spells to your spells prepared. They do not count towards your limit.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Death Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Death Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell false life}, {@spell ray of sickness}"],["3rd","{@spell blindness/deafness}, {@spell ray of enfeeblement}"],["5th","{@spell animate dead}, {@spell vampiric touch}"],["7th","{@spell blight}, {@spell death ward}"],["9th","{@spell antilife shell}, {@spell cloudkill}"]]', '{"type":"table","caption":"Death Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell false life}, {@spell ray of sickness}"],["3rd","{@spell blindness/deafness}, {@spell ray of enfeeblement}"],["5th","{@spell animate dead}, {@spell vampiric touch}"],["7th","{@spell blight}, {@spell death ward}"],["9th","{@spell antilife shell}, {@spell cloudkill}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Death Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiency|Cleric||Death|DMG|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Death Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Reaper|Cleric||Death|DMG|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiency', 6, 96, 'Cleric', 'PHB', 'Death', 'DMG', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiency' AND class_name = 'Cleric' AND level = 1), 0, 'When the cleric chooses this domain at 1st level, he or she gains proficiency with martial weapons.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Reaper', 6, 96, 'Cleric', 'PHB', 'Death', 'DMG', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Reaper' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, the cleric learns one {@filter necromancy cantrip|spells|level=0|school=N} of his or her choice from any spell list. When the cleric casts a necromancy cantrip that normally targets only one creature, the spell can instead target two creatures within range and within 5 feet of each other.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Touch of Death', 6, 96, 'Cleric', 'PHB', 'Death', 'DMG', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Touch of Death' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, the cleric can use Channel Divinity to destroy another creature''s life force by touch.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Touch of Death' AND class_name = 'Cleric' AND level = 2), 1, 'When the cleric hits a creature with a melee attack, the cleric can use Channel Divinity to deal extra necrotic damage to the target. The damage equals 5 + twice his or her cleric level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Inescapable Destruction', 6, 96, 'Cleric', 'PHB', 'Death', 'DMG', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Inescapable Destruction' AND class_name = 'Cleric' AND level = 6), 0, 'Starting at 6th level, the cleric''s ability to channel negative energy becomes more potent. Necrotic damage dealt by the character''s cleric spells and Channel Divinity options ignores resistance to necrotic damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Death', 'DMG', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 6, 96, 'Cleric', 'PHB', 'Death', 'DMG', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, the cleric gains the ability to infuse his or her weapon strikes with necrotic energy. Once on each of the cleric''s turns when he or she hits a creature with a weapon attack, the cleric can cause the attack to deal an extra {@damage 1d8} necrotic damage to the target. When the cleric reaches 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Improved Reaper', 6, 96, 'Cleric', 'PHB', 'Death', 'DMG', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Improved Reaper' AND class_name = 'Cleric' AND level = 17), 0, 'Starting at 17th level, when the cleric casts a Necromancy spell of 1st through 5th-level that targets only one creature, the spell can instead target two creatures within range and within 5 feet of each other. If the spell consumes its material components, the cleric must provide them for each target.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Knowledge Domain', 1, 59, 'Cleric', 'PHB', 'Knowledge', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Knowledge Domain' AND class_name = 'Cleric' AND level = 1), 0, 'The gods of knowledge—including {@deity Oghma}, {@deity Boccob|Greyhawk}, {@deity Gilean|Dragonlance}, {@deity Aureon|Eberron}, and {@deity Thoth|Egyptian}—value learning and understanding above all. Some teach that knowledge is to be gathered and shared in libraries and universities, or promote the practical knowledge of craft and invention. Some deities hoard knowledge and keep its secrets to themselves. And some promise their followers that they will gain tremendous power if they unlock the secrets of the multiverse. Followers of these gods study esoteric lore, collect old tomes, delve into the secret places of the earth, and learn all they can. Some gods of knowledge promote the practical knowledge of craft and invention, including smith deities like {@deity Gond}, {@deity Reorx|Dragonlance}, {@deity Onatar|Eberron}, {@deity Moradin|Nonhuman}, {@deity Hephaestus|Greek}, and {@deity Goibhniu|Celtic}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Knowledge Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, you add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Knowledge Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Knowledge Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell command}, {@spell identify}"],["3rd","{@spell augury}, {@spell suggestion}"],["5th","{@spell nondetection}, {@spell speak with dead}"],["7th","{@spell arcane eye}, {@spell confusion}"],["9th","{@spell legend lore}, {@spell scrying}"]]', '{"type":"table","caption":"Knowledge Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell command}, {@spell identify}"],["3rd","{@spell augury}, {@spell suggestion}"],["5th","{@spell nondetection}, {@spell speak with dead}"],["7th","{@spell arcane eye}, {@spell confusion}"],["9th","{@spell legend lore}, {@spell scrying}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Knowledge Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Blessings of Knowledge|Cleric||Knowledge||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessings of Knowledge', 1, 59, 'Cleric', 'PHB', 'Knowledge', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessings of Knowledge' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, you learn two languages of your choice. You also become proficient in your choice of two of the following skills: {@skill Arcana}, {@skill History}, {@skill Nature}, or {@skill Religion}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessings of Knowledge' AND class_name = 'Cleric' AND level = 1), 1, 'Your proficiency bonus is doubled for any ability check you make that uses either of those skills.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Knowledge of the Ages', 1, 59, 'Cleric', 'PHB', 'Knowledge', 'PHB', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Knowledge of the Ages' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to tap into a divine well of knowledge. As an action, you choose one skill or tool. For 10 minutes, you have proficiency with the chosen skill or tool.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Read Thoughts', 1, 59, 'Cleric', 'PHB', 'Knowledge', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Read Thoughts' AND class_name = 'Cleric' AND level = 6), 0, 'At 6th level, you can use your Channel Divinity to read a creature''s thoughts. You can then use your access to the creature''s mind to command it.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Read Thoughts' AND class_name = 'Cleric' AND level = 6), 1, 'As an action, choose one creature that you can see within 60 feet of you. That creature must make a Wisdom saving throw. If the creature succeeds on the saving throw, you can''t use this feature on it again until you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Read Thoughts' AND class_name = 'Cleric' AND level = 6), 2, 'If the creature fails its save, you can read its surface thoughts (those foremost in its mind, reflecting its current emotions and what it is actively thinking about) when it is within 60 feet of you. This effect lasts for 1 minute.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Read Thoughts' AND class_name = 'Cleric' AND level = 6), 3, 'During that time, you can use your action to end this effect and cast the {@spell suggestion} spell on the creature without expending a spell slot. The target automatically fails its saving throw against the spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Knowledge', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Potent Spellcasting feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Potent Spellcasting', 1, 59, 'Cleric', 'PHB', 'Knowledge', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Potent Spellcasting' AND class_name = 'Cleric' AND level = 8), 0, 'Starting at 8th level, you add your Wisdom modifier to the damage you deal with any cleric cantrip.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Visions of the Past', 1, 59, 'Cleric', 'PHB', 'Knowledge', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Visions of the Past' AND class_name = 'Cleric' AND level = 17), 0, 'Starting at 17th level, you can call up visions of the past that relate to an object you hold or your immediate surroundings. You spend at least 1 minute in meditation and prayer, then receive dreamlike, shadowy glimpses of recent events. You can meditate in this way for a number of minutes equal to your Wisdom score and must maintain {@status concentration} during that time, as if you were casting a spell.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Visions of the Past' AND class_name = 'Cleric' AND level = 17), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Visions of the Past' AND class_name = 'Cleric' AND level = 17), 2, 'entries', NULL, '[{"type":"entries","name":"Object Reading","entries":["Holding an object as you meditate, you can see visions of the object''s previous owner. After meditating for 1 minute, you learn how the owner acquired and lost the object, as well as the most recent significant event involving the object and that owner. If the object was owned by another creature in the recent past (within a number of days equal to your Wisdom score), you can spend 1 additional minute for each owner to learn the same information about that creature."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Object Reading","entries":["Holding an object as you meditate, you can see visions of the object''s previous owner. After meditating for 1 minute, you learn how the owner acquired and lost the object, as well as the most recent significant event involving the object and that owner. If the object was owned by another creature in the recent past (within a number of days equal to your Wisdom score), you can spend 1 additional minute for each owner to learn the same information about that creature."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Visions of the Past' AND class_name = 'Cleric' AND level = 17), 3, 'entries', NULL, '[{"type":"entries","name":"Area Reading","entries":["As you meditate, you see visions of recent events in your immediate vicinity (a room, street, tunnel, clearing, or the like, up to a 50-foot cube), going back a number of days equal to your Wisdom score. For each minute you meditate, you learn about one significant event, beginning with the most recent. Significant events typically involve powerful emotions, such as battles and betrayals, marriages and murders, births and funerals. However, they might also include more mundane events that are nevertheless important in your current situation."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Area Reading","entries":["As you meditate, you see visions of recent events in your immediate vicinity (a room, street, tunnel, clearing, or the like, up to a 50-foot cube), going back a number of days equal to your Wisdom score. For each minute you meditate, you learn about one significant event, beginning with the most recent. Significant events typically involve powerful emotions, such as battles and betrayals, marriages and murders, births and funerals. However, they might also include more mundane events that are nevertheless important in your current situation."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Life Domain', 1, 60, 'Cleric', 'PHB', 'Life', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Life Domain' AND class_name = 'Cleric' AND level = 1), 0, 'The Life domain focuses on the vibrant positive energy—one of the fundamental forces of the universe—that sustains all life. The gods of life promote vitality and health through healing the sick and wounded, caring for those in need, and driving away the forces of death and undeath. Almost any non-evil deity can claim influence over this domain, particularly agricultural deities (such as {@deity Chauntea}, {@deity Arawai|Eberron}, and {@deity Demeter|Greek}), sun gods (such as {@deity Lathander}, {@deity Pelor|Greyhawk}, and {@deity Re-Horakhty|Egyptian}), gods of healing or endurance (such as {@deity Ilmater}, {@deity Mishakal|Dragonlance}, {@deity Apollo|Greek}, and {@deity Diancecht|Celtic}), and gods of home and community (such as {@deity Hestia|Greek}, {@deity Hathor|Egyptian}, and {@deity Boldrei|Eberron}).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Life Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, you add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Life Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Life Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell bless}, {@spell cure wounds}"],["3rd","{@spell lesser restoration}, {@spell spiritual weapon}"],["5th","{@spell beacon of hope}, {@spell revivify}"],["7th","{@spell death ward}, {@spell guardian of faith}"],["9th","{@spell mass cure wounds}, {@spell raise dead}"]]', '{"type":"table","caption":"Life Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell bless}, {@spell cure wounds}"],["3rd","{@spell lesser restoration}, {@spell spiritual weapon}"],["5th","{@spell beacon of hope}, {@spell revivify}"],["7th","{@spell death ward}, {@spell guardian of faith}"],["9th","{@spell mass cure wounds}, {@spell raise dead}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Life Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiency|Cleric||Life||1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Life Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Disciple of Life|Cleric||Life||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiency', 1, 60, 'Cleric', 'PHB', 'Life', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiency' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you gain proficiency with heavy armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Disciple of Life', 1, 60, 'Cleric', 'PHB', 'Life', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Disciple of Life' AND class_name = 'Cleric' AND level = 1), 0, 'Also starting at 1st level, your healing spells are more effective. Whenever you use a spell of 1st level or higher to restore hit points to a creature, the creature regains additional hit points equal to 2 + the spell''s level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Preserve Life', 1, 60, 'Cleric', 'PHB', 'Life', 'PHB', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Preserve Life' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to heal the badly injured.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Preserve Life' AND class_name = 'Cleric' AND level = 2), 1, 'As an action, you present your holy symbol and evoke healing energy that can restore a number of hit points equal to five times your cleric level. Choose any creatures within 30 feet of you, and divide those hit points among them. This feature can restore a creature to no more than half of its hit point maximum. You can''t use this feature on an undead or a construct.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Healer', 1, 60, 'Cleric', 'PHB', 'Life', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Healer' AND class_name = 'Cleric' AND level = 6), 0, 'Beginning at 6th level, the healing spells you cast on others heal you as well. When you cast a spell of 1st level or higher that restores hit points to a creature other than you, you regain hit points equal to 2 + the spell''s level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Life', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 1, 60, 'Cleric', 'PHB', 'Life', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} radiant damage to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Supreme Healing', 1, 60, 'Cleric', 'PHB', 'Life', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Supreme Healing' AND class_name = 'Cleric' AND level = 17), 0, 'Starting at 17th level, when you would normally roll one or more dice to restore hit points with a spell, you instead use the highest number possible for each die. For example, instead of restoring {@dice 2d6} hit points to a creature, you restore 12.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Light Domain', 1, 60, 'Cleric', 'PHB', 'Light', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Light Domain' AND class_name = 'Cleric' AND level = 1), 0, 'Gods of light—including {@deity Helm}, {@deity Lathander}, {@deity Pholtus|Greyhawk}, {@deity Branchala|Dragonlance}, {@deity the Silver Flame|Eberron}, {@deity Belenus|Celtic}, {@deity Apollo|Greek}, and {@deity Re-Horakhty|Egyptian}—promote the ideals of rebirth and renewal, truth, vigilance, and beauty, often using the symbol of the sun. Some of these gods are portrayed as the sun itself or as a charioteer who guides the sun across the sky. Others are tireless sentinels whose eyes pierce every shadow and see through every deception. Some are deities of beauty and artistry, who teach that art is a vehicle for the soul''s improvement. Clerics of a god of light are enlightened souls infused with radiance and the power of their gods'' discerning vision, charged with chasing away lies and burning away darkness.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Light Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, you add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Light Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Light Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell burning hands}, {@spell faerie fire}"],["3rd","{@spell flaming sphere}, {@spell scorching ray}"],["5th","{@spell daylight}, {@spell fireball}"],["7th","{@spell guardian of faith}, {@spell wall of fire}"],["9th","{@spell flame strike}, {@spell scrying}"]]', '{"type":"table","caption":"Light Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell burning hands}, {@spell faerie fire}"],["3rd","{@spell flaming sphere}, {@spell scorching ray}"],["5th","{@spell daylight}, {@spell fireball}"],["7th","{@spell guardian of faith}, {@spell wall of fire}"],["9th","{@spell flame strike}, {@spell scrying}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Light Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Cantrip|Cleric||Light||1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Light Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Warding Flare|Cleric||Light||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Cantrip', 1, 60, 'Cleric', 'PHB', 'Light', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Cantrip' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you gain the {@spell light} cantrip if you don''t already know it. This cantrip doesn''t count against the number of cleric cantrips you know.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Warding Flare', 1, 60, 'Cleric', 'PHB', 'Light', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Warding Flare' AND class_name = 'Cleric' AND level = 1), 0, 'Also at 1st level, you can interpose divine light between yourself and an attacking enemy. When you are attacked by a creature within 30 feet of you that you can see, you can use your reaction to impose disadvantage on the attack roll, causing light to flare before the attacker before it hits or misses. An attacker that can''t be {@condition blinded} is immune to this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Warding Flare' AND class_name = 'Cleric' AND level = 1), 1, 'You can use this feature a number of times equal to your Wisdom modifier (a minimum of once). You regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Radiance of the Dawn', 1, 60, 'Cleric', 'PHB', 'Light', 'PHB', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Radiance of the Dawn' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to harness sunlight, banishing darkness and dealing radiant damage to your foes.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Radiance of the Dawn' AND class_name = 'Cleric' AND level = 2), 1, 'As an action, you present your holy symbol, and any magical darkness within 30 feet of you is dispelled. Additionally, each hostile creature within 30 feet of you must make a Constitution saving throw. A creature takes radiant damage equal to {@damage 2d10} + your cleric level on a failed saving throw, and half as much damage on a successful one. A creature that has {@quickref Cover||3||total cover} from you is not affected.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Improved Flare', 1, 60, 'Cleric', 'PHB', 'Light', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Improved Flare' AND class_name = 'Cleric' AND level = 6), 0, 'Starting at 6th level, you can also use your Warding Flare feature when a creature that you can see within 30 feet of you attacks a creature other than you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Light', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Potent Spellcasting feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Potent Spellcasting', 1, 60, 'Cleric', 'PHB', 'Light', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Potent Spellcasting' AND class_name = 'Cleric' AND level = 8), 0, 'Starting at 8th level, you add your Wisdom modifier to the damage you deal with any cleric cantrip.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Corona of Light', 1, 60, 'Cleric', 'PHB', 'Light', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Corona of Light' AND class_name = 'Cleric' AND level = 17), 0, 'Starting at 17th level, you can use your action to activate an aura of sunlight that lasts for 1 minute or until you dismiss it using another action. You emit bright light in a 60-foot radius and dim light 30 feet beyond that. Your enemies in the bright light have disadvantage on saving throws against any spell that deals fire or radiant damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Nature Domain', 1, 61, 'Cleric', 'PHB', 'Nature', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature Domain' AND class_name = 'Cleric' AND level = 1), 0, 'Gods of nature are as varied as the natural world itself, from inscrutable gods of the deep forests (such as {@deity Silvanus}, {@deity Obad-Hai|Greyhawk}, {@deity Chislev|Dragonlance}, {@deity Balinor|Eberron}, and {@deity Pan|Greek}) to friendly deities associated with particular springs and groves (such as {@deity Eldath}). Druids revere nature as a whole and might serve one of these deities, practicing mysterious rites and reciting all-but-forgotten prayers in their own secret tongue. But many of these gods have clerics as well, champions who take a more active role in advancing the interests of a particular nature god. These clerics might hunt the evil monstrosities that despoil the woodlands, bless the harvest of the faithful, or wither the crops of those who anger their gods.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, you add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Nature Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Nature Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell animal friendship}, {@spell speak with animals}"],["3rd","{@spell barkskin}, {@spell spike growth}"],["5th","{@spell plant growth}, {@spell wind wall}"],["7th","{@spell dominate beast}, {@spell grasping vine}"],["9th","{@spell insect plague}, {@spell tree stride}"]]', '{"type":"table","caption":"Nature Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell animal friendship}, {@spell speak with animals}"],["3rd","{@spell barkskin}, {@spell spike growth}"],["5th","{@spell plant growth}, {@spell wind wall}"],["7th","{@spell dominate beast}, {@spell grasping vine}"],["9th","{@spell insect plague}, {@spell tree stride}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Nature Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Acolyte of Nature|Cleric||Nature||1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Nature Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiency|Cleric||Nature||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Acolyte of Nature', 1, 61, 'Cleric', 'PHB', 'Nature', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Acolyte of Nature' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, you learn one druid cantrip of your choice. This cantrip doesn''t count against the number of cleric cantrips you know. You also gain proficiency in one of the following skills of your choice: {@skill Animal Handling}, {@skill Nature}, or {@skill Survival}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiency', 1, 61, 'Cleric', 'PHB', 'Nature', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiency' AND class_name = 'Cleric' AND level = 1), 0, 'Also at 1st level, you gain proficiency with heavy armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Charm Animals and Plants', 1, 61, 'Cleric', 'PHB', 'Nature', 'PHB', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Charm Animals and Plants' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to charm animals and plants.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Charm Animals and Plants' AND class_name = 'Cleric' AND level = 2), 1, 'As an action, you present your holy symbol and invoke the name of your deity. Each beast or plant creature that can see you within 30 feet of you must make a Wisdom saving throw. If the creature fails its saving throw, it is {@condition charmed} by you for 1 minute or until it takes damage. While it is {@condition charmed} by you, it is friendly to you and other creatures you designate.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dampen Elements', 1, 61, 'Cleric', 'PHB', 'Nature', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dampen Elements' AND class_name = 'Cleric' AND level = 6), 0, 'Starting at 6th level, when you or a creature within 30 feet of you takes acid, cold, fire, lightning, or thunder damage, you can use your reaction to grant resistance to the creature against that instance of the damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Nature', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 1, 61, 'Cleric', 'PHB', 'Nature', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} cold, fire, or lightning damage (your choice) to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Master of Nature', 1, 61, 'Cleric', 'PHB', 'Nature', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Master of Nature' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you gain the ability to command animals and plant creatures. While creatures are {@condition charmed} by your Charm Animals and Plants feature, you can take a bonus action on your turn to verbally command what each of those creatures will do on its next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tempest Domain', 1, 62, 'Cleric', 'PHB', 'Tempest', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tempest Domain' AND class_name = 'Cleric' AND level = 1), 0, 'Gods whose portfolios include the Tempest domain—including {@deity Talos}, {@deity Umberlee}, {@deity Kord|Greyhawk}, {@deity Zeboim|Dragonlance}, {@deity the Devourer|Eberron}, {@deity Zeus|Greek}, and {@deity Thor|Norse}—govern storms, sea, and sky. They include gods of lightning and thunder, gods of earthquakes, some fire gods, and certain gods of violence, physical strength, and courage. In some pantheons, a god of this domain rules over other deities and is known for swift justice delivered by thunderbolts. In the pantheons of seafaring people, gods of this domain are ocean deities and the patrons of sailors. Tempest gods send their clerics to inspire fear in the common folk, either to keep those folk on the path of righteousness or to encourage them to offer sacrifices of propitiation to ward off divine wrath.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tempest Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, you add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tempest Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Tempest Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell fog cloud}, {@spell thunderwave}"],["3rd","{@spell gust of wind}, {@spell shatter}"],["5th","{@spell call lightning}, {@spell sleet storm}"],["7th","{@spell control water}, {@spell ice storm}"],["9th","{@spell destructive wave}, {@spell insect plague}"]]', '{"type":"table","caption":"Tempest Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell fog cloud}, {@spell thunderwave}"],["3rd","{@spell gust of wind}, {@spell shatter}"],["5th","{@spell call lightning}, {@spell sleet storm}"],["7th","{@spell control water}, {@spell ice storm}"],["9th","{@spell destructive wave}, {@spell insect plague}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tempest Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiencies|Cleric||Tempest||1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tempest Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Wrath of the Storm|Cleric||Tempest||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiencies', 1, 62, 'Cleric', 'PHB', 'Tempest', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, you gain proficiency with martial weapons and heavy armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wrath of the Storm', 1, 62, 'Cleric', 'PHB', 'Tempest', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wrath of the Storm' AND class_name = 'Cleric' AND level = 1), 0, 'Also at 1st level, you can thunderously rebuke attackers. When a creature within 5 feet of you that you can see hits you with an attack, you can use your reaction to cause the creature to make a Dexterity saving throw. The creature takes {@damage 2d8} lightning or thunder damage (your choice) on a failed saving throw, and half as much damage on a successful one.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wrath of the Storm' AND class_name = 'Cleric' AND level = 1), 1, 'You can use this feature a number of times equal to your Wisdom modifier (a minimum of once). You regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Destructive Wrath', 1, 62, 'Cleric', 'PHB', 'Tempest', 'PHB', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Destructive Wrath' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to wield the power of the storm with unchecked ferocity.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Destructive Wrath' AND class_name = 'Cleric' AND level = 2), 1, 'When you roll lightning or thunder damage, you can use your Channel Divinity to deal maximum damage, instead of rolling.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Thunderbolt Strike', 1, 62, 'Cleric', 'PHB', 'Tempest', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Thunderbolt Strike' AND class_name = 'Cleric' AND level = 6), 0, 'At 6th level, when you deal lightning damage to a Large or smaller creature, you can also push it up to 10 feet away from you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Tempest', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 1, 62, 'Cleric', 'PHB', 'Tempest', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} thunder damage to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Stormborn', 1, 62, 'Cleric', 'PHB', 'Tempest', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Stormborn' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you have a flying speed equal to your current walking speed whenever you are not underground or indoors.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Trickery Domain', 1, 62, 'Cleric', 'PHB', 'Trickery', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Trickery Domain' AND class_name = 'Cleric' AND level = 1), 0, 'Gods of trickery—such as {@deity Tymora}, {@deity Beshaba}, {@deity Olidammara|Greyhawk}, {@deity the Traveler|Eberron}, {@deity Garl Glittergold|Nonhuman}, and {@deity Loki|Norse}—are mischief-makers and instigators who stand as a constant challenge to the accepted order among both gods and mortals. They''re patrons of thieves, scoundrels, gamblers, rebels, and liberators. Their clerics are a disruptive force in the world, puncturing pride, mocking tyrants, stealing from the rich, freeing captives, and flouting hollow traditions. They prefer subterfuge, pranks, deception, and theft rather than direct confrontation.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Trickery Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, you add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Trickery Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Trickery Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell charm person}, {@spell disguise self}"],["3rd","{@spell mirror image}, {@spell pass without trace}"],["5th","{@spell blink}, {@spell dispel magic}"],["7th","{@spell dimension door}, {@spell polymorph}"],["9th","{@spell dominate person}, {@spell modify memory}"]]', '{"type":"table","caption":"Trickery Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell charm person}, {@spell disguise self}"],["3rd","{@spell mirror image}, {@spell pass without trace}"],["5th","{@spell blink}, {@spell dispel magic}"],["7th","{@spell dimension door}, {@spell polymorph}"],["9th","{@spell dominate person}, {@spell modify memory}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Trickery Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Blessing of the Trickster|Cleric||Trickery||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessing of the Trickster', 1, 62, 'Cleric', 'PHB', 'Trickery', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessing of the Trickster' AND class_name = 'Cleric' AND level = 1), 0, 'Starting when you choose this domain at 1st level, you can use your action to touch a willing creature other than yourself to give it advantage on Dexterity ({@skill Stealth}) checks. This blessing lasts for 1 hour or until you use this feature again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Invoke Duplicity', 1, 62, 'Cleric', 'PHB', 'Trickery', 'PHB', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Invoke Duplicity' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to create an illusory duplicate of yourself.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Invoke Duplicity' AND class_name = 'Cleric' AND level = 2), 1, 'As an action, you create a perfect illusion of yourself that lasts for 1 minute, or until you lose your {@status concentration} (as if you were {@status concentration||concentrating} on a spell). The illusion appears in an unoccupied space that you can see within 30 feet of you. As a bonus action on your turn, you can move the illusion up to 30 feet to a space you can see, but it must remain within 120 feet of you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Invoke Duplicity' AND class_name = 'Cleric' AND level = 2), 2, 'For the duration, you can cast spells as though you were in the illusion''s space, but you must use your own senses. Additionally, when both you and your illusion are within 5 feet of a creature that can see the illusion, you have advantage on attack rolls against that creature, given how distracting the illusion is to the target.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Cloak of Shadows', 1, 62, 'Cleric', 'PHB', 'Trickery', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Cloak of Shadows' AND class_name = 'Cleric' AND level = 6), 0, 'Starting at 6th level, you can use your Channel Divinity to vanish.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Cloak of Shadows' AND class_name = 'Cleric' AND level = 6), 1, 'As an action, you become {@condition invisible} until the end of your next turn. You become visible if you attack or cast a spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Trickery', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 1, 62, 'Cleric', 'PHB', 'Trickery', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with poison—a gift from your deity. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} poison damage to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Improved Duplicity', 1, 62, 'Cleric', 'PHB', 'Trickery', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Improved Duplicity' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you can create up to four duplicates of yourself, instead of one, when you use Invoke Duplicity. As a bonus action on your turn, you can move any number of them up to 30 feet, to a maximum range of 120 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('War Domain', 1, 63, 'Cleric', 'PHB', 'War', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'War Domain' AND class_name = 'Cleric' AND level = 1), 0, 'War has many manifestations. It can make heroes of ordinary people. It can be desperate and horrific, with acts of cruelty and cowardice eclipsing instances of excellence and courage. In either case, the gods of war watch over warriors and reward them for their great deeds. The clerics of such gods excel in battle, inspiring others to fight the good fight or offering acts of violence as prayers. Gods of war include champions of honor and chivalry (such as {@deity Torm}, {@deity Heironeous|Greyhawk}, and {@deity Kiri-Jolith|Dragonlance}) as well as gods of destruction and pillage (such as {@deity Erythnul|Greyhawk}, {@deity the Fury|Eberron}, {@deity Gruumsh|Nonhuman}, and {@deity Ares|Greek}) and gods of conquest and domination (such as {@deity Bane}, {@deity Hextor|Greyhawk}, and {@deity Maglubiyet|Nonhuman}). Other war gods (such as {@deity Tempus}, {@deity Nike|Greek}, and {@deity Nuada|Celtic}) take a more neutral stance, promoting war in all its manifestations and supporting warriors in any circumstance.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'War Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'War Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'War Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell divine favor}, {@spell shield of faith}"],["3rd","{@spell magic weapon}, {@spell spiritual weapon}"],["5th","{@spell crusader''s mantle}, {@spell spirit guardians}"],["7th","{@spell freedom of movement}, {@spell stoneskin}"],["9th","{@spell flame strike}, {@spell hold monster}"]]', '{"type":"table","caption":"War Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell divine favor}, {@spell shield of faith}"],["3rd","{@spell magic weapon}, {@spell spiritual weapon}"],["5th","{@spell crusader''s mantle}, {@spell spirit guardians}"],["7th","{@spell freedom of movement}, {@spell stoneskin}"],["9th","{@spell flame strike}, {@spell hold monster}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'War Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiencies|Cleric||War||1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'War Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"War Priest|Cleric||War||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiencies', 1, 63, 'Cleric', 'PHB', 'War', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, you gain proficiency with martial weapons and heavy armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('War Priest', 1, 63, 'Cleric', 'PHB', 'War', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'War Priest' AND class_name = 'Cleric' AND level = 1), 0, 'From 1st level, your god delivers bolts of inspiration to you while you are engaged in battle. When you use the {@action Attack} action, you can make one weapon attack as a bonus action. You can use this feature a number of times equal to your Wisdom modifier (a minimum of once). You regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Guided Strike', 1, 63, 'Cleric', 'PHB', 'War', 'PHB', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Guided Strike' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to strike with supernatural accuracy. When you make an attack roll, you can use your Channel Divinity to gain a +10 bonus to the roll. You make this choice after you see the roll, but before the DM says whether the attack hits or misses.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: War God''s Blessing', 1, 63, 'Cleric', 'PHB', 'War', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: War God''s Blessing' AND class_name = 'Cleric' AND level = 6), 0, 'At 6th level, when a creature within 30 feet of you makes an attack roll, you can use your reaction to grant that creature a +10 bonus to the roll, using your Channel Divinity. You make this choice after you see the roll, but before the DM says whether the attack hits or misses.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'War', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 1, 63, 'Cleric', 'PHB', 'War', 'PHB', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} damage of the same type dealt by the weapon to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Avatar of Battle', 1, 63, 'Cleric', 'PHB', 'War', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Avatar of Battle' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you gain resistance to bludgeoning, piercing, and slashing damage from nonmagical attacks.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ambition Domain (PSA)', null, 27, 'Cleric', 'PHB', 'Ambition (PSA)', 'PSA', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Ambition Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 0, 'quote', NULL, '["The worthy shall strive for greatness—supremacy in life leads to supremacy in the afterlife."]', NULL, NULL, NULL, NULL, '{"type":"quote","entries":["The worthy shall strive for greatness—supremacy in life leads to supremacy in the afterlife."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ambition Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 1, 'Bontu has fully embraced this dictum, and though she expends little effort in teaching it, she surely leads by example. Her viziers subtly plant the seeds that flower into the ambition the God-Pharaoh desires. Through insinuation, they remind acolytes and initiates alike that achieving one''s place in the afterlife at the expense of others is not shameful, but is proof of the initiate''s determination and drive. Nothing is more important than that drive, they suggest—not the bonds of a crop, not friendship or love. Not even devotion to a deity.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ambition Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 2, 'At each indicated cleric level, add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Ambition Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 3, 'table', NULL, NULL, NULL, 'Ambition Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell bane}, {@spell disguise self}"],["3rd","{@spell mirror image}, {@spell ray of enfeeblement}"],["5th","{@spell bestow curse}, {@spell vampiric touch}"],["7th","{@spell death ward}, {@spell dimension door}"],["9th","{@spell dominate person}, {@spell modify memory}"]]', '{"type":"table","caption":"Ambition Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell bane}, {@spell disguise self}"],["3rd","{@spell mirror image}, {@spell ray of enfeeblement}"],["5th","{@spell bestow curse}, {@spell vampiric touch}"],["7th","{@spell death ward}, {@spell dimension door}"],["9th","{@spell dominate person}, {@spell modify memory}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Ambition Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Warding Flare|Cleric||Ambition (PSA)|PSA|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Warding Flare', null, 27, 'Cleric', 'PHB', 'Ambition (PSA)', 'PSA', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Warding Flare' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you can interpose divine light between yourself and an attacking enemy. When you are attacked by a creature within 30 feet of you that you can see, you can use your reaction to impose disadvantage on the attack roll, causing light to flare before the attacker before it hits or misses. An attacker that can''t be {@condition blinded} is immune to this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Warding Flare' AND class_name = 'Cleric' AND level = 1), 1, 'You can use this feature a number of times equal to your Wisdom modifier (a minimum of once). You regain all expended uses when you finish a long rest.');
