INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Potent Spellcasting feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Potent Spellcasting', null, 27, 'Cleric', 'PHB', 'Ambition (PSA)', 'PSA', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Potent Spellcasting' AND class_name = 'Cleric' AND level = 8), 0, 'Starting at 8th level, you add your Wisdom modifier to the damage you deal with any cleric cantrip.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Improved Duplicity', null, 27, 'Cleric', 'PHB', 'Ambition (PSA)', 'PSA', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Improved Duplicity' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you can create up to four duplicates of yourself, instead of one, when you use Invoke Duplicity. As a bonus action on your turn, you can move any number of them up to 30 feet, to a maximum range of 120 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Knowledge Domain (PSA)', null, 25, 'Cleric', 'PHB', 'Knowledge (PSA)', 'PSA', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Knowledge Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 0, 'quote', NULL, '["The worthy shall cultivate a nimble mind that can perceive the wonders beyond imagination that wait in the afterlife."]', NULL, NULL, NULL, NULL, '{"type":"quote","entries":["The worthy shall cultivate a nimble mind that can perceive the wonders beyond imagination that wait in the afterlife."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Knowledge Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 1, 'Kefnet''s task is to pass on this teaching of the God-Pharaoh and elucidate its meaning. He teaches that the afterlife will be inhabited only by those who have proved by their wits that they are worthy of dwelling in the glorious presence of the God-Pharaoh. He trains acolytes and initiates to push their limits and challenge their mental capacity with spells of ever-greater power.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Knowledge Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 2, 'Kefnet''s domain is identical to the {@class Cleric|PHB|Knowledge domain in the Player''s Handbook|Knowledge|PHB}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Knowledge Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Knowledge Domain|Cleric||Knowledge||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Solidarity Domain (PSA)', null, 24, 'Cleric', 'PHB', 'Solidarity (PSA)', 'PSA', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Solidarity Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 0, 'quote', NULL, '["The worthy must know and respect all others whom the God-Pharaoh deems worthy, for in the afterlife, all will be united in purpose and action."]', NULL, NULL, NULL, NULL, '{"type":"quote","entries":["The worthy must know and respect all others whom the God-Pharaoh deems worthy, for in the afterlife, all will be united in purpose and action."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Solidarity Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 1, 'Oketra is charged with expounding upon this teaching of the God-Pharaoh, instilling in every initiate the virtue of solidarity. She forges each group of children into a crop of acolytes with just one purpose: to be judged worthy of a glorious afterlife. And she instills in each crop the ability to unite in a single action in pursuit of that purpose. She is fond of poetic imagery to communicate her ideals.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Solidarity Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 2, 'At each indicated cleric level, add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Solidarity Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 3, 'table', NULL, NULL, NULL, 'Solidarity Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell bless}, {@spell guiding bolt}"],["3rd","{@spell aid}, {@spell warding bond}"],["5th","{@spell beacon of hope}, {@spell crusader''s mantle}"],["7th","{@spell aura of life}, {@spell guardian of faith}"],["9th","{@spell circle of power}, {@spell mass cure wounds}"]]', '{"type":"table","caption":"Solidarity Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell bless}, {@spell guiding bolt}"],["3rd","{@spell aid}, {@spell warding bond}"],["5th","{@spell beacon of hope}, {@spell crusader''s mantle}"],["7th","{@spell aura of life}, {@spell guardian of faith}"],["9th","{@spell circle of power}, {@spell mass cure wounds}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Solidarity Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiency|Cleric||Solidarity (PSA)|PSA|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Solidarity Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Solidarity''s Action|Cleric||Solidarity (PSA)|PSA|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiency', null, 24, 'Cleric', 'PHB', 'Solidarity (PSA)', 'PSA', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiency' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you gain proficiency with heavy armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Solidarity''s Action', null, 24, 'Cleric', 'PHB', 'Solidarity (PSA)', 'PSA', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Solidarity''s Action' AND class_name = 'Cleric' AND level = 1), 0, 'Also at 1st level, when you take the {@action Help} action to aid an ally''s attack, you can make one weapon attack as a bonus action. You can use this feature a number of times equal to your Wisdom modifier (minimum of once). You regain expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Preserve Life', null, 24, 'Cleric', 'PHB', 'Solidarity (PSA)', 'PSA', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Preserve Life' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to heal the badly injured.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Preserve Life' AND class_name = 'Cleric' AND level = 2), 1, 'As an action, you present your holy symbol and evoke healing energy that can restore a number of hit points equal to five times your cleric level. Choose any creatures within 30 feet of you, and divide those hit points among them. This feature can restore a creature to no more than half of its hit point maximum. You can''t use this feature on an undead or a construct.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oketra''s Blessing', null, 24, 'Cleric', 'PHB', 'Solidarity (PSA)', 'PSA', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oketra''s Blessing' AND class_name = 'Cleric' AND level = 6), 0, 'At 6th level, when a creature within 30 feet of you makes an attack roll, you can use your reaction to grant that creature a +10 bonus to the roll, using your Channel Divinity. You make this choice after you see the roll, but before the DM says whether the attack hits or misses.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Solidarity (PSA)', 'PSA', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', null, 24, 'Cleric', 'PHB', 'Solidarity (PSA)', 'PSA', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} damage of the same type dealt by the weapon to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Supreme Healing', null, 24, 'Cleric', 'PHB', 'Solidarity (PSA)', 'PSA', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Supreme Healing' AND class_name = 'Cleric' AND level = 17), 0, 'Starting at 17th level, when you would normally roll one or more dice to restore hit points with a spell, you instead use the highest number possible for each die. For example, instead of restoring {@dice 2d6} hit points to a creature, you restore 12.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Strength Domain (PSA)', null, 26, 'Cleric', 'PHB', 'Strength (PSA)', 'PSA', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Strength Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 0, 'quote', NULL, '["The worthy shall hone a strong body that can withstand the boundless energies of the afterlife."]', NULL, NULL, NULL, NULL, '{"type":"quote","entries":["The worthy shall hone a strong body that can withstand the boundless energies of the afterlife."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Strength Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 1, 'It falls to Rhonas to instill this teaching in those who would enter the afterlife—but to his mind, the words themselves don''t matter. Strength can''t be taught. It must be built through practice and training. Rhonas demonstrates his teachings by his example, rather than by giving his students any kind of academic instruction. He welcomes the people of Naktamun to stand by the Hekma and watch him as he storms into the desert to battle the greatest horrors. He encourages them to observe his indomitable strength, for though they will never equal it, they can aspire to mimicry. He invites them to scrutinize every move and practice what they see.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Strength Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 2, 'At each indicated cleric level, add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Strength Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 3, 'table', NULL, NULL, NULL, 'Strength Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell divine favor}, {@spell shield of faith}"],["3rd","{@spell enhance ability}, {@spell protection from poison}"],["5th","{@spell haste}, {@spell protection from energy}"],["7th","{@spell dominate beast}, {@spell stoneskin}"],["9th","{@spell destructive wave}, {@spell insect plague}"]]', '{"type":"table","caption":"Strength Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell divine favor}, {@spell shield of faith}"],["3rd","{@spell enhance ability}, {@spell protection from poison}"],["5th","{@spell haste}, {@spell protection from energy}"],["7th","{@spell dominate beast}, {@spell stoneskin}"],["9th","{@spell destructive wave}, {@spell insect plague}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Strength Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Acolyte of Strength|Cleric||Strength (PSA)|PSA|1"}');
