INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Avatar of Battle' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you gain resistance to bludgeoning, piercing, and slashing damage from nonmagical attacks.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Zeal Domain (PSA)', null, 28, 'Cleric', 'PHB', 'Zeal (PSA)', 'PSA', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Zeal Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 0, 'quote', NULL, '["The worthy shall rush to the God-Pharaoh''s side with relentless zeal, rising to overcome every obstacle in their way."]', NULL, NULL, NULL, NULL, '{"type":"quote","entries":["The worthy shall rush to the God-Pharaoh''s side with relentless zeal, rising to overcome every obstacle in their way."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Zeal Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 1, 'The God-Pharaoh expects those he welcomes into the afterlife to desire it above all other pleasures and achievements, and for them to show their dedication, passion, and fervor through their actions. Hazoret is charged with cultivating this zeal in the initiates who come under her care, and she has undertaken the task with appropriate enthusiasm. She recognizes, however, that the best way to teach zeal is by demonstrating it.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Zeal Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 2, 'At each indicated cleric level, add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Zeal Domain (PSA)' AND class_name = 'Cleric' AND level = 1), 3, 'table', NULL, NULL, NULL, 'Zeal Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell searing smite}, {@spell thunderous smite}"],["3rd","{@spell magic weapon}, {@spell shatter}"],["5th","{@spell haste}, {@spell fireball}"],["7th","{@spell fire shield||fire shield (warm shield only)}, {@spell freedom of movement}"],["9th","{@spell destructive wave}, {@spell flame strike}"]]', '{"type":"table","caption":"Zeal Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell searing smite}, {@spell thunderous smite}"],["3rd","{@spell magic weapon}, {@spell shatter}"],["5th","{@spell haste}, {@spell fireball}"],["7th","{@spell fire shield||fire shield (warm shield only)}, {@spell freedom of movement}"],["9th","{@spell destructive wave}, {@spell flame strike}"]]}');
