INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, you gain proficiency with martial weapons and heavy armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Priest of Zeal', null, 28, 'Cleric', 'PHB', 'Zeal (PSA)', 'PSA', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Priest of Zeal' AND class_name = 'Cleric' AND level = 1), 0, 'From 1st level, Hazoret delivers bolts of inspiration to you while you are engaged in battle. When you use the {@action Attack} action, you can make one weapon attack as a bonus action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Priest of Zeal' AND class_name = 'Cleric' AND level = 1), 1, 'You can use this feature a number of times equal to your Wisdom modifier (a minimum of once). You regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Consuming Fervor', null, 28, 'Cleric', 'PHB', 'Zeal (PSA)', 'PSA', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Consuming Fervor' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to channel your zeal into unchecked ferocity.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Consuming Fervor' AND class_name = 'Cleric' AND level = 2), 1, 'When you roll fire or thunder damage, you can use your Channel Divinity to deal maximum damage instead of rolling.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Resounding Strike', null, 28, 'Cleric', 'PHB', 'Zeal (PSA)', 'PSA', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Resounding Strike' AND class_name = 'Cleric' AND level = 6), 0, 'At 6th level, when you deal thunder damage to a Large or smaller creature, you can also push it up to 10 feet away from you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Zeal (PSA)', 'PSA', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', null, 28, 'Cleric', 'PHB', 'Zeal (PSA)', 'PSA', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} damage of the same type dealt by the weapon to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blaze of Glory', null, 28, 'Cleric', 'PHB', 'Zeal (PSA)', 'PSA', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blaze of Glory' AND class_name = 'Cleric' AND level = 17), 0, 'Starting at 17th level, you can delay death for an instant to perform a final heroic act.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blaze of Glory' AND class_name = 'Cleric' AND level = 17), 1, 'When you are reduced to 0 hit points by an attacker you can see, even if you would be killed outright, you can use your reaction to move up to your speed toward the attacker and make one melee weapon attack against it, as long as the movement brings it within your reach. You make this attack with advantage. If the attack hits, the creature takes an extra {@damage 5d10} fire damage and an extra {@damage 5d10} damage of the weapon''s type. You then fall {@condition unconscious} and begin making death saving throws as normal, or you die if the damage you took would have killed you outright.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blaze of Glory' AND class_name = 'Cleric' AND level = 17), 2, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcana Domain', null, 125, 'Cleric', 'PHB', 'Arcana', 'SCAG', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcana Domain' AND class_name = 'Cleric' AND level = 1), 0, 'Magic is an energy that suffuses the multiverse and that fuels both destruction and creation. Gods of the Arcana domain know the secrets and potential of magic intimately. For some of these gods, magical knowledge is a great responsibility that comes with a special understanding of the nature of reality. Other gods of Arcana see magic as pure power, to be used as its wielder sees fit.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcana Domain' AND class_name = 'Cleric' AND level = 1), 1, 'The gods of this domain are often associated with knowledge, as learning and arcane power tend to go hand-in-hand. In the Realms, deities of this domain include {@deity Azuth|Faerûnian|SCAG} and {@deity Mystra|Faerûnian|SCAG}, as well as {@deity Corellon Larethian|Elven|SCAG} of the elven pantheon. In other worlds, this domain includes {@deity Hecate|Greek}, {@deity Math Mathonwy|Celtic}, and {@deity Isis|Egyptian}; the triple moon gods of {@deity Solinari|Dragonlance}, {@deity Lunitari|Dragonlance}, and {@deity Nuitari|Dragonlance} of Krynn; and {@deity Boccob|Greyhawk}, {@deity Vecna|Greyhawk}, and {@deity Wee Jas|Greyhawk} of Greyhawk.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcana Domain' AND class_name = 'Cleric' AND level = 1), 2, 'At each indicated cleric level, add the listed spells to your spells prepared. They do not count towards your limit.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcana Domain' AND class_name = 'Cleric' AND level = 1), 3, 'table', NULL, NULL, NULL, 'Arcana Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell detect magic}, {@spell magic missile}"],["3rd","{@spell magic weapon}, {@spell Nystul''s magic aura}"],["5th","{@spell dispel magic}, {@spell magic circle}"],["7th","{@spell arcane eye}, {@spell Leomund''s secret chest}"],["9th","{@spell planar binding}, {@spell teleportation circle}"]]', '{"type":"table","caption":"Arcana Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell detect magic}, {@spell magic missile}"],["3rd","{@spell magic weapon}, {@spell Nystul''s magic aura}"],["5th","{@spell dispel magic}, {@spell magic circle}"],["7th","{@spell arcane eye}, {@spell Leomund''s secret chest}"],["9th","{@spell planar binding}, {@spell teleportation circle}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcana Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Arcane Initiate|Cleric||Arcana|SCAG|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Initiate', null, 125, 'Cleric', 'PHB', 'Arcana', 'SCAG', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Initiate' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you gain proficiency in the {@skill Arcana} skill, and you gain two cantrips of your choice from the {@filter wizard spell list|spells|class=wizard|level=0}. For you, these cantrips count as cleric cantrips.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Arcane Abjuration', null, 125, 'Cleric', 'PHB', 'Arcana', 'SCAG', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Arcane Abjuration' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to abjure otherworldly creatures.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Arcane Abjuration' AND class_name = 'Cleric' AND level = 2), 1, 'As an action, you present your holy symbol, and one celestial, elemental, fey, or fiend of your choice that is within 30 feet of you must make a Wisdom saving throw, provided that the creature can see or hear you. If the creature fails its saving throw, it is turned for 1 minute or until it takes any damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Arcane Abjuration' AND class_name = 'Cleric' AND level = 2), 2, 'A turned creature must spend its turns trying to move as far away from you as it can, and it can''t willingly end its move in a space within 30 feet of you. It also can''t take reactions. For its action, it can use only the {@action Dash} action or try to escape from an effect that prevents it from moving. If there''s nowhere to move, then the creature can use the {@action Dodge} action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Arcane Abjuration' AND class_name = 'Cleric' AND level = 2), 3, 'After you reach 5th level, when a creature fails its saving throw against your Arcane Abjuration feature, the creature is banished for 1 minute (as in the {@spell banishment} spell, no {@status concentration} required) if it isn''t on its plane of origin, and its challenge rating is at or below a certain threshold, as shown below.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Arcane Abjuration' AND class_name = 'Cleric' AND level = 2), 4, 'table', NULL, NULL, NULL, 'Arcane Banishment', '["Cleric level","Banishes Creatures of CR..."]', '[["5th","1/2 or lower"],["8th","1 or lower"],["11th","2 or lower"],["14th","3 or lower"],["17th","4 or lower"]]', '{"type":"table","caption":"Arcane Banishment","colLabels":["Cleric level","Banishes Creatures of CR..."],"colStyles":["col-2 text-center","col-10"],"rows":[["5th","1/2 or lower"],["8th","1 or lower"],["11th","2 or lower"],["14th","3 or lower"],["17th","4 or lower"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spell Breaker', null, 125, 'Cleric', 'PHB', 'Arcana', 'SCAG', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spell Breaker' AND class_name = 'Cleric' AND level = 6), 0, 'Starting at 6th level, when you restore hit points to an ally with a spell of 1st level or higher, you can also end one spell of your choice on that creature. The level of the spell you end must be equal to or lower than the level of the spell slot you use to cast the healing spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Arcana', 'SCAG', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Potent Spellcasting feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Potent Spellcasting', null, 125, 'Cleric', 'PHB', 'Arcana', 'SCAG', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Potent Spellcasting' AND class_name = 'Cleric' AND level = 8), 0, 'Starting at 8th level, you add your Wisdom modifier to the damage you deal with any cleric cantrip.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Mastery', null, 125, 'Cleric', 'PHB', 'Arcana', 'SCAG', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Mastery' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you choose four spells from the Wizard spell list, one from each of the following levels: 6th, 7th, 8th, and 9th. You add them to your list of domain spells. Like your other domain spells, they are always prepared and count as cleric spells for you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Order Domain', 2, 31, 'Cleric', 'PHB', 'Order', 'TCE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Order Domain' AND class_name = 'Cleric' AND level = 1), 0, 'The Order Domain represents discipline, as well as devotion to a society or an institution and strict obedience to the laws governing it. On Ravnica, the domain is favored by clerics of the Azorius Senate, who use it to maintain and enforce the law, and of the Orzhov Syndicate, who exploit law and order for their personal gain. On other worlds, gods who grant access to this domain include {@deity Bane}, {@deity Tyr}, {@deity Majere|Dragonlance}, {@deity Erathis|Dawn War|DMG}, {@deity Pholtus|Greyhawk}, {@deity Wee Jas|Greyhawk}, {@deity Aureon|Eberron}, {@deity Maglubiyet|Nonhuman}, {@deity Nuada|Celtic}, {@deity Athena|Greek}, {@deity Anubis|Egyptian}, {@deity Forseti|Norse}, and {@deity Asmodeus|Dawn War|DMG}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Order Domain' AND class_name = 'Cleric' AND level = 1), 1, 'The ideal of order is obedience to the law above all else, rather than to a specific individual or the passing influence of emotion or popular rule. Clerics of order are typically concerned with how things are done, rather than whether an action''s results are just. Following the law and obeying its edicts is critical, especially when it benefits these clerics and their guilds or deities.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Order Domain' AND class_name = 'Cleric' AND level = 1), 2, 'Law establishes hierarchies. Those selected by the law to lead must be obeyed. Those who obey must do so to the best of their ability. In this manner, law creates an intricate web of obligations that allows society to forge order and security in a chaotic multiverse.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Order Domain' AND class_name = 'Cleric' AND level = 1), 3, 'At each indicated cleric level, you add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Order Domain' AND class_name = 'Cleric' AND level = 1), 4, 'table', NULL, NULL, NULL, 'Order Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell command}, {@spell heroism}"],["3rd","{@spell hold person}, {@spell zone of truth}"],["5th","{@spell mass healing word}, {@spell slow}"],["7th","{@spell compulsion}, {@spell locate creature}"],["9th","{@spell commune}, {@spell dominate person}"]]', '{"type":"table","caption":"Order Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell command}, {@spell heroism}"],["3rd","{@spell hold person}, {@spell zone of truth}"],["5th","{@spell mass healing word}, {@spell slow}"],["7th","{@spell compulsion}, {@spell locate creature}"],["9th","{@spell commune}, {@spell dominate person}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Order Domain' AND class_name = 'Cleric' AND level = 1), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiencies|Cleric||Order|TCE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Order Domain' AND class_name = 'Cleric' AND level = 1), 6, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Voice of Authority|Cleric||Order|TCE|1"}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Order Domain' AND class_name = 'Cleric' AND level = 1), 'GGR', 25);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiencies', 2, 31, 'Cleric', 'PHB', 'Order', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you gain proficiency with heavy armor. You also gain proficiency in the {@skill Intimidation} or {@skill Persuasion} skill (your choice).');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Cleric' AND level = 1), 'GGR', 25);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Voice of Authority', 2, 31, 'Cleric', 'PHB', 'Order', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Voice of Authority' AND class_name = 'Cleric' AND level = 1), 0, 'Starting at 1st level, you can invoke the power of law to drive an ally to attack. If you cast a spell with a spell slot of 1st level or higher and target an ally with the spell, that ally can use their reaction immediately after the spell to make one weapon attack against a creature of your choice that you can see.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Voice of Authority' AND class_name = 'Cleric' AND level = 1), 1, 'If the spell targets more than one ally, you choose the ally who can make the attack.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Voice of Authority' AND class_name = 'Cleric' AND level = 1), 'GGR', 25);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Order''s Demand', 2, 31, 'Cleric', 'PHB', 'Order', 'TCE', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Order''s Demand' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to exert an intimidating presence over others.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Order''s Demand' AND class_name = 'Cleric' AND level = 2), 1, 'As an action, you present your holy symbol, and each creature of your choice that can see or hear you within 30 feet of you must succeed on a Wisdom saving throw or be {@condition charmed} by you until the end of your next turn or until the {@condition charmed} creature takes any damage. You can also cause any of the {@condition charmed} creatures to drop what they are holding when they fail the saving throw.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Order''s Demand' AND class_name = 'Cleric' AND level = 2), 'GGR', 25);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Embodiment of the Law', 2, 31, 'Cleric', 'PHB', 'Order', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Embodiment of the Law' AND class_name = 'Cleric' AND level = 6), 0, 'At 6th level, you become remarkably adept at channeling magical energy to compel others.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Embodiment of the Law' AND class_name = 'Cleric' AND level = 6), 1, 'If you cast a spell of the enchantment school using a spell slot of 1st level or higher, you can change the spell''s casting time to 1 bonus action for this casting, provided the spell''s casting time is normally 1 action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Embodiment of the Law' AND class_name = 'Cleric' AND level = 6), 2, 'You can use this feature a number of times equal to your Wisdom modifier (minimum of once), and you regain all expended uses of it when you finish a long rest.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Embodiment of the Law' AND class_name = 'Cleric' AND level = 6), 'GGR', 25);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 31, 'Cleric', 'PHB', 'Order', 'TCE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 2, 31, 'Cleric', 'PHB', 'Order', 'TCE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} psychic damage to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 'GGR', 25);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Order''s Wrath', 2, 31, 'Cleric', 'PHB', 'Order', 'TCE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Order''s Wrath' AND class_name = 'Cleric' AND level = 17), 0, 'Starting at 17th level, enemies you designate for destruction wilt under the combined efforts of you and your allies. If you deal your Divine Strike damage to a creature on your turn, you can curse that creature until the start of your next turn. The next time one of your allies hits the cursed creature with an attack, the target also takes {@damage 2d8} psychic damage, and the curse ends. You can curse a creature in this way only once per turn.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Order''s Wrath' AND class_name = 'Cleric' AND level = 17), 'GGR', 25);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Peace Domain', 2, 32, 'Cleric', 'PHB', 'Peace', 'TCE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Peace Domain' AND class_name = 'Cleric' AND level = 1), 0, 'The balm of peace thrives at the heart of healthy communities, between friendly nations, and in the souls of the kindhearted. The gods of peace inspire people of all sorts to resolve conflict and to stand up against those forces that try to prevent peace from flourishing. See the Peace Deities table for a list of some of the gods associated with this domain.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Peace Domain' AND class_name = 'Cleric' AND level = 1), 1, 'Clerics of the Peace Domain preside over the signing of treaties, and they are often asked to arbitrate in disputes. These clerics'' blessings draw people together and help them shoulder one another''s burdens, and the clerics'' magic aids those who are driven to fight for the way of peace.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Peace Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Peace Deities', '["Example Deity","Pantheon"]', '[["{@deity Angharradh|Elven|SCAG}","Elven"],["{@deity Berronar Truesilver|Dwarven|SCAG}","Dwarven"],["{@deity Boldrei|Eberron}","Eberron"],["{@deity Cyrrollalee|Halfling|SCAG}","Halfling"],["{@deity Eldath}","Forgotten Realms"],["{@deity Gaerdal Ironhand|Gnomish|SCAG}","Gnomish"],["{@deity Paladine|Dragonlance}","Dragonlance"],["{@deity Rao|Greyhawk}","Greyhawk"]]', '{"type":"table","caption":"Peace Deities","colLabels":["Example Deity","Pantheon"],"colStyles":["col-6 text-center","col-6 text-center"],"rows":[["{@deity Angharradh|Elven|SCAG}","Elven"],["{@deity Berronar Truesilver|Dwarven|SCAG}","Dwarven"],["{@deity Boldrei|Eberron}","Eberron"],["{@deity Cyrrollalee|Halfling|SCAG}","Halfling"],["{@deity Eldath}","Forgotten Realms"],["{@deity Gaerdal Ironhand|Gnomish|SCAG}","Gnomish"],["{@deity Paladine|Dragonlance}","Dragonlance"],["{@deity Rao|Greyhawk}","Greyhawk"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Peace Domain' AND class_name = 'Cleric' AND level = 1), 3, 'entries', 'Domain Spells', '["{@i 1st-level Peace Domain feature}","You gain domain spells at the cleric levels listed in the Peace Domain Spells table. See the Divine Domain class feature for how domain spells work.",{"type":"table","caption":"Peace Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell heroism}, {@spell sanctuary}"],["3rd","{@spell aid}, {@spell warding bond}"],["5th","{@spell beacon of hope}, {@spell sending}"],["7th","{@spell aura of purity}, {@spell Otiluke''s resilient sphere}"],["9th","{@spell greater restoration}, {@spell Rary''s telepathic bond}"]]}]', NULL, NULL, NULL, NULL, '{"name":"Domain Spells","type":"entries","entries":["{@i 1st-level Peace Domain feature}","You gain domain spells at the cleric levels listed in the Peace Domain Spells table. See the Divine Domain class feature for how domain spells work.",{"type":"table","caption":"Peace Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell heroism}, {@spell sanctuary}"],["3rd","{@spell aid}, {@spell warding bond}"],["5th","{@spell beacon of hope}, {@spell sending}"],["7th","{@spell aura of purity}, {@spell Otiluke''s resilient sphere}"],["9th","{@spell greater restoration}, {@spell Rary''s telepathic bond}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Peace Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Implement of Peace|Cleric||Peace|TCE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Peace Domain' AND class_name = 'Cleric' AND level = 1), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Emboldening Bond|Cleric||Peace|TCE|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Emboldening Bond', 2, 32, 'Cleric', 'PHB', 'Peace', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Emboldening Bond' AND class_name = 'Cleric' AND level = 1), 0, '{@i 1st-level Peace Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Emboldening Bond' AND class_name = 'Cleric' AND level = 1), 1, 'You can forge an empowering bond among people who are at peace with one another. As an action, you choose a number of willing creatures within 30 feet of you (this can include yourself) equal to your proficiency bonus. You create a magical bond among them for 10 minutes or until you use this feature again. While any bonded creature is within 30 feet of another, the creature can roll a {@dice d4} and add the number rolled to an attack roll, an ability check, or a saving throw it makes. Each creature can add the {@dice d4} no more than once per turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Emboldening Bond' AND class_name = 'Cleric' AND level = 1), 2, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Implement of Peace', 2, 32, 'Cleric', 'PHB', 'Peace', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Implement of Peace' AND class_name = 'Cleric' AND level = 1), 0, '{@i 1st-level Peace Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Implement of Peace' AND class_name = 'Cleric' AND level = 1), 1, 'You gain proficiency in the {@skill Insight}, {@skill Performance}, or {@skill Persuasion} skill (your choice).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Balm of Peace', 2, 32, 'Cleric', 'PHB', 'Peace', 'TCE', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Balm of Peace' AND class_name = 'Cleric' AND level = 2), 0, '{@i 2nd-level Peace Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Balm of Peace' AND class_name = 'Cleric' AND level = 2), 1, 'You can use your Channel Divinity to make your very presence a soothing balm. As an action, you can move up to your speed, without provoking opportunity attacks, and when you move within 5 feet of any other creature during this action, you can restore a number of hit points to that creature equal to {@dice 2d6} + your Wisdom modifier (minimum of 1 hit point). A creature can receive this healing only once whenever you take this action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Protective Bond', 2, 32, 'Cleric', 'PHB', 'Peace', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Protective Bond' AND class_name = 'Cleric' AND level = 6), 0, '{@i 6th-level Peace Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Protective Bond' AND class_name = 'Cleric' AND level = 6), 1, 'The bond you forge between people helps them protect each other. When a creature affected by your Emboldening Bond feature is about to take damage, a second bonded creature within 30 feet of the first can use its reaction to teleport to an unoccupied space within 5 feet of the first creature. The second creature then takes all the damage instead.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 32, 'Cleric', 'PHB', 'Peace', 'TCE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Potent Spellcasting feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Potent Spellcasting', 2, 32, 'Cleric', 'PHB', 'Peace', 'TCE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Potent Spellcasting' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level Peace Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Potent Spellcasting' AND class_name = 'Cleric' AND level = 8), 1, 'You add your Wisdom modifier to the damage you deal with any cleric cantrip.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Expansive Bond', 2, 32, 'Cleric', 'PHB', 'Peace', 'TCE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Expansive Bond' AND class_name = 'Cleric' AND level = 17), 0, '{@i 17th-level Peace Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Expansive Bond' AND class_name = 'Cleric' AND level = 17), 1, 'The benefits of your Emboldening Bond and Protective Bond features now work when the creatures are within 60 feet of each other. Moreover, when a creature uses Protective Bond to take someone else''s damage, the creature has resistance to that damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Twilight Domain', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Twilight Domain' AND class_name = 'Cleric' AND level = 1), 0, 'The twilit transition from light into darkness often brings calm and even joy, as the day''s labors end and the hours of rest begin. The darkness can also bring terrors, but the gods of twilight guard against the horrors of the night.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Twilight Domain' AND class_name = 'Cleric' AND level = 1), 1, 'Clerics who serve these deities-examples of which appear on the Twilight Deities table-bring comfort to those who seek rest and protect them by venturing into the encroaching darkness to ensure that the dark is a comfort, not a terror.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Twilight Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Twilight Deities', '["Example Deity","Pantheon"]', '[["{@deity Boldrei|Eberron}","Eberron"],["{@deity Celestian|Greyhawk}","Greyhawk"],["{@deity Dol Arrah|Eberron}","Eberron"],["{@deity Helm}","Forgotten Realms"],["{@deity Ilmater}","Forgotten Realms"],["{@deity Mishakal|Dragonlance}","Dragonlance"],["{@deity Selûne}","Forgotten Realms"],["{@deity Yondalla|Nonhuman}","Halfling"]]', '{"type":"table","caption":"Twilight Deities","colLabels":["Example Deity","Pantheon"],"colStyles":["col-6 text-center","col-6 text-center"],"rows":[["{@deity Boldrei|Eberron}","Eberron"],["{@deity Celestian|Greyhawk}","Greyhawk"],["{@deity Dol Arrah|Eberron}","Eberron"],["{@deity Helm}","Forgotten Realms"],["{@deity Ilmater}","Forgotten Realms"],["{@deity Mishakal|Dragonlance}","Dragonlance"],["{@deity Selûne}","Forgotten Realms"],["{@deity Yondalla|Nonhuman}","Halfling"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Twilight Domain' AND class_name = 'Cleric' AND level = 1), 3, 'entries', 'Domain Spells', '["{@i 1st-level Twilight Domain feature}","You gain domain spells at the cleric levels listed in the Twilight Domain Spells table. See the Divine Domain class feature for how domain spells work.",{"type":"table","caption":"Twilight Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell faerie fire}, {@spell sleep}"],["3rd","{@spell moonbeam}, {@spell see invisibility}"],["5th","{@spell aura of vitality}, {@spell Leomund''s tiny hut}"],["7th","{@spell aura of life}, {@spell greater invisibility}"],["9th","{@spell circle of power}, {@spell mislead}"]]}]', NULL, NULL, NULL, NULL, '{"name":"Domain Spells","type":"entries","entries":["{@i 1st-level Twilight Domain feature}","You gain domain spells at the cleric levels listed in the Twilight Domain Spells table. See the Divine Domain class feature for how domain spells work.",{"type":"table","caption":"Twilight Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell faerie fire}, {@spell sleep}"],["3rd","{@spell moonbeam}, {@spell see invisibility}"],["5th","{@spell aura of vitality}, {@spell Leomund''s tiny hut}"],["7th","{@spell aura of life}, {@spell greater invisibility}"],["9th","{@spell circle of power}, {@spell mislead}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Twilight Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiencies|Cleric||Twilight|TCE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Twilight Domain' AND class_name = 'Cleric' AND level = 1), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Eyes of Night|Cleric||Twilight|TCE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Twilight Domain' AND class_name = 'Cleric' AND level = 1), 6, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Vigilant Blessing|Cleric||Twilight|TCE|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiencies', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Cleric' AND level = 1), 0, '{@i 1st-level Twilight Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Cleric' AND level = 1), 1, 'You gain proficiency with martial weapons and heavy armor.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eyes of Night', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eyes of Night' AND class_name = 'Cleric' AND level = 1), 0, '{@i 1st-level Twilight Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eyes of Night' AND class_name = 'Cleric' AND level = 1), 1, 'You can see through the deepest gloom. You have {@sense darkvision} out to a range of 300 feet. In that radius, you can see in dim light as if it were bright light and in darkness as if it were dim light.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eyes of Night' AND class_name = 'Cleric' AND level = 1), 2, 'As an action, you can magically share the {@sense darkvision} of this feature with willing creatures you can see within 10 feet of you, up to a number of creatures equal to your Wisdom modifier (minimum of one creature). The shared {@sense darkvision} lasts for 1 hour. Once you share it, you can''t do so again until you finish a long rest, unless you expend a spell slot of any level to share it again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Vigilant Blessing', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vigilant Blessing' AND class_name = 'Cleric' AND level = 1), 0, '{@i 1st-level Twilight Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vigilant Blessing' AND class_name = 'Cleric' AND level = 1), 1, 'The night has taught you to be vigilant. As an action, you give one creature you touch (including possibly yourself) advantage on the next initiative roll the creature makes. This benefit ends immediately after the roll or if you use this feature again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Twilight Sanctuary', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Twilight Sanctuary' AND class_name = 'Cleric' AND level = 2), 0, '{@i 2nd-level Twilight Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Twilight Sanctuary' AND class_name = 'Cleric' AND level = 2), 1, 'You can use your Channel Divinity to refresh your allies with soothing twilight.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Twilight Sanctuary' AND class_name = 'Cleric' AND level = 2), 2, 'As an action, you present your holy symbol, and a sphere of twilight emanates from you. The sphere is centered on you, has a 30-foot radius, and is filled with dim light. The sphere moves with you, and it lasts for 1 minute or until you are {@condition incapacitated} or die. Whenever a creature (including you) ends its turn in the sphere, you can grant that creature one of these benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Twilight Sanctuary' AND class_name = 'Cleric' AND level = 2), 3, 'list', NULL, '["You grant it temporary hit points equal to {@dice 1d6} plus your cleric level.","You end one effect on it causing it to be {@condition charmed} or {@condition frightened}."]', '["You grant it temporary hit points equal to {@dice 1d6} plus your cleric level.","You end one effect on it causing it to be {@condition charmed} or {@condition frightened}."]', NULL, NULL, NULL, '{"type":"list","items":["You grant it temporary hit points equal to {@dice 1d6} plus your cleric level.","You end one effect on it causing it to be {@condition charmed} or {@condition frightened}."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Steps of Night', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steps of Night' AND class_name = 'Cleric' AND level = 6), 0, '{@i 6th-level Twilight Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steps of Night' AND class_name = 'Cleric' AND level = 6), 1, 'You can draw on the mystical power of night to rise into the air. As a bonus action when you are in dim light or darkness, you can magically give yourself a flying speed equal to your walking speed for 1 minute. You can use this bonus action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level Twilight Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 1, 'You gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} radiant damage. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Twilight Shroud', 2, 34, 'Cleric', 'PHB', 'Twilight', 'TCE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Twilight Shroud' AND class_name = 'Cleric' AND level = 17), 0, '{@i 17th-level Twilight Domain feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Twilight Shroud' AND class_name = 'Cleric' AND level = 17), 1, 'The twilight that you summon offers a protective embrace: you and your allies have {@quickref Cover||3||half cover} while in the sphere created by your Twilight Sanctuary.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blood Domain', null, 168, 'Cleric', 'PHB', 'Blood', 'TDCSR', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blood Domain' AND class_name = 'Cleric' AND level = 1), 0, 'Developed in {@book Wildemount|TDCSR|3|Wildemount} by the {@book Claret Orders|TDCSR|2|Claret Orders}, the Blood Domain centers around the understanding of the natural life force as it exists within the body, and the divine conduit it can become. Those who take up this domain understand that the power of blood is the power of sacrifice, the balance of life and death, and the spirit''s anchor within the mortal shell.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blood Domain' AND class_name = 'Cleric' AND level = 1), 1, 'Gods who grant the power of the Blood Domain, including {@deity The Ruiner|Exandria|TDCSR} and {@deity The Matron of Ravens|Exandria|TDCSR}, direct their followers to tap into the connection between body and soul, exploit the hidden reserves of will within one''s own vitality, and corrupt the bodies of others through the secret rites of {@variantrule hemocraft|TDCSR}. Clerics of good gods use {@variantrule hemocraft|TDCSR} to fill their self-sacrifice with purpose and power, while clerics with fewer morals use the blood of others to achieve their own malevolent ends.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Blood Domain' AND class_name = 'Cleric' AND level = 1), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Domain Spells|Cleric|PHB|Blood|TDCSR|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Blood Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiencies|Cleric|PHB|Blood|TDCSR|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Blood Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bloodletting Focus|Cleric|PHB|Blood|TDCSR|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bloodletting Focus', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 1, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bloodletting Focus' AND class_name = 'Cleric' AND level = 1), 0, 'Starting at 1st level, your divine magic draws the blood from magically inflicted wounds, worsening the agony of your foes. When you cast a damage-dealing spell of 1st level or higher whose duration is instantaneous, any creature with blood that takes damage from the spell takes extra necrotic damage equal to 2 + the spell''s level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiencies', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 1, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you gain proficiency with martial weapons.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Domain Spells', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 1, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Domain Spells' AND class_name = 'Cleric' AND level = 1), 0, 'You gain domain spells at the cleric levels listed in the Blood Domain Spells table.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Domain Spells' AND class_name = 'Cleric' AND level = 1), 1, 'table', NULL, NULL, NULL, 'Blood Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell false life}, {@spell sleep}"],["3rd","{@spell hold person}, {@spell ray of enfeeblement}"],["5th","{@spell haste}, {@spell slow}"],["7th","{@spell blight}, {@spell stoneskin}"],["9th","{@spell dominate person}, {@spell hold monster}"]]', '{"type":"table","caption":"Blood Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell false life}, {@spell sleep}"],["3rd","{@spell hold person}, {@spell ray of enfeeblement}"],["5th","{@spell haste}, {@spell slow}"],["7th","{@spell blight}, {@spell stoneskin}"],["9th","{@spell dominate person}, {@spell hold monster}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Crimson Bond', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Crimson Bond' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to form a supernatural bond with a creature you can see, or with a creature for which you possess a blood sample. This bond lasts for 1 hour or until your {@status concentration} is broken (as if concentrating on a spell).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Crimson Bond' AND class_name = 'Cleric' AND level = 2), 1, 'While the bond is in effect, you can use an action to learn the target''s approximate distance and direction from you, as well as its current hit points and any conditions affecting it, as long as the target is within 10 miles of you. Alternatively, you can use your action to attempt to connect with the target''s senses. You take {@damage 2d6} necrotic damage and the target makes a Constitution {@quickref saving throws|PHB|2|1|saving throw} against your spell save DC. On a successful save, the bond ends. On a failure, you can choose to either see or hear through the target''s senses for a number of minutes equal to your Wisdom modifier (minimum 1 minute). During this time, you are {@condition blinded} or {@condition deafened} (respectively) with regard to your own senses. When the connection ends, the bond is lost.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Crimson Bond' AND class_name = 'Cleric' AND level = 2), 2, 'Regardless of the outcome, the target feels a wave of unease pass over it when it makes this save.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Blood Puppet', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Blood Puppet' AND class_name = 'Cleric' AND level = 6), 0, 'Starting at 6th level, you can use your Channel Divinity to briefly control a creature''s actions—whether that creature is living or dead. As an action, you target a Large or smaller creature or corpse within 60 feet of you that has blood. A creature you target must succeed on a Wisdom {@quickref saving throws|PHB|2|1|saving throw} against your spell save DC or become {@condition charmed} by you. An {@condition unconscious} creature automatically fails its {@quickref saving throws|PHB|2|1|saving throw}, and isn''t {@condition incapacitated} while you control its actions. A corpse targeted by this effect gains a semblance of life that you control.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Blood Puppet' AND class_name = 'Cleric' AND level = 6), 1, 'On the affected creature or animated corpse''s turn, you can command it (no action required) to move up to half its speed and use its action to do one of the following:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Blood Puppet' AND class_name = 'Cleric' AND level = 6), 2, 'list', NULL, '["{@action Use an Object|PHB|Interact with an object}","Make a single {@action attack}","Do nothing"]', '["{@action Use an Object|PHB|Interact with an object}","Make a single {@action attack}","Do nothing"]', NULL, NULL, NULL, '{"type":"list","items":["{@action Use an Object|PHB|Interact with an object}","Make a single {@action attack}","Do nothing"]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Blood Puppet' AND class_name = 'Cleric' AND level = 6), 3, 'An animated corpse or an {@condition unconscious} creature takes its turn immediately after yours, but can''t move or take actions unless you command it to do so. Its statistics are the same as when it was alive or conscious.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Blood Puppet' AND class_name = 'Cleric' AND level = 6), 4, 'An affected living creature makes a new {@quickref saving throws|PHB|2|1|saving throw} at the end of each of its turns, ending the effect on itself on a success. For any target, your control lasts for 1 minute or until your {@status concentration} is broken (as if concentrating on a spell).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sanguine Recall', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sanguine Recall' AND class_name = 'Cleric' AND level = 6), 0, 'At 6th level, you can sacrifice a portion of your own vitality to recover expended spell slots as an action. The spell slots can have a combined level equal to or less than half your cleric level (rounded up), and none of the slots can be 6th level or higher. You take {@damage 1d8} necrotic damage for each spell slot level recovered, which can''t be reduced in any way. You can''t use this feature again until you finish a {@quickref resting|PHB|2|0|long rest}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sanguine Recall' AND class_name = 'Cleric' AND level = 6), 1, 'For example, if you''re an 8th-level cleric, you can recover up to four levels of spell slots—a single 4th-level slot, two 2nd-level slots, a 3rd-level slot and a 1st-level slot, or four 1st-level slots. You then take {@damage 4d8} necrotic damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to cause the physical wounds you deal out to bleed profusely. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} necrotic damage to the target.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike (14th Level)', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike (14th Level)' AND class_name = 'Cleric' AND level = 14), 0, 'When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Blood Puppet (17th Level)', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Blood Puppet (17th Level)' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you can use this feature to target a Huge or smaller creature or corpse.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Vascular Corruption Aura', null, 169, 'Cleric', 'PHB', 'Blood', 'TDCSR', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vascular Corruption Aura' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you can use your action to emit a deathly aura of necrotic energy that causes the veins of nearby foes to burst and bleed. For 1 minute, any hostile creature with blood that moves within 30 feet of you for the first time on a turn or starts its turn there takes {@damage 3d6} necrotic damage. If a hostile creature with blood regains hit points while in the aura, it regains only half as many hit points as expected.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vascular Corruption Aura' AND class_name = 'Cleric' AND level = 17), 1, 'Once you use this feature, you can''t use it again until you finish a {@quickref resting|PHB|2|0|long rest}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Moon Domain', null, 169, 'Cleric', 'PHB', 'Moon', 'TDCSR', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Moon Domain' AND class_name = 'Cleric' AND level = 1), 0, 'Since the {@book Founding|TDCSR|1|The Founding}, all mortal creatures have looked to the skies and sensed the power and mystery carried by {@book Catha and Ruidus|TDCSR|1|Catha and Ruidus}, the two moons of Exandria. The {@deity The Moonweaver|Exandria|TDCSR} herself is said to guide her worshipers from within the pale glow of Catha, revealing hidden paths and granting inspired dreams to those who pray to that moon. Distant, dark Ruidus is often regarded with awe and apprehension, considered a portent for fell deeds and bad luck.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Moon Domain' AND class_name = 'Cleric' AND level = 1), 1, 'Clerics of the Moon Domain draw on their divine connection to the moons to wield light and shadow, invoke good fortune and ill favor, and reveal or conceal as they see fit. Most Moon clerics worship the {@deity The Moonweaver|Exandria|TDCSR|Moonweaver} herself, but her followers are as varied as the stars in the sky. Some seek to protect the vulnerable and do good in the world, while others meddle with impunity and distort the truth for selfish gain. Other Moon clerics worship not the gods but the moons themselves, especially those with an innate connection to the lunar cycles through {@variantrule Player Characters as Lycanthropes|MM|lycanthropy}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Moon Domain' AND class_name = 'Cleric' AND level = 1), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Domain Spells|Cleric|PHB|Moon|TDCSR|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Moon Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Clarity of Catha|Cleric|PHB|Moon|TDCSR|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Clarity of Catha', null, 170, 'Cleric', 'PHB', 'Moon', 'TDCSR', 1, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Clarity of Catha' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you learn to shine light upon the mind''s most dire moments, shielding those you protect. When a creature within 30 feet of you that you can see makes a Wisdom {@quickref saving throws|PHB|2|1|saving throw}, you can use your reaction to grant that creature {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on the save.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Clarity of Catha' AND class_name = 'Cleric' AND level = 1), 1, 'You can use this feature a number of times equal to your proficiency bonus, regaining all expended uses when you finish a {@quickref resting|PHB|2|0|long rest}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Domain Spells', null, 170, 'Cleric', 'PHB', 'Moon', 'TDCSR', 1, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Domain Spells' AND class_name = 'Cleric' AND level = 1), 0, 'You gain domain spells at the cleric levels listed in the Moon Domain Spells table.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Domain Spells' AND class_name = 'Cleric' AND level = 1), 1, 'table', NULL, NULL, NULL, 'Moon Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell faerie fire}, {@spell silent image}"],["3rd","{@spell invisibility}, {@spell moonbeam}"],["5th","{@spell hypnotic pattern}, {@spell major image}"],["7th","{@spell greater invisibility}, {@spell hallucinatory terrain}"],["9th","{@spell dream}, {@spell passwall}"]]', '{"type":"table","caption":"Moon Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell faerie fire}, {@spell silent image}"],["3rd","{@spell invisibility}, {@spell moonbeam}"],["5th","{@spell hypnotic pattern}, {@spell major image}"],["7th","{@spell greater invisibility}, {@spell hallucinatory terrain}"],["9th","{@spell dream}, {@spell passwall}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Blessing of the Full Moon', null, 170, 'Cleric', 'PHB', 'Moon', 'TDCSR', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Blessing of the Full Moon' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to infuse your allies with bestial power. As an action, you instill a willing creature of your choice within 30 feet of you that you can see with one of the following blessings of your choice:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Blessing of the Full Moon' AND class_name = 'Cleric' AND level = 2), 1, 'list', NULL, '[{"type":"item","name":"Blessing of the Watchful Moon","entries":["For 1 hour, the blessed creature''s speed increases by 10 feet, and it has {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Wisdom ({@skill Perception} or {@skill Survival}) checks involving smell or made to track a creature."]},{"type":"item","name":"Blessing of the Blood-Drenched Moon","entries":["For 10 minutes, the blessed creature has {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on attack rolls against a target if at least one of the blessed creature''s allies is within 5 feet of the target and the ally isn''t {@condition incapacitated}."]}]', '[{"type":"item","name":"Blessing of the Watchful Moon","entries":["For 1 hour, the blessed creature''s speed increases by 10 feet, and it has {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Wisdom ({@skill Perception} or {@skill Survival}) checks involving smell or made to track a creature."]},{"type":"item","name":"Blessing of the Blood-Drenched Moon","entries":["For 10 minutes, the blessed creature has {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on attack rolls against a target if at least one of the blessed creature''s allies is within 5 feet of the target and the ally isn''t {@condition incapacitated}."]}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Blessing of the Watchful Moon","entries":["For 1 hour, the blessed creature''s speed increases by 10 feet, and it has {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Wisdom ({@skill Perception} or {@skill Survival}) checks involving smell or made to track a creature."]},{"type":"item","name":"Blessing of the Blood-Drenched Moon","entries":["For 10 minutes, the blessed creature has {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on attack rolls against a target if at least one of the blessed creature''s allies is within 5 feet of the target and the ally isn''t {@condition incapacitated}."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Mind of Two Moons', null, 170, 'Cleric', 'PHB', 'Moon', 'TDCSR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Mind of Two Moons' AND class_name = 'Cleric' AND level = 6), 0, 'Starting at 6th level, you can use your Channel Divinity to invoke the twofold arcana of Exandria''s moons. By expending one use of Channel Divinity, you can cast a second {@status concentration} spell while already concentrating on a first spell, as long as both spells are on your list of Moon Domain spells. If you need to make a Constitution {@quickref saving throws|PHB|2|1|saving throw} to maintain your {@status concentration} on both spells, you make the save with {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage}. On a failure, you lose {@status concentration} on both spells.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Empowered Cantrips', null, 171, 'Cleric', 'PHB', 'Moon', 'TDCSR', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Empowered Cantrips' AND class_name = 'Cleric' AND level = 8), 0, 'Starting at 8th level, your cleric cantrips deal extra damage equal to your Wisdom modifier (minimum of 1).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eclipse of Ill Omen', null, 171, 'Cleric', 'PHB', 'Moon', 'TDCSR', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eclipse of Ill Omen' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, you can call upon the vermillion moon Ruidus to flare in the sky above you, eclipsing all other light. Its power surrounds you even where the sky can''t be seen, and even on other planes. As a bonus action, you can manifest an area of reddish, dim light in a 60-foot radius around you. In addition to the normal effects of dim light, creatures in the area make {@quickref saving throws|PHB|2|1} with {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage}. When you create this eclipse, you can choose any number of creatures that are unaffected by it.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eclipse of Ill Omen' AND class_name = 'Cleric' AND level = 17), 1, 'This eclipse lasts while you concentrate (as if concentrating on a spell) for up to 1 minute. Concentrating on this feature counts as concentrating on a Moon Domain spell for the purpose of your {@subclassFeature Channel Divinity: Mind of Two Moons|Cleric|PHB|Moon|TDCSR|6|TDCSR|Mind of Two Moons} feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eclipse of Ill Omen' AND class_name = 'Cleric' AND level = 17), 2, 'Additionally, once per turn when you deal radiant damage to any creatures in this area of dim light, you can curse one of those creatures until the eclipse ends (no action required). A creature cursed in this way has its speed halved and can''t regain hit points.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eclipse of Ill Omen' AND class_name = 'Cleric' AND level = 17), 3, 'Once you use this feature, you can''t use it again until you finish a {@quickref resting|PHB|2|0|long rest}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Forge Domain', 3, 18, 'Cleric', 'PHB', 'Forge', 'XGE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Forge Domain' AND class_name = 'Cleric' AND level = 1), 0, 'The gods of the forge are patrons of artisans who work with metal, from a humble blacksmith who keeps a village in horseshoes and plow blades to the mighty elf artisan whose diamond-tipped arrows of mithral have felled demon lords. The gods of the forge teach that, with patience and hard work, even the most intractable metal can be transformed from a lump of ore to a beautifully wrought object. Clerics of these deities search for objects lost to the forces of darkness, liberate mines overrun by orcs, and uncover rare and wondrous materials necessary to create potent magic items. Followers of these gods take great pride in their work, and they are willing to craft and use heavy armor and powerful weapons to protect them. Deities of this domain include {@deity Gond}, {@deity Reorx|Dragonlance}, {@deity Onatar|Eberron}, {@deity Moradin|Nonhuman}, {@deity Hephaestus|Greek}, and {@deity Goibhniu|Celtic}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Forge Domain' AND class_name = 'Cleric' AND level = 1), 1, 'At each indicated cleric level, add the listed spells to your spells prepared.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Forge Domain' AND class_name = 'Cleric' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Forge Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell identify}, {@spell searing smite}"],["3rd","{@spell heat metal}, {@spell magic weapon}"],["5th","{@spell elemental weapon}, {@spell protection from energy}"],["7th","{@spell fabricate}, {@spell wall of fire}"],["9th","{@spell animate objects}, {@spell creation}"]]', '{"type":"table","caption":"Forge Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell identify}, {@spell searing smite}"],["3rd","{@spell heat metal}, {@spell magic weapon}"],["5th","{@spell elemental weapon}, {@spell protection from energy}"],["7th","{@spell fabricate}, {@spell wall of fire}"],["9th","{@spell animate objects}, {@spell creation}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Forge Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiency|Cleric||Forge|XGE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Forge Domain' AND class_name = 'Cleric' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Blessing of the Forge|Cleric||Forge|XGE|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessing of the Forge', 3, 18, 'Cleric', 'PHB', 'Forge', 'XGE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessing of the Forge' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, you gain the ability to imbue magic into a weapon or armor. At the end of a long rest, you can touch one nonmagical object that is a suit of armor or a {@filter simple or martial weapon|items|source=phb|category=basic|type=martial weapon;simple weapon}. Until the end of your next long rest or until you die, the object becomes a magic item, granting a +1 bonus to AC if it''s armor or a +1 bonus to attack and damage rolls if it''s a weapon.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessing of the Forge' AND class_name = 'Cleric' AND level = 1), 1, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiency', 3, 18, 'Cleric', 'PHB', 'Forge', 'XGE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiency' AND class_name = 'Cleric' AND level = 1), 0, 'When you choose this domain at 1st level, you gain proficiency with heavy armor and {@item smith''s tools|phb}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Artisan''s Blessing', 3, 18, 'Cleric', 'PHB', 'Forge', 'XGE', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Artisan''s Blessing' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to create simple items.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Artisan''s Blessing' AND class_name = 'Cleric' AND level = 2), 1, 'You conduct an hour-long ritual that crafts a nonmagical item that must include some metal: a {@filter simple or martial weapon|items|source=phb|category=basic|type=martial weapon;simple weapon}, a suit of armor, ten pieces of ammunition, a set of tools, or another metal object (see chapter 5, "Equipment," in the Player''s Handbook for examples of these items). The creation is completed at the end of the hour, coalescing in an unoccupied space of your choice on a surface within 5 feet of you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Artisan''s Blessing' AND class_name = 'Cleric' AND level = 2), 2, 'The thing you create can be something that is worth no more than 100 gp. As part of this ritual, you must lay out metal, which can include coins, with a value equal to the creation. The metal irretrievably coalesces and transforms into the creation at the ritual''s end, magically forming even nonmetal parts of the creation. The ritual can create a duplicate of a nonmagical item that contains metal, such as a key, if you possess the original during the ritual.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Soul of the Forge', 3, 18, 'Cleric', 'PHB', 'Forge', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Soul of the Forge' AND class_name = 'Cleric' AND level = 6), 0, 'Starting at 6th level, your mastery of the forge grants you special abilities:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Soul of the Forge' AND class_name = 'Cleric' AND level = 6), 1, 'list', NULL, '["You gain resistance to fire damage.","While wearing heavy armor, you gain a +1 bonus to AC."]', '["You gain resistance to fire damage.","While wearing heavy armor, you gain a +1 bonus to AC."]', NULL, NULL, NULL, '{"type":"list","items":["You gain resistance to fire damage.","While wearing heavy armor, you gain a +1 bonus to AC."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Forge', 'XGE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Divine Strike feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Strike', 3, 18, 'Cleric', 'PHB', 'Forge', 'XGE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Strike' AND class_name = 'Cleric' AND level = 8), 0, 'At 8th level, you gain the ability to infuse your weapon strikes with the fiery power of the forge. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra {@damage 1d8} fire damage to the target. When you reach 14th level, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Saint of Forge and Fire', 3, 18, 'Cleric', 'PHB', 'Forge', 'XGE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Saint of Forge and Fire' AND class_name = 'Cleric' AND level = 17), 0, 'At 17th level, your blessed affinity with fire and metal becomes more powerful:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Saint of Forge and Fire' AND class_name = 'Cleric' AND level = 17), 1, 'list', NULL, '["You gain immunity to fire damage","While wearing heavy armor, you have resistance to bludgeoning, piercing, and slashing damage from nonmagical attacks."]', '["You gain immunity to fire damage","While wearing heavy armor, you have resistance to bludgeoning, piercing, and slashing damage from nonmagical attacks."]', NULL, NULL, NULL, '{"type":"list","items":["You gain immunity to fire damage","While wearing heavy armor, you have resistance to bludgeoning, piercing, and slashing damage from nonmagical attacks."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Grave Domain', 3, 19, 'Cleric', 'PHB', 'Grave', 'XGE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Grave Domain' AND class_name = 'Cleric' AND level = 1), 0, 'Gods of the grave watch over the line between life and death. To these deities, death and the afterlife are a foundational part of the multiverse. To desecrate the peace of the dead is an abomination. Deities of the grave include {@deity Kelemvor}, {@deity Wee Jas|Greyhawk}, the ancestral spirits of {@deity the Undying Court|Eberron}, {@deity Hades|Greek}, {@deity Anubis|Egyptian}, and {@deity Osiris|Egyptian}. Followers of these deities seek to put wandering spirits to rest, destroy the undead, and ease the suffering of the dying. Their magic also allows them to stave off death for a time, particularly for a person who still has some great work to accomplish in the world. This is a delay of death, not a denial of it, for death will eventually get its due.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Grave Domain' AND class_name = 'Cleric' AND level = 1), 1, 'table', NULL, NULL, NULL, 'Grave Domain Spells', '["Cleric Level","Spells"]', '[["1st","{@spell bane}, {@spell false life}"],["3rd","{@spell gentle repose}, {@spell ray of enfeeblement}"],["5th","{@spell revivify}, {@spell vampiric touch}"],["7th","{@spell blight}, {@spell death ward}"],["9th","{@spell antilife shell}, {@spell raise dead}"]]', '{"type":"table","caption":"Grave Domain Spells","colLabels":["Cleric Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell bane}, {@spell false life}"],["3rd","{@spell gentle repose}, {@spell ray of enfeeblement}"],["5th","{@spell revivify}, {@spell vampiric touch}"],["7th","{@spell blight}, {@spell death ward}"],["9th","{@spell antilife shell}, {@spell raise dead}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Grave Domain' AND class_name = 'Cleric' AND level = 1), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Circle of Mortality|Cleric||Grave|XGE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Grave Domain' AND class_name = 'Cleric' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Eyes of the Grave|Cleric||Grave|XGE|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of Mortality', 3, 19, 'Cleric', 'PHB', 'Grave', 'XGE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Mortality' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, you gain the ability to manipulate the line between life and death. When you would normally roll one or more dice to restore hit points with a spell to a creature at 0 hit points, you instead use the highest number possible for each die.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Mortality' AND class_name = 'Cleric' AND level = 1), 1, 'In addition, you learn the {@spell spare the dying} cantrip, which doesn''t count against the number of cleric cantrips you know. For you, it has a range of 30 feet, and you can cast it as a bonus action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eyes of the Grave', 3, 19, 'Cleric', 'PHB', 'Grave', 'XGE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eyes of the Grave' AND class_name = 'Cleric' AND level = 1), 0, 'At 1st level, you gain the ability to occasionally sense the presence of the undead, whose existence is an insult to the natural cycle of life. As an action, you can open your awareness to magically detect undead. Until the end of your next turn, you know the location of any undead within 60 feet of you that isn''t behind {@quickref Cover||3||total cover} and that isn''t protected from divination magic. This sense doesn''t tell you anything about a creature''s capabilities or identity.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eyes of the Grave' AND class_name = 'Cleric' AND level = 1), 1, 'You can use this feature a number of times equal to your Wisdom modifier (minimum of once). You regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity: Path to the Grave', 3, 19, 'Cleric', 'PHB', 'Grave', 'XGE', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Path to the Grave' AND class_name = 'Cleric' AND level = 2), 0, 'Starting at 2nd level, you can use your Channel Divinity to mark another creature''s life force for termination.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity: Path to the Grave' AND class_name = 'Cleric' AND level = 2), 1, 'As an action, you choose one creature you can see within 30 feet of you, cursing it until the end of your next turn. The next time you or an ally of yours hits the cursed creature with an attack, the creature has vulnerability to all of that attack''s damage, and then the curse ends.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sentinel at Death''s Door', 3, 19, 'Cleric', 'PHB', 'Grave', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sentinel at Death''s Door' AND class_name = 'Cleric' AND level = 6), 0, 'At 6th level, you gain the ability to impede death''s progress. As a reaction when you or a creature you can see within 30 feet of you suffers a critical hit, you can turn that hit into a normal hit. Any effects triggered by a critical hit are canceled.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sentinel at Death''s Door' AND class_name = 'Cleric' AND level = 6), 1, 'You can use this feature a number of times equal to your Wisdom modifier (minimum of once). You regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blessed Strikes', 2, 30, 'Cleric', 'PHB', 'Grave', 'XGE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 0, '{@i 8th-level cleric {@variantrule optional class features|tce|optional feature}, which replaces the Potent Spellcasting feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blessed Strikes' AND class_name = 'Cleric' AND level = 8), 1, 'You are blessed with divine might in battle. When a creature takes damage from one of your cantrips or weapon attacks, you can also deal {@damage 1d8} radiant damage to that creature. Once you deal this damage, you can''t use this feature again until the start of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Potent Spellcasting', 3, 19, 'Cleric', 'PHB', 'Grave', 'XGE', 8, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Potent Spellcasting' AND class_name = 'Cleric' AND level = 8), 0, 'Starting at 8th level, you add your Wisdom modifier to the damage you deal with any cleric cantrip.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Keeper of Souls', 3, 19, 'Cleric', 'PHB', 'Grave', 'XGE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Keeper of Souls' AND class_name = 'Cleric' AND level = 17), 0, 'Starting at 17th level, you can seize a trace of vitality from a parting soul and use it to heal the living. When an enemy you can see dies within 60 feet of you, you or one creature of your choice that is within 60 feet of you regains hit points equal to the enemy''s number of Hit Dice. You can use this feature only if you aren''t {@condition incapacitated}. Once you use it, you can''t do so again until the start of your next turn.');
