INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Druidic', 1, 64, 'Druid', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Druidic' AND class_name = 'Druid' AND level = 1), 0, 'You know {@language Druidic}, the secret language of druids. You can speak the language and use it to leave hidden messages. You and others who know this language automatically spot such a message. Others spot the message''s presence with a successful {@dc 15} Wisdom ({@skill Perception}) check but can''t decipher it without magic.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spellcasting', 1, 64, 'Druid', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Druid' AND level = 1), 0, 'Drawing on the divine essence of nature itself, you can cast spells to shape that essence to your will. See {@book chapter 10|PHB|10} for the general rules of spellcasting and {@book chapter 11|PHB|11} for the {@filter druid spell list|spells|class=druid}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Druid' AND level = 1), 1, 'entries', 'Cantrips', '["At 1st level, you know two cantrips of your choice from the {@filter druid spell list|spells|level=0|class=druid}. You learn additional druid cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Druid table."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Cantrips","entries":["At 1st level, you know two cantrips of your choice from the {@filter druid spell list|spells|level=0|class=druid}. You learn additional druid cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Druid table."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Druid' AND level = 1), 2, 'entries', 'Preparing and Casting Spells', '["The Druid table shows how many spell slots you have to cast your {@filter druid spells|spells|class=druid} of 1st level and higher. To cast one of these druid spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","You prepare the list of druid spells that are available for you to cast, choosing from the druid spell list. When you do so, choose a number of druid spells equal to your Wisdom modifier + your druid level (minimum of one spell). The spells must be of a level for which you have spell slots.","For example, if you are a 3rd-level druid, you have four 1st-level and two 2nd-level spell slots. With a Wisdom of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination. If you prepare the 1st-level spell {@spell cure wounds}, you can cast it using a 1st-level or 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.","You can also change your list of prepared spells when you finish a long rest. Preparing a new list of druid spells requires time spent in prayer and meditation: at least 1 minute per spell level for each spell on your list."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Preparing and Casting Spells","entries":["The Druid table shows how many spell slots you have to cast your {@filter druid spells|spells|class=druid} of 1st level and higher. To cast one of these druid spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","You prepare the list of druid spells that are available for you to cast, choosing from the druid spell list. When you do so, choose a number of druid spells equal to your Wisdom modifier + your druid level (minimum of one spell). The spells must be of a level for which you have spell slots.","For example, if you are a 3rd-level druid, you have four 1st-level and two 2nd-level spell slots. With a Wisdom of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination. If you prepare the 1st-level spell {@spell cure wounds}, you can cast it using a 1st-level or 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.","You can also change your list of prepared spells when you finish a long rest. Preparing a new list of druid spells requires time spent in prayer and meditation: at least 1 minute per spell level for each spell on your list."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Druid' AND level = 1), 3, 'entries', 'Spellcasting Ability', '["Wisdom is your spellcasting ability for your druid spells, since your magic draws upon your devotion and attunement to nature. You use your Wisdom whenever a spell refers to your spellcasting ability. In addition, you use your Wisdom modifier when setting the saving throw DC for a druid spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["wis"]},{"type":"abilityAttackMod","name":"Spell","attributes":["wis"]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Ability","entries":["Wisdom is your spellcasting ability for your druid spells, since your magic draws upon your devotion and attunement to nature. You use your Wisdom whenever a spell refers to your spellcasting ability. In addition, you use your Wisdom modifier when setting the saving throw DC for a druid spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["wis"]},{"type":"abilityAttackMod","name":"Spell","attributes":["wis"]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Druid' AND level = 1), 4, 'entries', 'Ritual Casting', '["You can cast a druid spell as a ritual if that spell has the ritual tag and you have the spell prepared."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Ritual Casting","entries":["You can cast a druid spell as a ritual if that spell has the ritual tag and you have the spell prepared."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Druid' AND level = 1), 5, 'entries', 'Spellcasting Focus', '["You can use a {@item druidic focus|phb} as a spellcasting focus for your druid spells."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Focus","entries":["You can use a {@item druidic focus|phb} as a spellcasting focus for your druid spells."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Druid Circle', 1, 64, 'Druid', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Druid Circle' AND class_name = 'Druid' AND level = 2), 0, 'At 2nd level, you choose to identify with a circle of druids from the list of available circles. Your choice grants you features at 2nd level and again at 6th, 10th, and 14th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wild Companion', 2, 35, 'Druid', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Companion' AND class_name = 'Druid' AND level = 2), 0, '{@i 2nd-level druid {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Companion' AND class_name = 'Druid' AND level = 2), 1, 'You gain the ability to summon a spirit that assumes an animal form: as an action, you can expend a use of your Wild Shape feature to cast the {@spell find familiar} spell, without material components.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Companion' AND class_name = 'Druid' AND level = 2), 2, 'When you cast the spell in this way, the familiar is a fey instead of a beast, and the familiar disappears after a number of hours equal to half your druid level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wild Shape', 1, 64, 'Druid', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Shape' AND class_name = 'Druid' AND level = 2), 0, 'Starting at 2nd level, you can use your action to magically assume the shape of a beast that you have seen before. You can use this feature twice. You regain expended uses when you finish a short or long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Shape' AND class_name = 'Druid' AND level = 2), 1, 'Your druid level determines the beasts you can transform into, as shown in the Beast Shapes table. At 2nd level, for example, you can transform into any beast that has a challenge rating of 1/4 or lower that doesn''t have a flying or swimming speed.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Wild Shape' AND class_name = 'Druid' AND level = 2), 2, 'table', NULL, NULL, NULL, 'Beast Shapes', '["Level","Max. CR","Limitations","Example"]', '[["2nd","{@filter 1/4|bestiary|challenge rating=[&0;&1/4]|type=beast|speed type=!fly;!swim|miscellaneous=!swarm}","No flying or swimming speed","Wolf"],["4th","{@filter 1/2|bestiary|challenge rating=[&0;&1/2]|type=beast|speed type=!fly|miscellaneous=!swarm}","No flying speed","Crocodile"],["8th","{@filter 1|bestiary|challenge rating=[&0;&1]|type=beast|miscellaneous=!swarm}","—","Giant eagle"]]', '{"type":"table","caption":"Beast Shapes","colLabels":["Level","Max. CR","Limitations","Example"],"colStyles":["col-2 text-center","col-2 text-center","col-5","col-3"],"rows":[["2nd","{@filter 1/4|bestiary|challenge rating=[&0;&1/4]|type=beast|speed type=!fly;!swim|miscellaneous=!swarm}","No flying or swimming speed","Wolf"],["4th","{@filter 1/2|bestiary|challenge rating=[&0;&1/2]|type=beast|speed type=!fly|miscellaneous=!swarm}","No flying speed","Crocodile"],["8th","{@filter 1|bestiary|challenge rating=[&0;&1]|type=beast|miscellaneous=!swarm}","—","Giant eagle"]]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Shape' AND class_name = 'Druid' AND level = 2), 3, 'You can stay in a beast shape for a number of hours equal to half your druid level (rounded down). You then revert to your normal form unless you expend another use of this feature. You can revert to your normal form earlier by using a bonus action on your turn. You automatically revert if you fall {@condition unconscious}, drop to 0 hit points, or die.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wild Shape' AND class_name = 'Druid' AND level = 2), 4, 'While you are transformed, the following rules apply:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Wild Shape' AND class_name = 'Druid' AND level = 2), 5, 'list', NULL, '["Your game statistics are replaced by the statistics of the beast, but you retain your alignment, personality, and Intelligence, Wisdom, and Charisma scores. You also retain all of your skill and saving throw proficiencies, in addition to gaining those of the creature. If the creature has the same proficiency as you and the bonus in its stat block is higher than yours, use the creature''s bonus instead of yours. If the creature has any legendary or lair actions, you can''t use them.","When you transform, you assume the beast''s hit points and Hit Dice. When you revert to your normal form, you return to the number of hit points you had before you transformed. However, if you revert as a result of dropping to 0 hit points, any excess damage carries over to your normal form. For example, if you take 10 damage in animal form and have only 1 hit point left, you revert and take 9 damage. As long as the excess damage doesn''t reduce your normal form to 0 hit points, you aren''t knocked {@condition unconscious}.","You can''t cast spells, and your ability to speak or take any action that requires hands is limited to the capabilities of your beast form. Transforming doesn''t break your {@status concentration} on a spell you''ve already cast, however, or prevent you from taking actions that are part of a spell, such as {@spell call lightning}, that you''ve already cast.","You retain the benefit of any features from your class, race, or other source and can use them if the new form is physically capable of doing so. However, you can''t use any of your special senses, such as {@sense darkvision}, unless your new form also has that sense.","You choose whether your equipment falls to the ground in your space, merges into your new form, or is worn by it. Worn equipment functions as normal, but the DM decides whether it is practical for the new form to wear a piece of equipment, based on the creature''s shape and size. Your equipment doesn''t change size or shape to match the new form, and any equipment that the new form can''t wear must either fall to the ground or merge with it. Equipment that merges with the form has no effect until you leave the form."]', '["Your game statistics are replaced by the statistics of the beast, but you retain your alignment, personality, and Intelligence, Wisdom, and Charisma scores. You also retain all of your skill and saving throw proficiencies, in addition to gaining those of the creature. If the creature has the same proficiency as you and the bonus in its stat block is higher than yours, use the creature''s bonus instead of yours. If the creature has any legendary or lair actions, you can''t use them.","When you transform, you assume the beast''s hit points and Hit Dice. When you revert to your normal form, you return to the number of hit points you had before you transformed. However, if you revert as a result of dropping to 0 hit points, any excess damage carries over to your normal form. For example, if you take 10 damage in animal form and have only 1 hit point left, you revert and take 9 damage. As long as the excess damage doesn''t reduce your normal form to 0 hit points, you aren''t knocked {@condition unconscious}.","You can''t cast spells, and your ability to speak or take any action that requires hands is limited to the capabilities of your beast form. Transforming doesn''t break your {@status concentration} on a spell you''ve already cast, however, or prevent you from taking actions that are part of a spell, such as {@spell call lightning}, that you''ve already cast.","You retain the benefit of any features from your class, race, or other source and can use them if the new form is physically capable of doing so. However, you can''t use any of your special senses, such as {@sense darkvision}, unless your new form also has that sense.","You choose whether your equipment falls to the ground in your space, merges into your new form, or is worn by it. Worn equipment functions as normal, but the DM decides whether it is practical for the new form to wear a piece of equipment, based on the creature''s shape and size. Your equipment doesn''t change size or shape to match the new form, and any equipment that the new form can''t wear must either fall to the ground or merge with it. Equipment that merges with the form has no effect until you leave the form."]', NULL, NULL, NULL, '{"type":"list","items":["Your game statistics are replaced by the statistics of the beast, but you retain your alignment, personality, and Intelligence, Wisdom, and Charisma scores. You also retain all of your skill and saving throw proficiencies, in addition to gaining those of the creature. If the creature has the same proficiency as you and the bonus in its stat block is higher than yours, use the creature''s bonus instead of yours. If the creature has any legendary or lair actions, you can''t use them.","When you transform, you assume the beast''s hit points and Hit Dice. When you revert to your normal form, you return to the number of hit points you had before you transformed. However, if you revert as a result of dropping to 0 hit points, any excess damage carries over to your normal form. For example, if you take 10 damage in animal form and have only 1 hit point left, you revert and take 9 damage. As long as the excess damage doesn''t reduce your normal form to 0 hit points, you aren''t knocked {@condition unconscious}.","You can''t cast spells, and your ability to speak or take any action that requires hands is limited to the capabilities of your beast form. Transforming doesn''t break your {@status concentration} on a spell you''ve already cast, however, or prevent you from taking actions that are part of a spell, such as {@spell call lightning}, that you''ve already cast.","You retain the benefit of any features from your class, race, or other source and can use them if the new form is physically capable of doing so. However, you can''t use any of your special senses, such as {@sense darkvision}, unless your new form also has that sense.","You choose whether your equipment falls to the ground in your space, merges into your new form, or is worn by it. Worn equipment functions as normal, but the DM decides whether it is practical for the new form to wear a piece of equipment, based on the creature''s shape and size. Your equipment doesn''t change size or shape to match the new form, and any equipment that the new form can''t wear must either fall to the ground or merge with it. Equipment that merges with the form has no effect until you leave the form."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 64, 'Druid', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Druid' AND level = 4), 0, 'When you reach 4th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Druid' AND level = 4), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Cantrip Versatility', 2, 35, 'Druid', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cantrip Versatility' AND class_name = 'Druid' AND level = 4), 0, '{@i 4th-level druid {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cantrip Versatility' AND class_name = 'Druid' AND level = 4), 1, 'Whenever you reach a level in this class that grants the Ability Score Improvement feature, you can replace one cantrip you learned from this class''s Spellcasting feature with another cantrip from the {@filter druid spell list|spells|level=0|class=Druid}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wild Shape Improvement', 1, 64, 'Druid', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
