INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Beast Spells', 1, 64, 'Druid', 'PHB', NULL, NULL, 18, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Beast Spells' AND class_name = 'Druid' AND level = 18), 0, 'Beginning at 18th level, you can cast many of your druid spells in any shape you assume using Wild Shape. You can perform the somatic and verbal components of a druid spell while in a beast shape, but you aren''t able to provide material components.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Timeless Body', 1, 64, 'Druid', 'PHB', NULL, NULL, 18, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Timeless Body' AND class_name = 'Druid' AND level = 18), 0, 'Starting at 18th level, the primal magic that you wield causes you to age more slowly. For every 10 years that pass, your body ages only 1 year.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 64, 'Druid', 'PHB', NULL, NULL, 19, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Druid' AND level = 19), 0, 'When you reach 19th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Druid' AND level = 19), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Archdruid', 1, 64, 'Druid', 'PHB', NULL, NULL, 20, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Archdruid' AND class_name = 'Druid' AND level = 20), 0, 'At 20th level, you can use your Wild Shape an unlimited number of times.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Archdruid' AND class_name = 'Druid' AND level = 20), 1, 'Additionally, you can ignore the verbal and somatic components of your druid spells, as well as any material components that lack a cost and aren''t consumed by a spell. You gain this benefit in both your normal shape and your beast shape from Wild Shape.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of the Land', 1, 68, 'Druid', 'PHB', 'Land', 'PHB', 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Land' AND class_name = 'Druid' AND level = 2), 0, 'The Circle of the Land is made up of mystics and sages who safeguard ancient knowledge and rites through a vast oral tradition. These druids meet within sacred circles of trees or standing stones to whisper primal secrets in {@language Druidic}. The circle''s wisest members preside as the chief priests of communities that hold to the Old Faith and serve as advisors to the rulers of those folk. As a member of this circle, your magic is influenced by the land where you were initiated into the circle''s mysterious rites.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Land' AND class_name = 'Druid' AND level = 2), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Cantrip|Druid||Land||2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Land' AND class_name = 'Druid' AND level = 2), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Natural Recovery|Druid||Land||2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Land' AND class_name = 'Druid' AND level = 2), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Circle Spells|Druid||Land||2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Cantrip', 1, 68, 'Druid', 'PHB', 'Land', 'PHB', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Cantrip' AND class_name = 'Druid' AND level = 2), 0, 'You learn one additional druid cantrip of your choice. This cantrip doesn''t count against the number of druid cantrips you know.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle Spells', 1, 68, 'Druid', 'PHB', 'Land', 'PHB', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 0, 'Your mystical connection to the land infuses you with the ability to cast certain spells. At 3rd, 5th, 7th, and 9th level you gain access to circle spells connected to the land where you became a druid. Choose that land—arctic, coast, desert, forest, grassland, mountain, swamp, or Underdark—and consult the associated list of spells.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 1, 'Once you gain access to a circle spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 2, 'table', NULL, NULL, NULL, 'Arctic', '["Druid Level","Circle Spells"]', '[["3rd","{@spell hold person}, {@spell spike growth}"],["5th","{@spell sleet storm}, {@spell slow}"],["7th","{@spell freedom of movement}, {@spell ice storm}"],["9th","{@spell commune with nature}, {@spell cone of cold}"]]', '{"type":"table","caption":"Arctic","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell hold person}, {@spell spike growth}"],["5th","{@spell sleet storm}, {@spell slow}"],["7th","{@spell freedom of movement}, {@spell ice storm}"],["9th","{@spell commune with nature}, {@spell cone of cold}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 3, 'table', NULL, NULL, NULL, 'Coast', '["Druid Level","Circle Spells"]', '[["3rd","{@spell mirror image}, {@spell misty step}"],["5th","{@spell water breathing}, {@spell water walk}"],["7th","{@spell control water}, {@spell freedom of movement}"],["9th","{@spell conjure elemental}, {@spell scrying}"]]', '{"type":"table","caption":"Coast","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell mirror image}, {@spell misty step}"],["5th","{@spell water breathing}, {@spell water walk}"],["7th","{@spell control water}, {@spell freedom of movement}"],["9th","{@spell conjure elemental}, {@spell scrying}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 4, 'table', NULL, NULL, NULL, 'Desert', '["Druid Level","Circle Spells"]', '[["3rd","{@spell blur}, {@spell silence}"],["5th","{@spell create food and water}, {@spell protection from energy}"],["7th","{@spell blight}, {@spell hallucinatory terrain}"],["9th","{@spell insect plague}, {@spell wall of stone}"]]', '{"type":"table","caption":"Desert","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell blur}, {@spell silence}"],["5th","{@spell create food and water}, {@spell protection from energy}"],["7th","{@spell blight}, {@spell hallucinatory terrain}"],["9th","{@spell insect plague}, {@spell wall of stone}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 5, 'table', NULL, NULL, NULL, 'Forest', '["Druid Level","Circle Spells"]', '[["3rd","{@spell barkskin}, {@spell spider climb}"],["5th","{@spell call lightning}, {@spell plant growth}"],["7th","{@spell divination}, {@spell freedom of movement}"],["9th","{@spell commune with nature}, {@spell tree stride}"]]', '{"type":"table","caption":"Forest","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell barkskin}, {@spell spider climb}"],["5th","{@spell call lightning}, {@spell plant growth}"],["7th","{@spell divination}, {@spell freedom of movement}"],["9th","{@spell commune with nature}, {@spell tree stride}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 6, 'table', NULL, NULL, NULL, 'Grassland', '["Druid Level","Circle Spells"]', '[["3rd","{@spell invisibility}, {@spell pass without trace}"],["5th","{@spell daylight}, {@spell haste}"],["7th","{@spell divination}, {@spell freedom of movement}"],["9th","{@spell dream}, {@spell insect plague}"]]', '{"type":"table","caption":"Grassland","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell invisibility}, {@spell pass without trace}"],["5th","{@spell daylight}, {@spell haste}"],["7th","{@spell divination}, {@spell freedom of movement}"],["9th","{@spell dream}, {@spell insect plague}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 7, 'table', NULL, NULL, NULL, 'Mountain', '["Druid Level","Circle Spells"]', '[["3rd","{@spell spider climb}, {@spell spike growth}"],["5th","{@spell lightning bolt}, {@spell meld into stone}"],["7th","{@spell stone shape}, {@spell stoneskin}"],["9th","{@spell passwall}, {@spell wall of stone}"]]', '{"type":"table","caption":"Mountain","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell spider climb}, {@spell spike growth}"],["5th","{@spell lightning bolt}, {@spell meld into stone}"],["7th","{@spell stone shape}, {@spell stoneskin}"],["9th","{@spell passwall}, {@spell wall of stone}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 8, 'table', NULL, NULL, NULL, 'Swamp', '["Druid Level","Circle Spells"]', '[["3rd","{@spell darkness}, {@spell Melf''s acid arrow}"],["5th","{@spell water walk}, {@spell stinking cloud}"],["7th","{@spell freedom of movement}, {@spell locate creature}"],["9th","{@spell insect plague}, {@spell scrying}"]]', '{"type":"table","caption":"Swamp","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell darkness}, {@spell Melf''s acid arrow}"],["5th","{@spell water walk}, {@spell stinking cloud}"],["7th","{@spell freedom of movement}, {@spell locate creature}"],["9th","{@spell insect plague}, {@spell scrying}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 9, 'table', NULL, NULL, NULL, 'Underdark', '["Druid Level","Circle Spells"]', '[["3rd","{@spell spider climb}, {@spell web}"],["5th","{@spell gaseous form}, {@spell stinking cloud}"],["7th","{@spell greater invisibility}, {@spell stone shape}"],["9th","{@spell cloudkill}, {@spell insect plague}"]]', '{"type":"table","caption":"Underdark","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell spider climb}, {@spell web}"],["5th","{@spell gaseous form}, {@spell stinking cloud}"],["7th","{@spell greater invisibility}, {@spell stone shape}"],["9th","{@spell cloudkill}, {@spell insect plague}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Natural Recovery', 1, 68, 'Druid', 'PHB', 'Land', 'PHB', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Natural Recovery' AND class_name = 'Druid' AND level = 2), 0, 'Starting at 2nd level, you can regain some of your magical energy by sitting in meditation and communing with nature. During a short rest, you choose expended spell slots to recover. The spell slots can have a combined level that is equal to or less than half your druid level (rounded up), and none of the slots can be 6th level or higher. You can''t use this feature again until you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Natural Recovery' AND class_name = 'Druid' AND level = 2), 1, 'For example, when you are a 4th-level druid, you can recover up to two levels worth of spell slots. You can recover either a 2nd-level slot or two 1st-level slots.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Land''s Stride', 1, 68, 'Druid', 'PHB', 'Land', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Land''s Stride' AND class_name = 'Druid' AND level = 6), 0, 'Starting at 6th level, moving through nonmagical {@quickref difficult terrain||3} costs you no extra movement. You can also pass through nonmagical plants without being slowed by them and without taking damage from them if they have thorns, spines, or a similar hazard.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Land''s Stride' AND class_name = 'Druid' AND level = 6), 1, 'In addition, you have advantage on saving throws against plants that are magically created or manipulated to impede movement, such as those created by the {@spell entangle} spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Nature''s Ward', 1, 68, 'Druid', 'PHB', 'Land', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature''s Ward' AND class_name = 'Druid' AND level = 10), 0, 'When you reach 10th level, you can''t be {@condition charmed} or {@condition frightened} by elementals or fey, and you are immune to poison and disease.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Nature''s Sanctuary', 1, 68, 'Druid', 'PHB', 'Land', 'PHB', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature''s Sanctuary' AND class_name = 'Druid' AND level = 14), 0, 'When you reach 14th level, creatures of the natural world sense your connection to nature and become hesitant to attack you. When a beast or plant creature attacks you, that creature must make a Wisdom saving throw against your druid spell save DC. On a failed save, the creature must choose a different target, or the attack automatically misses. On a successful save, the creature is immune to this effect for 24 hours.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature''s Sanctuary' AND class_name = 'Druid' AND level = 14), 1, 'The creature is aware of this effect before it makes its attack against you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of the Moon', 1, 69, 'Druid', 'PHB', 'Moon', 'PHB', 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Moon' AND class_name = 'Druid' AND level = 2), 0, 'Druids of the Circle of the Moon are fierce guardians of the wilds. Their order gathers under the full moon to share news and trade warnings. They haunt the deepest parts of the wilderness, where they might go for weeks on end before crossing paths with another humanoid creature, let alone another druid.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Moon' AND class_name = 'Druid' AND level = 2), 1, 'Changeable as the moon, a druid of this circle might prowl as a great cat one night, soar over the treetops as an eagle the next day, and crash through the undergrowth in bear form to drive off a trespassing monster. The wild is in the druid''s blood.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Moon' AND class_name = 'Druid' AND level = 2), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Combat Wild Shape|Druid||Moon||2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Moon' AND class_name = 'Druid' AND level = 2), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Circle Forms|Druid||Moon||2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle Forms', 1, 69, 'Druid', 'PHB', 'Moon', 'PHB', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Forms' AND class_name = 'Druid' AND level = 2), 0, 'The rites of your circle grant you the ability to transform into more dangerous animal forms. Starting at 2nd level, you can use your Wild Shape to transform into a {@filter beast with a challenge rating as high as 1|bestiary|challenge rating=[&0;&1]|type=beast|speed type=!fly;!swim|miscellaneous=!swarm} (you ignore the Max. CR column of the Beast Shapes table, but must abide by the other limitations there).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Forms' AND class_name = 'Druid' AND level = 2), 1, 'Starting at 6th level, you can transform into a beast with a challenge rating as high as your druid level divided by 3, rounded down.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Forms' AND class_name = 'Druid' AND level = 2), 2, 'table', NULL, NULL, NULL, 'Circle of the Moon Beast Shapes', '["Level","Max. CR","Limitations"]', '[["2nd","{@filter 1|bestiary|challenge rating=[&0;&1]|type=beast|speed type=!fly;!swim|miscellaneous=!swarm}","No flying or swimming speed"],["4th","{@filter 1|bestiary|challenge rating=[&0;&1]|type=beast|speed type=!fly|miscellaneous=!swarm}","No flying speed"],["6th","{@filter 2|bestiary|challenge rating=[&0;&2]|type=beast|speed type=!fly|miscellaneous=!swarm}","No flying speed"],["8th","{@filter 2|bestiary|challenge rating=[&0;&2]|type=beast|miscellaneous=!swarm}","—"],["9th","{@filter 3|bestiary|challenge rating=[&0;&3]|type=beast|miscellaneous=!swarm}","—"],["12th","{@filter 4|bestiary|challenge rating=[&0;&4]|type=beast|miscellaneous=!swarm}","—"],["15th","{@filter 5|bestiary|challenge rating=[&0;&5]|type=beast|miscellaneous=!swarm}","—"],["18th","{@filter 6|bestiary|challenge rating=[&0;&6]|type=beast|miscellaneous=!swarm}","—"]]', '{"type":"table","caption":"Circle of the Moon Beast Shapes","colLabels":["Level","Max. CR","Limitations"],"colStyles":["col-2 text-center","col-2 text-center","col-8"],"rows":[["2nd","{@filter 1|bestiary|challenge rating=[&0;&1]|type=beast|speed type=!fly;!swim|miscellaneous=!swarm}","No flying or swimming speed"],["4th","{@filter 1|bestiary|challenge rating=[&0;&1]|type=beast|speed type=!fly|miscellaneous=!swarm}","No flying speed"],["6th","{@filter 2|bestiary|challenge rating=[&0;&2]|type=beast|speed type=!fly|miscellaneous=!swarm}","No flying speed"],["8th","{@filter 2|bestiary|challenge rating=[&0;&2]|type=beast|miscellaneous=!swarm}","—"],["9th","{@filter 3|bestiary|challenge rating=[&0;&3]|type=beast|miscellaneous=!swarm}","—"],["12th","{@filter 4|bestiary|challenge rating=[&0;&4]|type=beast|miscellaneous=!swarm}","—"],["15th","{@filter 5|bestiary|challenge rating=[&0;&5]|type=beast|miscellaneous=!swarm}","—"],["18th","{@filter 6|bestiary|challenge rating=[&0;&6]|type=beast|miscellaneous=!swarm}","—"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Combat Wild Shape', 1, 69, 'Druid', 'PHB', 'Moon', 'PHB', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Combat Wild Shape' AND class_name = 'Druid' AND level = 2), 0, 'You gain the ability to use Wild Shape on your turn as a bonus action, rather than as an action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Combat Wild Shape' AND class_name = 'Druid' AND level = 2), 1, 'Additionally, while you are transformed by Wild Shape, you can use a bonus action to expend one spell slot to regain {@dice 1d8} hit points per level of the spell slot expended.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Primal Strike', 1, 69, 'Druid', 'PHB', 'Moon', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Strike' AND class_name = 'Druid' AND level = 6), 0, 'Starting at 6th level, your attacks in beast form count as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elemental Wild Shape', 1, 69, 'Druid', 'PHB', 'Moon', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Wild Shape' AND class_name = 'Druid' AND level = 10), 0, 'At 10th level, you can expend two uses of Wild Shape at the same time to transform into an {@creature air elemental}, an {@creature earth elemental}, a {@creature fire elemental}, or a {@creature water elemental}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Thousand Forms', 1, 69, 'Druid', 'PHB', 'Moon', 'PHB', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Thousand Forms' AND class_name = 'Druid' AND level = 14), 0, 'By 14th level, you have learned to use magic to alter your physical form in more subtle ways. You can cast the {@spell alter self} spell at will.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of Spores', 2, 36, 'Druid', 'PHB', 'Spores', 'TCE', 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Spores' AND class_name = 'Druid' AND level = 2), 0, 'Druids of the Circle of Spores find beauty in decay. They see within mold and other fungi the ability to transform lifeless material into abundant, albeit somewhat strange, life.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Spores' AND class_name = 'Druid' AND level = 2), 1, 'These druids believe that life and death are parts of a grand cycle, with one leading to the other and then back again. Death isn''t the end of life, but instead a change of state that sees life shift into a new form.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Spores' AND class_name = 'Druid' AND level = 2), 2, 'Druids of this circle have a complex relationship with the undead. Unlike most other druids, they see nothing inherently wrong with undeath, which they consider to be a companion to life and death. But these druids believe that the natural cycle is healthiest when each segment of it is vibrant and changing. Undead that seek to replace all life with undeath, or that try to avoid passing to a final rest, violate the cycle and must be thwarted.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of Spores' AND class_name = 'Druid' AND level = 2), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Circle Spells|Druid||Spores|TCE|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of Spores' AND class_name = 'Druid' AND level = 2), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Halo of Spores|Druid||Spores|TCE|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of Spores' AND class_name = 'Druid' AND level = 2), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Symbiotic Entity|Druid||Spores|TCE|2"}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Circle of Spores' AND class_name = 'Druid' AND level = 2), 'GGR', 26);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle Spells', 2, 36, 'Druid', 'PHB', 'Spores', 'TCE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 0, 'Your symbiotic link to fungus and your ability to tap into the cycle of life and death grants you access to certain spells. At 2nd level, you learn the {@spell chill touch} cantrip. At 3rd, 5th, 7th, and 9th level you gain access to the spells listed for that level in the Circle of Spores Spells table.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 1, 'Once you gain access to one of these spells, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 2, 'table', NULL, NULL, NULL, 'Circle of Spores Spells', '["Druid Level","Circle Spells"]', '[["3rd","{@spell blindness/deafness}, {@spell gentle repose}"],["5th","{@spell animate dead}, {@spell gaseous form}"],["7th","{@spell blight}, {@spell confusion}"],["9th","{@spell cloudkill}, {@spell contagion}"]]', '{"type":"table","caption":"Circle of Spores Spells","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell blindness/deafness}, {@spell gentle repose}"],["5th","{@spell animate dead}, {@spell gaseous form}"],["7th","{@spell blight}, {@spell confusion}"],["9th","{@spell cloudkill}, {@spell contagion}"]]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 'GGR', 26);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Halo of Spores', 2, 36, 'Druid', 'PHB', 'Spores', 'TCE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Halo of Spores' AND class_name = 'Druid' AND level = 2), 0, 'Starting at 2nd level, you are surrounded by invisible, necrotic spores that are harmless until you unleash them on a creature nearby. When a creature you can see moves into a space within 10 feet of you or starts its turn there, you can use your reaction to deal {@damage 1d4} necrotic damage to that creature unless it succeeds on a Constitution saving throw against your spell save DC. The necrotic damage increases to {@damage 1d6} at 6th level, {@damage 1d8} at 10th level, and {@damage 1d10} at 14th level.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Halo of Spores' AND class_name = 'Druid' AND level = 2), 'GGR', 26);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Symbiotic Entity', 2, 36, 'Druid', 'PHB', 'Spores', 'TCE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Symbiotic Entity' AND class_name = 'Druid' AND level = 2), 0, 'At 2nd level, you gain the ability to channel magic into your spores. As an action, you can expend a use of your Wild Shape feature to awaken those spores, rather than transforming into a beast form, and you gain 4 temporary hit points for each level you have in this class. While this feature is active, you gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Symbiotic Entity' AND class_name = 'Druid' AND level = 2), 1, 'list', NULL, '["When you deal your Halo of Spores damage, roll the damage die a second time and add it to the total.","Your melee weapon attacks deal an extra {@damage 1d6} necrotic damage to any target they hit."]', '["When you deal your Halo of Spores damage, roll the damage die a second time and add it to the total.","Your melee weapon attacks deal an extra {@damage 1d6} necrotic damage to any target they hit."]', NULL, NULL, NULL, '{"type":"list","items":["When you deal your Halo of Spores damage, roll the damage die a second time and add it to the total.","Your melee weapon attacks deal an extra {@damage 1d6} necrotic damage to any target they hit."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Symbiotic Entity' AND class_name = 'Druid' AND level = 2), 2, 'These benefits last for 10 minutes, until you lose all these temporary hit points, or until you use your Wild Shape again.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Symbiotic Entity' AND class_name = 'Druid' AND level = 2), 'GGR', 26);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fungal Infestation', 2, 36, 'Druid', 'PHB', 'Spores', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fungal Infestation' AND class_name = 'Druid' AND level = 6), 0, 'At 6th level, your spores gain the ability to infest a corpse and animate it. If a beast or a humanoid that is Small or Medium dies within 10 feet of you, you can use your reaction to animate it, causing it to stand up immediately with 1 hit point. The creature uses the {@creature zombie} stat block in the {@book Monster Manual|MM}. It remains animate for 1 hour, after which time it collapses and dies.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fungal Infestation' AND class_name = 'Druid' AND level = 6), 1, 'In combat, the zombie''s turn comes immediately after yours. It obeys your mental commands, and the only action it can take is the {@action Attack} action, making one melee attack.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fungal Infestation' AND class_name = 'Druid' AND level = 6), 2, 'You can use this feature a number of times equal to your Wisdom modifier (minimum of once), and you regain all expended uses of it when you finish a long rest.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Fungal Infestation' AND class_name = 'Druid' AND level = 6), 'GGR', 26);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spreading Spores', 2, 36, 'Druid', 'PHB', 'Spores', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spreading Spores' AND class_name = 'Druid' AND level = 10), 0, 'At 10th level, you gain the ability to seed an area with deadly spores. As a bonus action while your Symbiotic Entity feature is active, you can hurl spores up to 30 feet away, where they swirl in a 10-foot cube for 1 minute. The spores disappear early if you use this feature again, if you dismiss them as a bonus action, or if your Symbiotic Entity feature is no longer active.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spreading Spores' AND class_name = 'Druid' AND level = 10), 1, 'Whenever a creature moves into the cube or starts its turn there, that creature takes your Halo of Spores damage, unless the creature succeeds on a Constitution saving throw against your spell save DC. A creature can take this damage no more than once per turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spreading Spores' AND class_name = 'Druid' AND level = 10), 2, 'While the cube of spores persists, you can''t use your Halo of Spores reaction.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Spreading Spores' AND class_name = 'Druid' AND level = 10), 'GGR', 26);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fungal Body', 2, 36, 'Druid', 'PHB', 'Spores', 'TCE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fungal Body' AND class_name = 'Druid' AND level = 14), 0, 'At 14th level, the fungal spores in your body alter you: you can''t be {@condition blinded}, {@condition deafened}, {@condition frightened}, or {@condition poisoned}, and any critical hit against you counts as a normal hit instead, unless you''re {@condition incapacitated}.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Fungal Body' AND class_name = 'Druid' AND level = 14), 'GGR', 26);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of Stars', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Stars' AND class_name = 'Druid' AND level = 2), 0, 'The Circle of Stars allows druids to draw on the power of starlight. These druids have tracked heavenly patterns since time immemorial, discovering secrets hidden amid the constellations. By revealing and understanding these secrets, the Circle of the Stars seeks to harness the powers of the cosmos.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Stars' AND class_name = 'Druid' AND level = 2), 1, 'Many druids of this circle keep records of the constellations and the stars'' effects on the world. Some groups document these observations at megalithic sites, which serve as enigmatic libraries of lore. These repositories might take the form of stone circles, pyramids, petroglyphs, and underground temples-any construction durable enough to protect the circle''s sacred knowledge even against a great cataclysm.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of Stars' AND class_name = 'Druid' AND level = 2), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Star Map|Druid||Stars|TCE|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of Stars' AND class_name = 'Druid' AND level = 2), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Starry Form|Druid||Stars|TCE|2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Star Map', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Star Map' AND class_name = 'Druid' AND level = 2), 0, '{@i 2nd-level Circle of the Stars feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Star Map' AND class_name = 'Druid' AND level = 2), 1, 'You''ve created a star chart as part of your heavenly studies. It is a Tiny object and can serve as a spellcasting focus for your druid spells. You determine its form by rolling on the Star Map table or by choosing one.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Star Map' AND class_name = 'Druid' AND level = 2), 2, 'table', NULL, NULL, NULL, 'Star Map', '["d6","Map Form"]', '[["1","A scroll covered with depictions of constellations"],["2","A stone tablet with fine holes drilled through it"],["3","A speckled owlbear hide, tooled with raised marks"],["4","A collection of maps bound in an ebony cover"],["5","A crystal that projects starry patterns when placed before a light"],["6","Glass disks that depict constellations"]]', '{"type":"table","caption":"Star Map","colLabels":["d6","Map Form"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","A scroll covered with depictions of constellations"],["2","A stone tablet with fine holes drilled through it"],["3","A speckled owlbear hide, tooled with raised marks"],["4","A collection of maps bound in an ebony cover"],["5","A crystal that projects starry patterns when placed before a light"],["6","Glass disks that depict constellations"]]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Star Map' AND class_name = 'Druid' AND level = 2), 3, 'While holding this map, you have these benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Star Map' AND class_name = 'Druid' AND level = 2), 4, 'list', NULL, '["You know the {@spell guidance} cantrip.","You have the {@spell guiding bolt} spell prepared. It counts as a druid spell for you, and it doesn''t count against the number of spells you can have prepared.","You can cast {@spell guiding bolt} without expending a spell slot. You can do so a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest."]', '["You know the {@spell guidance} cantrip.","You have the {@spell guiding bolt} spell prepared. It counts as a druid spell for you, and it doesn''t count against the number of spells you can have prepared.","You can cast {@spell guiding bolt} without expending a spell slot. You can do so a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest."]', NULL, NULL, NULL, '{"type":"list","items":["You know the {@spell guidance} cantrip.","You have the {@spell guiding bolt} spell prepared. It counts as a druid spell for you, and it doesn''t count against the number of spells you can have prepared.","You can cast {@spell guiding bolt} without expending a spell slot. You can do so a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Star Map' AND class_name = 'Druid' AND level = 2), 5, 'If you lose the map, you can perform a 1-hour ceremony to magically create a replacement. This ceremony can be performed during a short or long rest, and it destroys the previous map.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Starry Form', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Starry Form' AND class_name = 'Druid' AND level = 2), 0, '{@i 2nd-level Circle of the Stars feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Starry Form' AND class_name = 'Druid' AND level = 2), 1, 'As a bonus action, you can expend a use of your Wild Shape feature to take on a starry form, rather than transforming into a beast.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Starry Form' AND class_name = 'Druid' AND level = 2), 2, 'While in your starry form, you retain your game statistics, but your body becomes luminous; your joints glimmer like stars, and glowing lines connect them as on a star chart. This form sheds bright light in a 10-foot radius and dim light for an additional 10 feet. The form lasts for 10 minutes. It ends early if you dismiss it (no action required), are {@condition incapacitated}, die, or use this feature again.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Starry Form' AND class_name = 'Druid' AND level = 2), 3, 'Whenever you assume your starry form, choose which of the following constellations glimmers on your body; your choice gives you certain benefits while in the form:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Starry Form' AND class_name = 'Druid' AND level = 2), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Archer|Druid||Stars|TCE|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Starry Form' AND class_name = 'Druid' AND level = 2), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Chalice|Druid||Stars|TCE|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Starry Form' AND class_name = 'Druid' AND level = 2), 6, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Dragon|Druid||Stars|TCE|2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Archer', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Archer' AND class_name = 'Druid' AND level = 2), 0, 'A constellation of an archer appears on you. When you activate this form, and as a bonus action on your subsequent turns while it lasts, you can make a ranged spell attack, hurling a luminous arrow that targets one creature within 60 feet of you. On a hit, the attack deals radiant damage equal to {@damage 1d8} + your Wisdom modifier.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Chalice', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Chalice' AND class_name = 'Druid' AND level = 2), 0, 'A constellation of a life-giving goblet appears on you. Whenever you cast a spell using a spell slot that restores hit points to a creature, you or another creature within 30 feet of you can regain hit points equal to {@dice 1d8} + your Wisdom modifier.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dragon', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dragon' AND class_name = 'Druid' AND level = 2), 0, 'A constellation of a wise dragon appears on you. When you make an Intelligence or a Wisdom check or a Constitution saving throw to maintain {@status concentration} on a spell, you can treat a roll of 9 or lower on the {@dice d20} as a 10.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Cosmic Omen', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cosmic Omen' AND class_name = 'Druid' AND level = 6), 0, '{@i 6th-level Circle of the Stars feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cosmic Omen' AND class_name = 'Druid' AND level = 6), 1, 'Whenever you finish a long rest, you can consult your Star Map for omens. When you do so, roll a die. Until you finish your next long rest, you gain access to a special reaction based on whether you rolled an even or an odd number on the die:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Cosmic Omen' AND class_name = 'Druid' AND level = 6), 2, 'list', NULL, '[{"type":"item","name":"Weal (even)","entry":"Whenever a creature you can see within 30 feet of you is about to make an attack roll, a saving throw, or an ability check, you can use your reaction to roll a {@dice d6} and add the number rolled to the total."},{"type":"item","name":"Woe (odd)","entry":"Whenever a creature you can see within 30 feet of you is about to make an attack roll, a saving throw, or an ability check, you can use your reaction to roll a {@dice d6} and subtract the number rolled from the total."}]', '[{"type":"item","name":"Weal (even)","entry":"Whenever a creature you can see within 30 feet of you is about to make an attack roll, a saving throw, or an ability check, you can use your reaction to roll a {@dice d6} and add the number rolled to the total."},{"type":"item","name":"Woe (odd)","entry":"Whenever a creature you can see within 30 feet of you is about to make an attack roll, a saving throw, or an ability check, you can use your reaction to roll a {@dice d6} and subtract the number rolled from the total."}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Weal (even)","entry":"Whenever a creature you can see within 30 feet of you is about to make an attack roll, a saving throw, or an ability check, you can use your reaction to roll a {@dice d6} and add the number rolled to the total."},{"type":"item","name":"Woe (odd)","entry":"Whenever a creature you can see within 30 feet of you is about to make an attack roll, a saving throw, or an ability check, you can use your reaction to roll a {@dice d6} and subtract the number rolled from the total."}]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cosmic Omen' AND class_name = 'Druid' AND level = 6), 3, 'You can use this reaction a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Twinkling Constellations', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Twinkling Constellations' AND class_name = 'Druid' AND level = 10), 0, '{@i 10th-level Circle of the Stars feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Twinkling Constellations' AND class_name = 'Druid' AND level = 10), 1, 'The constellations of your Starry Form improve. The {@damage 1d8} of the Archer and the Chalice becomes {@damage 2d8}, and while the Dragon is active, you have a flying speed of 20 feet and can hover.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Twinkling Constellations' AND class_name = 'Druid' AND level = 10), 2, 'Moreover, at the start of each of your turns while in your Starry Form, you can change which constellation glimmers on your body.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Full of Stars', 2, 38, 'Druid', 'PHB', 'Stars', 'TCE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Full of Stars' AND class_name = 'Druid' AND level = 14), 0, '{@i 14th-level Circle of the Stars feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Full of Stars' AND class_name = 'Druid' AND level = 14), 1, 'While in your Starry Form, you become partially incorporeal, giving you resistance to bludgeoning, piercing, and slashing damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of Wildfire', 2, 39, 'Druid', 'PHB', 'Wildfire', 'TCE', 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Wildfire' AND class_name = 'Druid' AND level = 2), 0, 'Druids within the Circle of Wildfire understand that destruction is sometimes the precursor of creation, such as when a forest fire promotes later growth. These druids bond with a primal spirit that harbors both destructive and creative power, allowing the druids to create controlled flames that burn away one thing but give life to another.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of Wildfire' AND class_name = 'Druid' AND level = 2), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Circle Spells|Druid||Wildfire|TCE|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of Wildfire' AND class_name = 'Druid' AND level = 2), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Summon Wildfire Spirit|Druid||Wildfire|TCE|2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle Spells', 2, 39, 'Druid', 'PHB', 'Wildfire', 'TCE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 0, '{@i 2nd-level Circle of Wildfire feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 1, 'You have formed a bond with a wildfire spirit, a primal being of creation and destruction. Your link with this spirit grants you access to some spells when you reach certain levels in this class, as shown on the Circle of Wildfire Spells table.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 2, 'Once you gain access to one of these spells, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle Spells' AND class_name = 'Druid' AND level = 2), 3, 'table', NULL, NULL, NULL, 'Circle of Wildfire Spells', '["Druid Level","Circle Spells"]', '[["2nd","{@spell burning hands}, {@spell cure wounds}"],["3rd","{@spell flaming sphere}, {@spell scorching ray}"],["5th","{@spell plant growth}, {@spell revivify}"],["7th","{@spell aura of life}, {@spell fire shield}"],["9th","{@spell flame strike}, {@spell mass cure wounds}"]]', '{"type":"table","caption":"Circle of Wildfire Spells","colLabels":["Druid Level","Circle Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["2nd","{@spell burning hands}, {@spell cure wounds}"],["3rd","{@spell flaming sphere}, {@spell scorching ray}"],["5th","{@spell plant growth}, {@spell revivify}"],["7th","{@spell aura of life}, {@spell fire shield}"],["9th","{@spell flame strike}, {@spell mass cure wounds}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Summon Wildfire Spirit', 2, 39, 'Druid', 'PHB', 'Wildfire', 'TCE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Summon Wildfire Spirit' AND class_name = 'Druid' AND level = 2), 0, '{@i 2nd-level Circle of Wildfire feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Summon Wildfire Spirit' AND class_name = 'Druid' AND level = 2), 1, 'You can summon the primal spirit bound to your soul. As an action, you can expend one use of your Wild Shape feature to summon your wildfire spirit, rather than assuming a beast form.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Summon Wildfire Spirit' AND class_name = 'Druid' AND level = 2), 2, 'The spirit appears in an unoccupied space of your choice that you can see within 30 feet of you. Each creature within 10 feet of the spirit (other than you) when it appears must succeed on a Dexterity saving throw against your spell save DC or take {@damage 2d6} fire damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Summon Wildfire Spirit' AND class_name = 'Druid' AND level = 2), 3, 'The spirit is friendly to you and your companions and obeys your commands. See this creature''s game statistics in the {@creature Wildfire Spirit|TCE} stat block, which uses your proficiency bonus (PB) in several places. You determine the spirit''s appearance. Some spirits take the form of a humanoid figure made of gnarled branches covered in flame, while others look like beasts wreathed in fire.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Summon Wildfire Spirit' AND class_name = 'Druid' AND level = 2), 4, 'In combat, the spirit shares your initiative count, but it takes its turn immediately after yours. The only action it takes on its turn is the {@action Dodge} action, unless you take a bonus action on your turn to command it to take another action. That action can be one in its stat block or some other action. If you are {@condition incapacitated}, the spirit can take any action of its choice, not just {@action Dodge}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Summon Wildfire Spirit' AND class_name = 'Druid' AND level = 2), 5, 'The spirit manifests for 1 hour, until it is reduced to 0 hit points, until you use this feature to summon the spirit again, or until you die.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Enhanced Bond', 2, 39, 'Druid', 'PHB', 'Wildfire', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Enhanced Bond' AND class_name = 'Druid' AND level = 6), 0, '{@i 6th-level Circle of Wildfire feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Enhanced Bond' AND class_name = 'Druid' AND level = 6), 1, 'The bond with your wildfire spirit enhances your destructive and restorative spells. Whenever you cast a spell that deals fire damage or restores hit points while your wildfire spirit is summoned, roll a {@dice d8}, and you gain a bonus equal to the number rolled to one damage or healing roll of the spell.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Enhanced Bond' AND class_name = 'Druid' AND level = 6), 2, 'In addition, when you cast a spell with a range other than self, the spell can originate from you or your wildfire spirit.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Cauterizing Flames', 2, 39, 'Druid', 'PHB', 'Wildfire', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cauterizing Flames' AND class_name = 'Druid' AND level = 10), 0, '{@i 10th-level Circle of Wildfire feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cauterizing Flames' AND class_name = 'Druid' AND level = 10), 1, 'You gain the ability to turn death into magical flames that can heal or incinerate. When a Small or larger creature dies within 30 feet of you or your wildfire spirit, a harmless spectral flame springs forth in the dead creature''s space and flickers there for 1 minute. When a creature you can see enters that space, you can use your reaction to extinguish the spectral flame there and either heal the creature or deal fire damage to it. The healing or damage equals {@dice 2d10} + your Wisdom modifier.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cauterizing Flames' AND class_name = 'Druid' AND level = 10), 2, 'You can use this reaction a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blazing Revival', 2, 39, 'Druid', 'PHB', 'Wildfire', 'TCE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blazing Revival' AND class_name = 'Druid' AND level = 14), 0, '{@i 14th-level Circle of Wildfire feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blazing Revival' AND class_name = 'Druid' AND level = 14), 1, 'The bond with your wildfire spirit can save you from death. If the spirit is within 120 feet of you when you are reduced to 0 hit points and thereby fall {@condition unconscious}, you can cause the spirit to drop to 0 hit points. You then regain half your hit points and immediately rise to your feet.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blazing Revival' AND class_name = 'Druid' AND level = 14), 2, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of the Blighted', null, 171, 'Druid', 'PHB', 'Blighted', 'TDCSR', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Blighted' AND class_name = 'Druid' AND level = 2), 0, 'Those who channel the magic of life and nature often find themselves drawn to a particular shrine or natural site, binding their body and spirit to these places of power. Such druids draw vitality from their chosen location, protecting it with their lives—but not all succeed in defending these sacred realms.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Blighted' AND class_name = 'Druid' AND level = 2), 1, 'Whether through the subtle corruption of vile magics, the tainted presence of an ancient terror, or a terrible mistake unleashed by druids themselves, a land can become cursed with magic that warps any druid bound to that land. Twisted by their connection to an unhallowed territory, these guardians come to embody the defiled nature they serve, exalting the natural process of decay and using it to further their goals—or sometimes to seek a means to end the curse that plagues them.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Blighted' AND class_name = 'Druid' AND level = 2), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Defile Ground|Druid|PHB|Blighted|TDCSR|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Blighted' AND class_name = 'Druid' AND level = 2), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Blighted Shape|Druid|PHB|Blighted|TDCSR|2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Blighted Shape', null, 172, 'Druid', 'PHB', 'Blighted', 'TDCSR', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blighted Shape' AND class_name = 'Druid' AND level = 2), 0, 'Also at 2nd level, your physical form begins to show effects of the corruption you wield, tracing blackened veins across your skin, producing gnarled, bony protrusions, or other eerie phenomena. You gain proficiency in the Charisma ({@skill Intimidation}) skill.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Blighted Shape' AND class_name = 'Druid' AND level = 2), 1, 'Additionally, while you are transformed by your Wild Shape feature, you gain a +2 bonus to AC as gnarled spines protrude from your body. Your beast form also gains {@sense darkvision} with a radius of 60 feet, or an additional 60 feet of {@sense darkvision} if it already has that sense.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Defile Ground', null, 172, 'Druid', 'PHB', 'Blighted', 'TDCSR', 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Defile Ground' AND class_name = 'Druid' AND level = 2), 0, 'Starting at 2nd level when you choose this circle, you can use a bonus action to corrupt a patch of land or an area of water in a 10-foot radius centered on a point within 60 feet of you. This corruption lasts for 1 minute. The corrupted area is {@quickref difficult terrain||3} for creatures that are hostile to you. Additionally, when a creature in the area takes damage from an attack or spell for the first time on a turn, it takes an extra {@damage 1d4} necrotic damage. You can move this patch of corruption up to 30 feet as a bonus action. Flying creatures are unaffected by the corruption.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Defile Ground' AND class_name = 'Druid' AND level = 2), 1, 'Once you use this feature, you can''t use it again until you finish a {@quickref resting|PHB|2|0|short or long rest}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Call of the Shadowseeds', null, 172, 'Druid', 'PHB', 'Blighted', 'TDCSR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Call of the Shadowseeds' AND class_name = 'Druid' AND level = 6), 0, 'At 6th level, you learn to summon the feral children of the forest from the life force of your enemies. When a creature that is not undead or a construct takes damage within the area of your {@subclassFeature Defile Ground|Druid|PHB|Blighted|TDCSR|2} feature, you can use your reaction to summon a {@creature blighted sapling|TDCSR} in an unoccupied space within 5 feet of the creature. You can direct the {@creature blighted sapling|TDCSR|sapling} to make an attack against any creature within 5 feet of it as a part of this reaction. The {@creature blighted sapling|TDCSR|sapling} then acts on your initiative, obeying your verbal commands.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Call of the Shadowseeds' AND class_name = 'Druid' AND level = 6), 1, 'The {@creature blighted sapling|TDCSR} remains in your service until it''s reduced to 0 hit points, until the end of your next {@quickref resting|PHB|2|0|long rest}, or until you summon another {@creature blighted sapling|TDCSR|sapling}, at which point it crumbles into foul-smelling mulch. You can use this feature a number of times equal to your proficiency bonus, regaining all expended uses when you finish a {@quickref resting|PHB|2|0|long rest}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Call of the Shadowseeds' AND class_name = 'Druid' AND level = 6), 2, 'The {@creature blighted sapling|TDCSR|sapling} uses the statistics below. Its features scale based on your level and proficiency bonus (PB).');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Call of the Shadowseeds' AND class_name = 'Druid' AND level = 6), 3, 'statblock', 'Blighted Sapling', NULL, NULL, NULL, NULL, NULL, '{"type":"statblock","name":"Blighted Sapling","source":"TDCSR","page":172,"tag":"creature","collapsed":true}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Defile Ground (10th Level)', null, 172, 'Druid', 'PHB', 'Blighted', 'TDCSR', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Defile Ground (10th Level)' AND class_name = 'Druid' AND level = 10), 0, 'Starting at 10th level, the area of your defiled ground increases to a 20-foot radius. Additionally, the extra damage dealt by your defiled ground increases to {@damage 1d6} when you reach 10th level in this class.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Foul Conjuration', null, 172, 'Druid', 'PHB', 'Blighted', 'TDCSR', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Foul Conjuration' AND class_name = 'Druid' AND level = 10), 0, 'Starting at 10th level, the creatures you conjure are malformed and bloated with toxins. Any beast, fey, or plant (including your {@creature blighted sapling|TDCSR}) summoned or created by your spells or class features gains the following traits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Foul Conjuration' AND class_name = 'Druid' AND level = 10), 1, 'entries', 'Blighted Resilience', '["The creature has immunity to necrotic and poison damage and to the {@condition poisoned} condition."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Blighted Resilience","page":172,"entries":["The creature has immunity to necrotic and poison damage and to the {@condition poisoned} condition."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Foul Conjuration' AND class_name = 'Druid' AND level = 10), 2, 'entries', 'Toxic Demise', '["When the creature is reduced to 0 hit points, it explodes in a burst of toxic mulch or fetid viscera. Each creature within 5 feet of the exploding creature must succeed on a Constitution {@quickref saving throws|PHB|2|1|saving throw} against your spell save DC or take necrotic damage based on the creature''s challenge rating (see the table below). As an action, you can also cause a summoned creature to explode, immediately killing it.",{"type":"table","caption":"Toxic Demise Damage","colLabels":["Creature CR","Damage"],"colStyles":["col-4 text-center","col-8"],"rows":[["1/4 or lower","{@damage 1d4} necrotic damage"],["1/2","{@damage 1d6} necrotic damage"],["1 or higher","{@damage (#$prompt_number:min=1,title=Enter the creature''s CR!,default=1$#)d8|A number of d8s of necrotic damage equal to the creature''s challenge rating}"],["No challenge rating","{@damage (#$prompt_number:min=1,title=Enter your Proficiency Bonus!,default=1$#)d6|A number of d6s of necrotic damage equal to your proficiency bonus}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Toxic Demise","page":172,"entries":["When the creature is reduced to 0 hit points, it explodes in a burst of toxic mulch or fetid viscera. Each creature within 5 feet of the exploding creature must succeed on a Constitution {@quickref saving throws|PHB|2|1|saving throw} against your spell save DC or take necrotic damage based on the creature''s challenge rating (see the table below). As an action, you can also cause a summoned creature to explode, immediately killing it.",{"type":"table","caption":"Toxic Demise Damage","colLabels":["Creature CR","Damage"],"colStyles":["col-4 text-center","col-8"],"rows":[["1/4 or lower","{@damage 1d4} necrotic damage"],["1/2","{@damage 1d6} necrotic damage"],["1 or higher","{@damage (#$prompt_number:min=1,title=Enter the creature''s CR!,default=1$#)d8|A number of d8s of necrotic damage equal to the creature''s challenge rating}"],["No challenge rating","{@damage (#$prompt_number:min=1,title=Enter your Proficiency Bonus!,default=1$#)d6|A number of d6s of necrotic damage equal to your proficiency bonus}"]]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Defile Ground (14th Level)', null, 172, 'Druid', 'PHB', 'Blighted', 'TDCSR', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Defile Ground (14th Level)' AND class_name = 'Druid' AND level = 14), 0, 'The extra damage dealt by your defiled ground increases to {@damage 1d8} when you reach 14th level in this class.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Incarnation of Corruption', null, 173, 'Druid', 'PHB', 'Blighted', 'TDCSR', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Incarnation of Corruption' AND class_name = 'Druid' AND level = 14), 0, 'At 14th level, your physical form begins to take on the tainted traits of the land you are bonded to. Your skin grows ashen, and your eyes darken or turn completely white. Spines and jagged spurs emerge from your body, granting you resistance to necrotic damage and a +2 bonus to AC.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Incarnation of Corruption' AND class_name = 'Druid' AND level = 14), 1, 'Additionally, whenever you start your turn within the radius of the corruption created by your {@subclassFeature Defile Ground|Druid|PHB|Blighted|TDCSR|2} feature, you can use a bonus action to gain temporary hit points equal to your proficiency bonus.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of Dreams', 3, 22, 'Druid', 'PHB', 'Dreams', 'XGE', 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of Dreams' AND class_name = 'Druid' AND level = 2), 0, 'Druids who are members of the Circle of Dreams hail from regions that have strong ties to the Feywild and its dreamlike realms. The druids'' guardianship of the natural world makes for a natural alliance between them and good-aligned fey. These druids seek to fill the world with dreamy wonder. Their magic mends wounds and brings joy to downcast hearts, and the realms they protect are gleaming, fruitful places, where dream and reality blur together and where the weary can find rest.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of Dreams' AND class_name = 'Druid' AND level = 2), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Balm of the Summer Court|Druid||Dreams|XGE|2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Balm of the Summer Court', 3, 22, 'Druid', 'PHB', 'Dreams', 'XGE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Balm of the Summer Court' AND class_name = 'Druid' AND level = 2), 0, 'At 2nd level, you become imbued with the blessings of the Summer Court. You are a font of energy that offers respite from injuries. You have a pool of fey energy represented by a number of {@dice d6}s equal to your druid level.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Balm of the Summer Court' AND class_name = 'Druid' AND level = 2), 1, 'As a bonus action, you can choose one creature you can see within 120 feet of you and spend a number of those dice equal to half your druid level or less. Roll the spent dice and add them together. The target regains a number of hit points equal to the total. The target also gains 1 temporary hit point per die spent.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Balm of the Summer Court' AND class_name = 'Druid' AND level = 2), 2, 'You regain all expended dice when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hearth of Moonlight and Shadow', 3, 22, 'Druid', 'PHB', 'Dreams', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hearth of Moonlight and Shadow' AND class_name = 'Druid' AND level = 6), 0, 'At 6th level, home can be wherever you are. During a short or long rest, you can invoke the shadowy power of the Gloaming Court to help guard your respite. At the start of the rest, you touch a point in space, and an invisible, 30-foot-radius sphere of magic appears, centered on that point. {@quickref Cover||3||Total cover} blocks the sphere.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hearth of Moonlight and Shadow' AND class_name = 'Druid' AND level = 6), 1, 'While within the sphere, you and your allies gain a +5 bonus to Dexterity ({@skill Stealth}) and Wisdom ({@skill Perception}) checks, and any light from open flames in the sphere (a campfire, torches, or the like) isn''t visible outside it.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hearth of Moonlight and Shadow' AND class_name = 'Druid' AND level = 6), 2, 'The sphere vanishes at the end of the rest or when you leave the sphere.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hidden Paths', 3, 22, 'Druid', 'PHB', 'Dreams', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hidden Paths' AND class_name = 'Druid' AND level = 10), 0, 'Starting at 10th level, you can use the hidden, magical pathways that some fey use to traverse space in the blink of an eye. As a bonus action on your turn, you can teleport up to 60 feet to an unoccupied space you can see. Alternatively, you can use your action to teleport one willing creature you touch up to 30 feet to an unoccupied space you can see.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hidden Paths' AND class_name = 'Druid' AND level = 10), 1, 'You can use this feature a number of times equal to your Wisdom modifier (minimum of once), and you regain all expended uses of it when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Walker in Dreams', 3, 22, 'Druid', 'PHB', 'Dreams', 'XGE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Walker in Dreams' AND class_name = 'Druid' AND level = 14), 0, 'At 14th level, the magic of the Feywild grants you the ability to travel mentally or physically through dreamlands.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Walker in Dreams' AND class_name = 'Druid' AND level = 14), 1, 'When you finish a short rest, you can cast one of the following spells, without expending a spell slot or requiring material components: {@spell dream} (with you as the messenger), {@spell scrying}, or {@spell teleportation circle}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Walker in Dreams' AND class_name = 'Druid' AND level = 14), 2, 'This use of {@spell teleportation circle} is special. Rather than opening a portal to a permanent {@spell teleportation circle}, it opens a portal to the last location where you finished a long rest on your current plane of existence. If you haven''t taken a long rest on your current plane, the spell fails but isn''t wasted.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Walker in Dreams' AND class_name = 'Druid' AND level = 14), 3, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Circle of the Shepherd', 3, 23, 'Druid', 'PHB', 'Shepherd', 'XGE', 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Shepherd' AND class_name = 'Druid' AND level = 2), 0, 'Druids of the Circle of the Shepherd commune with the spirits of nature, especially the spirits of beasts and the fey, and call to those spirits for aid. These druids recognize that all living things play a role in the natural world, yet they focus on protecting animals and fey creatures that have difficulty defending themselves. Shepherds, as they are known, see such creatures as their charges. They ward off monsters that threaten them, rebuke hunters who kill more prey than necessary, and prevent civilization from encroaching on rare animal habitats and on sites sacred to the fey. Many of these druids are happiest far from cities and towns, content to spend their days in the company of animals and the fey creatures of the wilds.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Shepherd' AND class_name = 'Druid' AND level = 2), 1, 'Members of this circle become adventurers to oppose forces that threaten their charges or to seek knowledge and power that will help them safeguard their charges better. Wherever these druids go, the spirits of the wilderness are with them.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Shepherd' AND class_name = 'Druid' AND level = 2), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Speech of the Woods|Druid||Shepherd|XGE|2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Circle of the Shepherd' AND class_name = 'Druid' AND level = 2), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Spirit Totem|Druid||Shepherd|XGE|2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Speech of the Woods', 3, 23, 'Druid', 'PHB', 'Shepherd', 'XGE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Speech of the Woods' AND class_name = 'Druid' AND level = 2), 0, 'At 2nd level, you gain the ability to converse with beasts and many fey.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Speech of the Woods' AND class_name = 'Druid' AND level = 2), 1, 'You learn to speak, read, and write {@language Sylvan}. In addition, beasts can understand your speech, and you gain the ability to decipher their noises and motions. Most beasts lack the intelligence to convey or understand sophisticated concepts, but a friendly beast could relay what it has seen or heard in the recent past. This ability doesn''t grant you friendship with beasts, though you can combine this ability with gifts to curry favor with them as you would with any nonplayer character.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spirit Totem', 3, 23, 'Druid', 'PHB', 'Shepherd', 'XGE', 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Totem' AND class_name = 'Druid' AND level = 2), 0, 'Starting at 2nd level, you can call forth nature spirits to influence the world around you. As a bonus action, you can magically summon an incorporeal spirit to a point you can see within 60 feet of you. The spirit creates an aura in a 30-foot radius around that point. It counts as neither a creature nor an object, though it has the spectral appearance of the creature it represents.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Totem' AND class_name = 'Druid' AND level = 2), 1, 'As a bonus action, you can move the spirit up to 60 feet to a point you can see.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Totem' AND class_name = 'Druid' AND level = 2), 2, 'The spirit persists for 1 minute or until you''re {@condition incapacitated}. Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Totem' AND class_name = 'Druid' AND level = 2), 3, 'The effect of the spirit''s aura depends on the type of spirit you summon from the options below.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spirit Totem' AND class_name = 'Druid' AND level = 2), 4, 'entries', 'Bear Spirit', '["The bear spirit grants you and your allies its might and endurance. Each creature of your choice in the aura when the spirit appears gains temporary hit points equal to 5 + your druid level. In addition, you and your allies gain advantage on Strength checks and Strength saving throws while in the aura."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Bear Spirit","entries":["The bear spirit grants you and your allies its might and endurance. Each creature of your choice in the aura when the spirit appears gains temporary hit points equal to 5 + your druid level. In addition, you and your allies gain advantage on Strength checks and Strength saving throws while in the aura."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spirit Totem' AND class_name = 'Druid' AND level = 2), 5, 'entries', 'Hawk Spirit', '["The hawk spirit is a consummate hunter, aiding you and your allies with its keen sight. When a creature makes an attack roll against a target in the spirit''s aura, you can use your reaction to grant advantage to that attack roll. In addition, you and your allies have advantage on Wisdom ({@skill Perception}) checks while in the aura."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Hawk Spirit","entries":["The hawk spirit is a consummate hunter, aiding you and your allies with its keen sight. When a creature makes an attack roll against a target in the spirit''s aura, you can use your reaction to grant advantage to that attack roll. In addition, you and your allies have advantage on Wisdom ({@skill Perception}) checks while in the aura."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spirit Totem' AND class_name = 'Druid' AND level = 2), 6, 'entries', 'Unicorn Spirit', '["The unicorn spirit lends its protection to those nearby. You and your allies gain advantage on all ability checks made to detect creatures in the spirit''s aura. In addition, if you cast a spell using a spell slot that restores hit points to any creature inside or outside the aura, each creature of your choice in the aura also regains hit points equal to your druid level."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Unicorn Spirit","entries":["The unicorn spirit lends its protection to those nearby. You and your allies gain advantage on all ability checks made to detect creatures in the spirit''s aura. In addition, if you cast a spell using a spell slot that restores hit points to any creature inside or outside the aura, each creature of your choice in the aura also regains hit points equal to your druid level."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mighty Summoner', 3, 23, 'Druid', 'PHB', 'Shepherd', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mighty Summoner' AND class_name = 'Druid' AND level = 6), 0, 'Starting at 6th level, beasts and fey that you conjure are more resilient than normal. Any beast or fey summoned or created by a spell that you cast gains the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Mighty Summoner' AND class_name = 'Druid' AND level = 6), 1, 'list', NULL, '["The creature appears with more hit points than normal: 2 extra hit points per Hit Die it has.","The damage from its natural weapons is considered magical for the purpose of overcoming immunity and resistance to nonmagical attacks and damage."]', '["The creature appears with more hit points than normal: 2 extra hit points per Hit Die it has.","The damage from its natural weapons is considered magical for the purpose of overcoming immunity and resistance to nonmagical attacks and damage."]', NULL, NULL, NULL, '{"type":"list","items":["The creature appears with more hit points than normal: 2 extra hit points per Hit Die it has.","The damage from its natural weapons is considered magical for the purpose of overcoming immunity and resistance to nonmagical attacks and damage."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Guardian Spirit', 3, 23, 'Druid', 'PHB', 'Shepherd', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Guardian Spirit' AND class_name = 'Druid' AND level = 10), 0, 'Beginning at 10th level, your Spirit Totem safeguards the beasts and fey that you call forth with your magic. When a beast or fey that you summoned or created with a spell ends its turn in your Spirit Totem aura, that creature regains a number of hit points equal to half your druid level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Faithful Summons', 3, 23, 'Druid', 'PHB', 'Shepherd', 'XGE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Faithful Summons' AND class_name = 'Druid' AND level = 14), 0, 'Starting at 14th level, the nature spirits you commune with protect you when you are the most defenseless. If you are reduced to 0 hit points or are {@condition incapacitated} against your will, you can immediately gain the benefits of {@spell conjure animals} as if it were cast using a 9th-level spell slot. It summons four beasts of your choice that are challenge rating 2 or lower. The conjured beasts appear within 20 feet of you. If they receive no commands from you, they protect you from harm and attack your foes. The spell lasts for 1 hour, requiring no {@status concentration}, or until you dismiss it (no action required).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Faithful Summons' AND class_name = 'Druid' AND level = 14), 1, 'Once you use this feature, you can''t use it again until you finish a long rest.');
