INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fighting Style' AND class_name = 'Fighter' AND level = 1), 0, 'You adopt a particular style of fighting as your specialty. Choose one of the following options. You can''t take the same Fighting Style option more than once, even if you get to choose again.');
