INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Attack' AND class_name = 'Fighter' AND level = 5), 1, 'The number of attacks increases to three when you reach 11th level in this class and to four when you reach 20th level in this class.');
