INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Echo Knight', 10, 183, 'Fighter', 'PHB', 'Echo Knight', 'EGW', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Echo Knight' AND class_name = 'Fighter' AND level = 3), 0, 'A mysterious and feared frontline warrior of the Kryn Dynasty, the Echo Knight has mastered the art of using dunamis to summon the fading shades of unrealized timelines to aid them in battle. Surrounded by echoes of their own might, they charge into the fray as a cycling swarm of shadows and strikes.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Echo Knight' AND class_name = 'Fighter' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Manifest Echo|Fighter||Echo Knight|EGW|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Echo Knight' AND class_name = 'Fighter' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Unleash Incarnation|Fighter||Echo Knight|EGW|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Manifest Echo', 10, 183, 'Fighter', 'PHB', 'Echo Knight', 'EGW', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Manifest Echo' AND class_name = 'Fighter' AND level = 3), 0, '{@i 3rd-level Echo Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Manifest Echo' AND class_name = 'Fighter' AND level = 3), 1, 'You can use a bonus action to magically manifest an echo of yourself in an unoccupied space you can see within 15 feet of you. This echo is a magical, translucent, gray image of you that lasts until it is destroyed, until you dismiss it as a bonus action, until you manifest another echo, or until you''re {@condition incapacitated}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Manifest Echo' AND class_name = 'Fighter' AND level = 3), 2, 'Your echo has AC 14 + your proficiency bonus, 1 hit point, and immunity to all conditions. If it has to make a saving throw, it uses your saving throw bonus for the roll. It is the same size as you, and it occupies its space. On your turn, you can mentally command the echo to move up to 30 feet in any direction (no action required). If your echo is ever more than 30 feet from you at the end of your turn, it is destroyed.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Manifest Echo' AND class_name = 'Fighter' AND level = 3), 3, 'You can use the echo in the following ways:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Manifest Echo' AND class_name = 'Fighter' AND level = 3), 4, 'list', NULL, '["As a bonus action, you can teleport, magically swapping places with your echo at a cost of 15 feet of your movement, regardless of the distance between the two of you.","When you take the {@action Attack} action on your turn, any attack you make with that action can originate from your space or the echo''s space. You make this choice for each attack.","When a creature that you can see within 5 feet of your echo moves at least 5 feet away from it, you can use your reaction to make an opportunity attack against that creature as if you were in the echo''s space."]', '["As a bonus action, you can teleport, magically swapping places with your echo at a cost of 15 feet of your movement, regardless of the distance between the two of you.","When you take the {@action Attack} action on your turn, any attack you make with that action can originate from your space or the echo''s space. You make this choice for each attack.","When a creature that you can see within 5 feet of your echo moves at least 5 feet away from it, you can use your reaction to make an opportunity attack against that creature as if you were in the echo''s space."]', NULL, NULL, NULL, '{"type":"list","items":["As a bonus action, you can teleport, magically swapping places with your echo at a cost of 15 feet of your movement, regardless of the distance between the two of you.","When you take the {@action Attack} action on your turn, any attack you make with that action can originate from your space or the echo''s space. You make this choice for each attack.","When a creature that you can see within 5 feet of your echo moves at least 5 feet away from it, you can use your reaction to make an opportunity attack against that creature as if you were in the echo''s space."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unleash Incarnation', 10, 183, 'Fighter', 'PHB', 'Echo Knight', 'EGW', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unleash Incarnation' AND class_name = 'Fighter' AND level = 3), 0, '{@i 3rd-level Echo Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unleash Incarnation' AND class_name = 'Fighter' AND level = 3), 1, 'You can heighten your echo''s fury. Whenever you take the {@action Attack} action, you can make one additional melee attack from the echo''s position.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unleash Incarnation' AND class_name = 'Fighter' AND level = 3), 2, 'You can use this feature a number of times equal to your Constitution modifier (a minimum of once). You regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Echo Avatar', 10, 183, 'Fighter', 'PHB', 'Echo Knight', 'EGW', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Echo Avatar' AND class_name = 'Fighter' AND level = 7), 0, '{@i 7th-level Echo Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Echo Avatar' AND class_name = 'Fighter' AND level = 7), 1, 'You can temporarily transfer your consciousness to your echo. As an action, you can see through your echo''s eyes and hear through its ears. During this time, you are {@condition deafened} and {@condition blinded}. You can sustain this effect for up to 10 minutes, and you can end it at any time (requires no action). While your echo is being used in this way, it can be up to 1,000 feet away from you without being destroyed.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Shadow Martyr', 10, 183, 'Fighter', 'PHB', 'Echo Knight', 'EGW', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Shadow Martyr' AND class_name = 'Fighter' AND level = 10), 0, '{@i 10th-level Echo Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Shadow Martyr' AND class_name = 'Fighter' AND level = 10), 1, 'You can make your echo throw itself in front of an attack directed at another creature that you can see. Before the attack roll is made, you can use your reaction to teleport the echo to an unoccupied space within 5 feet of the targeted creature. The attack roll that triggered the reaction is instead made against your echo.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Shadow Martyr' AND class_name = 'Fighter' AND level = 10), 2, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Reclaim Potential', 10, 183, 'Fighter', 'PHB', 'Echo Knight', 'EGW', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Reclaim Potential' AND class_name = 'Fighter' AND level = 15), 0, '{@i 15th-level Echo Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Reclaim Potential' AND class_name = 'Fighter' AND level = 15), 1, 'You''ve learned to absorb the fleeting magic of your echo. When an echo of yours is destroyed by taking damage, you can gain a number of temporary hit points equal to {@dice 2d6} + your Constitution modifier, provided you don''t already have temporary hit points.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Reclaim Potential' AND class_name = 'Fighter' AND level = 15), 2, 'You can use this feature a number of times equal to your Constitution modifier (a minimum of once). You regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Legion of One', 10, 183, 'Fighter', 'PHB', 'Echo Knight', 'EGW', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Legion of One' AND class_name = 'Fighter' AND level = 18), 0, '{@i 18th-level Echo Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Legion of One' AND class_name = 'Fighter' AND level = 18), 1, 'You can use a bonus action to create two echoes with your Manifest Echo feature, and these echoes can coexist. If you try to create a third echo, the previous two echoes are destroyed. Anything you can do from one echo''s position can be done from the other''s instead.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Legion of One' AND class_name = 'Fighter' AND level = 18), 2, 'In addition, when you roll initiative and have no uses of your Unleash Incarnation feature left, you regain one use of that feature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Battle Master', 1, 73, 'Fighter', 'PHB', 'Battle Master', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Battle Master' AND class_name = 'Fighter' AND level = 3), 0, 'Those who emulate the archetypal Battle Master employ martial techniques passed down through generations. To a Battle Master, combat is an academic field, sometimes including subjects beyond battle such as weaponsmithing and calligraphy. Not every fighter absorbs the lessons of history, theory, and artistry that are reflected in the Battle Master archetype, but those who do are well-rounded fighters of great skill and knowledge.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Master' AND class_name = 'Fighter' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Student of War|Fighter||Battle Master||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Master' AND class_name = 'Fighter' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Combat Superiority|Fighter||Battle Master||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Master' AND class_name = 'Fighter' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Maneuvers|Fighter||Battle Master||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Battle Master' AND class_name = 'Fighter' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Maneuver Options|Fighter||Battle Master||3|TCE"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Combat Superiority', 1, 73, 'Fighter', 'PHB', 'Battle Master', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Combat Superiority' AND class_name = 'Fighter' AND level = 3), 0, 'When you choose this archetype at 3rd level, you learn maneuvers that are fueled by special dice called superiority dice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Combat Superiority' AND class_name = 'Fighter' AND level = 3), 1, 'entries', NULL, '[{"type":"entries","name":"Maneuvers","entries":["You learn three maneuvers of your choice, which are listed under \"Maneuvers\" below. Many maneuvers enhance an attack in some way. You can use only one maneuver per attack.","You learn two additional maneuvers of your choice at 7th, 10th, and 15th level. Each time you learn new maneuvers, you can also replace one maneuver you know with a different one."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Maneuvers","entries":["You learn three maneuvers of your choice, which are listed under \"Maneuvers\" below. Many maneuvers enhance an attack in some way. You can use only one maneuver per attack.","You learn two additional maneuvers of your choice at 7th, 10th, and 15th level. Each time you learn new maneuvers, you can also replace one maneuver you know with a different one."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Combat Superiority' AND class_name = 'Fighter' AND level = 3), 2, 'entries', NULL, '[{"type":"entries","name":"Superiority Dice","entries":["You have four superiority dice, which are {@dice d8}s. A superiority die is expended when you use it. You regain all of your expended superiority dice when you finish a short or long rest.","You gain another superiority die at 7th level and one more at 15th level."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Superiority Dice","entries":["You have four superiority dice, which are {@dice d8}s. A superiority die is expended when you use it. You regain all of your expended superiority dice when you finish a short or long rest.","You gain another superiority die at 7th level and one more at 15th level."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Combat Superiority' AND class_name = 'Fighter' AND level = 3), 3, 'entries', NULL, '[{"type":"entries","name":"Saving Throws","entries":["Some of your maneuvers require your target to make a saving throw to resist the maneuver''s effects. The saving throw DC is calculated as follows:",{"type":"abilityDc","name":"Maneuver","attributes":["str","dex"]}]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Saving Throws","entries":["Some of your maneuvers require your target to make a saving throw to resist the maneuver''s effects. The saving throw DC is calculated as follows:",{"type":"abilityDc","name":"Maneuver","attributes":["str","dex"]}]}]}');
