INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Maneuver Options' AND class_name = 'Fighter' AND level = 3), 0, '{@i 3rd-level fighter {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Maneuver Options' AND class_name = 'Fighter' AND level = 3), 1, 'If you have access to maneuvers, the following maneuvers are added to the list of options available to you. Maneuvers are available to Battle Masters but also to characters who have a special feature like the {@optfeature Superior Technique|TCE} fighting style or the {@feat Martial Adept} feat.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Maneuver Options' AND class_name = 'Fighter' AND level = 3), 2, 'entries', NULL, '[{"type":"options","entries":[{"type":"refOptionalfeature","optionalfeature":"Ambush|TCE"},{"type":"refOptionalfeature","optionalfeature":"Bait and Switch|TCE"},{"type":"refOptionalfeature","optionalfeature":"Brace|TCE"},{"type":"refOptionalfeature","optionalfeature":"Commanding Presence|TCE"},{"type":"refOptionalfeature","optionalfeature":"Grappling Strike|TCE"},{"type":"refOptionalfeature","optionalfeature":"Quick Toss|TCE"},{"type":"refOptionalfeature","optionalfeature":"Tactical Assessment|TCE"}]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"options","entries":[{"type":"refOptionalfeature","optionalfeature":"Ambush|TCE"},{"type":"refOptionalfeature","optionalfeature":"Bait and Switch|TCE"},{"type":"refOptionalfeature","optionalfeature":"Brace|TCE"},{"type":"refOptionalfeature","optionalfeature":"Commanding Presence|TCE"},{"type":"refOptionalfeature","optionalfeature":"Grappling Strike|TCE"},{"type":"refOptionalfeature","optionalfeature":"Quick Toss|TCE"},{"type":"refOptionalfeature","optionalfeature":"Tactical Assessment|TCE"}]}]}');
