INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Know Your Enemy' AND class_name = 'Fighter' AND level = 7), 0, 'If you spend at least 1 minute observing or interacting with another creature outside combat, you can learn certain information about its capabilities compared to your own. The DM tells you if the creature is your equal, superior, or inferior in regard to two of the following characteristics of your choice:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Know Your Enemy' AND class_name = 'Fighter' AND level = 7), 1, 'list', NULL, '["Strength score","Dexterity score","Constitution score","Armor Class","Current hit points","Total class levels (if any)","Fighter class levels (if any)"]', '["Strength score","Dexterity score","Constitution score","Armor Class","Current hit points","Total class levels (if any)","Fighter class levels (if any)"]', NULL, NULL, NULL, '{"type":"list","items":["Strength score","Dexterity score","Constitution score","Armor Class","Current hit points","Total class levels (if any)","Fighter class levels (if any)"]}');
