INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Relentless' AND class_name = 'Fighter' AND level = 15), 0, 'Starting at 15th level, when you roll initiative and have no superiority dice remaining, you regain 1 superiority die.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Improved Combat Superiority (d12)', 1, 73, 'Fighter', 'PHB', 'Battle Master', 'PHB', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
