INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Champion', 1, 72, 'Fighter', 'PHB', 'Champion', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Champion' AND class_name = 'Fighter' AND level = 3), 0, 'The archetypal Champion focuses on the development of raw physical power honed to deadly perfection. Those who model themselves on this archetype combine rigorous training with physical excellence to deal devastating blows.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Champion' AND class_name = 'Fighter' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Improved Critical|Fighter||Champion||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Improved Critical', 1, 72, 'Fighter', 'PHB', 'Champion', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Improved Critical' AND class_name = 'Fighter' AND level = 3), 0, 'Beginning when you choose this archetype at 3rd level, your weapon attacks score a critical hit on a roll of 19 or 20.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Remarkable Athlete', 1, 72, 'Fighter', 'PHB', 'Champion', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Remarkable Athlete' AND class_name = 'Fighter' AND level = 7), 0, 'Starting at 7th level, you can add half your proficiency bonus (round up) to any Strength, Dexterity, or Constitution check you make that doesn''t already use your proficiency bonus.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Remarkable Athlete' AND class_name = 'Fighter' AND level = 7), 1, 'In addition, when you make a running long jump, the distance you can cover increases by a number of feet equal to your Strength modifier.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Additional Fighting Style', 1, 72, 'Fighter', 'PHB', 'Champion', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Fighting Style' AND class_name = 'Fighter' AND level = 10), 0, 'At 10th level, you can choose a second option from the Fighting Style class feature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Superior Critical', 1, 72, 'Fighter', 'PHB', 'Champion', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Superior Critical' AND class_name = 'Fighter' AND level = 15), 0, 'Starting at 15th level, your weapon attacks score a critical hit on a roll of 18-20.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Survivor', 1, 72, 'Fighter', 'PHB', 'Champion', 'PHB', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Survivor' AND class_name = 'Fighter' AND level = 18), 0, 'At 18th level, you attain the pinnacle of resilience in battle. At the start of each of your turns, you regain hit points equal to 5 + your Constitution modifier if you have no more than half of your hit points left. You don''t gain this benefit if you have 0 hit points.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eldritch Knight', 1, 74, 'Fighter', 'PHB', 'Eldritch Knight', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Knight' AND class_name = 'Fighter' AND level = 3), 0, 'The archetypal Eldritch Knight combines the martial mastery common to all fighters with a careful study of magic. Eldritch Knights use magical techniques similar to those practiced by wizards. They focus their study on two of the eight schools of magic—abjuration and evocation. Abjuration spells grant an Eldritch Knight additional protection in battle, and evocation spells deal damage to many foes at once, extending the fighter''s reach in combat. These knights learn a comparatively small number of spells, committing them to memory instead of keeping them in a spellbook.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Knight' AND class_name = 'Fighter' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Spellcasting|Fighter||Eldritch Knight||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Knight' AND class_name = 'Fighter' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Weapon Bond|Fighter||Eldritch Knight||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spellcasting', 1, 74, 'Fighter', 'PHB', 'Eldritch Knight', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Fighter' AND level = 3), 0, 'When you reach 3rd level, you augment your martial prowess with the ability to cast spells. See {@book chapter 10|PHB|10} for the general rules of spellcasting and {@book chapter 11|PHB|11} for the {@filter wizard spell list|spells|class=wizard}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Fighter' AND level = 3), 1, 'entries', 'Cantrips', '["You learn two cantrips of your choice from the {@filter wizard spell list|spells|class=wizard}. You learn an additional wizard cantrip of your choice at 10th level."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Cantrips","entries":["You learn two cantrips of your choice from the {@filter wizard spell list|spells|class=wizard}. You learn an additional wizard cantrip of your choice at 10th level."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Fighter' AND level = 3), 2, 'entries', NULL, '[{"type":"entries","name":"Spell Slots","entries":["The Eldritch Knight Spellcasting table shows how many spell slots you have to cast your {@filter wizard spells|spells|class=wizard} of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","For example, if you know the 1st-level spell {@spell shield} and have a 1st-level and a 2nd-level spell slot available, you can cast {@spell shield} using either slot."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Spell Slots","entries":["The Eldritch Knight Spellcasting table shows how many spell slots you have to cast your {@filter wizard spells|spells|class=wizard} of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","For example, if you know the 1st-level spell {@spell shield} and have a 1st-level and a 2nd-level spell slot available, you can cast {@spell shield} using either slot."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Fighter' AND level = 3), 3, 'entries', NULL, '[{"type":"entries","name":"Spells Known of 1st-Level and Higher","entries":["You know three 1st-level wizard spells of your choice, two of which you must choose from the abjuration and evocation spells on the wizard spell list.","The Spells Known column of the Eldritch Knight Spellcasting table shows when you learn more wizard spells of 1st level or higher. Each of these spells must be an abjuration or evocation spell of your choice, and must be of a level for which you have spell slots. For instance, when you reach 7th level in this class, you can learn one new spell of 1st or 2nd level.","The spells you learn at 8th, 14th, and 20th level can come from any school of magic.","Whenever you gain a level in this class, you can replace one of the wizard spells you know with another spell of your choice from the wizard spell list. The new spell must be of a level for which you have spell slots, and it must be an abjuration or evocation spell, unless you''re replacing the spell you gained at 3rd, 8th, 14th, or 20th level from any school of magic."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Spells Known of 1st-Level and Higher","entries":["You know three 1st-level wizard spells of your choice, two of which you must choose from the abjuration and evocation spells on the wizard spell list.","The Spells Known column of the Eldritch Knight Spellcasting table shows when you learn more wizard spells of 1st level or higher. Each of these spells must be an abjuration or evocation spell of your choice, and must be of a level for which you have spell slots. For instance, when you reach 7th level in this class, you can learn one new spell of 1st or 2nd level.","The spells you learn at 8th, 14th, and 20th level can come from any school of magic.","Whenever you gain a level in this class, you can replace one of the wizard spells you know with another spell of your choice from the wizard spell list. The new spell must be of a level for which you have spell slots, and it must be an abjuration or evocation spell, unless you''re replacing the spell you gained at 3rd, 8th, 14th, or 20th level from any school of magic."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Fighter' AND level = 3), 4, 'entries', NULL, '[{"type":"entries","name":"Spellcasting Ability","entries":["Intelligence is your spellcasting ability for your wizard spells, since you learn your spells through study and memorization. You use your Intelligence whenever a spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for a wizard spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["int"]},{"type":"abilityAttackMod","name":"Spell","attributes":["int"]}]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Spellcasting Ability","entries":["Intelligence is your spellcasting ability for your wizard spells, since you learn your spells through study and memorization. You use your Intelligence whenever a spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for a wizard spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["int"]},{"type":"abilityAttackMod","name":"Spell","attributes":["int"]}]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Weapon Bond', 1, 74, 'Fighter', 'PHB', 'Eldritch Knight', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Weapon Bond' AND class_name = 'Fighter' AND level = 3), 0, 'At 3rd level, you learn a ritual that creates a magical bond between yourself and one weapon. You perform the ritual over the course of 1 hour, which can be done during a short rest. The weapon must be within your reach throughout the ritual, at the conclusion of which you touch the weapon and forge the bond.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Weapon Bond' AND class_name = 'Fighter' AND level = 3), 1, 'Once you have bonded a weapon to yourself, you can''t be disarmed of that weapon unless you are {@condition incapacitated}. If it is on the same plane of existence, you can summon that weapon as a bonus action on your turn, causing it to teleport instantly to your hand.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Weapon Bond' AND class_name = 'Fighter' AND level = 3), 2, 'You can have up to two bonded weapons, but can summon only one at a time with your bonus action. If you attempt to bond with a third weapon, you must break the bond with one of the other two.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('War Magic', 1, 74, 'Fighter', 'PHB', 'Eldritch Knight', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'War Magic' AND class_name = 'Fighter' AND level = 7), 0, 'Beginning at 7th level, when you use your action to cast a cantrip, you can make one weapon attack as a bonus action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eldritch Strike', 1, 74, 'Fighter', 'PHB', 'Eldritch Knight', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Strike' AND class_name = 'Fighter' AND level = 10), 0, 'At 10th level, you learn how to make your weapon strikes undercut a creature''s resistance to your spells. When you hit a creature with a weapon attack, that creature has disadvantage on the next saving throw it makes against a spell you cast before the end of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Charge', 1, 74, 'Fighter', 'PHB', 'Eldritch Knight', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Charge' AND class_name = 'Fighter' AND level = 15), 0, 'At 15th level, you gain the ability to teleport up to 30 feet to an unoccupied space you can see when you use your Action Surge. You can teleport before or after the additional action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Improved War Magic', 1, 74, 'Fighter', 'PHB', 'Eldritch Knight', 'PHB', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Improved War Magic' AND class_name = 'Fighter' AND level = 18), 0, 'Starting at 18th level, when you use your action to cast a spell, you can make one weapon attack as a bonus action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Purple Dragon Knight (Banneret)', null, 128, 'Fighter', 'PHB', 'Purple Dragon Knight (Banneret)', 'SCAG', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Purple Dragon Knight (Banneret)' AND class_name = 'Fighter' AND level = 3), 0, 'Purple Dragon Knights are warriors who hail from the kingdom of Cormyr. Pledged to protect the crown, they take the fight against evil beyond the kingdom''s borders. They are tasked with wandering the land as knights errant, relying on their judgment, bravery, and fidelity to guide them in defeating evildoers.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Purple Dragon Knight (Banneret)' AND class_name = 'Fighter' AND level = 3), 1, 'A Purple Dragon Knight inspires greatness in others by committing brave deeds in battle. The mere presence of a knight in a hamlet is enough to cause some orcs and bandits to seek easier prey. A lone knight is a skilled warrior, but a knight leading a band of allies can transform even the most poorly equipped militia into a ferocious war band.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Purple Dragon Knight (Banneret)' AND class_name = 'Fighter' AND level = 3), 2, 'A knight prefers to lead through deeds, not words. As a knight spearheads an attack, the knight''s actions can awaken reserves of courage and conviction in allies that they never suspected they had.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Purple Dragon Knight (Banneret)' AND class_name = 'Fighter' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Restriction: Knighthood|Fighter||Purple Dragon Knight (Banneret)|SCAG|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Purple Dragon Knight (Banneret)' AND class_name = 'Fighter' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Rallying Cry|Fighter||Purple Dragon Knight (Banneret)|SCAG|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Rallying Cry', null, 128, 'Fighter', 'PHB', 'Purple Dragon Knight (Banneret)', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rallying Cry' AND class_name = 'Fighter' AND level = 3), 0, 'When you choose this archetype at 3rd level, you learn how to inspire your allies to fight on past their injuries.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rallying Cry' AND class_name = 'Fighter' AND level = 3), 1, 'When you use your Second Wind feature, you can choose up to three creatures within 60 feet of you that are allied with you. Each one regains hit points equal to your fighter level, provided that the creature can see or hear you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Restriction: Knighthood', null, 128, 'Fighter', 'PHB', 'Purple Dragon Knight (Banneret)', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Restriction: Knighthood' AND class_name = 'Fighter' AND level = 3), 0, 'Purple Dragon Knights are tied to a specific order of Cormyrean knighthood.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Restriction: Knighthood' AND class_name = 'Fighter' AND level = 3), 1, 'Banneret serves as the generic name for this archetype if you use it in other campaign settings or to model warlords other than the Purple Dragon Knights.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Royal Envoy', null, 128, 'Fighter', 'PHB', 'Purple Dragon Knight (Banneret)', 'SCAG', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Royal Envoy' AND class_name = 'Fighter' AND level = 7), 0, 'A Purple Dragon Knight serves as an envoy of the Cormyrean crown. Knights of high standing are expected to conduct themselves with grace.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Royal Envoy' AND class_name = 'Fighter' AND level = 7), 1, 'At 7th level, you gain proficiency in the {@skill Persuasion} skill. If you are already proficient in it, you gain proficiency in one of the following skills of your choice: {@skill Animal Handling}, {@skill Insight}, {@skill Intimidation}, or {@skill Performance}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Royal Envoy' AND class_name = 'Fighter' AND level = 7), 2, 'Your proficiency bonus is doubled for any ability check you make that uses {@skill Persuasion}. You receive this benefit regardless of the skill proficiency you gain from this feature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Inspiring Surge', null, 128, 'Fighter', 'PHB', 'Purple Dragon Knight (Banneret)', 'SCAG', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Inspiring Surge' AND class_name = 'Fighter' AND level = 10), 0, 'Starting at 10th level, when you use your Action Surge feature, you can choose one creature within 60 feet of you that is allied with you. That creature can make one melee or ranged weapon attack with its reaction, provided that it can see or hear you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Inspiring Surge' AND class_name = 'Fighter' AND level = 10), 1, 'Starting at 18th level, you can choose two allies within 60 feet of you, rather than one.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bulwark', null, 128, 'Fighter', 'PHB', 'Purple Dragon Knight (Banneret)', 'SCAG', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bulwark' AND class_name = 'Fighter' AND level = 15), 0, 'Beginning at 15th level, you can extend the benefit of your Indomitable feature to an ally. When you decide to use Indomitable to reroll an Intelligence, a Wisdom, or a Charisma saving throw and you aren''t {@condition incapacitated}, you can choose one ally within 60 feet of you that also failed its saving throw against the same effect. If that creature can see or hear you, it can reroll its saving throw and must use the new roll.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Inspiring Surge', null, 128, 'Fighter', 'PHB', 'Purple Dragon Knight (Banneret)', 'SCAG', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Inspiring Surge' AND class_name = 'Fighter' AND level = 18), 0, 'Starting at 18th level, you can choose two allies within 60 feet of you, rather than one.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Protective Field', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Protective Field' AND class_name = 'Fighter' AND level = 3), 0, 'When you or another creature you can see within 30 feet of you takes damage, you can use your reaction to expend one Psionic Energy die, roll the die, and reduce the damage taken by the number rolled plus your Intelligence modifier (minimum reduction of 1), as you create a momentary shield of telekinetic force.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Psi Warrior', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psi Warrior' AND class_name = 'Fighter' AND level = 3), 0, 'Awake to the psionic power within, a Psi Warrior is a fighter who augments their physical might with psi-infused weapon strikes, telekinetic lashes, and barriers of mental force. Many githyanki train to become such warriors, as do some of the most disciplined high elves. In the world of Eberron, many young kalashtar dream of becoming Psi Warriors.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psi Warrior' AND class_name = 'Fighter' AND level = 3), 1, 'As a Psi Warrior, you might have honed your psionic abilities through solo discipline, unlocked it under the tutelage of a master, or refined it at an academy dedicated to wielding the mind''s power as both weapon and shield.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Psi Warrior' AND class_name = 'Fighter' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Psionic Power|Fighter||Psi Warrior|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Psionic Strike', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psionic Strike' AND class_name = 'Fighter' AND level = 3), 0, 'You can propel your weapons with psionic force. Once on each of your turns, immediately after you hit a target within 30 feet of you with an attack and deal damage to it with a weapon, you can expend one Psionic Energy die, rolling it and dealing force damage to the target equal to the number rolled plus your Intelligence modifier.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Telekinetic Movement', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Telekinetic Movement' AND class_name = 'Fighter' AND level = 3), 0, 'You can move an object or a creature with your mind. As an action, you target one loose object that is Large or smaller or one willing creature, other than yourself. If you can see the target and it is within 30 feet of you, you can move it up to 30 feet to an unoccupied space you can see. Alternatively, if it is a Tiny object, you can move it to or from your hand. Either way, you can move the target horizontally, vertically, or both. Once you take this action, you can''t do so again until you finish a short or long rest, unless you expend a Psionic Energy die to take it again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Psionic Power', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psionic Power' AND class_name = 'Fighter' AND level = 3), 0, '{@i 3rd-level Psi Warrior feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psionic Power' AND class_name = 'Fighter' AND level = 3), 1, 'You harbor a wellspring of psionic energy within yourself. This energy is represented by your Psionic Energy dice, which are each a {@dice d6}. You have a number of these dice equal to twice your proficiency bonus, and they fuel various psionic powers you have, which are detailed below.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psionic Power' AND class_name = 'Fighter' AND level = 3), 2, 'Some of your powers expend the Psionic Energy die they use, as specified in a power''s description, and you can''t use a power if it requires you to use a die when your dice are all expended. You regain all your expended Psionic Energy dice when you finish a long rest. In addition, as a bonus action, you can regain one expended Psionic Energy die, but you can''t do so again until you finish a short or long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psionic Power' AND class_name = 'Fighter' AND level = 3), 3, 'When you reach certain levels in this class, the size of your Psionic Energy dice increases: at 5th level ({@dice d8}), 11th level ({@dice d10}), and 17th level ({@dice d12}).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psionic Power' AND class_name = 'Fighter' AND level = 3), 4, 'The powers below use your Psionic Energy dice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Psionic Power' AND class_name = 'Fighter' AND level = 3), 5, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Protective Field|Fighter|PHB|Psi Warrior|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Psionic Strike|Fighter|PHB|Psi Warrior|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Telekinetic Movement|Fighter|PHB|Psi Warrior|TCE|3"}]', NULL, NULL, NULL, NULL, '{"type":"options","style":"list-hang-notitle","entries":[{"type":"refSubclassFeature","subclassFeature":"Protective Field|Fighter|PHB|Psi Warrior|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Psionic Strike|Fighter|PHB|Psi Warrior|TCE|3"},{"type":"refSubclassFeature","subclassFeature":"Telekinetic Movement|Fighter|PHB|Psi Warrior|TCE|3"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Psi-Powered Leap', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 7, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Psi-Powered Leap' AND class_name = 'Fighter' AND level = 7), 0, 'As a bonus action, you can propel your body with your mind. You gain a flying speed equal to twice your walking speed until the end of the current turn. Once you take this bonus action, you can''t do so again until you finish a short or long rest, unless you expend a Psionic Energy die to take it again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Telekinetic Thrust', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 7, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Telekinetic Thrust' AND class_name = 'Fighter' AND level = 7), 0, 'When you deal damage to a target with your Psionic Strike, you can force the target to make a Strength saving throw against a DC equal to 8 + your proficiency bonus + your Intelligence modifier. If the save fails, you can knock the target {@condition prone} or move it up to 10 feet in any direction horizontally.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Telekinetic Adept', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Telekinetic Adept' AND class_name = 'Fighter' AND level = 7), 0, '{@i 7th-level Psi Warrior feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Telekinetic Adept' AND class_name = 'Fighter' AND level = 7), 1, 'You have mastered new ways to use your telekinetic abilities, detailed below.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Telekinetic Adept' AND class_name = 'Fighter' AND level = 7), 2, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Psi-Powered Leap|Fighter|PHB|Psi Warrior|TCE|7"},{"type":"refSubclassFeature","subclassFeature":"Telekinetic Thrust|Fighter|PHB|Psi Warrior|TCE|7"}]', NULL, NULL, NULL, NULL, '{"type":"options","style":"list-hang-notitle","entries":[{"type":"refSubclassFeature","subclassFeature":"Psi-Powered Leap|Fighter|PHB|Psi Warrior|TCE|7"},{"type":"refSubclassFeature","subclassFeature":"Telekinetic Thrust|Fighter|PHB|Psi Warrior|TCE|7"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Guarded Mind', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Guarded Mind' AND class_name = 'Fighter' AND level = 10), 0, '{@i 10th-level Psi Warrior feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Guarded Mind' AND class_name = 'Fighter' AND level = 10), 1, 'The psionic energy flowing through you has bolstered your mind. You have resistance to psychic damage. Moreover, if you start your turn {@condition charmed} or {@condition frightened}, you can expend a Psionic Energy die and end every effect on yourself subjecting you to those conditions.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bulwark of Force', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bulwark of Force' AND class_name = 'Fighter' AND level = 15), 0, '{@i 15th-level Psi Warrior feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bulwark of Force' AND class_name = 'Fighter' AND level = 15), 1, 'You can shield yourself and others with telekinetic force. As a bonus action, you can choose creatures, which can include you, that you can see within 30 feet of you, up to a number of creatures equal to your Intelligence modifier (minimum of one creature). Each of the chosen creatures is protected by {@quickref Cover||3||half cover} for 1 minute or until you''re {@condition incapacitated}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bulwark of Force' AND class_name = 'Fighter' AND level = 15), 2, 'Once you take this bonus action, you can''t do so again until you finish a long rest, unless you expend a Psionic Energy die to take it again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Telekinetic Master', 2, 42, 'Fighter', 'PHB', 'Psi Warrior', 'TCE', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Telekinetic Master' AND class_name = 'Fighter' AND level = 18), 0, '{@i 18th-level Psi Warrior feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Telekinetic Master' AND class_name = 'Fighter' AND level = 18), 1, 'Your ability to move creatures and objects with your mind is matched by few. You can cast the {@spell telekinesis} spell, requiring no components, and your spellcasting ability for the spell is Intelligence. On each of your turns while you concentrate on the spell, including the turn when you cast it, you can make one attack with a weapon as a bonus action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Telekinetic Master' AND class_name = 'Fighter' AND level = 18), 2, 'Once you cast the spell with this feature, you can''t do so again until you finish a long rest, unless you expend a Psionic Energy die to cast it again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Rune Knight', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rune Knight' AND class_name = 'Fighter' AND level = 3), 0, 'Rune Knights enhance their martial prowess using the supernatural power of runes, an ancient practice that originated with giants. Rune cutters can be found among any family of giants, and you likely learned your methods first or second hand from such a mystical artisan. Whether you found the giant''s work carved into a hill or cave, learned of the runes from a sage, or met the giant in person, you studied the giant''s craft and learned how to apply magic runes to empower your equipment.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Rune Knight' AND class_name = 'Fighter' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiencies|Fighter||Rune Knight|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Rune Knight' AND class_name = 'Fighter' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Rune Carver|Fighter||Rune Knight|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Rune Knight' AND class_name = 'Fighter' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Giant''s Might|Fighter||Rune Knight|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiencies', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Fighter' AND level = 3), 0, '{@i 3rd-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Fighter' AND level = 3), 1, 'You gain proficiency with {@item smith''s tools|PHB}, and you learn to speak, read, and write {@language Giant}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Giant''s Might', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Giant''s Might' AND class_name = 'Fighter' AND level = 3), 0, '{@i 3rd-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Giant''s Might' AND class_name = 'Fighter' AND level = 3), 1, 'You have learned how to imbue yourself with the might of giants. As a bonus action, you magically gain the following benefits, which last for 1 minute:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Giant''s Might' AND class_name = 'Fighter' AND level = 3), 2, 'list', NULL, '["If you are smaller than Large, you become Large, along with anything you are wearing. If you lack the room to become Large, your size doesn''t change.","You have advantage on Strength checks and Strength saving throws.","Once on each of your turns, one of your attacks with a weapon or an unarmed strike can deal an extra {@damage 1d6} damage to a target on a hit."]', '["If you are smaller than Large, you become Large, along with anything you are wearing. If you lack the room to become Large, your size doesn''t change.","You have advantage on Strength checks and Strength saving throws.","Once on each of your turns, one of your attacks with a weapon or an unarmed strike can deal an extra {@damage 1d6} damage to a target on a hit."]', NULL, NULL, NULL, '{"type":"list","items":["If you are smaller than Large, you become Large, along with anything you are wearing. If you lack the room to become Large, your size doesn''t change.","You have advantage on Strength checks and Strength saving throws.","Once on each of your turns, one of your attacks with a weapon or an unarmed strike can deal an extra {@damage 1d6} damage to a target on a hit."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Giant''s Might' AND class_name = 'Fighter' AND level = 3), 3, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses of it when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Rune Carver', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rune Carver' AND class_name = 'Fighter' AND level = 3), 0, '{@i 3rd-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rune Carver' AND class_name = 'Fighter' AND level = 3), 1, 'You can use magic runes to enhance your gear. You learn two runes of your choice, from among the runes described below, and each time you gain a level in this class, you can replace one rune you know with a different one from this feature. When you reach certain levels in this class, you learn additional runes, as shown in the Runes Known table.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rune Carver' AND class_name = 'Fighter' AND level = 3), 2, 'Whenever you finish a long rest, you can touch a number of objects equal to the number of runes you know, and you inscribe a different rune onto each of the objects. To be eligible, an object must be a weapon, a suit of armor, a shield, a piece of jewelry, or something else you can wear or hold in a hand. Your rune remains on an object until you finish a long rest, and an object can bear only one of your runes at a time.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Rune Carver' AND class_name = 'Fighter' AND level = 3), 3, 'table', NULL, NULL, NULL, 'Runes Known', '["Fighter Level","Number of Runes"]', '[["3rd","2"],["7th","3"],["10th","4"],["15th","5"]]', '{"type":"table","caption":"Runes Known","colLabels":["Fighter Level","Number of Runes"],"colStyles":["col-6 text-center","col-6 text-center"],"rows":[["3rd","2"],["7th","3"],["10th","4"],["15th","5"]]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rune Carver' AND class_name = 'Fighter' AND level = 3), 4, 'The following runes are available to you when you learn a rune. If a rune has a level requirement, you must be at least that level in this class to learn the rune. If a rune requires a saving throw, your Rune Magic save DC equals 8 + your proficiency bonus + your Constitution modifier.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Rune Carver' AND class_name = 'Fighter' AND level = 3), 5, 'options', NULL, '[{"type":"refOptionalfeature","optionalfeature":"Cloud Rune|TCE"},{"type":"refOptionalfeature","optionalfeature":"Fire Rune|TCE"},{"type":"refOptionalfeature","optionalfeature":"Frost Rune|TCE"},{"type":"refOptionalfeature","optionalfeature":"Stone Rune|TCE"},{"type":"refOptionalfeature","optionalfeature":"Hill Rune|TCE","name":"Hill Rune (7th Level or Higher)"},{"type":"refOptionalfeature","optionalfeature":"Storm Rune|TCE","name":"Storm Rune (7th Level or Higher)"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":2,"entries":[{"type":"refOptionalfeature","optionalfeature":"Cloud Rune|TCE"},{"type":"refOptionalfeature","optionalfeature":"Fire Rune|TCE"},{"type":"refOptionalfeature","optionalfeature":"Frost Rune|TCE"},{"type":"refOptionalfeature","optionalfeature":"Stone Rune|TCE"},{"type":"refOptionalfeature","optionalfeature":"Hill Rune|TCE","name":"Hill Rune (7th Level or Higher)"},{"type":"refOptionalfeature","optionalfeature":"Storm Rune|TCE","name":"Storm Rune (7th Level or Higher)"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Additional Rune Known', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Rune Known' AND class_name = 'Fighter' AND level = 7), 0, '{@i 7th-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Rune Known' AND class_name = 'Fighter' AND level = 7), 1, 'You learn an additional Rune.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Runic Shield', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Runic Shield' AND class_name = 'Fighter' AND level = 7), 0, '{@i 7th-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Runic Shield' AND class_name = 'Fighter' AND level = 7), 1, 'You learn to invoke your rune magic to protect your allies. When another creature you can see within 60 feet of you is hit by an attack roll, you can use your reaction to force the attacker to reroll the {@dice d20} and use the new roll.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Runic Shield' AND class_name = 'Fighter' AND level = 7), 2, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Additional Rune Known', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Rune Known' AND class_name = 'Fighter' AND level = 10), 0, '{@i 10th-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Rune Known' AND class_name = 'Fighter' AND level = 10), 1, 'You learn an additional Rune.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Great Stature', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Great Stature' AND class_name = 'Fighter' AND level = 10), 0, '{@i 10th-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Great Stature' AND class_name = 'Fighter' AND level = 10), 1, 'The magic of your runes permanently alters you. When you gain this feature, roll {@dice 3d4}. You grow a number of inches in height equal to the roll.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Great Stature' AND class_name = 'Fighter' AND level = 10), 2, 'Moreover, the extra damage you deal with your Giant''s Might feature increases to {@dice 1d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Additional Rune Known', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Rune Known' AND class_name = 'Fighter' AND level = 15), 0, '{@i 15th-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Rune Known' AND class_name = 'Fighter' AND level = 15), 1, 'You learn an additional Rune.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Master of Runes', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Master of Runes' AND class_name = 'Fighter' AND level = 15), 0, '{@i 15th-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Master of Runes' AND class_name = 'Fighter' AND level = 15), 1, 'You can invoke each rune you know from your Rune Carver feature twice, rather than once, and you regain all expended uses when you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Runic Juggernaut', 2, 44, 'Fighter', 'PHB', 'Rune Knight', 'TCE', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Runic Juggernaut' AND class_name = 'Fighter' AND level = 18), 0, '{@i 18th-level Rune Knight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Runic Juggernaut' AND class_name = 'Fighter' AND level = 18), 1, 'You learn how to amplify your rune-powered transformation. As a result, the extra damage you deal with the Giant''s Might feature increases to {@dice 1d10}. Moreover, when you use that feature, your size can increase to Huge, and while you are that size, your reach increases by 5 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Archer', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Archer' AND class_name = 'Fighter' AND level = 3), 0, 'An Arcane Archer studies a unique elven method of archery that weaves magic into attacks to produce supernatural effects. Arcane Archers are some of the most elite warriors among the elves. They stand watch over the fringes of elven domains, keeping a keen eye out for trespassers and using magic-infused arrows to defeat monsters and invaders before they can reach elven settlements. Over the centuries, the methods of these elf archers have been learned by members of other races who can also balance arcane aptitude with archery.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcane Archer' AND class_name = 'Fighter' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Arcane Archer Lore|Fighter||Arcane Archer|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcane Archer' AND class_name = 'Fighter' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Arcane Shot|Fighter||Arcane Archer|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcane Archer' AND class_name = 'Fighter' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Arcane Shot Options|Fighter||Arcane Archer|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Archer Lore', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Archer Lore' AND class_name = 'Fighter' AND level = 3), 0, 'At 3rd level, you learn magical theory or some of the secrets of nature—typical for practitioners of this elven martial tradition. You choose to gain proficiency in either the {@skill Arcana} or the {@skill Nature} skill, and you choose to learn either the {@spell prestidigitation} or the {@spell druidcraft} cantrip.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Shot', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Shot' AND class_name = 'Fighter' AND level = 3), 0, 'At 3rd level, you learn to unleash special magical effects with some of your shots. When you gain this feature, you learn two Arcane Shot options of your choice (see "Arcane Shot Options" below).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Shot' AND class_name = 'Fighter' AND level = 3), 1, 'Once per turn when you fire an arrow from a shortbow or longbow as part of the {@action Attack} action, you can apply one of your Arcane Shot options to that arrow. You decide to use the option when the arrow hits a creature, unless the option doesn''t involve an attack roll. You have two uses of this ability, and you regain all expended uses of it when you finish a short or long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Shot' AND class_name = 'Fighter' AND level = 3), 2, 'You gain an additional Arcane Shot option of your choice when you reach certain levels in this class: 7th, 10th, 15th, and 18th level. Each option also improves when you become an 18th-level fighter.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Shot Options', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Shot Options' AND class_name = 'Fighter' AND level = 3), 0, 'The Arcane Shot feature lets you choose options for it at certain levels. The options are presented here in alphabetical order. They are all magical effects, and each one is associated with one of the schools of magic.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Shot Options' AND class_name = 'Fighter' AND level = 3), 1, 'If an option requires a saving throw, your Arcane Shot save DC is calculated as follows:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcane Shot Options' AND class_name = 'Fighter' AND level = 3), 2, 'abilityDc', 'Arcane Shot', NULL, NULL, NULL, NULL, NULL, '{"type":"abilityDc","name":"Arcane Shot","attributes":["int"]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arcane Shot Options' AND class_name = 'Fighter' AND level = 3), 3, 'options', NULL, '[{"type":"refOptionalfeature","optionalfeature":"Banishing Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Beguiling Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Bursting Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Enfeebling Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Grasping Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Piercing Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Seeking Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Shadow Arrow|XGE"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":2,"entries":[{"type":"refOptionalfeature","optionalfeature":"Banishing Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Beguiling Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Bursting Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Enfeebling Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Grasping Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Piercing Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Seeking Arrow|XGE"},{"type":"refOptionalfeature","optionalfeature":"Shadow Arrow|XGE"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Additional Arcane Shot Option', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Arcane Shot Option' AND class_name = 'Fighter' AND level = 7), 0, 'You gain an additional Arcane Shot option of your choice when you reach 7th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Curving Shot', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Curving Shot' AND class_name = 'Fighter' AND level = 7), 0, 'At 7th level, you learn how to direct an errant arrow toward a new target. When you make an attack roll with a magic arrow and miss, you can use a bonus action to reroll the attack roll against a different target within 60 feet of the original target.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Magic Arrow', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic Arrow' AND class_name = 'Fighter' AND level = 7), 0, 'At 7th level, you gain the ability to infuse arrows with magic. Whenever you fire a nonmagical arrow from a shortbow or longbow, you can make it magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage. The magic fades from the arrow immediately after it hits or misses its target.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Additional Arcane Shot Option', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Arcane Shot Option' AND class_name = 'Fighter' AND level = 10), 0, 'You gain an additional Arcane Shot option of your choice when you reach 10th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Additional Arcane Shot Option', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Arcane Shot Option' AND class_name = 'Fighter' AND level = 15), 0, 'You gain an additional Arcane Shot option of your choice when you reach 15th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ever-Ready Shot', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ever-Ready Shot' AND class_name = 'Fighter' AND level = 15), 0, 'Starting at 15th level, your magical archery is available whenever battle starts. If you roll initiative and have no uses of Arcane Shot remaining, you regain one use of it.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Additional Arcane Shot Option', 3, 28, 'Fighter', 'PHB', 'Arcane Archer', 'XGE', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Additional Arcane Shot Option' AND class_name = 'Fighter' AND level = 18), 0, 'You gain an additional Arcane Shot option of your choice when you reach 18th level. Each option also improves when you become an 18th-level fighter.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Cavalier', 3, 30, 'Fighter', 'PHB', 'Cavalier', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cavalier' AND class_name = 'Fighter' AND level = 3), 0, 'The archetypal Cavalier excels at mounted combat. Usually born among the nobility and raised at court, a Cavalier is equally at home leading a cavalry charge or exchanging repartee at a state dinner. Cavaliers also learn how to guard those in their charge from harm, often serving as the protectors of their superiors and of the weak. Compelled to right wrongs or earn prestige, many of these fighters leave their lives of comfort to embark on glorious adventure.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Cavalier' AND class_name = 'Fighter' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiency|Fighter||Cavalier|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Cavalier' AND class_name = 'Fighter' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Born to the Saddle|Fighter||Cavalier|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Cavalier' AND class_name = 'Fighter' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Unwavering Mark|Fighter||Cavalier|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiency', 3, 30, 'Fighter', 'PHB', 'Cavalier', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiency' AND class_name = 'Fighter' AND level = 3), 0, 'When you choose this archetype at 3rd level, you gain proficiency in one of the following skills of your choice: {@skill Animal Handling}, {@skill History}, {@skill Insight}, {@skill Performance}, or {@skill Persuasion}. Alternatively, you learn one language of your choice.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Born to the Saddle', 3, 30, 'Fighter', 'PHB', 'Cavalier', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Born to the Saddle' AND class_name = 'Fighter' AND level = 3), 0, 'Starting at 3rd level, your mastery as a rider becomes apparent. You have advantage on saving throws made to avoid falling off your mount. If you fall off your mount and descend no more than 10 feet, you can land on your feet if you''re not {@condition incapacitated}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Born to the Saddle' AND class_name = 'Fighter' AND level = 3), 1, 'Finally, mounting or dismounting a creature costs you only 5 feet of movement, rather than half your speed.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unwavering Mark', 3, 30, 'Fighter', 'PHB', 'Cavalier', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unwavering Mark' AND class_name = 'Fighter' AND level = 3), 0, 'Starting at 3rd level, you can menace your foes, foiling their attacks and punishing them for harming others. When you hit a creature with a melee weapon attack, you can mark the creature until the end of your next turn. This effect ends early if you are {@condition incapacitated} or you die, or if someone else marks the creature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unwavering Mark' AND class_name = 'Fighter' AND level = 3), 1, 'While it is within 5 feet of you, a creature marked by you has disadvantage on any attack roll that doesn''t target you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unwavering Mark' AND class_name = 'Fighter' AND level = 3), 2, 'In addition, if a creature marked by you deals damage to anyone other than you, you can make a special melee weapon attack against the marked creature as a bonus action on your next turn. You have advantage on the attack roll, and if it hits, the attack''s weapon deals extra damage to the target equal to half your fighter level.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unwavering Mark' AND class_name = 'Fighter' AND level = 3), 3, 'Regardless of the number of creatures you mark, you can make this special attack a number of times equal to your Strength modifier (minimum of once), and you regain all expended uses of it when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Warding Maneuver', 3, 30, 'Fighter', 'PHB', 'Cavalier', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Warding Maneuver' AND class_name = 'Fighter' AND level = 7), 0, 'At 7th level, you learn to fend off strikes directed at you, your mount, or other creatures nearby. If you or a creature you can see within 5 feet of you is hit by an attack, you can roll {@dice 1d8} as a reaction if you''re wielding a melee weapon or a shield. Roll the die, and add the number rolled to the target''s AC against that attack. If the attack still hits, the target has resistance against the attack''s damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Warding Maneuver' AND class_name = 'Fighter' AND level = 7), 1, 'You can use this feature a number of times equal to your Constitution modifier (minimum of once), and you regain all expended uses of it when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hold the Line', 3, 30, 'Fighter', 'PHB', 'Cavalier', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hold the Line' AND class_name = 'Fighter' AND level = 10), 0, 'At 10th level, you become a master of locking down your enemies. Creatures provoke an opportunity attack from you when they move 5 feet or more while within your reach, and if you hit a creature with an opportunity attack, the target''s speed is reduced to 0 until the end of the current turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ferocious Charger', 3, 30, 'Fighter', 'PHB', 'Cavalier', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ferocious Charger' AND class_name = 'Fighter' AND level = 15), 0, 'Starting at 15th level, you can run down your foes, whether you''re mounted or not. If you move at least 10 feet in a straight line right before attacking a creature and you hit it with the attack, that target must succeed on a Strength saving throw ({@dc 8} + your proficiency bonus + your Strength modifier) or be knocked {@condition prone}. You can use this feature only once on each of your turns.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Vigilant Defender', 3, 30, 'Fighter', 'PHB', 'Cavalier', 'XGE', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vigilant Defender' AND class_name = 'Fighter' AND level = 18), 0, 'Starting at 18th level, you respond to danger with extraordinary vigilance. In combat, you get a special reaction that you can take once on every creature''s turn, except your turn. You can use this special reaction only to make an opportunity attack, and you can''t use it on the same turn that you take your normal reaction.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Samurai', 3, 31, 'Fighter', 'PHB', 'Samurai', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Samurai' AND class_name = 'Fighter' AND level = 3), 0, 'The Samurai is a fighter who draws on an implacable fighting spirit to overcome enemies. A Samurai''s resolve is nearly unbreakable, and the enemies in a Samurai''s path have two choices: yield or die fighting.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Samurai' AND class_name = 'Fighter' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiency|Fighter||Samurai|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Samurai' AND class_name = 'Fighter' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Fighting Spirit|Fighter||Samurai|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiency', 3, 31, 'Fighter', 'PHB', 'Samurai', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiency' AND class_name = 'Fighter' AND level = 3), 0, 'When you choose this archetype at 3rd level, you gain proficiency in one of the following skills of your choice: {@skill History}, {@skill Insight}, {@skill Performance}, or {@skill Persuasion}. Alternatively, you learn one language of your choice.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fighting Spirit', 3, 31, 'Fighter', 'PHB', 'Samurai', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fighting Spirit' AND class_name = 'Fighter' AND level = 3), 0, 'Starting at 3rd level, your intensity in battle can shield you and help you strike true. As a bonus action on your turn, you can give yourself advantage on weapon attack rolls until the end of the current turn. When you do so, you also gain 5 temporary hit points. The number of temporary hit points increases when you reach certain levels in this class, increasing to 10 at 10th level and 15 at 15th level.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fighting Spirit' AND class_name = 'Fighter' AND level = 3), 1, 'You can use this feature three times, and you regain all expended uses of it when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elegant Courtier', 3, 31, 'Fighter', 'PHB', 'Samurai', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elegant Courtier' AND class_name = 'Fighter' AND level = 7), 0, 'Starting at 7th level, your discipline and attention to detail allow you to excel in social situations. Whenever you make a Charisma ({@skill Persuasion}) check, you gain a bonus to the check equal to your Wisdom modifier.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elegant Courtier' AND class_name = 'Fighter' AND level = 7), 1, 'Your self-control also causes you to gain proficiency in Wisdom saving throws. If you already have this proficiency, you instead gain proficiency in Intelligence or Charisma saving throws (your choice).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tireless Spirit', 3, 31, 'Fighter', 'PHB', 'Samurai', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tireless Spirit' AND class_name = 'Fighter' AND level = 10), 0, 'Starting at 10th level, when you roll initiative and have no uses of Fighting Spirit remaining, you regain one use.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Rapid Strike', 3, 31, 'Fighter', 'PHB', 'Samurai', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rapid Strike' AND class_name = 'Fighter' AND level = 15), 0, 'Starting at 15th level, you learn to trade accuracy for swift strikes. If you take the {@action Attack} action on your turn and have advantage on an attack roll against one of the targets, you can forgo the advantage for that roll to make an additional weapon attack against that target, as part of the same action. You can do so no more than once per turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Strength before Death', 3, 31, 'Fighter', 'PHB', 'Samurai', 'XGE', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Strength before Death' AND class_name = 'Fighter' AND level = 18), 0, 'Starting at 18th level, your fighting spirit can delay the grasp of death. If you take damage that reduces you to 0 hit points and doesn''t kill you outright, you can use your reaction to delay falling {@condition unconscious}, and you can immediately take an extra turn, interrupting the current turn. While you have 0 hit points during that extra turn, taking damage causes death saving throw failures as normal, and three death saving throw failures can still kill you. When the extra turn ends, you fall {@condition unconscious} if you still have 0 hit points.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Strength before Death' AND class_name = 'Fighter' AND level = 18), 1, 'Once you use this feature, you can''t use it again until you finish a long rest.');
