INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Martial Arts', 1, 76, 'Monk', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Martial Arts' AND class_name = 'Monk' AND level = 1), 0, 'Your practice of martial arts gives you mastery of combat styles that use unarmed strikes and monk weapons, which are {@item shortsword|phb|shortswords} and any {@filter simple melee weapons that don''t have the two-handed or heavy property|items|source=phb|category=basic|type=simple weapon;melee weapon=sand|property=!heavy;!two-handed}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Martial Arts' AND class_name = 'Monk' AND level = 1), 1, 'You gain the following benefits while you are unarmed or wielding only monk weapons and you aren''t wearing armor or wielding a {@item shield|phb}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Martial Arts' AND class_name = 'Monk' AND level = 1), 2, 'list', NULL, '["You can use Dexterity instead of Strength for the attack and damage rolls of your unarmed strikes and monk weapons.","You can roll a {@dice d4} in place of the normal damage of your unarmed strike or monk weapon. This die changes as you gain monk levels, as shown in the Martial Arts column of the Monk table.","When you use the {@action Attack} action with an unarmed strike or a monk weapon on your turn, you can make one unarmed strike as a bonus action. For example, if you take the {@action Attack} action and attack with a quarterstaff, you can also make an unarmed strike as a bonus action, assuming you haven''t already taken a bonus action this turn."]', '["You can use Dexterity instead of Strength for the attack and damage rolls of your unarmed strikes and monk weapons.","You can roll a {@dice d4} in place of the normal damage of your unarmed strike or monk weapon. This die changes as you gain monk levels, as shown in the Martial Arts column of the Monk table.","When you use the {@action Attack} action with an unarmed strike or a monk weapon on your turn, you can make one unarmed strike as a bonus action. For example, if you take the {@action Attack} action and attack with a quarterstaff, you can also make an unarmed strike as a bonus action, assuming you haven''t already taken a bonus action this turn."]', NULL, NULL, NULL, '{"type":"list","items":["You can use Dexterity instead of Strength for the attack and damage rolls of your unarmed strikes and monk weapons.","You can roll a {@dice d4} in place of the normal damage of your unarmed strike or monk weapon. This die changes as you gain monk levels, as shown in the Martial Arts column of the Monk table.","When you use the {@action Attack} action with an unarmed strike or a monk weapon on your turn, you can make one unarmed strike as a bonus action. For example, if you take the {@action Attack} action and attack with a quarterstaff, you can also make an unarmed strike as a bonus action, assuming you haven''t already taken a bonus action this turn."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Martial Arts' AND class_name = 'Monk' AND level = 1), 3, 'Certain monasteries use specialized forms of the monk weapons. For example, you might use a club that is two lengths of wood connected by a short chain (called a nunchaku) or a sickle with a shorter, straighter blade (called a kama).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unarmored Defense', 1, 76, 'Monk', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unarmored Defense' AND class_name = 'Monk' AND level = 1), 0, 'Beginning at 1st level, while you are wearing no armor and not wielding a shield, your AC equals 10 + your Dexterity modifier + your Wisdom modifier.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dedicated Weapon', 2, 48, 'Monk', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dedicated Weapon' AND class_name = 'Monk' AND level = 2), 0, '{@i 2nd-level monk {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dedicated Weapon' AND class_name = 'Monk' AND level = 2), 1, 'You train yourself to use a variety of weapons as monk weapons, not just simple melee weapons and shortswords. Whenever you finish a short or long rest, you can touch one weapon, focus your ki on it, and then count that weapon as a monk weapon until you use this feature again.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dedicated Weapon' AND class_name = 'Monk' AND level = 2), 2, 'The chosen weapon must meet these criteria:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Dedicated Weapon' AND class_name = 'Monk' AND level = 2), 3, 'list', NULL, '["The weapon must be a {@filter simple or martial weapon|items|source=phb|category=basic|type=martial weapon;simple weapon|property=!heavy;!special}.","You must be proficient with it.","It must lack the heavy and special properties."]', '["The weapon must be a {@filter simple or martial weapon|items|source=phb|category=basic|type=martial weapon;simple weapon|property=!heavy;!special}.","You must be proficient with it.","It must lack the heavy and special properties."]', NULL, NULL, NULL, '{"type":"list","items":["The weapon must be a {@filter simple or martial weapon|items|source=phb|category=basic|type=martial weapon;simple weapon|property=!heavy;!special}.","You must be proficient with it.","It must lack the heavy and special properties."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Flurry of Blows', 1, 76, 'Monk', 'PHB', NULL, NULL, 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Flurry of Blows' AND class_name = 'Monk' AND level = 2), 0, 'Immediately after you take the {@action Attack} action on your turn, you can spend 1 ki point to make two unarmed strikes as a bonus action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ki', 1, 76, 'Monk', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ki' AND class_name = 'Monk' AND level = 2), 0, 'Starting at 2nd level, your training allows you to harness the mystic energy of ki. Your access to this energy is represented by a number of ki points. Your monk level determines the number of points you have, as shown in the Ki Points column of the Monk table.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ki' AND class_name = 'Monk' AND level = 2), 1, 'You can spend these points to fuel various ki features. You start knowing three such features: Flurry of Blows, Patient Defense, and Step of the Wind. You learn more ki features as you gain levels in this class.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ki' AND class_name = 'Monk' AND level = 2), 2, 'When you spend a ki point, it is unavailable until you finish a short or long rest, at the end of which you draw all of your expended ki back into yourself. You must spend at least 30 minutes of the rest meditating to regain your ki points.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ki' AND class_name = 'Monk' AND level = 2), 3, 'Some of your ki features require your target to make a saving throw to resist the feature''s effects. The saving throw DC is calculated as follows:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Ki' AND class_name = 'Monk' AND level = 2), 4, 'abilityDc', 'Ki', NULL, NULL, NULL, NULL, NULL, '{"type":"abilityDc","name":"Ki","attributes":["wis"]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Ki' AND class_name = 'Monk' AND level = 2), 5, 'entries', NULL, '[{"type":"refClassFeature","classFeature":"Flurry of Blows|Monk||2"},{"type":"refClassFeature","classFeature":"Patient Defense|Monk||2"},{"type":"refClassFeature","classFeature":"Step of the Wind|Monk||2"}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"refClassFeature","classFeature":"Flurry of Blows|Monk||2"},{"type":"refClassFeature","classFeature":"Patient Defense|Monk||2"},{"type":"refClassFeature","classFeature":"Step of the Wind|Monk||2"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Patient Defense', 1, 76, 'Monk', 'PHB', NULL, NULL, 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Patient Defense' AND class_name = 'Monk' AND level = 2), 0, 'You can spend 1 ki point to take the {@action Dodge} action as a bonus action on your turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Step of the Wind', 1, 76, 'Monk', 'PHB', NULL, NULL, 2, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Step of the Wind' AND class_name = 'Monk' AND level = 2), 0, 'You can spend 1 ki point to take the {@action Disengage} or {@action Dash} action as a bonus action on your turn, and your jump distance is doubled for the turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unarmored Movement', 1, 76, 'Monk', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unarmored Movement' AND class_name = 'Monk' AND level = 2), 0, 'Starting at 2nd level, your speed increases by 10 feet while you are not wearing armor or wielding a shield. This bonus increases when you reach certain monk levels, as shown in the Monk table.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unarmored Movement' AND class_name = 'Monk' AND level = 2), 1, 'At 9th level, you gain the ability to move along vertical surfaces and across liquids on your turn without falling during the move.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Deflect Missiles', 1, 76, 'Monk', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Deflect Missiles' AND class_name = 'Monk' AND level = 3), 0, 'Starting at 3rd level, you can use your reaction to deflect or catch the missile when you are hit by a ranged weapon attack. When you do so, the damage you take from the attack is reduced by {@dice 1d10} + your Dexterity modifier + your monk level.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Deflect Missiles' AND class_name = 'Monk' AND level = 3), 1, 'If you reduce the damage to 0, you can catch the missile if it is small enough for you to hold in one hand and you have at least one hand free. If you catch a missile in this way, you can spend 1 ki point to make a ranged attack (range 20/60 feet) with the weapon or piece of ammunition you just caught, as part of the same reaction. You make this attack with proficiency, regardless of your weapon proficiencies, and the missile counts as a monk weapon for the attack.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ki-Fueled Attack', 2, 48, 'Monk', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ki-Fueled Attack' AND class_name = 'Monk' AND level = 3), 0, '{@i 3rd-level monk {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ki-Fueled Attack' AND class_name = 'Monk' AND level = 3), 1, 'If you spend 1 ki point or more as part of your action on your turn, you can make one attack with an unarmed strike or a monk weapon as a bonus action before the end of the turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Monastic Tradition', 1, 76, 'Monk', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Monastic Tradition' AND class_name = 'Monk' AND level = 3), 0, 'When you reach 3rd level, you commit yourself to a monastic tradition, chosen from the list of available traditions. Your tradition grants you features at 3rd level and again at 6th, 11th, and 17th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 76, 'Monk', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 4), 0, 'When you reach 4th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 4), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Quickened Healing', 2, 48, 'Monk', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Quickened Healing' AND class_name = 'Monk' AND level = 4), 0, '{@i 4th-level monk {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Quickened Healing' AND class_name = 'Monk' AND level = 4), 1, 'As an action, you can spend 2 ki points and roll a Martial Arts die. You regain a number of hit points equal to the number rolled plus your proficiency bonus.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Slow Fall', 1, 76, 'Monk', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Slow Fall' AND class_name = 'Monk' AND level = 4), 0, 'Beginning at 4th level, you can use your reaction when you fall to reduce any falling damage you take by an amount equal to five times your monk level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extra Attack', 1, 76, 'Monk', 'PHB', NULL, NULL, 5, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Attack' AND class_name = 'Monk' AND level = 5), 0, 'Beginning at 5th level, you can attack twice, instead of once, whenever you take the {@action Attack} action on your turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Focused Aim', 2, 48, 'Monk', 'PHB', NULL, NULL, 5, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Focused Aim' AND class_name = 'Monk' AND level = 5), 0, '{@i 5th-level monk {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Focused Aim' AND class_name = 'Monk' AND level = 5), 1, 'When you miss with an attack roll, you can spend 1 to 3 ki points to increase your attack roll by 2 for each of these ki points you spend, potentially turning the miss into a hit.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Stunning Strike', 1, 76, 'Monk', 'PHB', NULL, NULL, 5, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Stunning Strike' AND class_name = 'Monk' AND level = 5), 0, 'Starting at 5th level, you can interfere with the flow of ki in an opponent''s body. When you hit another creature with a melee weapon attack, you can spend 1 ki point to attempt a stunning strike. The target must succeed on a Constitution saving throw or be {@condition stunned} until the end of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ki-Empowered Strikes', 1, 76, 'Monk', 'PHB', NULL, NULL, 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ki-Empowered Strikes' AND class_name = 'Monk' AND level = 6), 0, 'Starting at 6th level, your unarmed strikes count as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Monastic Tradition feature', 1, 76, 'Monk', 'PHB', NULL, NULL, 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Monastic Tradition feature' AND class_name = 'Monk' AND level = 6), 0, 'At 6th level, you gain one feature granted by your Monastic Tradition.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Evasion', 1, 76, 'Monk', 'PHB', NULL, NULL, 7, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Evasion' AND class_name = 'Monk' AND level = 7), 0, 'At 7th level, your instinctive agility lets you dodge out of the way of certain area effects, such as a blue dragon''s lightning breath or a {@spell fireball} spell. When you are subjected to an effect that allows you to make a Dexterity saving throw to take only half damage, you instead take no damage if you succeed on the saving throw, and only half damage if you fail.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Stillness of Mind', 1, 76, 'Monk', 'PHB', NULL, NULL, 7, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Stillness of Mind' AND class_name = 'Monk' AND level = 7), 0, 'Starting at 7th level, you can use your action to end one effect on yourself that is causing you to be {@condition charmed} or {@condition frightened}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 76, 'Monk', 'PHB', NULL, NULL, 8, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 8), 0, 'When you reach 8th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 8), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unarmored Movement improvement', 1, 76, 'Monk', 'PHB', NULL, NULL, 9, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unarmored Movement improvement' AND class_name = 'Monk' AND level = 9), 0, 'At 9th level, you gain the ability to move along vertical surfaces and across liquids on your turn without falling during the move.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Purity of Body', 1, 76, 'Monk', 'PHB', NULL, NULL, 10, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Purity of Body' AND class_name = 'Monk' AND level = 10), 0, 'At 10th level, your mastery of the ki flowing through you makes you immune to disease and poison.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Monastic Tradition feature', 1, 76, 'Monk', 'PHB', NULL, NULL, 11, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Monastic Tradition feature' AND class_name = 'Monk' AND level = 11), 0, 'At 11th level, you gain one feature granted by your Monastic Tradition.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 76, 'Monk', 'PHB', NULL, NULL, 12, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 12), 0, 'When you reach 12th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 12), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tongue of the Sun and Moon', 1, 76, 'Monk', 'PHB', NULL, NULL, 13, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tongue of the Sun and Moon' AND class_name = 'Monk' AND level = 13), 0, 'Starting at 13th level, you learn to touch the ki of other minds so that you understand all spoken languages. Moreover, any creature that can understand a language can understand what you say.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Diamond Soul', 1, 76, 'Monk', 'PHB', NULL, NULL, 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Diamond Soul' AND class_name = 'Monk' AND level = 14), 0, 'Beginning at 14th level, your mastery of ki grants you proficiency in all saving throws.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Diamond Soul' AND class_name = 'Monk' AND level = 14), 1, 'Additionally, whenever you make a saving throw and fail, you can spend 1 ki point to reroll it and take the second result.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Timeless Body', 1, 76, 'Monk', 'PHB', NULL, NULL, 15, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Timeless Body' AND class_name = 'Monk' AND level = 15), 0, 'At 15th level, your ki sustains you so that you suffer none of the frailty of old age, and you can''t be aged magically. You can still die of old age, however. In addition, you no longer need food or water.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 76, 'Monk', 'PHB', NULL, NULL, 16, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 16), 0, 'When you reach 16th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 16), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Monastic Tradition feature', 1, 76, 'Monk', 'PHB', NULL, NULL, 17, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Monastic Tradition feature' AND class_name = 'Monk' AND level = 17), 0, 'At 17th level, you gain one feature granted by your Monastic Tradition.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Empty Body', 1, 76, 'Monk', 'PHB', NULL, NULL, 18, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Empty Body' AND class_name = 'Monk' AND level = 18), 0, 'Beginning at 18th level, you can use your action to spend 4 ki points to become {@condition invisible} for 1 minute. During that time, you also have resistance to all damage but force damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Empty Body' AND class_name = 'Monk' AND level = 18), 1, 'Additionally, you can spend 8 ki points to cast the {@spell astral projection} spell, without needing material components. When you do so, you can''t take any other creatures with you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 76, 'Monk', 'PHB', NULL, NULL, 19, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 19), 0, 'When you reach 19th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Monk' AND level = 19), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Perfect Self', 1, 76, 'Monk', 'PHB', NULL, NULL, 20, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Perfect Self' AND class_name = 'Monk' AND level = 20), 0, 'At 20th level, when you roll for initiative and have no ki points remaining, you regain 4 ki points.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Ascendant Dragon', null, 13, 'Monk', 'PHB', 'Ascendant Dragon', 'FTD', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Ascendant Dragon' AND class_name = 'Monk' AND level = 3), 0, 'The dragon god Bahamut is known to travel the Material Plane in the guise of a young monk, and legend says that he founded the first monastery of the Way of the Ascendant Dragon in this guise. The fundamental teaching of this tradition holds that by emulating dragons, a monk becomes a more integrated part of the world and its magic. By altering their spirit to resonate with draconic might, monks who follow this tradition augment their prowess in battle, bolster their allies, and can even soar through the air on draconic wings. But all this power is in service of a greater goal: achieving a spiritual unity with the essence of the Material Plane.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Ascendant Dragon' AND class_name = 'Monk' AND level = 3), 1, 'As a follower of the Way of the Ascendant Dragon, you decide how you unlocked the power of dragons within yourself. The Ascendant Dragon Origin table offers a number of possibilities.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Ascendant Dragon' AND class_name = 'Monk' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Ascendant Dragon Origin', '["d6","Origin"]', '[["1","You honed your abilities by aligning your spirit with a dragon''s world-altering power."],["2","A dragon personally took an active role in shaping your inner energy."],["3","You studied at a monastery that traces its teachings back centuries or more to a single dragon''s instruction, or one that is devoted to a dragon god."],["4","You spent long stretches meditating in the region around an ancient dragon''s lair, absorbing that lair''s ambient magic."],["5","You found a scroll written in Draconic that contained inspiring new techniques."],["6","After a dream featuring a five-handed dragonborn, you awoke with the mystical breath of dragons."]]', '{"type":"table","caption":"Ascendant Dragon Origin","colLabels":["d6","Origin"],"colStyles":["col-1 text-center","col-11"],"rows":[["1","You honed your abilities by aligning your spirit with a dragon''s world-altering power."],["2","A dragon personally took an active role in shaping your inner energy."],["3","You studied at a monastery that traces its teachings back centuries or more to a single dragon''s instruction, or one that is devoted to a dragon god."],["4","You spent long stretches meditating in the region around an ancient dragon''s lair, absorbing that lair''s ambient magic."],["5","You found a scroll written in Draconic that contained inspiring new techniques."],["6","After a dream featuring a five-handed dragonborn, you awoke with the mystical breath of dragons."]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Ascendant Dragon' AND class_name = 'Monk' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Draconic Disciple|Monk||Ascendant Dragon|FTD|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Ascendant Dragon' AND class_name = 'Monk' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Breath of the Dragon|Monk||Ascendant Dragon|FTD|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Breath of the Dragon', null, 13, 'Monk', 'PHB', 'Ascendant Dragon', 'FTD', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Breath of the Dragon' AND class_name = 'Monk' AND level = 3), 0, '{@i 3rd-level Way of the Ascendant Dragon feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Breath of the Dragon' AND class_name = 'Monk' AND level = 3), 1, 'You can channel destructive waves of energy, like those created by the dragons you emulate. When you take the {@action Attack} action on your turn, you can replace one of the attacks with an exhalation of draconic energy in either a 20-foot cone or a 30-foot line that is 5 feet wide (your choice). Choose a damage type: acid, cold, fire, lightning, or poison. Each creature in that area must make a Dexterity saving throw against your ki save DC, taking damage of the chosen type equal to two rolls of your Martial Arts die on a failed save, or half as much damage on a successful one.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Breath of the Dragon' AND class_name = 'Monk' AND level = 3), 2, 'At 11th level, the damage of this feature increases to three rolls of your Martial Arts die.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Breath of the Dragon' AND class_name = 'Monk' AND level = 3), 3, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest. While you have no uses available, you can spend 2 ki points to use this feature again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Draconic Disciple', null, 13, 'Monk', 'PHB', 'Ascendant Dragon', 'FTD', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Draconic Disciple' AND class_name = 'Monk' AND level = 3), 0, '{@i 3rd-level Way of the Ascendant Dragon feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Draconic Disciple' AND class_name = 'Monk' AND level = 3), 1, 'You can channel draconic power to magnify your presence and imbue your unarmed strikes with the essence of a dragon''s breath. You gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Draconic Disciple' AND class_name = 'Monk' AND level = 3), 2, 'list', NULL, '[{"type":"item","name":"Draconic Presence","entry":"If you fail a Charisma ({@skill Intimidation}) or Charisma ({@skill Persuasion}) check, you can use your reaction to reroll the check, as you tap into the mighty presence of dragons. Once this feature turns a failure into a success, you can''t use it again until you finish a long rest."},{"type":"item","name":"Draconic Strike","entry":"When you damage a target with an unarmed strike, you can change the damage type to acid, cold, fire, lightning, or poison."},{"type":"item","name":"Tongue of Dragons","entry":"You learn to speak, read, and write {@language Draconic} or one other language of your choice."}]', '[{"type":"item","name":"Draconic Presence","entry":"If you fail a Charisma ({@skill Intimidation}) or Charisma ({@skill Persuasion}) check, you can use your reaction to reroll the check, as you tap into the mighty presence of dragons. Once this feature turns a failure into a success, you can''t use it again until you finish a long rest."},{"type":"item","name":"Draconic Strike","entry":"When you damage a target with an unarmed strike, you can change the damage type to acid, cold, fire, lightning, or poison."},{"type":"item","name":"Tongue of Dragons","entry":"You learn to speak, read, and write {@language Draconic} or one other language of your choice."}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Draconic Presence","entry":"If you fail a Charisma ({@skill Intimidation}) or Charisma ({@skill Persuasion}) check, you can use your reaction to reroll the check, as you tap into the mighty presence of dragons. Once this feature turns a failure into a success, you can''t use it again until you finish a long rest."},{"type":"item","name":"Draconic Strike","entry":"When you damage a target with an unarmed strike, you can change the damage type to acid, cold, fire, lightning, or poison."},{"type":"item","name":"Tongue of Dragons","entry":"You learn to speak, read, and write {@language Draconic} or one other language of your choice."}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wings Unfurled', null, 13, 'Monk', 'PHB', 'Ascendant Dragon', 'FTD', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wings Unfurled' AND class_name = 'Monk' AND level = 6), 0, '{@i 6th-level Way of the Ascendant Dragon feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wings Unfurled' AND class_name = 'Monk' AND level = 6), 1, 'When you use your Step of the Wind, you can unfurl spectral draconic wings from your back that vanish at the end of your turn. While the wings exist, you have a flying speed equal to your walking speed.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wings Unfurled' AND class_name = 'Monk' AND level = 6), 2, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aspect of the Wyrm', null, 13, 'Monk', 'PHB', 'Ascendant Dragon', 'FTD', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aspect of the Wyrm' AND class_name = 'Monk' AND level = 11), 0, '{@i 11th-level Way of the Ascendant Dragon feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aspect of the Wyrm' AND class_name = 'Monk' AND level = 11), 1, 'The power of your draconic spirit now radiates from you, warding your allies or inspiring fear in your enemies. As a bonus action, you can create an aura of draconic power that radiates 10 feet from you for 1 minute. For the duration, you gain one of the following effects of your choice:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Aspect of the Wyrm' AND class_name = 'Monk' AND level = 11), 2, 'list', NULL, '[{"type":"item","name":"Frightful Presence","entry":"When you create this aura, and as a bonus action on subsequent turns, you can choose a creature within the aura. The target must succeed on a Wisdom saving throw against your ki save DC or become {@condition frightened} of you for 1 minute. The target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a successful save."},{"type":"item","name":"Resistance","entry":"Choose a damage type when you activate this aura: acid, cold, fire, lightning, or poison. You and your allies within the aura have resistance to that damage."}]', '[{"type":"item","name":"Frightful Presence","entry":"When you create this aura, and as a bonus action on subsequent turns, you can choose a creature within the aura. The target must succeed on a Wisdom saving throw against your ki save DC or become {@condition frightened} of you for 1 minute. The target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a successful save."},{"type":"item","name":"Resistance","entry":"Choose a damage type when you activate this aura: acid, cold, fire, lightning, or poison. You and your allies within the aura have resistance to that damage."}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Frightful Presence","entry":"When you create this aura, and as a bonus action on subsequent turns, you can choose a creature within the aura. The target must succeed on a Wisdom saving throw against your ki save DC or become {@condition frightened} of you for 1 minute. The target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a successful save."},{"type":"item","name":"Resistance","entry":"Choose a damage type when you activate this aura: acid, cold, fire, lightning, or poison. You and your allies within the aura have resistance to that damage."}]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aspect of the Wyrm' AND class_name = 'Monk' AND level = 11), 3, 'Once you create this aura, you can''t create it again until you finish a long rest, unless you expend 3 ki points to create it again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ascendant Aspect', null, 13, 'Monk', 'PHB', 'Ascendant Dragon', 'FTD', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ascendant Aspect' AND class_name = 'Monk' AND level = 17), 0, '{@i 17th-level Way of the Ascendant Dragon feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ascendant Aspect' AND class_name = 'Monk' AND level = 17), 1, 'Your draconic spirit reaches its peak. You gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Ascendant Aspect' AND class_name = 'Monk' AND level = 17), 2, 'list', NULL, '[{"type":"item","name":"Augment Breath","entry":"When you use your Breath of the Dragon, you can spend 1 ki point to augment its shape and power. The exhalation of draconic energy becomes either a 60-foot cone or a 90-foot line that is 5 feet wide (your choice), and each creature in that area takes damage equal to four rolls of your Martial Arts die on a failed save, or half as much damage on a successful one."},{"type":"item","name":"Blindsight","entry":"You gain {@sense blindsight} out to 10 feet. Within that range, you can effectively see anything that isn''t behind {@quickref Cover||3||total cover}, even if you''re {@condition blinded} or in darkness. Moreover, you can see an {@condition invisible} creature within that range, unless the creature successfully hides from you."},{"type":"item","name":"Explosive Fury","entry":"When you activate your Aspect of the Wyrm, draconic fury explodes from you. Choose any number of creatures you can see in your aura. Each of those creatures must succeed on a Dexterity saving throw against your ki save DC or take {@damage 3d10} acid, cold, fire, lightning, or poison damage (your choice)."}]', '[{"type":"item","name":"Augment Breath","entry":"When you use your Breath of the Dragon, you can spend 1 ki point to augment its shape and power. The exhalation of draconic energy becomes either a 60-foot cone or a 90-foot line that is 5 feet wide (your choice), and each creature in that area takes damage equal to four rolls of your Martial Arts die on a failed save, or half as much damage on a successful one."},{"type":"item","name":"Blindsight","entry":"You gain {@sense blindsight} out to 10 feet. Within that range, you can effectively see anything that isn''t behind {@quickref Cover||3||total cover}, even if you''re {@condition blinded} or in darkness. Moreover, you can see an {@condition invisible} creature within that range, unless the creature successfully hides from you."},{"type":"item","name":"Explosive Fury","entry":"When you activate your Aspect of the Wyrm, draconic fury explodes from you. Choose any number of creatures you can see in your aura. Each of those creatures must succeed on a Dexterity saving throw against your ki save DC or take {@damage 3d10} acid, cold, fire, lightning, or poison damage (your choice)."}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Augment Breath","entry":"When you use your Breath of the Dragon, you can spend 1 ki point to augment its shape and power. The exhalation of draconic energy becomes either a 60-foot cone or a 90-foot line that is 5 feet wide (your choice), and each creature in that area takes damage equal to four rolls of your Martial Arts die on a failed save, or half as much damage on a successful one."},{"type":"item","name":"Blindsight","entry":"You gain {@sense blindsight} out to 10 feet. Within that range, you can effectively see anything that isn''t behind {@quickref Cover||3||total cover}, even if you''re {@condition blinded} or in darkness. Moreover, you can see an {@condition invisible} creature within that range, unless the creature successfully hides from you."},{"type":"item","name":"Explosive Fury","entry":"When you activate your Aspect of the Wyrm, draconic fury explodes from you. Choose any number of creatures you can see in your aura. Each of those creatures must succeed on a Dexterity saving throw against your ki save DC or take {@damage 3d10} acid, cold, fire, lightning, or poison damage (your choice)."}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Four Elements', 1, 80, 'Monk', 'PHB', 'Four Elements', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Four Elements' AND class_name = 'Monk' AND level = 3), 0, 'You follow a monastic tradition that teaches you to harness the elements. When you focus your ki, you can align yourself with the forces of creation and bend the four elements to your will, using them as an extension of your body. Some members of this tradition dedicate themselves to a single element, but others weave the elements together.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Four Elements' AND class_name = 'Monk' AND level = 3), 1, 'Many monks of this tradition tattoo their bodies with representations of their ki powers, commonly imagined as coiling dragons, but also as phoenixes, fish, plants, mountains, and cresting waves.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Four Elements' AND class_name = 'Monk' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Disciple of the Elements|Monk||Four Elements||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Four Elements' AND class_name = 'Monk' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Elemental Disciplines|Monk||Four Elements||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Disciple of the Elements', 1, 80, 'Monk', 'PHB', 'Four Elements', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Disciple of the Elements' AND class_name = 'Monk' AND level = 3), 0, 'You learn magical disciplines that harness the power of the four elements. A discipline requires you to spend ki points each time you use it.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Disciple of the Elements' AND class_name = 'Monk' AND level = 3), 1, 'You know the Elemental Attunement discipline and one other elemental discipline of your choice. You learn one additional elemental discipline of your choice at 6th, 11th, and 17th level.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Disciple of the Elements' AND class_name = 'Monk' AND level = 3), 2, 'Whenever you learn a new elemental discipline, you can also replace one elemental discipline that you already know with a different discipline.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Disciple of the Elements' AND class_name = 'Monk' AND level = 3), 3, 'entries', 'Casting Elemental Spells', '["Some elemental disciplines allow you to cast spells. See {@book chapter 10|PHB|10} of the Player''s Handbook for the general rules of spellcasting. To cast one of these spells, you use its casting time and other rules, but you don''t need to provide material components for it.","Once you reach 5th level in this class, you can spend additional ki points to increase the level of an elemental discipline spell that you cast, provided that the spell has an enhanced effect at a higher level, as {@spell burning hands} does. The spell''s level increases by 1 for each additional ki point you spend. For example, if you are a 5th-level monk and use Sweeping Cinder Strike to cast {@spell burning hands}, you can spend 3 ki points to cast it as a 2nd-level spell (the discipline''s base cost of 2 ki points plus 1).","The maximum number of ki points you can spend to cast a spell in this way (including its base ki point cost and any additional ki points you spend to increase its level) is determined by your monk level, as shown in the Spells and Ki Points table. At 5th level, you may spend up to 3 ki points; this increases to 4 ki points at 9th level, 5 at 13th level, and 6 at 17th level.",{"type":"table","caption":"Spells and Ki Points","colLabels":["Monk Levels","Maximum Ki Points for a Spell"],"colStyles":["col-5 text-center","col-7 text-center"],"rows":[["5th-8th",3],["9th-12th",4],["13th-16th",5],["17th-20th",6]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Casting Elemental Spells","entries":["Some elemental disciplines allow you to cast spells. See {@book chapter 10|PHB|10} of the Player''s Handbook for the general rules of spellcasting. To cast one of these spells, you use its casting time and other rules, but you don''t need to provide material components for it.","Once you reach 5th level in this class, you can spend additional ki points to increase the level of an elemental discipline spell that you cast, provided that the spell has an enhanced effect at a higher level, as {@spell burning hands} does. The spell''s level increases by 1 for each additional ki point you spend. For example, if you are a 5th-level monk and use Sweeping Cinder Strike to cast {@spell burning hands}, you can spend 3 ki points to cast it as a 2nd-level spell (the discipline''s base cost of 2 ki points plus 1).","The maximum number of ki points you can spend to cast a spell in this way (including its base ki point cost and any additional ki points you spend to increase its level) is determined by your monk level, as shown in the Spells and Ki Points table. At 5th level, you may spend up to 3 ki points; this increases to 4 ki points at 9th level, 5 at 13th level, and 6 at 17th level.",{"type":"table","caption":"Spells and Ki Points","colLabels":["Monk Levels","Maximum Ki Points for a Spell"],"colStyles":["col-5 text-center","col-7 text-center"],"rows":[["5th-8th",3],["9th-12th",4],["13th-16th",5],["17th-20th",6]]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elemental Disciplines', 1, 80, 'Monk', 'PHB', 'Four Elements', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Disciplines' AND class_name = 'Monk' AND level = 3), 0, 'The elemental disciplines are presented in alphabetical order. If a discipline requires a level, you must be the level in this class to learn the discipline.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Elemental Disciplines' AND class_name = 'Monk' AND level = 3), 1, 'options', NULL, '[{"type":"refOptionalfeature","optionalfeature":"Breath of Winter","name":"Breath of Winter (17th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Clench of the North Wind","name":"Clench of the North Wind (6th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Elemental Attunement","data":{"isRequiredOption":true}},{"type":"refOptionalfeature","optionalfeature":"Eternal Mountain Defense","name":"Eternal Mountain Defense (17th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Fangs of the Fire Snake"},{"type":"refOptionalfeature","optionalfeature":"Fist of Four Thunders"},{"type":"refOptionalfeature","optionalfeature":"Unbroken Air"},{"type":"refOptionalfeature","optionalfeature":"Flames of the Phoenix","name":"Flames of the Phoenix (11th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Gong of the Summit","name":"Gong of the Summit (6th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Mist Stance","name":"Mist Stance (11th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Ride the Wind","name":"Ride the Wind (11th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"River of Hungry Flame","name":"River of Hungry Flame (17th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Rush of the Gale Spirits"},{"type":"refOptionalfeature","optionalfeature":"Shape the Flowing River"},{"type":"refOptionalfeature","optionalfeature":"Sweeping Cinder Strike"},{"type":"refOptionalfeature","optionalfeature":"Water Whip"},{"type":"refOptionalfeature","optionalfeature":"Wave of Rolling Earth","name":"Wave of Rolling Earth (17th Level Required)"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":2,"entries":[{"type":"refOptionalfeature","optionalfeature":"Breath of Winter","name":"Breath of Winter (17th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Clench of the North Wind","name":"Clench of the North Wind (6th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Elemental Attunement","data":{"isRequiredOption":true}},{"type":"refOptionalfeature","optionalfeature":"Eternal Mountain Defense","name":"Eternal Mountain Defense (17th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Fangs of the Fire Snake"},{"type":"refOptionalfeature","optionalfeature":"Fist of Four Thunders"},{"type":"refOptionalfeature","optionalfeature":"Unbroken Air"},{"type":"refOptionalfeature","optionalfeature":"Flames of the Phoenix","name":"Flames of the Phoenix (11th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Gong of the Summit","name":"Gong of the Summit (6th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Mist Stance","name":"Mist Stance (11th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Ride the Wind","name":"Ride the Wind (11th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"River of Hungry Flame","name":"River of Hungry Flame (17th Level Required)"},{"type":"refOptionalfeature","optionalfeature":"Rush of the Gale Spirits"},{"type":"refOptionalfeature","optionalfeature":"Shape the Flowing River"},{"type":"refOptionalfeature","optionalfeature":"Sweeping Cinder Strike"},{"type":"refOptionalfeature","optionalfeature":"Water Whip"},{"type":"refOptionalfeature","optionalfeature":"Wave of Rolling Earth","name":"Wave of Rolling Earth (17th Level Required)"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extra Elemental Discipline', 1, 80, 'Monk', 'PHB', 'Four Elements', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Elemental Discipline' AND class_name = 'Monk' AND level = 6), 0, 'You learn one additional elemental discipline of your choice. You should know 2 elemental disciplines, as well as Elemental Attunement.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Elemental Discipline' AND class_name = 'Monk' AND level = 6), 1, 'Whenever you learn a new elemental discipline, you can also replace one elemental discipline that you already know with a different discipline.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extra Elemental Discipline', 1, 80, 'Monk', 'PHB', 'Four Elements', 'PHB', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Elemental Discipline' AND class_name = 'Monk' AND level = 11), 0, 'You learn one additional elemental discipline of your choice. You should know 3 elemental disciplines, as well as Elemental Attunement.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Elemental Discipline' AND class_name = 'Monk' AND level = 11), 1, 'Whenever you learn a new elemental discipline, you can also replace one elemental discipline that you already know with a different discipline.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extra Elemental Discipline', 1, 80, 'Monk', 'PHB', 'Four Elements', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Elemental Discipline' AND class_name = 'Monk' AND level = 17), 0, 'You learn one additional elemental discipline of your choice. You should know 4 elemental disciplines, as well as Elemental Attunement.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Elemental Discipline' AND class_name = 'Monk' AND level = 17), 1, 'Whenever you learn a new elemental discipline, you can also replace one elemental discipline that you already know with a different discipline.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Open Hand', 1, 79, 'Monk', 'PHB', 'Open Hand', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Open Hand' AND class_name = 'Monk' AND level = 3), 0, 'Monks of the Way of the Open Hand are the ultimate masters of martial arts combat, whether armed or unarmed. They learn techniques to push and trip their opponents, manipulate ki to heal damage to their bodies, and practice advanced meditation that can protect them from harm.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Open Hand' AND class_name = 'Monk' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Open Hand Technique|Monk||Open Hand||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Open Hand Technique', 1, 79, 'Monk', 'PHB', 'Open Hand', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Open Hand Technique' AND class_name = 'Monk' AND level = 3), 0, 'You can manipulate your enemy''s ki when you harness your own. Whenever you hit a creature with one of the attacks granted by your Flurry of Blows, you can impose one of the following effects on that target.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Open Hand Technique' AND class_name = 'Monk' AND level = 3), 1, 'list', NULL, '["It must succeed on a Dexterity saving throw or be knocked {@condition prone}.","It must make a Strength saving throw. If it fails, you can push it up to 15 feet away from you.","It can''t take reactions until the end of your next turn."]', '["It must succeed on a Dexterity saving throw or be knocked {@condition prone}.","It must make a Strength saving throw. If it fails, you can push it up to 15 feet away from you.","It can''t take reactions until the end of your next turn."]', NULL, NULL, NULL, '{"type":"list","items":["It must succeed on a Dexterity saving throw or be knocked {@condition prone}.","It must make a Strength saving throw. If it fails, you can push it up to 15 feet away from you.","It can''t take reactions until the end of your next turn."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Wholeness of Body', 1, 79, 'Monk', 'PHB', 'Open Hand', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Wholeness of Body' AND class_name = 'Monk' AND level = 6), 0, 'You gain the ability to heal yourself. As an action, you can regain hit points equal to three times your monk level. You must finish a long rest before you can use this feature again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tranquility', 1, 79, 'Monk', 'PHB', 'Open Hand', 'PHB', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tranquility' AND class_name = 'Monk' AND level = 11), 0, 'Beginning at 11th level, you can enter a special meditation that surrounds you with an aura of peace. At the end of a long rest, you gain the effect of a {@spell sanctuary} spell that lasts until the start of your next long rest (the spell can end early as normal). The saving throw DC for the spell equals 8 + your Wisdom modifier + your proficiency bonus.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Quivering Palm', 1, 79, 'Monk', 'PHB', 'Open Hand', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Quivering Palm' AND class_name = 'Monk' AND level = 17), 0, 'You gain the ability to set up lethal vibrations in someone''s body. When you hit a creature with an unarmed strike, you can spend 3 ki points to start these imperceptible vibrations, which last for a number of days equal to your monk level. The vibrations are harmless unless you use your action to end them. To do so, you and the target must be on the same plane of existence. When you use this action, the creature must make a Constitution saving throw. If it fails, it is reduced to 0 hit points. If it succeeds, it takes {@damage 10d10} necrotic damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Quivering Palm' AND class_name = 'Monk' AND level = 17), 1, 'You can have only one creature under the effect of this feature at a time. You can choose to end the vibrations harmlessly without using an action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of Shadow', 1, 80, 'Monk', 'PHB', 'Shadow', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of Shadow' AND class_name = 'Monk' AND level = 3), 0, 'Monks of the Way of Shadow follow a tradition that values stealth and subterfuge. These monks might be called ninjas or shadow dancers, and they serve as spies and assassins. Sometimes the members of a ninja monastery are family members, forming a clan sworn to secrecy about their arts and missions. Other monasteries are more like thieves'' guilds, hiring out their services to nobles, rich merchants, or anyone else who can pay their fees. Regardless of their methods, the heads of these monasteries expect the unquestioning obedience of their students.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of Shadow' AND class_name = 'Monk' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Shadow Arts|Monk||Shadow||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Shadow Arts', 1, 80, 'Monk', 'PHB', 'Shadow', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Shadow Arts' AND class_name = 'Monk' AND level = 3), 0, 'You can use your ki to duplicate the effects of certain spells. As an action, you can spend 2 ki points to cast {@spell darkness}, {@spell darkvision}, {@spell pass without trace}, or {@spell silence}, without providing material components. Additionally, you gain the {@spell minor illusion} cantrip if you don''t already know it.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Shadow Step', 1, 80, 'Monk', 'PHB', 'Shadow', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Shadow Step' AND class_name = 'Monk' AND level = 6), 0, 'You gain the ability to step from one shadow into another. When you are in dim light or darkness, as a bonus action you can teleport up to 60 feet to an unoccupied space you can see that is also in dim light or darkness. You then have advantage on the first melee attack you make before the end of the turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Cloak of Shadows', 1, 80, 'Monk', 'PHB', 'Shadow', 'PHB', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cloak of Shadows' AND class_name = 'Monk' AND level = 11), 0, 'By 11th level, you have learned to become one with the shadows. When you are in an area of dim light or darkness, you can use your action to become {@condition invisible}. You remain {@condition invisible} until you make an attack, cast a spell, or are in an area of bright light.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Opportunist', 1, 80, 'Monk', 'PHB', 'Shadow', 'PHB', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Opportunist' AND class_name = 'Monk' AND level = 17), 0, 'At 17th level, you can exploit a creature''s momentary distraction when it is hit by an attack. Whenever a creature within 5 feet of you is hit by an attack made by a creature other than you, you can use your reaction to make a melee attack against that creature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Long Death', null, 130, 'Monk', 'PHB', 'Long Death', 'SCAG', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Long Death' AND class_name = 'Monk' AND level = 3), 0, 'Monks of the Way of the Long Death are obsessed with the meaning and mechanics of dying. They capture creatures and prepare elaborate experiments to capture, record, and understand the moments of their demise. They use this knowledge to guide their understanding of martial arts, yielding a deadly fighting style.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Long Death' AND class_name = 'Monk' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Touch of Death|Monk||Long Death|SCAG|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Touch of Death', null, 130, 'Monk', 'PHB', 'Long Death', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Touch of Death' AND class_name = 'Monk' AND level = 3), 0, 'Starting when you choose this tradition at 3rd level, your study of death allows you to extract vitality from another creature as it nears its demise. When you reduce a creature within 5 feet of you to 0 hit points, you gain temporary hit points equal to your Wisdom modifier + your monk level (minimum of 1 temporary hit point).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hour of Reaping', null, 130, 'Monk', 'PHB', 'Long Death', 'SCAG', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hour of Reaping' AND class_name = 'Monk' AND level = 6), 0, 'At 6th level, you gain the ability to unsettle or terrify those around you as an action, for your soul has been touched by the shadow of death. When you take this action, each creature within 30 feet of you that can see you must succeed on a Wisdom saving throw or be {@condition frightened} of you until the end of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mastery of Death', null, 130, 'Monk', 'PHB', 'Long Death', 'SCAG', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mastery of Death' AND class_name = 'Monk' AND level = 11), 0, 'Beginning at 11th level, you use your familiarity with death to escape its grasp. When you are reduced to 0 hit points, you can expend 1 ki point (no action required) to have 1 hit point instead.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Touch of the Long Death', null, 130, 'Monk', 'PHB', 'Long Death', 'SCAG', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Touch of the Long Death' AND class_name = 'Monk' AND level = 17), 0, 'Starting at 17th level, your touch can channel the energy of death into a creature. As an action, you touch one creature within 5 feet of you, and you expend 1 to 10 ki points. The target must make a Constitution saving throw, and it takes {@damage 2d10} necrotic damage per ki point spent on a failed save, or half as much damage on a successful one.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Astral Self', 2, 50, 'Monk', 'PHB', 'Astral Self', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Astral Self' AND class_name = 'Monk' AND level = 3), 0, 'A monk who follows the Way of the Astral Self believes their body is an illusion. They see their ki as a representation of their true form, an astral self. This astral self has the capacity to be a force of order or disorder, with some monasteries training students to use their power to protect the weak and other instructing aspirants in how to manifest their true selves in service to the mighty.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Astral Self' AND class_name = 'Monk' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Forms of Your Astral Self|Monk||Astral Self|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Astral Self' AND class_name = 'Monk' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Arms of the Astral Self|Monk||Astral Self|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arms of the Astral Self', 2, 50, 'Monk', 'PHB', 'Astral Self', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arms of the Astral Self' AND class_name = 'Monk' AND level = 3), 0, '{@i 3rd-level Way of the Astral Self feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arms of the Astral Self' AND class_name = 'Monk' AND level = 3), 1, 'Your mastery of your ki allows you to summon a portion of your astral self. As a bonus action, you can spend 1 ki point to summon the arms of your astral self. When you do so, each creature of your choice that you can see within 10 feet of you must succeed on a Dexterity saving throw or take force damage equal to two rolls of your Martial Arts die.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arms of the Astral Self' AND class_name = 'Monk' AND level = 3), 2, 'For 10 minutes, these spectral arms hover near your shoulders or surround your arms (your choice). You determine the arms'' appearance, and they vanish early if you are {@condition incapacitated} or die.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arms of the Astral Self' AND class_name = 'Monk' AND level = 3), 3, 'While the spectral arms are present, you gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Arms of the Astral Self' AND class_name = 'Monk' AND level = 3), 4, 'list', NULL, '["You can use your Wisdom modifier in place of your Strength modifier when making Strength checks and Strength saving throws.","You can use the spectral arms to make unarmed strikes.","When you make an unarmed strike with the arms on your turn, your reach for it is 5 feet greater than normal.","The unarmed strikes you make with the arms can use your Wisdom modifier in place of your Strength or Dexterity modifier for the attack and damage rolls, and their damage type is force."]', '["You can use your Wisdom modifier in place of your Strength modifier when making Strength checks and Strength saving throws.","You can use the spectral arms to make unarmed strikes.","When you make an unarmed strike with the arms on your turn, your reach for it is 5 feet greater than normal.","The unarmed strikes you make with the arms can use your Wisdom modifier in place of your Strength or Dexterity modifier for the attack and damage rolls, and their damage type is force."]', NULL, NULL, NULL, '{"type":"list","items":["You can use your Wisdom modifier in place of your Strength modifier when making Strength checks and Strength saving throws.","You can use the spectral arms to make unarmed strikes.","When you make an unarmed strike with the arms on your turn, your reach for it is 5 feet greater than normal.","The unarmed strikes you make with the arms can use your Wisdom modifier in place of your Strength or Dexterity modifier for the attack and damage rolls, and their damage type is force."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Forms of Your Astral Self', 2, 50, 'Monk', 'PHB', 'Astral Self', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Forms of Your Astral Self' AND class_name = 'Monk' AND level = 3), 0, 'The astral self is a translucent embodiment of the monk''s soul. As a result, an astral self can reflect aspects of a monk''s background, ideals, flaws, and bonds, and an astral self doesn''t necessarily look anything like the monk. For example, the astral self of a lanky human might be reminiscent of a minotaur-the strength of which the monk feels within. Similarly, an orc monk might manifest gossamer arms and a delicate visage, representing the gentle beauty of the orc''s soul. Each astral self is unique, and some of the monks of this monastic tradition are known more for the appearance of their astral self than for their physical appearance.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Forms of Your Astral Self' AND class_name = 'Monk' AND level = 3), 1, 'When choosing this path, consider the quirks that define your monk. Are you obsessed with something? Are you driven by justice or a selfish desire? Any of these motivations could manifest in the form of your astral self.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Visage of the Astral Self', 2, 50, 'Monk', 'PHB', 'Astral Self', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Visage of the Astral Self' AND class_name = 'Monk' AND level = 6), 0, '{@i 6th-level Way of the Astral Self feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Visage of the Astral Self' AND class_name = 'Monk' AND level = 6), 1, 'You can summon the visage of your astral self. As a bonus action, or as part of the bonus action you take to activate Arms of the Astral Self, you can spend 1 ki point to summon this visage for 10 minutes. It vanishes early if you are {@condition incapacitated} or die.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Visage of the Astral Self' AND class_name = 'Monk' AND level = 6), 2, 'The spectral visage covers your face like a helmet or mask. You determine its appearance.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Visage of the Astral Self' AND class_name = 'Monk' AND level = 6), 3, 'While the spectral visage is present, you gain the following benefits.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Visage of the Astral Self' AND class_name = 'Monk' AND level = 6), 4, 'entries', 'Astral Sight', '["You can see normally in darkness, both magical and nonmagical, to a distance of 120 feet."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Astral Sight","entries":["You can see normally in darkness, both magical and nonmagical, to a distance of 120 feet."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Visage of the Astral Self' AND class_name = 'Monk' AND level = 6), 5, 'entries', 'Wisdom of the Spirit', '["You have advantage on Wisdom ({@skill Insight}) and Charisma ({@skill Intimidation}) checks."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Wisdom of the Spirit","entries":["You have advantage on Wisdom ({@skill Insight}) and Charisma ({@skill Intimidation}) checks."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Visage of the Astral Self' AND class_name = 'Monk' AND level = 6), 6, 'entries', 'Word of the Spirit', '["When you speak, you can direct your words to a creature of your choice that you can see within 60 feet of you, making it so only that creature can hear you. Alternatively, you can amplify your voice so that all creatures within 600 feet can hear you."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Word of the Spirit","entries":["When you speak, you can direct your words to a creature of your choice that you can see within 60 feet of you, making it so only that creature can hear you. Alternatively, you can amplify your voice so that all creatures within 600 feet can hear you."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Body of the Astral Self', 2, 50, 'Monk', 'PHB', 'Astral Self', 'TCE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Body of the Astral Self' AND class_name = 'Monk' AND level = 11), 0, '{@i 11th-level Way of the Astral Self feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Body of the Astral Self' AND class_name = 'Monk' AND level = 11), 1, 'When you have both your astral arms and visage summoned, you can cause the body of your astral self to appear (no action required). This spectral body covers your physical form like a suit of armor, connecting with the arms and visage. You determine its appearance.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Body of the Astral Self' AND class_name = 'Monk' AND level = 11), 2, 'While the spectral body is present, you gain the following benefits.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Body of the Astral Self' AND class_name = 'Monk' AND level = 11), 3, 'entries', 'Deflect Energy', '["When you take acid, cold, fire, force, lightning, or thunder damage, you can use your reaction to deflect it. When you do so, the damage you take is reduced by {@dice 1d10} + your Wisdom modifier (minimum reduction of 1)."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Deflect Energy","entries":["When you take acid, cold, fire, force, lightning, or thunder damage, you can use your reaction to deflect it. When you do so, the damage you take is reduced by {@dice 1d10} + your Wisdom modifier (minimum reduction of 1)."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Body of the Astral Self' AND class_name = 'Monk' AND level = 11), 4, 'entries', 'Empowered Arms', '["Once on each of your turns when you hit a target with the Arms of the Astral Self, you can deal extra damage to the target equal to your Martial Arts die."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Empowered Arms","entries":["Once on each of your turns when you hit a target with the Arms of the Astral Self, you can deal extra damage to the target equal to your Martial Arts die."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Awakened Astral Self', 2, 50, 'Monk', 'PHB', 'Astral Self', 'TCE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Awakened Astral Self' AND class_name = 'Monk' AND level = 17), 0, '{@i 17th-level Way of the Astral Self feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Awakened Astral Self' AND class_name = 'Monk' AND level = 17), 1, 'Your connection to your astral self is complete, allowing you to unleash its full potential. As a bonus action, you can spend 5 ki points to summon the arms, visage, and body of your astral self and awaken it for 10 minutes. This awakening ends early if you are {@condition incapacitated} or die.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Awakened Astral Self' AND class_name = 'Monk' AND level = 17), 2, 'While your astral self is awakened, you gain the following benefits.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Awakened Astral Self' AND class_name = 'Monk' AND level = 17), 3, 'entries', 'Armor of the Spirit', '["You gain a +2 bonus to Armor Class."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Armor of the Spirit","entries":["You gain a +2 bonus to Armor Class."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Awakened Astral Self' AND class_name = 'Monk' AND level = 17), 4, 'entries', 'Astral Barrage', '["Whenever you use the Extra Attack feature to attack twice, you can instead attack three times if all the attacks are made with your astral arms."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Astral Barrage","entries":["Whenever you use the Extra Attack feature to attack twice, you can instead attack three times if all the attacks are made with your astral arms."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of Mercy', 2, 49, 'Monk', 'PHB', 'Mercy', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of Mercy' AND class_name = 'Monk' AND level = 3), 0, 'Monks of the Way of Mercy learn to manipulate the life force of others to bring aid to those in need. They are wandering physicians to the poor and hurt. However, to those beyond their help, they bring a swift end as an act of mercy.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of Mercy' AND class_name = 'Monk' AND level = 3), 1, 'Those who follow the Way of Mercy might be members of a religious order, administering to the needy and making grim choices rooted in reality rather than idealism. Some might be gentle-voiced healers, beloved by their communities, while others might be masked bringers of macabre mercies.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of Mercy' AND class_name = 'Monk' AND level = 3), 2, 'The walkers of this way usually don robes with deep cowls, and they often conceal their faces with masks, presenting themselves as the faceless bringers of life and death.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of Mercy' AND class_name = 'Monk' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Implements of Mercy|Monk||Mercy|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of Mercy' AND class_name = 'Monk' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Hand of Healing|Monk||Mercy|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of Mercy' AND class_name = 'Monk' AND level = 3), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Hand of Harm|Monk||Mercy|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hand of Harm', 2, 49, 'Monk', 'PHB', 'Mercy', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hand of Harm' AND class_name = 'Monk' AND level = 3), 0, '{@i 3rd-level Way of Mercy feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hand of Harm' AND class_name = 'Monk' AND level = 3), 1, 'You use your ki to inflict wounds. When you hit a creature with an unarmed strike, you can spend 1 ki point to deal extra necrotic damage equal to one roll of your Martial Arts die + your Wisdom modifier. You can use this feature only once per turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hand of Healing', 2, 49, 'Monk', 'PHB', 'Mercy', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hand of Healing' AND class_name = 'Monk' AND level = 3), 0, '{@i 3rd-level Way of Mercy feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hand of Healing' AND class_name = 'Monk' AND level = 3), 1, 'Your mystical touch can mend wounds. As an action, you can spend 1 ki point to touch a creature and restore a number of hit points equal to a roll of your Martial Arts die + your Wisdom modifier.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hand of Healing' AND class_name = 'Monk' AND level = 3), 2, 'When you use your Flurry of Blows, you can replace one of the unarmed strikes with a use of this feature without spending a ki point for the healing.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Implements of Mercy', 2, 49, 'Monk', 'PHB', 'Mercy', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Implements of Mercy' AND class_name = 'Monk' AND level = 3), 0, '{@i 3rd-level Way of Mercy feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Implements of Mercy' AND class_name = 'Monk' AND level = 3), 1, 'You gain proficiency in the {@skill Insight} and {@skill Medicine} skills, and you gain proficiency with the {@item herbalism kit|PHB}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Implements of Mercy' AND class_name = 'Monk' AND level = 3), 2, 'You also gain a special mask, which you often wear when using the features of this subclass. You determine its appearance, or generate it randomly by rolling on the Merciful Mask table.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Implements of Mercy' AND class_name = 'Monk' AND level = 3), 3, 'table', NULL, NULL, NULL, 'Merciful Mask', '["d6","Mask Appearance"]', '[["1","Raven"],["2","Blank and white"],["3","Crying visage"],["4","Laughing visage"],["5","Skull"],["6","Butterfly"]]', '{"type":"table","caption":"Merciful Mask","colLabels":["d6","Mask Appearance"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","Raven"],["2","Blank and white"],["3","Crying visage"],["4","Laughing visage"],["5","Skull"],["6","Butterfly"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Physician''s Touch', 2, 49, 'Monk', 'PHB', 'Mercy', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Physician''s Touch' AND class_name = 'Monk' AND level = 6), 0, '{@i 6th-level Way of Mercy feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Physician''s Touch' AND class_name = 'Monk' AND level = 6), 1, 'You can administer even greater cures with a touch, and if you feel it''s necessary, you can use your knowledge to cause harm.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Physician''s Touch' AND class_name = 'Monk' AND level = 6), 2, 'When you use Hand of Healing on a creature, you can also end one disease or one of the following conditions affecting the creature: {@condition blinded}, {@condition deafened}, {@condition paralyzed}, {@condition poisoned}, or {@condition stunned}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Physician''s Touch' AND class_name = 'Monk' AND level = 6), 3, 'When you use Hand of Harm on a creature, you can subject that creature to the {@condition poisoned} condition until the end of your next turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Flurry of Healing and Harm', 2, 49, 'Monk', 'PHB', 'Mercy', 'TCE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Flurry of Healing and Harm' AND class_name = 'Monk' AND level = 11), 0, '{@i 11th-level Way of Mercy feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Flurry of Healing and Harm' AND class_name = 'Monk' AND level = 11), 1, 'You can now mete out a flurry of comfort and hurt. When you use Flurry of Blows, you can now replace each of the unarmed strikes with a use of your Hand of Healing, without spending ki points for the healing.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Flurry of Healing and Harm' AND class_name = 'Monk' AND level = 11), 2, 'In addition, when you make an unarmed strike with Flurry of Blows, you can use Hand of Harm with that strike without spending the ki point for Hand of Harm. You can still use Hand of Harm only once per turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hand of Ultimate Mercy', 2, 49, 'Monk', 'PHB', 'Mercy', 'TCE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hand of Ultimate Mercy' AND class_name = 'Monk' AND level = 17), 0, '{@i 17th-level Way of Mercy feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hand of Ultimate Mercy' AND class_name = 'Monk' AND level = 17), 1, 'Your mastery of life energy opens the door to the ultimate mercy. As an action, you can touch the corpse of a creature that died within the past 24 hours and expend 5 ki points. The creature then returns to life, regaining a number of hit points equal to {@dice 4d10} + your Wisdom modifier. If the creature died while subject to any of the following conditions, it revives with them removed: {@condition blinded}, {@condition deafened}, {@condition paralyzed}, {@condition poisoned}, and {@condition stunned}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hand of Ultimate Mercy' AND class_name = 'Monk' AND level = 17), 2, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Cobalt Soul', null, 173, 'Monk', 'PHB', 'Cobalt Soul', 'TDCSR', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Cobalt Soul' AND class_name = 'Monk' AND level = 3), 0, 'Driven by the pursuit of knowledge and its scholars'' worship of {@deity The Knowing Mentor|Exandria|TDCSR}, the {@book Library of the Cobalt Soul|TDCSR|2|Library of the Cobalt Soul} is one of the best-respected and most heavily guarded repositories of tomes, history, and information in all Exandria. People from all lands come to the library to seek knowledge, and those particularly dedicated to the virtues of truth often pledge their minds and bodies to the {@book Cobalt Soul''s|TDCSR|2|Library of the Cobalt Soul} cause. To become a member of the {@book Cobalt Soul|TDCSR|2|Library of the Cobalt Soul} is to give oneself over to a quest dedicated to unveiling life''s mysteries, bringing light to the secrets of concealed evil, and guarding the most powerful and dangerous of truths from those whose unwholesome thirst for knowledge might bring death and suffering to others.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Cobalt Soul' AND class_name = 'Monk' AND level = 3), 1, 'The monks of the {@book Cobalt Soul|TDCSR|2|Library of the Cobalt Soul} are the embodiment of the maxim: "Know your enemy." Through tireless research, they steel themselves against the unrelenting tides of evil. Through rigorous training, they learn to break through their foes'' mental and physical defenses. Then, once the fight is done, they record their findings for future generations of monks to study.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Cobalt Soul' AND class_name = 'Monk' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Extract Aspects|Monk|PHB|Cobalt Soul|TDCSR|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extract Aspects', null, 173, 'Monk', 'PHB', 'Cobalt Soul', 'TDCSR', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extract Aspects' AND class_name = 'Monk' AND level = 3), 0, 'Starting at 3rd level, you can strike pressure points to intuit crucial information about a foe. When you hit a creature with one of the attacks granted by your Flurry of Blows, you can analyze it. Whenever an analyzed creature misses you with an attack, you can immediately use your reaction to make an unarmed strike against that creature if it''s within your reach. This benefit lasts until you finish a {@quickref resting|PHB|2|0|short or long rest}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extract Aspects' AND class_name = 'Monk' AND level = 3), 1, 'Additionally, when you analyze a creature, you learn all of its damage vulnerabilities, damage resistances, damage immunities, and condition immunities.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extort Truth', null, 174, 'Monk', 'PHB', 'Cobalt Soul', 'TDCSR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extort Truth' AND class_name = 'Monk' AND level = 6), 0, 'Starting at 6th level, you can precisely strike a hidden cluster of nerves on a creature, temporarily preventing it from masking its true thoughts and intent. When you hit a creature with an unarmed strike, you can spend 1 ki point to force it to make a Charisma {@quickref saving throws|PHB|2|1|saving throw}. On a failed save, the creature is unable to speak a deliberate lie, and all Charisma checks directed at the creature are made with {@quickref Advantage and Disadvantage|PHB|2|0|advantage} for up to 10 minutes. You know if it succeeded or failed on its {@quickref saving throws|PHB|2|1|saving throw}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extort Truth' AND class_name = 'Monk' AND level = 6), 1, 'An affected creature is aware of the effect and can thus avoid answering questions to which it would normally respond with a lie. Such a creature can be evasive in its answers as long as the effect lasts.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extort Truth' AND class_name = 'Monk' AND level = 6), 2, 'If you wish to impose this effect on a creature without injuring it, you can attack the creature to simply touch it, dealing no damage on a hit.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mystical Erudition', null, 174, 'Monk', 'PHB', 'Cobalt Soul', 'TDCSR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mystical Erudition' AND class_name = 'Monk' AND level = 6), 0, 'Also by 6th level, you have extensively studied the history and lore within the archives of the {@book Cobalt Soul|TDCSR|2|Library of the Cobalt Soul}. You learn one language of your choice, and you gain proficiency with one of the following skills of your choice: {@skill Arcana}, {@skill History}, {@skill Investigation}, {@skill Nature}, or {@skill Religion}. If you already have proficiency in one of the listed skills, you can instead choose to double your proficiency bonus for any ability check you make that uses the chosen proficiency.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mind of Mercury', null, 174, 'Monk', 'PHB', 'Cobalt Soul', 'TDCSR', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mind of Mercury' AND class_name = 'Monk' AND level = 11), 0, 'Starting at 11th level, you''ve honed your awareness and reflexes through mental aptitude and pattern recognition. Once per turn, if you''ve already taken your reaction, you may spend 1 ki point to take an additional reaction. You can use only one reaction per triggering effect.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mystical Erudition (11th Level)', null, 174, 'Monk', 'PHB', 'Cobalt Soul', 'TDCSR', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mystical Erudition (11th Level)' AND class_name = 'Monk' AND level = 11), 0, 'You gain an additional language and an additional skill proficiency from the list: {@skill Arcana}, {@skill History}, {@skill Investigation}, {@skill Nature}, or {@skill Religion}—(or you can double the bonus of an existing proficiency from the list) at 11th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Debilitating Barrage', null, 174, 'Monk', 'PHB', 'Cobalt Soul', 'TDCSR', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Debilitating Barrage' AND class_name = 'Monk' AND level = 17), 0, 'Upon reaching 17th level, you''ve gained the knowledge to manipulate a creature''s ki to undermine their fortitude. When you hit a creature with an unarmed strike, you can spend 3 ki points to cause the creature to gain vulnerability to one damage type of your choice for 1 minute, or until the end of a turn in which it has taken damage of that type.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Debilitating Barrage' AND class_name = 'Monk' AND level = 17), 1, 'If a creature has resistance to the damage type you choose, this resistance is suppressed for 1 minute, rather than gaining vulnerability. A creature that is immune to the damage type you choose is unaffected. A creature who is affected by this feature cannot be affected by it again for 24 hours.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mystical Erudition (17th Level)', null, 174, 'Monk', 'PHB', 'Cobalt Soul', 'TDCSR', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mystical Erudition (17th Level)' AND class_name = 'Monk' AND level = 17), 0, 'You gain an additional language and an additional skill proficiency from the list: {@skill Arcana}, {@skill History}, {@skill Investigation}, {@skill Nature}, or {@skill Religion}—(or you can double the bonus of an existing proficiency from the list) at 17th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Drunken Master', 3, 33, 'Monk', 'PHB', 'Drunken Master', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Drunken Master' AND class_name = 'Monk' AND level = 3), 0, 'The Way of the Drunken Master teaches its students to move with the jerky, unpredictable movements of a drunkard. A drunken master sways, tottering on unsteady feet, to present what seems like an incompetent combatant who proves frustrating to engage. The drunken master''s erratic stumbles conceal a carefully executed dance of blocks, parries, advances, attacks, and retreats.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Drunken Master' AND class_name = 'Monk' AND level = 3), 1, 'A drunken master often enjoys playing the fool to bring gladness to the despondent or to demonstrate humility to the arrogant, but when battle is joined, the drunken master can be a maddening, masterful foe.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Drunken Master' AND class_name = 'Monk' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Proficiencies|Monk||Drunken Master|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Drunken Master' AND class_name = 'Monk' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Drunken Technique|Monk||Drunken Master|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Proficiencies', 3, 33, 'Monk', 'PHB', 'Drunken Master', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Proficiencies' AND class_name = 'Monk' AND level = 3), 0, 'When you choose this tradition at 3rd level, you gain proficiency in the {@skill Performance} skill if you don''t already have it. Your martial arts technique mixes combat training with the precision of a dancer and the antics of a jester. You also gain proficiency with {@item brewer''s supplies|phb} if you don''t already have it.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Drunken Technique', 3, 33, 'Monk', 'PHB', 'Drunken Master', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drunken Technique' AND class_name = 'Monk' AND level = 3), 0, 'At 3rd level, you learn how to twist and turn quickly as part of your Flurry of Blows. Whenever you use Flurry of Blows, you gain the benefit of the {@action Disengage} action, and your walking speed increases by 10 feet until the end of the current turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Leap to Your Feet', 3, 34, 'Monk', 'PHB', 'Drunken Master', 'XGE', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Leap to Your Feet' AND class_name = 'Monk' AND level = 6), 0, 'When you''re {@condition prone}, you can stand up by spending 5 feet of movement, rather than half your speed.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Redirect Attack', 3, 34, 'Monk', 'PHB', 'Drunken Master', 'XGE', 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Redirect Attack' AND class_name = 'Monk' AND level = 6), 0, 'When a creature misses you with a melee attack roll, you can spend 1 ki point as a reaction to cause that attack to hit one creature of your choice, other than the attacker, that you can see within 5 feet of you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tipsy Sway', 3, 33, 'Monk', 'PHB', 'Drunken Master', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tipsy Sway' AND class_name = 'Monk' AND level = 6), 0, 'Starting at 6th level, you can move in sudden, swaying ways. You gain the following benefits.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tipsy Sway' AND class_name = 'Monk' AND level = 6), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Leap to Your Feet|Monk|PHB|Drunken Master|XGE|6"},{"type":"refSubclassFeature","subclassFeature":"Redirect Attack|Monk|PHB|Drunken Master|XGE|6"}]', NULL, NULL, NULL, NULL, '{"type":"options","entries":[{"type":"refSubclassFeature","subclassFeature":"Leap to Your Feet|Monk|PHB|Drunken Master|XGE|6"},{"type":"refSubclassFeature","subclassFeature":"Redirect Attack|Monk|PHB|Drunken Master|XGE|6"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Drunkard''s Luck', 3, 33, 'Monk', 'PHB', 'Drunken Master', 'XGE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drunkard''s Luck' AND class_name = 'Monk' AND level = 11), 0, 'Starting at 11th level, you always seem to get a lucky bounce at the right moment. When you make an ability check, an attack roll, or a saving throw and have disadvantage on the roll, you can spend 2 ki points to cancel the disadvantage for that roll.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Intoxicated Frenzy', 3, 33, 'Monk', 'PHB', 'Drunken Master', 'XGE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Intoxicated Frenzy' AND class_name = 'Monk' AND level = 17), 0, 'At 17th level, you gain the ability to make an overwhelming number of attacks against a group of enemies. When you use your Flurry of Blows, you can make up to three additional attacks with it (up to a total of five Flurry of Blows attacks), provided that each Flurry of Blows attack targets a different creature this turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Agile Parry', 3, 34, 'Monk', 'PHB', 'Kensei', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Agile Parry' AND class_name = 'Monk' AND level = 3), 0, 'If you make an unarmed strike as part of the {@action Attack} action on your turn and are holding a kensei weapon, you can use it to defend yourself if it is a melee weapon. You gain a +2 bonus to AC until the start of your next turn, while the weapon is in your hand and you aren''t {@condition incapacitated}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Kensei''s Shot', 3, 34, 'Monk', 'PHB', 'Kensei', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Kensei''s Shot' AND class_name = 'Monk' AND level = 3), 0, 'You can use a bonus action on your turn to make your ranged attacks with a kensei weapon more deadly. When you do so, any target you hit with a ranged attack using a kensei weapon takes an extra {@damage 1d4} damage of the weapon''s type. You retain this benefit until the end of the current turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Kensei', 3, 34, 'Monk', 'PHB', 'Kensei', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Kensei' AND class_name = 'Monk' AND level = 3), 0, 'Monks of the Way of the Kensei train relentlessly with their weapons, to the point where the weapon becomes an extension of the body. Founded on a mastery of sword fighting, the tradition has expanded to include many different weapons.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Kensei' AND class_name = 'Monk' AND level = 3), 1, 'A kensei sees a weapon in much the same way a calligrapher or painter regards a pen or brush. Whatever the weapon, the kensei views it as a tool used to express the beauty and precision of the martial arts. That such mastery makes a kensei a peerless warrior is but a side effect of intense devotion, practice, and study.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Kensei' AND class_name = 'Monk' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Path of the Kensei|Monk||Kensei|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Path of the Kensei', 3, 34, 'Monk', 'PHB', 'Kensei', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Path of the Kensei' AND class_name = 'Monk' AND level = 3), 0, 'When you choose this tradition at 3rd level, your special martial arts training leads you to master the use of certain weapons. This path also includes instruction in the deft strokes of calligraphy or painting. You gain the following benefits.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Kensei' AND class_name = 'Monk' AND level = 3), 1, 'entries', 'Kensei Weapons', '["Choose two types of weapons to be your kensei weapons: one melee weapon and one ranged weapon. Each of these weapons can be any {@filter simple or martial weapon that lacks the heavy and special properties|items|source=phb|type=simple weapon;martial weapon|property=!heavy;!special|category=basic}. The longbow is also a valid choice. You gain proficiency with these weapons if you don''t already have it. Weapons of the chosen types are monk weapons for you. Many of this tradition''s features work only with your kensei weapons. When you reach 6th, 11th, and 17th level in this class, you can choose another type of weapon—either melee or ranged—to be a kensei weapon for you, following the criteria above."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Kensei Weapons","entries":["Choose two types of weapons to be your kensei weapons: one melee weapon and one ranged weapon. Each of these weapons can be any {@filter simple or martial weapon that lacks the heavy and special properties|items|source=phb|type=simple weapon;martial weapon|property=!heavy;!special|category=basic}. The longbow is also a valid choice. You gain proficiency with these weapons if you don''t already have it. Weapons of the chosen types are monk weapons for you. Many of this tradition''s features work only with your kensei weapons. When you reach 6th, 11th, and 17th level in this class, you can choose another type of weapon—either melee or ranged—to be a kensei weapon for you, following the criteria above."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Kensei' AND class_name = 'Monk' AND level = 3), 2, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Agile Parry|Monk|PHB|Kensei|XGE|3"},{"type":"refSubclassFeature","subclassFeature":"Kensei''s Shot|Monk|PHB|Kensei|XGE|3"}]', NULL, NULL, NULL, NULL, '{"type":"options","entries":[{"type":"refSubclassFeature","subclassFeature":"Agile Parry|Monk|PHB|Kensei|XGE|3"},{"type":"refSubclassFeature","subclassFeature":"Kensei''s Shot|Monk|PHB|Kensei|XGE|3"}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Path of the Kensei' AND class_name = 'Monk' AND level = 3), 3, 'entries', 'Way of the Brush', '["You gain proficiency with your choice of {@item calligrapher''s supplies|phb} or {@item painter''s supplies|phb}."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Way of the Brush","entries":["You gain proficiency with your choice of {@item calligrapher''s supplies|phb} or {@item painter''s supplies|phb}."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('One with the Blade', 3, 34, 'Monk', 'PHB', 'Kensei', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'One with the Blade' AND class_name = 'Monk' AND level = 6), 0, 'At 6th level, you extend your ki into your kensei weapons, granting you the following benefits.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'One with the Blade' AND class_name = 'Monk' AND level = 6), 1, 'entries', 'Magic Kensei Weapons', '["Your attacks with your kensei weapons count as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Magic Kensei Weapons","entries":["Your attacks with your kensei weapons count as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'One with the Blade' AND class_name = 'Monk' AND level = 6), 2, 'entries', 'Deft Strike', '["When you hit a target with a kensei weapon, you can spend 1 ki point to cause the weapon to deal extra damage to the target equal to your Martial Arts die. You can use this feature only once on each of your turns."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Deft Strike","entries":["When you hit a target with a kensei weapon, you can spend 1 ki point to cause the weapon to deal extra damage to the target equal to your Martial Arts die. You can use this feature only once on each of your turns."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sharpen the Blade', 3, 34, 'Monk', 'PHB', 'Kensei', 'XGE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sharpen the Blade' AND class_name = 'Monk' AND level = 11), 0, 'At 11th level, you gain the ability to augment your weapons further with your ki. As a bonus action, you can expend up to 3 ki points to grant one kensei weapon you touch a bonus to attack and damage rolls when you attack with it. The bonus equals the number of ki points you spent. This bonus lasts for 1 minute or until you use this feature again. This feature has no effect on a magic weapon that already has a bonus to attack and damage rolls.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unerring Accuracy', 3, 34, 'Monk', 'PHB', 'Kensei', 'XGE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unerring Accuracy' AND class_name = 'Monk' AND level = 17), 0, 'At 17th level, your mastery of weapons grants you extraordinary accuracy. If you miss with an attack roll using a monk weapon on your turn, you can reroll it. You can use this feature only once on each of your turns.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Way of the Sun Soul', 3, 35, 'Monk', 'PHB', 'Sun Soul', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Way of the Sun Soul' AND class_name = 'Monk' AND level = 3), 0, 'Monks of the Way of the Sun Soul learn to channel their life energy into searing bolts of light. They teach that meditation can unlock the ability to unleash the indomitable light shed by the soul of every living creature.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Way of the Sun Soul' AND class_name = 'Monk' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Radiant Sun Bolt|Monk||Sun Soul|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Radiant Sun Bolt', 3, 35, 'Monk', 'PHB', 'Sun Soul', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Radiant Sun Bolt' AND class_name = 'Monk' AND level = 3), 0, 'Starting when you choose this tradition at 3rd level, you can hurl searing bolts of magical radiance.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Radiant Sun Bolt' AND class_name = 'Monk' AND level = 3), 1, 'You gain a new attack option that you can use with the {@action Attack} action. The special attack is a ranged spell attack with a range of 30 feet. You are proficient with it, and you add your Dexterity modifier to its attack and damage rolls. Its damage is radiant, and its damage die is a {@damage d4}. This die changes as you gain monk levels, as shown in the Martial Arts column of the Monk table.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Radiant Sun Bolt' AND class_name = 'Monk' AND level = 3), 2, 'When you take the {@action Attack} action on your turn and use this special attack as part of it, you can spend 1 ki point to make the special attack twice as a bonus action. When you gain the Extra Attack feature, this special attack can be used for any of the attacks you make as part of the {@action Attack} action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Searing Arc Strike', 3, 35, 'Monk', 'PHB', 'Sun Soul', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Searing Arc Strike' AND class_name = 'Monk' AND level = 6), 0, 'At 6th level, you gain the ability to channel your ki into searing waves of energy. Immediately after you take the {@action Attack} action on your turn, you can spend 2 ki points to cast the {@spell burning hands} spell as a bonus action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Searing Arc Strike' AND class_name = 'Monk' AND level = 6), 1, 'You can spend additional ki points to cast {@spell burning hands} as a higher-level spell. Each additional ki point you spend increases the spell''s level by 1. The maximum number of ki points (2 plus any additional points) that you can spend on the spell equals half your monk level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Searing Sunburst', 3, 35, 'Monk', 'PHB', 'Sun Soul', 'XGE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Searing Sunburst' AND class_name = 'Monk' AND level = 11), 0, 'At 11th level, you gain the ability to create an orb of light that erupts into a devastating explosion. As an action, you magically create an orb and hurl it at a point you choose within 150 feet, where it erupts into a sphere of radiant light for a brief but deadly instant.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Searing Sunburst' AND class_name = 'Monk' AND level = 11), 1, 'Each creature in that 20-foot-radius sphere must succeed on a Constitution saving throw or take {@damage 2d6} radiant damage. A creature doesn''t need to make the save if the creature is behind {@quickref Cover||3||total cover} that is opaque.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Searing Sunburst' AND class_name = 'Monk' AND level = 11), 2, 'You can increase the sphere''s damage by spending ki points. Each point you spend, to a maximum of 3, increases the damage by {@damage 2d6}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sun Shield', 3, 35, 'Monk', 'PHB', 'Sun Soul', 'XGE', 17, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sun Shield' AND class_name = 'Monk' AND level = 17), 0, 'At 17th level, you become wreathed in a luminous, magical aura. You shed bright light in a 30-foot radius and dim light for an additional 30 feet. You can extinguish or restore the light as a bonus action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sun Shield' AND class_name = 'Monk' AND level = 17), 1, 'If a creature hits you with a melee attack while this light shines, you can use your reaction to deal radiant damage to the creature. The radiant damage equals 5 + your Wisdom modifier.');
