INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 82, 'Paladin', 'PHB', NULL, NULL, 16, null, false, false) ON CONFLICT (name) DO NOTHING;
