INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sacred Oath feature', 1, 82, 'Paladin', 'PHB', NULL, NULL, 20, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sacred Oath feature' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, you gain a feature granted to you by your Sacred Oath.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oathbreaker', 6, 97, 'Paladin', 'PHB', 'Oathbreaker', 'DMG', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oathbreaker' AND class_name = 'Paladin' AND level = 3), 0, 'An Oathbreaker is a paladin who breaks his or her sacred oaths to pursue some dark ambition or serve an evil power. Whatever light burned in the paladin''s heart has been extinguished. Only darkness remains.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oathbreaker' AND class_name = 'Paladin' AND level = 3), 1, 'A paladin must be evil and at least 3rd level to become an Oathbreaker. The paladin replaces the features specific to his or her Sacred Oath with Oathbreaker features.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oathbreaker' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oathbreaker Spells|Paladin||Oathbreaker|DMG|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oathbreaker' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin||Oathbreaker|DMG|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity', 6, 97, 'Paladin', 'PHB', 'Oathbreaker', 'DMG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 0, 'An Oathbreaker paladin of 3rd level or higher gains the following two Channel Divinity options.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Control Undead|Paladin||Oathbreaker|DMG|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Dreadful Aspect|Paladin||Oathbreaker|DMG|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Control Undead', 6, 97, 'Paladin', 'PHB', 'Oathbreaker', 'DMG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Control Undead' AND class_name = 'Paladin' AND level = 3), 0, 'As an action, the paladin targets one undead creature he or she can see within 30 feet of him or her. The target must make a Wisdom saving throw. On a failed save, the target must obey the paladin''s commands for the next 24 hours, or until the paladin uses this Channel Divinity option again. An undead whose challenge rating is equal to or greater than the paladin''s level is immune to this effect.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dreadful Aspect', 6, 97, 'Paladin', 'PHB', 'Oathbreaker', 'DMG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dreadful Aspect' AND class_name = 'Paladin' AND level = 3), 0, 'As an action, the paladin channels the darkest emotions and focuses them into a burst of magical menace. Each creature of the paladin''s choice within 30 feet of the paladin must make a Wisdom saving throw if it can see the paladin. On a failed save, the target is {@condition frightened} of the paladin for 1 minute. If a creature {@condition frightened} by this effect ends its turn more than 30 feet away from the paladin, it can attempt another Wisdom saving throw to end the effect on it.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oathbreaker Spells', 6, 97, 'Paladin', 'PHB', 'Oathbreaker', 'DMG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oathbreaker Spells' AND class_name = 'Paladin' AND level = 3), 0, 'You gain oathbreaker spells at the paladin levels listed.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oathbreaker Spells' AND class_name = 'Paladin' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Oathbreaker Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell hellish rebuke}, {@spell inflict wounds}"],["5th","{@spell crown of madness}, {@spell darkness}"],["9th","{@spell animate dead}, {@spell bestow curse}"],["13th","{@spell blight}, {@spell confusion}"],["17th","{@spell contagion}, {@spell dominate person}"]]', '{"type":"table","caption":"Oathbreaker Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell hellish rebuke}, {@spell inflict wounds}"],["5th","{@spell crown of madness}, {@spell darkness}"],["9th","{@spell animate dead}, {@spell bestow curse}"],["13th","{@spell blight}, {@spell confusion}"],["17th","{@spell contagion}, {@spell dominate person}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of Hate', 6, 97, 'Paladin', 'PHB', 'Oathbreaker', 'DMG', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Hate' AND class_name = 'Paladin' AND level = 7), 0, 'Starting at 7th level, the paladin, as well any fiends and undead within 10 feet of the paladin, gains a bonus to melee weapon damage rolls equal to the paladin''s Charisma modifier (minimum of +1). A creature can benefit from this feature from only one paladin at a time.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Hate' AND class_name = 'Paladin' AND level = 7), 1, 'At 18th level, the range of this aura increases to 30 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Supernatural Resistance', 6, 97, 'Paladin', 'PHB', 'Oathbreaker', 'DMG', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Supernatural Resistance' AND class_name = 'Paladin' AND level = 15), 0, 'At 15th level, the paladin gains resistance to bludgeoning, piercing, and slashing damage from nonmagical weapons.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dread Lord', 6, 97, 'Paladin', 'PHB', 'Oathbreaker', 'DMG', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dread Lord' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, the paladin can, as an action, surround himself or herself with an aura of gloom that lasts for 1 minute. The aura reduces any bright light in a 30-foot radius around the paladin to dim light. Whenever an enemy that is {@condition frightened} by the paladin starts its turn in the aura, it takes {@damage 4d10} psychic damage. Additionally, the paladin and creatures he or she chooses in the aura are draped in deeper shadow. Creatures that rely on sight have disadvantage on attack rolls against creatures draped in this shadow.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dread Lord' AND class_name = 'Paladin' AND level = 20), 1, 'While the aura lasts, the paladin can use a bonus action on his or her turn to cause the shadows in the aura to attack one creature. The paladin makes a melee spell attack against the target. If the attack hits, the target takes necrotic damage equal to {@damage 3d10} + the paladin''s Charisma modifier.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dread Lord' AND class_name = 'Paladin' AND level = 20), 2, 'After activating the aura, the paladin can''t do so again until he or she finishes a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of the Ancients', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Ancients' AND class_name = 'Paladin' AND level = 3), 0, 'The Oath of the Ancients is as old as the race of elves and the rituals of the druids. Sometimes called fey knights, green knights, or horned knights, paladins who swear this oath cast their lot with the side of the light in the cosmic struggle against darkness because they love the beautiful and life-giving things of the world, not necessarily because they believe in principles of honor, courage, and justice. They adorn their armor and clothing with images of growing things—leaves, antlers, or flowers—to reflect their commitment to preserving life and light in the world.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Ancients' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tenets of the Ancients|Paladin||Ancients||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Ancients' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oath Spells|Paladin||Ancients||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Ancients' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin||Ancients||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 0, 'When you take this oath at 3rd level, you gain the following two Channel Divinity options.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Nature''s Wrath|Paladin||Ancients||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Turn the Faithless|Paladin||Ancients||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Nature''s Wrath', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature''s Wrath' AND class_name = 'Paladin' AND level = 3), 0, 'You can use your Channel Divinity to invoke primeval forces to ensnare a foe. As an action, you can cause spectral vines to spring up and reach for a creature within 10 feet of you that you can see. The creature must succeed on a Strength or Dexterity saving throw (its choice) or be {@condition restrained}. While {@condition restrained} by the vines, the creature repeats the saving throw at the end of each of its turns. On a success, it frees itself and the vines vanish.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath Spells', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 0, 'You gain oath spells at the paladin levels listed.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Oath of the Ancients Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell ensnaring strike}, {@spell speak with animals}"],["5th","{@spell moonbeam}, {@spell misty step}"],["9th","{@spell plant growth}, {@spell protection from energy}"],["13th","{@spell ice storm}, {@spell stoneskin}"],["17th","{@spell commune with nature}, {@spell tree stride}"]]', '{"type":"table","caption":"Oath of the Ancients Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell ensnaring strike}, {@spell speak with animals}"],["5th","{@spell moonbeam}, {@spell misty step}"],["9th","{@spell plant growth}, {@spell protection from energy}"],["13th","{@spell ice storm}, {@spell stoneskin}"],["17th","{@spell commune with nature}, {@spell tree stride}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tenets of the Ancients', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Ancients' AND class_name = 'Paladin' AND level = 3), 0, 'The tenets of the Oath of the Ancients have been preserved for uncounted centuries. This oath emphasizes the principles of good above any concerns of law or chaos. Its four central principles are simple.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Ancients' AND class_name = 'Paladin' AND level = 3), 1, 'entries', NULL, '[{"type":"entries","name":"Kindle the Light","entries":["Through your acts of mercy, kindness, and forgiveness, kindle the light of hope in the world, beating back despair."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Kindle the Light","entries":["Through your acts of mercy, kindness, and forgiveness, kindle the light of hope in the world, beating back despair."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Ancients' AND class_name = 'Paladin' AND level = 3), 2, 'entries', NULL, '[{"type":"entries","name":"Shelter the Light","entries":["Where there is good, beauty, love, and laughter in the world, stand against the wickedness that would swallow it. Where life flourishes, stand against the forces that would render it barren."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Shelter the Light","entries":["Where there is good, beauty, love, and laughter in the world, stand against the wickedness that would swallow it. Where life flourishes, stand against the forces that would render it barren."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Ancients' AND class_name = 'Paladin' AND level = 3), 3, 'entries', NULL, '[{"type":"entries","name":"Preserve Your Own Light","entries":["Delight in song and laughter, in beauty and art. If you allow the light to die in your own heart, you can''t preserve it in the world."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Preserve Your Own Light","entries":["Delight in song and laughter, in beauty and art. If you allow the light to die in your own heart, you can''t preserve it in the world."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Ancients' AND class_name = 'Paladin' AND level = 3), 4, 'entries', NULL, '[{"type":"entries","name":"Be the Light","entries":["Be a glorious beacon for all who live in despair. Let the light of your joy and courage shine forth in all your deeds."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Be the Light","entries":["Be a glorious beacon for all who live in despair. Let the light of your joy and courage shine forth in all your deeds."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Turn the Faithless', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Turn the Faithless' AND class_name = 'Paladin' AND level = 3), 0, 'You can use your Channel Divinity to utter ancient words that are painful for fey and fiends to hear. As an action, you present your holy symbol, and each fey or fiend within 30 feet of you that can hear you must make a Wisdom saving throw. On a failed save, the creature is turned for 1 minute or until it takes damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Turn the Faithless' AND class_name = 'Paladin' AND level = 3), 1, 'A turned creature must spend its turns trying to move as far away from you as it can, and it can''t willingly move to a space within 30 feet of you. It also can''t take reactions. For its action, it can use only the {@action Dash} action or try to escape from an effect that prevents it from moving. If there''s nowhere to move, the creature can use the {@action Dodge} action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Turn the Faithless' AND class_name = 'Paladin' AND level = 3), 2, 'If the creature''s true form is concealed by an illusion, shapeshifting, or other effect, that form is revealed while it is turned.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of Warding', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Warding' AND class_name = 'Paladin' AND level = 7), 0, 'Beginning at 7th level, ancient magic lies so heavily upon you that it forms an eldritch ward. You and friendly creatures within 10 feet of you have resistance to damage from spells.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Warding' AND class_name = 'Paladin' AND level = 7), 1, 'At 18th level, the range of this aura increases to 30 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Undying Sentinel', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Undying Sentinel' AND class_name = 'Paladin' AND level = 15), 0, 'Starting at 15th level, when you are reduced to 0 hit points and are not killed outright, you can choose to drop to 1 hit point instead. Once you use this ability, you can''t use it again until you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Undying Sentinel' AND class_name = 'Paladin' AND level = 15), 1, 'Additionally, you suffer none of the drawbacks of old age, and you can''t be aged magically.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elder Champion', 1, 86, 'Paladin', 'PHB', 'Ancients', 'PHB', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elder Champion' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, you can assume the form of an ancient force of nature, taking on an appearance you choose. For example, your skin might turn green or take on a bark-like texture, your hair might become leafy or moss-like, or you might sprout antlers or a lion-like mane.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elder Champion' AND class_name = 'Paladin' AND level = 20), 1, 'Using your action, you undergo a transformation. For 1 minute, you gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Elder Champion' AND class_name = 'Paladin' AND level = 20), 2, 'list', NULL, '["At the start of each of your turns, you regain 10 hit points.","Whenever you cast a paladin spell that has a casting time of 1 action, you can cast it using your bonus action instead.","Enemy creatures within 10 feet of you have disadvantage on saving throws against your paladin spells and Channel Divinity options."]', '["At the start of each of your turns, you regain 10 hit points.","Whenever you cast a paladin spell that has a casting time of 1 action, you can cast it using your bonus action instead.","Enemy creatures within 10 feet of you have disadvantage on saving throws against your paladin spells and Channel Divinity options."]', NULL, NULL, NULL, '{"type":"list","items":["At the start of each of your turns, you regain 10 hit points.","Whenever you cast a paladin spell that has a casting time of 1 action, you can cast it using your bonus action instead.","Enemy creatures within 10 feet of you have disadvantage on saving throws against your paladin spells and Channel Divinity options."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elder Champion' AND class_name = 'Paladin' AND level = 20), 3, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of Devotion', 1, 85, 'Paladin', 'PHB', 'Devotion', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of Devotion' AND class_name = 'Paladin' AND level = 3), 0, 'The Oath of Devotion binds a paladin to the loftiest ideals of justice, virtue, and order. Sometimes called cavaliers, white knights, or holy warriors, these paladins meet the ideal of the knight in shining armor, acting with honor in pursuit of justice and the greater good. They hold themselves to the highest standards of conduct, and some, for better or worse, hold the rest of the world to the same standards. Many who swear this oath are devoted to gods of law and good and use their gods'' tenets as the measure of their devotion. They hold angels—the perfect servants of good—as their ideals, and incorporate images of angelic wings into their helmets or coats of arms.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Devotion' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tenets of Devotion|Paladin||Devotion||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Devotion' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oath Spells|Paladin||Devotion||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Devotion' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin||Devotion||3"}');
