INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath Spells', 1, 85, 'Paladin', 'PHB', 'Devotion', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 0, 'You gain oath spells at the paladin levels listed.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Oath of Devotion Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell protection from evil and good}, {@spell sanctuary}"],["5th","{@spell lesser restoration}, {@spell zone of truth}"],["9th","{@spell beacon of hope}, {@spell dispel magic}"],["13th","{@spell freedom of movement}, {@spell guardian of faith}"],["17th","{@spell commune}, {@spell flame strike}"]]', '{"type":"table","caption":"Oath of Devotion Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell protection from evil and good}, {@spell sanctuary}"],["5th","{@spell lesser restoration}, {@spell zone of truth}"],["9th","{@spell beacon of hope}, {@spell dispel magic}"],["13th","{@spell freedom of movement}, {@spell guardian of faith}"],["17th","{@spell commune}, {@spell flame strike}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sacred Weapon', 1, 85, 'Paladin', 'PHB', 'Devotion', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sacred Weapon' AND class_name = 'Paladin' AND level = 3), 0, 'As an action, you can imbue one weapon that you are holding with positive energy, using your Channel Divinity. For 1 minute, you add your Charisma modifier to attack rolls made with that weapon (with a minimum bonus of +1). The weapon also emits bright light in a 20-foot radius and dim light 20 feet beyond that. If the weapon is not already magical, it becomes magical for the duration.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sacred Weapon' AND class_name = 'Paladin' AND level = 3), 1, 'You can end this effect on your turn as part of any other action. If you are no longer holding or carrying this weapon, or if you fall {@condition unconscious}, this effect ends.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tenets of Devotion', 1, 85, 'Paladin', 'PHB', 'Devotion', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Devotion' AND class_name = 'Paladin' AND level = 3), 0, 'Though the exact words and strictures of the Oath of Devotion vary, paladins of this oath share these tenets.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Devotion' AND class_name = 'Paladin' AND level = 3), 1, 'entries', NULL, '[{"type":"entries","name":"Honesty","entries":["Don''t lie or cheat. Let your word be your promise."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Honesty","entries":["Don''t lie or cheat. Let your word be your promise."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Devotion' AND class_name = 'Paladin' AND level = 3), 2, 'entries', NULL, '[{"type":"entries","name":"Courage","entries":["Never fear to act, though caution is wise."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Courage","entries":["Never fear to act, though caution is wise."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Devotion' AND class_name = 'Paladin' AND level = 3), 3, 'entries', NULL, '[{"type":"entries","name":"Compassion","entries":["Aid others, protect the weak, and punish those who threaten them. Show mercy to your foes, but temper it with wisdom."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Compassion","entries":["Aid others, protect the weak, and punish those who threaten them. Show mercy to your foes, but temper it with wisdom."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Devotion' AND class_name = 'Paladin' AND level = 3), 4, 'entries', NULL, '[{"type":"entries","name":"Honor","entries":["Treat others with fairness, and let your honorable deeds be an example to them. Do as much good as possible while causing the least amount of harm."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Honor","entries":["Treat others with fairness, and let your honorable deeds be an example to them. Do as much good as possible while causing the least amount of harm."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Devotion' AND class_name = 'Paladin' AND level = 3), 5, 'entries', NULL, '[{"type":"entries","name":"Duty","entries":["Be responsible for your actions and their consequences, protect those entrusted to your care, and obey those who have just authority over you."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Duty","entries":["Be responsible for your actions and their consequences, protect those entrusted to your care, and obey those who have just authority over you."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Turn the Unholy', 1, 85, 'Paladin', 'PHB', 'Devotion', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Turn the Unholy' AND class_name = 'Paladin' AND level = 3), 0, 'As an action, you present your holy symbol and speak a prayer censuring fiends and undead, using your Channel Divinity. Each fiend or undead that can see or hear you within 30 feet of you must make a Wisdom saving throw. If the creature fails its saving throw, it is turned for 1 minute or until it takes damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Turn the Unholy' AND class_name = 'Paladin' AND level = 3), 1, 'A turned creature must spend its turns trying to move as far away from you as it can, and it can''t willingly move to a space within 30 feet of you. It also can''t take reactions. For its action, it can use only the {@action Dash} action or try to escape from an effect that prevents it from moving. If there''s nowhere to move, the creature can use the {@action Dodge} action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of Devotion', 1, 85, 'Paladin', 'PHB', 'Devotion', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Devotion' AND class_name = 'Paladin' AND level = 7), 0, 'Starting at 7th level, you and friendly creatures within 10 feet of you can''t be {@condition charmed} while you are conscious.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Devotion' AND class_name = 'Paladin' AND level = 7), 1, 'At 18th level, the range of this aura increases to 30 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Purity of Spirit', 1, 85, 'Paladin', 'PHB', 'Devotion', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Purity of Spirit' AND class_name = 'Paladin' AND level = 15), 0, 'Beginning at 15th level, you are always under the effects of a {@spell protection from evil and good} spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Holy Nimbus', 1, 85, 'Paladin', 'PHB', 'Devotion', 'PHB', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Holy Nimbus' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, as an action, you can emanate an aura of sunlight. For 1 minute, bright light shines from you in a 30-foot radius, and dim light shines 30 feet beyond that.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Holy Nimbus' AND class_name = 'Paladin' AND level = 20), 1, 'Whenever an enemy creature starts its turn in the bright light, the creature takes 10 radiant damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Holy Nimbus' AND class_name = 'Paladin' AND level = 20), 2, 'In addition, for the duration, you have advantage on saving throws against spells cast by fiends or undead.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Holy Nimbus' AND class_name = 'Paladin' AND level = 20), 3, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of Vengeance', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of Vengeance' AND class_name = 'Paladin' AND level = 3), 0, 'The Oath of Vengeance is a solemn commitment to punish those who have committed a grievous sin. When evil forces slaughter helpless villagers, when an entire people turns against the will of the gods, when a thieves'' guild grows too violent and powerful, when a dragon rampages through the countryside—at times like these, paladins arise and swear an Oath of Vengeance to set right that which has gone wrong. To these paladins—sometimes called avengers or dark knights—their own purity is not as important as delivering justice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Vengeance' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tenets of Vengeance|Paladin||Vengeance||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Vengeance' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oath Spells|Paladin||Vengeance||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Vengeance' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin||Vengeance||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Abjure Enemy', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Abjure Enemy' AND class_name = 'Paladin' AND level = 3), 0, 'As an action, you present your holy symbol and speak a prayer of denunciation, using your Channel Divinity. Choose one creature within 60 feet of you that you can see. That creature must make a Wisdom saving throw, unless it is immune to being {@condition frightened}. Fiends and undead have disadvantage on this saving throw.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Abjure Enemy' AND class_name = 'Paladin' AND level = 3), 1, 'On a failed save, the creature is {@condition frightened} for 1 minute or until it takes any damage. While {@condition frightened}, the creature''s speed is 0, and it can''t benefit from any bonus to its speed.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Abjure Enemy' AND class_name = 'Paladin' AND level = 3), 2, 'On a successful save, the creature''s speed is halved for 1 minute or until the creature takes any damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 0, 'When you take this oath at 3rd level, you gain the following two Channel Divinity options.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Abjure Enemy|Paladin||Vengeance||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Vow of Enmity|Paladin||Vengeance||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath Spells', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 0, 'You gain oath spells at the paladin levels listed.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Oath of Vengeance Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell bane}, {@spell hunter''s mark}"],["5th","{@spell hold person}, {@spell misty step}"],["9th","{@spell haste}, {@spell protection from energy}"],["13th","{@spell banishment}, {@spell dimension door}"],["17th","{@spell hold monster}, {@spell scrying}"]]', '{"type":"table","caption":"Oath of Vengeance Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell bane}, {@spell hunter''s mark}"],["5th","{@spell hold person}, {@spell misty step}"],["9th","{@spell haste}, {@spell protection from energy}"],["13th","{@spell banishment}, {@spell dimension door}"],["17th","{@spell hold monster}, {@spell scrying}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tenets of Vengeance', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Vengeance' AND class_name = 'Paladin' AND level = 3), 0, 'The tenets of the Oath of Vengeance vary by paladin, but all the tenets revolve around punishing wrongdoers by any means necessary. Paladins who uphold these tenets are willing to sacrifice even their own righteousness to mete out justice upon those who do evil, so the paladins are often neutral or lawful neutral in alignment. The core principles of the tenets are brutally simple.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Vengeance' AND class_name = 'Paladin' AND level = 3), 1, 'entries', NULL, '[{"type":"entries","name":"Fight the Greater Evil","entries":["Faced with a choice of fighting my sworn foes or combating a lesser evil, I choose the greater evil."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Fight the Greater Evil","entries":["Faced with a choice of fighting my sworn foes or combating a lesser evil, I choose the greater evil."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Vengeance' AND class_name = 'Paladin' AND level = 3), 2, 'entries', NULL, '[{"type":"entries","name":"No Mercy for the Wicked","entries":["Ordinary foes might win my mercy, but my sworn enemies do not."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"No Mercy for the Wicked","entries":["Ordinary foes might win my mercy, but my sworn enemies do not."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Vengeance' AND class_name = 'Paladin' AND level = 3), 3, 'entries', NULL, '[{"type":"entries","name":"By Any Means Necessary","entries":["My qualms can''t get in the way of exterminating my foes."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"By Any Means Necessary","entries":["My qualms can''t get in the way of exterminating my foes."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Vengeance' AND class_name = 'Paladin' AND level = 3), 4, 'entries', NULL, '[{"type":"entries","name":"Restitution","entries":["If my foes wreak ruin on the world, it is because I failed to stop them. I must help those harmed by their misdeeds."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Restitution","entries":["If my foes wreak ruin on the world, it is because I failed to stop them. I must help those harmed by their misdeeds."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Vow of Enmity', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vow of Enmity' AND class_name = 'Paladin' AND level = 3), 0, 'As a bonus action, you can utter a vow of enmity against a creature you can see within 10 feet of you, using your Channel Divinity. You gain advantage on attack rolls against the creature for 1 minute or until it drops to 0 hit points or falls {@condition unconscious}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Relentless Avenger', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Relentless Avenger' AND class_name = 'Paladin' AND level = 7), 0, 'By 7th level, your supernatural focus helps you close off a foe''s retreat. When you hit a creature with an opportunity attack, you can move up to half your speed immediately after the attack and as part of the same reaction. This movement doesn''t provoke opportunity attacks.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Soul of Vengeance', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Soul of Vengeance' AND class_name = 'Paladin' AND level = 15), 0, 'Starting at 15th level, the authority with which you speak your Vow of Enmity gives you greater power over your foe. When a creature under the effect of your Vow of Enmity makes an attack, you can use your reaction to make a melee weapon attack against that creature if it is within range.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Avenging Angel', 1, 87, 'Paladin', 'PHB', 'Vengeance', 'PHB', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Avenging Angel' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, you can assume the form of an angelic avenger. Using your action, you undergo a transformation. For 1 hour, you gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Avenging Angel' AND class_name = 'Paladin' AND level = 20), 1, 'list', NULL, '["Wings sprout from your back and grant you a flying speed of 60 feet.","You emanate an aura of menace in a 30-foot radius. The first time any enemy creature enters the aura or starts its turn there during a battle, the creature must succeed on a Wisdom saving throw or become {@condition frightened} of you for 1 minute or until it takes any damage. {@action Attack} rolls against the {@condition frightened} creature have advantage."]', '["Wings sprout from your back and grant you a flying speed of 60 feet.","You emanate an aura of menace in a 30-foot radius. The first time any enemy creature enters the aura or starts its turn there during a battle, the creature must succeed on a Wisdom saving throw or become {@condition frightened} of you for 1 minute or until it takes any damage. {@action Attack} rolls against the {@condition frightened} creature have advantage."]', NULL, NULL, NULL, '{"type":"list","items":["Wings sprout from your back and grant you a flying speed of 60 feet.","You emanate an aura of menace in a 30-foot radius. The first time any enemy creature enters the aura or starts its turn there during a battle, the creature must succeed on a Wisdom saving throw or become {@condition frightened} of you for 1 minute or until it takes any damage. {@action Attack} rolls against the {@condition frightened} creature have advantage."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Avenging Angel' AND class_name = 'Paladin' AND level = 20), 2, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of the Crown', null, 132, 'Paladin', 'PHB', 'Crown', 'SCAG', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Crown' AND class_name = 'Paladin' AND level = 3), 0, 'The Oath of the Crown is sworn to the ideals of civilization, be it the spirit of a nation, fealty to a sovereign, or service to a deity of law and rulership. The paladins who swear this oath dedicate themselves to serving society and, in particular, the laws that hold society together. These paladins are the watchful guardians on the walls, standing against the chaotic tides of barbarism that threaten to tear down all that civilization has built, and are commonly known as guardians, exemplars, or sentinels. Often, paladins who swear this oath are members of an order of knighthood in service to a nation or sovereign, and undergo their oath as part of their admission to the order''s ranks.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Crown' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tenets of the Crown|Paladin||Crown|SCAG|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Crown' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oath Spells|Paladin||Crown|SCAG|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Crown' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin||Crown|SCAG|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Champion Challenge', null, 132, 'Paladin', 'PHB', 'Crown', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Champion Challenge' AND class_name = 'Paladin' AND level = 3), 0, 'As a bonus action, you issue a challenge that compels other creatures to do battle with you. Each creature of your choice that you can see within 30 feet of you must make a Wisdom saving throw. On a failed save, a creature can''t willingly move more than 30 feet away from you. This effect ends on the creature if you are {@condition incapacitated} or die or if the creature is more than 30 feet away from you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity', null, 132, 'Paladin', 'PHB', 'Crown', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 0, 'When you take this oath at 3rd level, you gain the following two Channel Divinity options.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Champion Challenge|Paladin||Crown|SCAG|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Turn the Tide|Paladin||Crown|SCAG|3"}');
