INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tenets of the Crown', null, 132, 'Paladin', 'PHB', 'Crown', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Crown' AND class_name = 'Paladin' AND level = 3), 0, 'The tenets of the Oath of the Crown are often set by the sovereign to which their oath is sworn, but generally emphasize the following tenets.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Crown' AND class_name = 'Paladin' AND level = 3), 1, 'entries', NULL, '[{"type":"entries","name":"Law","entries":["The law is paramount. It is the mortar that holds the stones of civilization together, and it must be respected."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Law","entries":["The law is paramount. It is the mortar that holds the stones of civilization together, and it must be respected."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Crown' AND class_name = 'Paladin' AND level = 3), 2, 'entries', NULL, '[{"type":"entries","name":"Loyalty","entries":["Your word is your bond. Without loyalty, oaths and laws are meaningless."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Loyalty","entries":["Your word is your bond. Without loyalty, oaths and laws are meaningless."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Crown' AND class_name = 'Paladin' AND level = 3), 3, 'entries', NULL, '[{"type":"entries","name":"Courage","entries":["You must be willing to do what needs to be done for the sake of order, even in the face of overwhelming odds. If you don''t act, then who will?"]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Courage","entries":["You must be willing to do what needs to be done for the sake of order, even in the face of overwhelming odds. If you don''t act, then who will?"]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Crown' AND class_name = 'Paladin' AND level = 3), 4, 'entries', NULL, '[{"type":"entries","name":"Responsibility","entries":["You must deal with the consequences of your actions, and you are responsible for fulfilling your duties and obligations."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Responsibility","entries":["You must deal with the consequences of your actions, and you are responsible for fulfilling your duties and obligations."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Turn the Tide', null, 132, 'Paladin', 'PHB', 'Crown', 'SCAG', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Turn the Tide' AND class_name = 'Paladin' AND level = 3), 0, 'As a bonus action, you can bolster injured creatures with your Channel Divinity. Each creature of your choice that can hear you within 30 feet of you regains hit points equal to {@dice 1d6} + your Charisma modifier (minimum of 1) if it has no more than half of its hit points.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Divine Allegiance', null, 132, 'Paladin', 'PHB', 'Crown', 'SCAG', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Divine Allegiance' AND class_name = 'Paladin' AND level = 7), 0, 'Starting at 7th level, when a creature within 5 feet of you takes damage, you can use your reaction to magically substitute your own health for that of the target creature, causing that creature not to take the damage. Instead, you take the damage. This damage to you can''t be reduced or prevented in any way.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Unyielding Spirit', null, 132, 'Paladin', 'PHB', 'Crown', 'SCAG', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Unyielding Spirit' AND class_name = 'Paladin' AND level = 15), 0, 'Starting at 15th level, you have advantage on saving throws to avoid becoming {@condition paralyzed} or {@condition stunned}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Exalted Champion', null, 132, 'Paladin', 'PHB', 'Crown', 'SCAG', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Exalted Champion' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, your presence on the field of battle is an inspiration to those dedicated to your cause. You can use your action to gain the following benefits for 1 hour:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Exalted Champion' AND class_name = 'Paladin' AND level = 20), 1, 'list', NULL, '["You have resistance to bludgeoning, piercing, and slashing damage from nonmagical weapons.","Your allies have advantage on death saving throws while within 30 feet of you.","You have advantage on Wisdom saving throws, as do your allies within 30 feet of you."]', '["You have resistance to bludgeoning, piercing, and slashing damage from nonmagical weapons.","Your allies have advantage on death saving throws while within 30 feet of you.","You have advantage on Wisdom saving throws, as do your allies within 30 feet of you."]', NULL, NULL, NULL, '{"type":"list","items":["You have resistance to bludgeoning, piercing, and slashing damage from nonmagical weapons.","Your allies have advantage on death saving throws while within 30 feet of you.","You have advantage on Wisdom saving throws, as do your allies within 30 feet of you."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Exalted Champion' AND class_name = 'Paladin' AND level = 20), 2, 'This effect ends early if you are {@condition incapacitated} or die. Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of Glory', 2, 53, 'Paladin', 'PHB', 'Glory', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of Glory' AND class_name = 'Paladin' AND level = 3), 0, 'Paladins who take the Oath of Glory believe they and their companions are destined to achieve glory through deeds of heroism. They train diligently and encourage their companions so they''re all ready when destiny calls.');
