INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Oath of Glory Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell guiding bolt}, {@spell heroism}"],["5th","{@spell enhance ability}, {@spell magic weapon}"],["9th","{@spell haste}, {@spell protection from energy}"],["13th","{@spell compulsion}, {@spell freedom of movement}"],["17th","{@spell commune}, {@spell flame strike}"]]', '{"type":"table","caption":"Oath of Glory Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell guiding bolt}, {@spell heroism}"],["5th","{@spell enhance ability}, {@spell magic weapon}"],["9th","{@spell haste}, {@spell protection from energy}"],["13th","{@spell compulsion}, {@spell freedom of movement}"],["17th","{@spell commune}, {@spell flame strike}"]]}');
