INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Glory' AND class_name = 'Paladin' AND level = 3), 0, 'The tenets of the Oath of Glory drive a paladin to attempt heroics that might one day shine in legend.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Glory' AND class_name = 'Paladin' AND level = 3), 1, 'entries', 'Actions over Words', '["Strive to be known by glorious deeds, not words."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Actions over Words","entries":["Strive to be known by glorious deeds, not words."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Glory' AND class_name = 'Paladin' AND level = 3), 2, 'entries', 'Challenges Are but Tests', '["Face hardships with courage, and encourage your allies to face them with you."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Challenges Are but Tests","entries":["Face hardships with courage, and encourage your allies to face them with you."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Glory' AND class_name = 'Paladin' AND level = 3), 3, 'entries', 'Hone the Body', '["Like raw stone, your body must be worked so its potential can be realized."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Hone the Body","entries":["Like raw stone, your body must be worked so its potential can be realized."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Glory' AND class_name = 'Paladin' AND level = 3), 4, 'entries', 'Discipline the Soul', '["You must marshal the discipline to overcome failings within yourself that threaten to dim the glory of you and your friends."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Discipline the Soul","entries":["You must marshal the discipline to overcome failings within yourself that threaten to dim the glory of you and your friends."]}');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Glory' AND class_name = 'Paladin' AND level = 3), 'MOT', 29);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Inspiring Smite', 2, 53, 'Paladin', 'PHB', 'Glory', 'TCE', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Inspiring Smite' AND class_name = 'Paladin' AND level = 3), 0, 'Immediately after you deal damage to a creature with your Divine Smite feature, you can use your Channel Divinity as a bonus action and distribute temporary hit points to creatures of your choice within 30 feet of you, which can include you. The total number of temporary hit points equals {@dice 2d8} + your level in this class, divided among the chosen creatures however you like.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Inspiring Smite' AND class_name = 'Paladin' AND level = 3), 'MOT', 29);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Peerless Athlete', 2, 53, 'Paladin', 'PHB', 'Glory', 'TCE', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Peerless Athlete' AND class_name = 'Paladin' AND level = 3), 0, 'As a bonus action, you can use your Channel Divinity to augment your athleticism. For the next 10 minutes, you have advantage on Strength ({@skill Athletics}) and Dexterity ({@skill Acrobatics}) checks; you can carry, push, drag, and lift twice as much weight as normal; and the distance of your long and high jumps increases by 10 feet (this extra distance costs movement as normal).');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Peerless Athlete' AND class_name = 'Paladin' AND level = 3), 'MOT', 29);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of Alacrity', 2, 53, 'Paladin', 'PHB', 'Glory', 'TCE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Alacrity' AND class_name = 'Paladin' AND level = 7), 0, '{@i 7th-level Oath of Glory feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Alacrity' AND class_name = 'Paladin' AND level = 7), 1, 'You emanate an aura that fills you and your companions with supernatural speed, allowing you to race across a battlefield in formation. Your walking speed increases by 10 feet. In addition, if you aren''t {@condition incapacitated}, the walking speed of any ally who starts their turn within 5 feet of you increases by 10 feet until the end of that turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Alacrity' AND class_name = 'Paladin' AND level = 7), 2, 'When you reach 18th level in this class, the range of the aura increases to 10 feet.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Aura of Alacrity' AND class_name = 'Paladin' AND level = 7), 'MOT', 29);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Glorious Defense', 2, 53, 'Paladin', 'PHB', 'Glory', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Glorious Defense' AND class_name = 'Paladin' AND level = 15), 0, '{@i 15th-level Oath of Glory feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Glorious Defense' AND class_name = 'Paladin' AND level = 15), 1, 'You can turn defense into a sudden strike. When you or another creature you can see within 10 feet of you is hit by an attack roll, you can use your reaction to grant a bonus to the target''s AC against that attack, potentially causing it to miss. The bonus equals your Charisma modifier (minimum of +1). If the attack misses, you can make one weapon attack against the attacker as part of this reaction, provided the attacker is within your weapon''s range.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Glorious Defense' AND class_name = 'Paladin' AND level = 15), 2, 'You can use this feature a number of times equal to your Charisma modifier (minimum of once), and you regain all expended uses when you finish a long rest.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Glorious Defense' AND class_name = 'Paladin' AND level = 15), 'MOT', 29);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Living Legend', 2, 53, 'Paladin', 'PHB', 'Glory', 'TCE', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Living Legend' AND class_name = 'Paladin' AND level = 20), 0, '{@i 20th-level Oath of Glory feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Living Legend' AND class_name = 'Paladin' AND level = 20), 1, 'You can empower yourself with the legends—whether true or exaggerated—of your great deeds. As a bonus action, you gain the following benefits for 1 minute:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Living Legend' AND class_name = 'Paladin' AND level = 20), 2, 'list', NULL, '["You are blessed with an otherworldly presence, gaining advantage on all Charisma checks.","Once on each of your turns when you make a weapon attack and miss, you can cause that attack to hit instead.","If you fail a saving throw, you can use your reaction to reroll it. You must use this new roll."]', '["You are blessed with an otherworldly presence, gaining advantage on all Charisma checks.","Once on each of your turns when you make a weapon attack and miss, you can cause that attack to hit instead.","If you fail a saving throw, you can use your reaction to reroll it. You must use this new roll."]', NULL, NULL, NULL, '{"type":"list","items":["You are blessed with an otherworldly presence, gaining advantage on all Charisma checks.","Once on each of your turns when you make a weapon attack and miss, you can cause that attack to hit instead.","If you fail a saving throw, you can use your reaction to reroll it. You must use this new roll."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Living Legend' AND class_name = 'Paladin' AND level = 20), 3, 'Once you use this feature, you can''t use it again until you finish a long rest, unless you expend a 5th-level spell slot to use it again.');
INSERT INTO feature_other_sources (feature_id, source_name, page) VALUES ((SELECT id FROM features WHERE name = 'Living Legend' AND class_name = 'Paladin' AND level = 20), 'MOT', 29);
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of the Watchers', 2, 54, 'Paladin', 'PHB', 'Watchers', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Watchers' AND class_name = 'Paladin' AND level = 3), 0, 'The Oath of the Watchers binds paladins to protect mortal realms from the predations of extraplanar creatures, many of which can lay waste to mortal soldiers. Thus, the Watchers hone their minds, spirits, and bodies to be the ultimate weapons against such threats.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Watchers' AND class_name = 'Paladin' AND level = 3), 1, 'Paladins who follow the Watchers'' oath are ever vigilant in spotting the influence of extraplanar forces, often establishing a network of spies and informants to gather information on suspected cults. To a Watcher, keeping a healthy suspicion and awareness about one''s surroundings is as natural as wearing armor in battle.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Watchers' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tenets of the Watchers|Paladin||Watchers|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Watchers' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oath Spells|Paladin||Watchers|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Watchers' AND class_name = 'Paladin' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin||Watchers|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity', 2, 54, 'Paladin', 'PHB', 'Watchers', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
