INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Oath of the Watchers Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell alarm}, {@spell detect magic}"],["5th","{@spell moonbeam}, {@spell see invisibility}"],["9th","{@spell counterspell}, {@spell nondetection}"],["13th","{@spell aura of purity}, {@spell banishment}"],["17th","{@spell hold monster}, {@spell scrying}"]]', '{"type":"table","caption":"Oath of the Watchers Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell alarm}, {@spell detect magic}"],["5th","{@spell moonbeam}, {@spell see invisibility}"],["9th","{@spell counterspell}, {@spell nondetection}"],["13th","{@spell aura of purity}, {@spell banishment}"],["17th","{@spell hold monster}, {@spell scrying}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tenets of the Watchers', 2, 54, 'Paladin', 'PHB', 'Watchers', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Watchers' AND class_name = 'Paladin' AND level = 3), 0, 'A paladin who assumes the Oath of the Watchers swears to safeguard mortal realms from otherworldly threats.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Watchers' AND class_name = 'Paladin' AND level = 3), 1, 'entries', 'Vigilance', '["The threats you face are cunning, powerful, and subversive. Be ever alert for their corruption."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Vigilance","entries":["The threats you face are cunning, powerful, and subversive. Be ever alert for their corruption."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Watchers' AND class_name = 'Paladin' AND level = 3), 2, 'entries', 'Loyalty', '["Never accept gifts or favors from fiends or those who truck with them. Stay true to your order, your comrades, and your duty."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Loyalty","entries":["Never accept gifts or favors from fiends or those who truck with them. Stay true to your order, your comrades, and your duty."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Watchers' AND class_name = 'Paladin' AND level = 3), 3, 'entries', 'Discipline', '["You are the shield against the endless terrors that lie beyond the stars. Your blade must be forever sharp and your mind keen to survive what lies beyond."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Discipline","entries":["You are the shield against the endless terrors that lie beyond the stars. Your blade must be forever sharp and your mind keen to survive what lies beyond."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Abjure the Extraplanar', 2, 54, 'Paladin', 'PHB', 'Watchers', 'TCE', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Abjure the Extraplanar' AND class_name = 'Paladin' AND level = 3), 0, 'You can use your Channel Divinity to castigate unworldly beings. As an action, you present your holy symbol and each aberration, celestial, elemental, fey, or fiend within 30 feet of you that can hear you must make a Wisdom saving throw. On a failed save, the creature is turned for 1 minute or until it takes damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Abjure the Extraplanar' AND class_name = 'Paladin' AND level = 3), 1, 'A turned creature must spend its turns trying to move as far away from you as it can, and it can''t willingly end its move in a space within 30 feet of you. For its action, it can use only the {@action Dash} action or try to escape from an effect that prevents it from moving. If there''s nowhere to move, the creature can take the {@action Dodge} action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Watcher''s Will', 2, 54, 'Paladin', 'PHB', 'Watchers', 'TCE', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Watcher''s Will' AND class_name = 'Paladin' AND level = 3), 0, 'You can use your Channel Divinity to invest your presence with the warding power of your faith. As an action, you can choose a number of creatures you can see within 30 feet of you, up to a number equal to your Charisma modifier (minimum of one creature). For 1 minute, you and the chosen creatures have advantage on Intelligence, Wisdom, and Charisma saving throws.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of the Sentinel', 2, 54, 'Paladin', 'PHB', 'Watchers', 'TCE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of the Sentinel' AND class_name = 'Paladin' AND level = 7), 0, '{@i 7th-level Oath of the Watchers feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of the Sentinel' AND class_name = 'Paladin' AND level = 7), 1, 'You emit an aura of alertness while you aren''t {@condition incapacitated}. When you and any creatures of your choice within 10 feet of you roll initiative, you all gain a bonus to initiative equal to your proficiency bonus.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of the Sentinel' AND class_name = 'Paladin' AND level = 7), 2, 'At 18th level, the range of this aura increases to 30 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Vigilant Rebuke', 2, 54, 'Paladin', 'PHB', 'Watchers', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vigilant Rebuke' AND class_name = 'Paladin' AND level = 15), 0, '{@i 15th-level Oath of the Watchers feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vigilant Rebuke' AND class_name = 'Paladin' AND level = 15), 1, 'You''ve learned how to chastise anyone who dares wield beguilements against you and your wards. Whenever you or a creature you can see within 30 feet of you succeeds on an Intelligence, a Wisdom, or a Charisma saving throw, you can use your reaction to deal {@damage 2d8} + your Charisma modifier force damage to the creature that forced the saving throw.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mortal Bulwark', 2, 54, 'Paladin', 'PHB', 'Watchers', 'TCE', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mortal Bulwark' AND class_name = 'Paladin' AND level = 20), 0, '{@i 20th-level Oath of the Watchers feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mortal Bulwark' AND class_name = 'Paladin' AND level = 20), 1, 'You manifest a spark of divine power in defense of the mortal realms. As a bonus action, you gain the following benefits for 1 minute:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Mortal Bulwark' AND class_name = 'Paladin' AND level = 20), 2, 'list', NULL, '["You gain {@sense truesight} with a range of 120 feet.","You have advantage on attack rolls against aberrations, celestials, elementals, fey, and fiends.","When you hit a creature with an attack roll and deal damage to it, you can also force it to make a Charisma saving throw against your spell save DC. On a failed save, the creature is magically banished to its native plane of existence if it''s currently not there. On a successful save, the creature can''t be banished by this feature for 24 hours."]', '["You gain {@sense truesight} with a range of 120 feet.","You have advantage on attack rolls against aberrations, celestials, elementals, fey, and fiends.","When you hit a creature with an attack roll and deal damage to it, you can also force it to make a Charisma saving throw against your spell save DC. On a failed save, the creature is magically banished to its native plane of existence if it''s currently not there. On a successful save, the creature can''t be banished by this feature for 24 hours."]', NULL, NULL, NULL, '{"type":"list","items":["You gain {@sense truesight} with a range of 120 feet.","You have advantage on attack rolls against aberrations, celestials, elementals, fey, and fiends.","When you hit a creature with an attack roll and deal damage to it, you can also force it to make a Charisma saving throw against your spell save DC. On a failed save, the creature is magically banished to its native plane of existence if it''s currently not there. On a successful save, the creature can''t be banished by this feature for 24 hours."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mortal Bulwark' AND class_name = 'Paladin' AND level = 20), 3, 'Once you use this bonus action, you can''t use it again until you finish a long rest, unless you expend a 5th-level spell slot to use it again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of the Open Sea', null, 174, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Open Sea' AND class_name = 'Paladin' AND level = 3), 0, 'The Oath of the Open Sea calls to seafaring warriors, swashbuckling sailors, and traveling guardians who seek the thrill of a limitless horizon. Driven to search for adventure and mystery across and beneath every endless oceanic expanse, paladins who swear this oath stand against those who would deny liberty to like-minded travelers, rooting out tyranny and corruption wherever it is found.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Open Sea' AND class_name = 'Paladin' AND level = 3), 1, 'Such guardians believe in the natural beauty of the sea, often making offerings and prayers to entities or deities such as the {@deity The Wildmother|Exandria|TDCSR|Wildmother} or the {@deity The Stormlord|Exandria|TDCSR|Stormlord} who influence safe passage. At the same time, they answer the call to hunt the monstrous creatures that too often bloody the waters with wanton violence and ill intent, and which terrorize the folk of the sea and shore.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Open Sea' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tenets of the Open Sea|Paladin|PHB|Open Sea|TDCSR|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Open Sea' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oath Spells|Paladin|PHB|Open Sea|TDCSR|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of the Open Sea' AND class_name = 'Paladin' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin|PHB|Open Sea|TDCSR|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity', null, 175, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 0, 'When you take this oath at 3rd level, you gain the following two Channel Divinity options.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Marine Layer|Paladin||Open Sea|TDCSR|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Fury of the Tides|Paladin||Open Sea|TDCSR|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 3, 'inset', 'Rules Tip: Visibility', '["Fog and other effects can obscure vision for you, your enemies, and your allies. When you heavily obscure an area using your Marine Layer Channel Divinity option, all creatures within the area have their vision completely blocked, and creatures outside the area can''t see in. Creatures that can''t see automatically fail {@quickref ability checks|PHB|2|0} that require sight. Also, attack rolls against creatures that can''t see have {@quickref Advantage and Disadvantage|PHB|2|0|advantage}, and their own attack rolls have {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage}.","Creatures in a lightly obscured area have {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage} only on Wisdom ({@skill Perception}) checks that rely on sight. The rules for when your vision is obscured are described completely in the fifth edition core rules."]', NULL, NULL, NULL, NULL, '{"type":"inset","name":"Rules Tip: Visibility","page":175,"entries":["Fog and other effects can obscure vision for you, your enemies, and your allies. When you heavily obscure an area using your Marine Layer Channel Divinity option, all creatures within the area have their vision completely blocked, and creatures outside the area can''t see in. Creatures that can''t see automatically fail {@quickref ability checks|PHB|2|0} that require sight. Also, attack rolls against creatures that can''t see have {@quickref Advantage and Disadvantage|PHB|2|0|advantage}, and their own attack rolls have {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage}.","Creatures in a lightly obscured area have {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage} only on Wisdom ({@skill Perception}) checks that rely on sight. The rules for when your vision is obscured are described completely in the fifth edition core rules."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fury of the Tides', null, 175, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fury of the Tides' AND class_name = 'Paladin' AND level = 3), 0, 'As a bonus action, you channel the powerful might of the waves to bolster your attacks for 1 minute. Once per turn for the duration, when you hit a creature with a weapon attack, you can choose to push the target 10 feet away from you. If pushed into an obstacle or another creature, the target takes bludgeoning damage equal to your Charisma modifier.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Marine Layer', null, 175, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Marine Layer' AND class_name = 'Paladin' AND level = 3), 0, 'As an action, you channel the sea to create a thick cloud of fog that surrounds you for 20 feet in all directions. The fog moves with you, remaining centered on you and making its area heavily obscured. You and each creature within 5 feet of you instead treat the area as lightly obscured. This fog lasts for 10 minutes, spreads around corners, and cannot be dispersed unless you choose to end this effect (no action required).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath Spells', null, 175, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 0, 'You gain oath spells at the paladin levels listed in the Oath of the Open Sea Spells table. Spells marked with an asterisk are new spells described on the {@filter spells page|spells|source=TDCSR}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Oath of the Open Sea Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell create or destroy water}, {@spell expeditious retreat}"],["5th","{@spell augury}, {@spell misty step}"],["9th","{@spell call lightning}, {@spell freedom of the waves|TDCSR}*"],["13th","{@spell control water}, {@spell freedom of movement}"],["17th","{@spell commune with nature}, {@spell freedom of the winds|TDCSR}*"]]', '{"type":"table","caption":"Oath of the Open Sea Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell create or destroy water}, {@spell expeditious retreat}"],["5th","{@spell augury}, {@spell misty step}"],["9th","{@spell call lightning}, {@spell freedom of the waves|TDCSR}*"],["13th","{@spell control water}, {@spell freedom of movement}"],["17th","{@spell commune with nature}, {@spell freedom of the winds|TDCSR}*"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tenets of the Open Sea', null, 174, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Open Sea' AND class_name = 'Paladin' AND level = 3), 0, 'Freedom can be a selfless virtue or a selfish want. For paladins who swear the Oath of the Open Sea, freedom is the highest calling, and a gift to be granted to all.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Open Sea' AND class_name = 'Paladin' AND level = 3), 1, 'entries', 'No Greater Life than a Life Lived Free', '["One should be free to chart their own path without oppression. Those who would exert their power to dominate others shall be smote."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"No Greater Life than a Life Lived Free","page":175,"entries":["One should be free to chart their own path without oppression. Those who would exert their power to dominate others shall be smote."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Open Sea' AND class_name = 'Paladin' AND level = 3), 2, 'entries', 'Trust the Skies', '["The guidance of a strong breeze. The rumbling warnings of a coming storm. Nature is a source of portent and council that should be heeded."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Trust the Skies","page":175,"entries":["The guidance of a strong breeze. The rumbling warnings of a coming storm. Nature is a source of portent and council that should be heeded."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Open Sea' AND class_name = 'Paladin' AND level = 3), 3, 'entries', 'Adapt Like the Water', '["The waters of the ocean can shift around any obstacle—or become the most impassable obstacle of all. They carve away the land to reveal the secrets of the past, or swallow the truth and hide it forever. To embrace this fluidity is to be ready for any challenge."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Adapt Like the Water","page":175,"entries":["The waters of the ocean can shift around any obstacle—or become the most impassable obstacle of all. They carve away the land to reveal the secrets of the past, or swallow the truth and hide it forever. To embrace this fluidity is to be ready for any challenge."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of the Open Sea' AND class_name = 'Paladin' AND level = 3), 4, 'entries', 'Explore the Uncharted', '["The world is filled with mystery. Through the pursuit of enigmatic ends, one can uncover those who hide their foul deeds, and find the path to becoming something great."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Explore the Uncharted","page":175,"entries":["The world is filled with mystery. Through the pursuit of enigmatic ends, one can uncover those who hide their foul deeds, and find the path to becoming something great."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of Liberation', null, 175, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Liberation' AND class_name = 'Paladin' AND level = 7), 0, 'Starting at 7th level, you fill nearby creatures with the energy of movement. While you''re not {@condition incapacitated}, you and creatures of your choice within 10 feet of you cannot be {@condition grappled} or {@condition restrained}, and {@quickref Underwater Combat|PHB|3|0|ignore penalties on movement and attacks while underwater}. Creatures that are already {@condition grappled} or {@condition restrained} when they enter the aura can spend 5 feet of movement to automatically escape unless they are bound by magic restraints.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Stormy Waters', null, 175, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Stormy Waters' AND class_name = 'Paladin' AND level = 15), 0, 'At 15th level, you can call on the force of crashing waters as a reaction whenever a creature moves into or out of your reach. The creature takes {@damage 1d12} bludgeoning damage and must succeed on a Strength {@quickref saving throws|PHB|2|1|saving throw} against your spell save DC or be knocked {@condition prone}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of Liberation (18th Level)', null, 175, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 18, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Liberation (18th Level)' AND class_name = 'Paladin' AND level = 18), 0, 'When you reach 18th level in this class, the aura affects creatures within 30 feet of you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mythic Swashbuckler', null, 175, 'Paladin', 'PHB', 'Open Sea', 'TDCSR', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mythic Swashbuckler' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, you learn to channel the spirits of historic sea captains to briefly become a paragon of heroic adventure. As an action, you embrace these spirits of the sea to gain the following benefits for 1 minute:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Mythic Swashbuckler' AND class_name = 'Paladin' AND level = 20), 1, 'list', NULL, '["You have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Strength ({@skill Athletics}) checks and you gain a climbing speed equal to your walking speed. If you already have a climbing speed, it is doubled.","If you are within 5 feet of a creature and no other creatures are within 5 feet of you, you have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on attack rolls against that creature.","You can take the {@action Dash} or {@action Disengage} action as a bonus action.","You have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Dexterity checks and Dexterity {@quickref saving throws|PHB|2|1} against effects you can see."]', '["You have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Strength ({@skill Athletics}) checks and you gain a climbing speed equal to your walking speed. If you already have a climbing speed, it is doubled.","If you are within 5 feet of a creature and no other creatures are within 5 feet of you, you have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on attack rolls against that creature.","You can take the {@action Dash} or {@action Disengage} action as a bonus action.","You have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Dexterity checks and Dexterity {@quickref saving throws|PHB|2|1} against effects you can see."]', NULL, NULL, NULL, '{"type":"list","items":["You have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Strength ({@skill Athletics}) checks and you gain a climbing speed equal to your walking speed. If you already have a climbing speed, it is doubled.","If you are within 5 feet of a creature and no other creatures are within 5 feet of you, you have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on attack rolls against that creature.","You can take the {@action Dash} or {@action Disengage} action as a bonus action.","You have {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on Dexterity checks and Dexterity {@quickref saving throws|PHB|2|1} against effects you can see."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mythic Swashbuckler' AND class_name = 'Paladin' AND level = 20), 2, 'Once you use this feature, you can''t use it again until you finish a {@quickref resting|PHB|2|0|long rest}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of Conquest', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of Conquest' AND class_name = 'Paladin' AND level = 3), 0, 'The Oath of Conquest calls to paladins who seek glory in battle and the subjugation of their enemies. It isn''t enough for these paladins to establish order. They must crush the forces of chaos. Sometimes called knight tyrants or iron mongers, those who swear this oath gather into grim orders that serve gods or philosophies of war and well-ordered might.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of Conquest' AND class_name = 'Paladin' AND level = 3), 1, 'Some of these paladins go so far as to consort with the powers of the Nine Hells, valuing the rule of law over the balm of mercy. The archdevil Bel, warlord of Avernus, counts many of these paladins—called hell knights—as his most ardent supporters. Hell knights cover their armor with trophies taken from fallen enemies, a grim warning to any who dare oppose them and the decrees of their lords. These knights are often most fiercely resisted by other paladins of this oath, who believe that the hell knights have wandered too far into darkness.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Conquest' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tenets of Conquest|Paladin||Conquest|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Conquest' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oath Spells|Paladin||Conquest|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Conquest' AND class_name = 'Paladin' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin||Conquest|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 0, 'When you take this oath at 3rd level, you gain the following two Channel Divinity options.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Conquering Presence|Paladin||Conquest|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Guided Strike|Paladin||Conquest|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Conquering Presence', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Conquering Presence' AND class_name = 'Paladin' AND level = 3), 0, 'You can use your Channel Divinity to exude a terrifying presence. As an action, you force each creature of your choice that you can see within 30 feet of you to make a Wisdom saving throw. On a failed save, a creature becomes {@condition frightened} of you for 1 minute. The {@condition frightened} creature can repeat this saving throw at the end of each of its turns, ending the effect on itself on a success.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Guided Strike', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Guided Strike' AND class_name = 'Paladin' AND level = 3), 0, 'You can use your Channel Divinity to strike with supernatural accuracy. When you make an attack roll, you can use your Channel Divinity to gain a +10 bonus to the roll. You make this choice after you see the roll, but before the DM says whether the attack hits or misses.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath Spells', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 0, 'You gain oath spells at the paladin levels listed.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Oath of Conquest Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell armor of Agathys}, {@spell command}"],["5th","{@spell hold person}, {@spell spiritual weapon}"],["9th","{@spell bestow curse}, {@spell fear}"],["13th","{@spell dominate beast}, {@spell stoneskin}"],["17th","{@spell cloudkill}, {@spell dominate person}"]]', '{"type":"table","caption":"Oath of Conquest Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell armor of Agathys}, {@spell command}"],["5th","{@spell hold person}, {@spell spiritual weapon}"],["9th","{@spell bestow curse}, {@spell fear}"],["13th","{@spell dominate beast}, {@spell stoneskin}"],["17th","{@spell cloudkill}, {@spell dominate person}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tenets of Conquest', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Conquest' AND class_name = 'Paladin' AND level = 3), 0, 'A paladin who takes this oath has the tenets of conquest seared on the upper arm.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Conquest' AND class_name = 'Paladin' AND level = 3), 1, 'entries', NULL, '[{"type":"entries","name":"Douse the Flame of Hope","entries":["It is not enough to merely defeat an enemy in battle. Your victory must be so overwhelming that your enemies'' will to fight is shattered forever. A blade can end a life. Fear can end an empire."]},{"type":"entries","name":"Rule with an Iron Fist","entries":["Once you have conquered, tolerate no dissent. Your word is law. Those who obey it shall be favored. Those who defy it shall be punished as an example to all who might follow."]},{"type":"entries","name":"Strength Above All","entries":["You shall rule until a stronger one arises. Then you must grow mightier and meet the challenge, or fall to your own ruin."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Douse the Flame of Hope","entries":["It is not enough to merely defeat an enemy in battle. Your victory must be so overwhelming that your enemies'' will to fight is shattered forever. A blade can end a life. Fear can end an empire."]},{"type":"entries","name":"Rule with an Iron Fist","entries":["Once you have conquered, tolerate no dissent. Your word is law. Those who obey it shall be favored. Those who defy it shall be punished as an example to all who might follow."]},{"type":"entries","name":"Strength Above All","entries":["You shall rule until a stronger one arises. Then you must grow mightier and meet the challenge, or fall to your own ruin."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of Conquest', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Conquest' AND class_name = 'Paladin' AND level = 7), 0, 'Starting at 7th level, you constantly emanate a menacing aura while you''re not {@condition incapacitated}. The aura extends 10 feet from you in every direction, but not through {@quickref Cover||3||total cover}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Conquest' AND class_name = 'Paladin' AND level = 7), 1, 'If a creature is {@condition frightened} of you, its speed is reduced to 0 while in the aura, and that creature takes psychic damage equal to half your paladin level if it starts its turn there.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of Conquest' AND class_name = 'Paladin' AND level = 7), 2, 'At 18th level, the range of this aura increases to 30 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Scornful Rebuke', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Scornful Rebuke' AND class_name = 'Paladin' AND level = 15), 0, 'Starting at 15th level, those who dare to strike you are psychically punished for their audacity. Whenever a creature hits you with an attack, that creature takes psychic damage equal to your Charisma modifier (minimum of 1) if you''re not {@condition incapacitated}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Invincible Conqueror', 3, 37, 'Paladin', 'PHB', 'Conquest', 'XGE', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Invincible Conqueror' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, you gain the ability to harness extraordinary martial prowess. As an action, you can magically become an avatar of conquest, gaining the following benefits for 1 minute:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Invincible Conqueror' AND class_name = 'Paladin' AND level = 20), 1, 'list', NULL, '["You have resistance to all damage.","When you take the {@action Attack} action on your turn, you can make one additional attack as part of that action.","Your melee weapon attacks score a critical hit on a roll of 19 or 20 on the {@dice d20}."]', '["You have resistance to all damage.","When you take the {@action Attack} action on your turn, you can make one additional attack as part of that action.","Your melee weapon attacks score a critical hit on a roll of 19 or 20 on the {@dice d20}."]', NULL, NULL, NULL, '{"type":"list","items":["You have resistance to all damage.","When you take the {@action Attack} action on your turn, you can make one additional attack as part of that action.","Your melee weapon attacks score a critical hit on a roll of 19 or 20 on the {@dice d20}."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Invincible Conqueror' AND class_name = 'Paladin' AND level = 20), 2, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath of Redemption', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of Redemption' AND class_name = 'Paladin' AND level = 3), 0, 'The Oath of Redemption sets a paladin on a difficult path, one that requires a holy warrior to use violence only as a last resort. Paladins who dedicate themselves to this oath believe that any person can be redeemed and that the path of benevolence and justice is one that anyone can walk. These paladins face evil creatures in the hope of turning their foes to the light, and they slay their enemies only when such a deed will clearly save other lives. Paladins who follow this path are known as redeemers.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath of Redemption' AND class_name = 'Paladin' AND level = 3), 1, 'While redeemers are idealists, they are no fools. Redeemers know that undead, demons, devils, and other supernatural threats can be inherently evil. Against such foes, paladins who swear this oath bring the full wrath of their weapons and spells to bear. Yet the redeemers still pray that, one day, even creatures of wickedness will invite their own redemption.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Redemption' AND class_name = 'Paladin' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tenets of Redemption|Paladin||Redemption|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Redemption' AND class_name = 'Paladin' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Oath Spells|Paladin||Redemption|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath of Redemption' AND class_name = 'Paladin' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Channel Divinity|Paladin||Redemption|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Channel Divinity', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Channel Divinity' AND class_name = 'Paladin' AND level = 3), 0, 'When you take this oath at 3rd level, you gain the following two Channel Divinity options.');
