INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Emissary of Peace', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Emissary of Peace' AND class_name = 'Paladin' AND level = 3), 0, 'You can use your Channel Divinity to augment your presence with divine power. As a bonus action, you grant yourself a +5 bonus to Charisma ({@skill Persuasion}) checks for the next 10 minutes.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oath Spells', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 0, 'You gain oath spells at the paladin levels listed.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Oath Spells' AND class_name = 'Paladin' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Oath of Redemption Spells', '["Paladin Level","Spells"]', '[["3rd","{@spell sanctuary}, {@spell sleep}"],["5th","{@spell calm emotions}, {@spell hold person}"],["9th","{@spell counterspell}, {@spell hypnotic pattern}"],["13th","{@spell Otiluke''s resilient sphere}, {@spell stoneskin}"],["17th","{@spell hold monster}, {@spell wall of force}"]]', '{"type":"table","caption":"Oath of Redemption Spells","colLabels":["Paladin Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell sanctuary}, {@spell sleep}"],["5th","{@spell calm emotions}, {@spell hold person}"],["9th","{@spell counterspell}, {@spell hypnotic pattern}"],["13th","{@spell Otiluke''s resilient sphere}, {@spell stoneskin}"],["17th","{@spell hold monster}, {@spell wall of force}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Rebuke the Violent', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Rebuke the Violent' AND class_name = 'Paladin' AND level = 3), 0, 'You can use your Channel Divinity to rebuke those who use violence. Immediately after an attacker within 30 feet of you deals damage with an attack against a creature other than you, you can use your reaction to force the attacker to make a Wisdom saving throw. On a failed save, the attacker takes radiant damage equal to the damage it just dealt. On a successful save, it takes half as much damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tenets of Redemption', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Redemption' AND class_name = 'Paladin' AND level = 3), 0, 'The tenets of the Oath of Redemption hold a paladin to a high standard of peace and justice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Redemption' AND class_name = 'Paladin' AND level = 3), 1, 'entries', NULL, '[{"type":"entries","name":"Peace","entries":["Violence is a weapon of last resort. Diplomacy and understanding are the paths to long-lasting peace."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Peace","entries":["Violence is a weapon of last resort. Diplomacy and understanding are the paths to long-lasting peace."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Redemption' AND class_name = 'Paladin' AND level = 3), 2, 'entries', NULL, '[{"type":"entries","name":"Innocence","entries":["All people begin life in an innocent state, and it is their environment or the influence of dark forces that drives them to evil. By setting the proper example, and working to heal the wounds of a deeply flawed world, you can set anyone on a righteous path."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Innocence","entries":["All people begin life in an innocent state, and it is their environment or the influence of dark forces that drives them to evil. By setting the proper example, and working to heal the wounds of a deeply flawed world, you can set anyone on a righteous path."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Redemption' AND class_name = 'Paladin' AND level = 3), 3, 'entries', NULL, '[{"type":"entries","name":"Patience","entries":["Change takes time. Those who have walked the path of the wicked must be given reminders to keep them honest and true. Once you have planted the seed of righteousness in a creature, you must work day after day to allow that seed to survive and flourish."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Patience","entries":["Change takes time. Those who have walked the path of the wicked must be given reminders to keep them honest and true. Once you have planted the seed of righteousness in a creature, you must work day after day to allow that seed to survive and flourish."]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Tenets of Redemption' AND class_name = 'Paladin' AND level = 3), 4, 'entries', NULL, '[{"type":"entries","name":"Wisdom","entries":["Your heart and mind must stay clear, for eventually you will be forced to admit defeat. While every creature can be redeemed, some are so far along the path of evil that you have no choice but to end their lives for the greater good. Any such action must be carefully weighed and the consequences fully understood, but once you have made the decision, follow through with it knowing your path is just."]}]', NULL, NULL, NULL, NULL, '{"type":"entries","entries":[{"type":"entries","name":"Wisdom","entries":["Your heart and mind must stay clear, for eventually you will be forced to admit defeat. While every creature can be redeemed, some are so far along the path of evil that you have no choice but to end their lives for the greater good. Any such action must be carefully weighed and the consequences fully understood, but once you have made the decision, follow through with it knowing your path is just."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Aura of the Guardian', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of the Guardian' AND class_name = 'Paladin' AND level = 7), 0, 'Starting at 7th level, you can shield others from harm at the cost of your own health. When a creature within 10 feet of you takes damage, you can use your reaction to magically take that damage, instead of that creature taking it. This feature doesn''t transfer any other effects that might accompany the damage, and this damage can''t be reduced in any way.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Aura of the Guardian' AND class_name = 'Paladin' AND level = 7), 1, 'At 18th level, the range of this aura increases to 30 feet.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Protective Spirit', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Protective Spirit' AND class_name = 'Paladin' AND level = 15), 0, 'Starting at 15th level, a holy presence mends your wounds in battle. You regain hit points equal to {@dice 1d6} + half your paladin level if you end your turn in combat with fewer than half of your hit points remaining and you aren''t {@condition incapacitated}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Emissary of Redemption', 3, 38, 'Paladin', 'PHB', 'Redemption', 'XGE', 20, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Emissary of Redemption' AND class_name = 'Paladin' AND level = 20), 0, 'At 20th level, you become an avatar of peace, which gives you two benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Emissary of Redemption' AND class_name = 'Paladin' AND level = 20), 1, 'list', NULL, '["You have resistance to all damage dealt by other creatures (their attacks, spells, and other effects).","Whenever a creature hits you with an attack, it takes radiant damage equal to half the damage you take from the attack."]', '["You have resistance to all damage dealt by other creatures (their attacks, spells, and other effects).","Whenever a creature hits you with an attack, it takes radiant damage equal to half the damage you take from the attack."]', NULL, NULL, NULL, '{"type":"list","items":["You have resistance to all damage dealt by other creatures (their attacks, spells, and other effects).","Whenever a creature hits you with an attack, it takes radiant damage equal to half the damage you take from the attack."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Emissary of Redemption' AND class_name = 'Paladin' AND level = 20), 2, 'If you attack a creature, cast a spell on it, or deal damage to it by any means but this feature, neither benefit works against that creature until you finish a long rest.');
