INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Deft Explorer', 2, 56, 'Ranger', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Deft Explorer' AND class_name = 'Ranger' AND level = 1), 0, '{@i 1st-level ranger {@variantrule optional class features|tce|optional feature}, which replaces the Natural Explorer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Deft Explorer' AND class_name = 'Ranger' AND level = 1), 1, 'You are an unsurpassed explorer and survivor, both in the wilderness and in dealing with others on your travels. You gain the Canny benefit below, and you gain an additional benefit below when you reach 6th level and 10th level in this class.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Deft Explorer' AND class_name = 'Ranger' AND level = 1), 2, 'entries', 'Canny', '["Choose one of your skill proficiencies. Your proficiency bonus is doubled for any ability check you make that uses the chosen skill.","You can also speak, read, and write two additional languages of your choice."]', NULL, NULL, NULL, NULL, '{"name":"Canny","type":"entries","entries":["Choose one of your skill proficiencies. Your proficiency bonus is doubled for any ability check you make that uses the chosen skill.","You can also speak, read, and write two additional languages of your choice."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Favored Enemy', 1, 89, 'Ranger', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Enemy' AND class_name = 'Ranger' AND level = 1), 0, 'Beginning at 1st level, you have significant experience studying, tracking, hunting, and even talking to a certain type of enemy.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Enemy' AND class_name = 'Ranger' AND level = 1), 1, 'Choose a type of favored enemy: {@filter aberrations|bestiary|type=aberration}, {@filter beasts|bestiary|type=beast}, {@filter celestials|bestiary|type=celestial}, {@filter constructs|bestiary|type=construct}, {@filter dragons|bestiary|type=dragon}, {@filter elementals|bestiary|type=elemental}, {@filter fey|bestiary|type=fey}, {@filter fiends|bestiary|type=fiend}, {@filter giants|bestiary|type=giant}, {@filter monstrosities|bestiary|type=monstrosity}, {@filter oozes|bestiary|type=ooze}, {@filter plants|bestiary|type=plant}, or {@filter undead|bestiary|type=undead}. Alternatively, you can select two races of {@filter humanoid|bestiary|type=humanoid} (such as {@creature gnoll||gnolls} and {@creature orc||orcs}) as favored enemies.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Enemy' AND class_name = 'Ranger' AND level = 1), 2, 'You have advantage on Wisdom ({@skill Survival}) checks to track your favored enemies, as well as on Intelligence checks to recall information about them.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Enemy' AND class_name = 'Ranger' AND level = 1), 3, 'When you gain this feature, you also learn one language of your choice that is spoken by your favored enemies, if they speak one at all.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Enemy' AND class_name = 'Ranger' AND level = 1), 4, 'You choose one additional favored enemy, as well as an associated language, at 6th and 14th level. As you gain levels, your choices should reflect the types of monsters you have encountered on your adventures.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Favored Foe', 2, 56, 'Ranger', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Foe' AND class_name = 'Ranger' AND level = 1), 0, '{@i 1st-level ranger {@variantrule optional class features|tce|optional feature}, which replaces the Favored Enemy feature and works with the Foe Slayer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Foe' AND class_name = 'Ranger' AND level = 1), 1, 'When you hit a creature with an attack roll, you can call on your mystical bond with nature to mark the target as your favored enemy for 1 minute or until you lose your {@status concentration} (as if you were {@status concentration||concentrating} on a spell).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Foe' AND class_name = 'Ranger' AND level = 1), 2, 'The first time on each of your turns that you hit the favored enemy and deal damage to it, including when you mark it, you can increase that damage by {@damage 1d4}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Foe' AND class_name = 'Ranger' AND level = 1), 3, 'You can use this feature to mark a favored enemy a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Foe' AND class_name = 'Ranger' AND level = 1), 4, 'This feature''s extra damage increases when you reach certain levels in this class: to {@damage 1d6} at 6th level and to {@damage 1d8} at 14th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Natural Explorer', 1, 89, 'Ranger', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Natural Explorer' AND class_name = 'Ranger' AND level = 1), 0, 'You are particularly familiar with one type of natural environment and are adept at traveling and surviving in such regions. Choose one type of favored terrain: arctic, coast, desert, forest, grassland, mountain, swamp, or the Underdark. When you make an Intelligence or Wisdom check related to your favored terrain, your proficiency bonus is doubled if you are using a skill that you''re proficient in.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Natural Explorer' AND class_name = 'Ranger' AND level = 1), 1, 'While traveling for an hour or more in your favored terrain, you gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Natural Explorer' AND class_name = 'Ranger' AND level = 1), 2, 'list', NULL, '["Difficult terrain doesn''t slow your group''s travel.","Your group can''t become lost except by magical means.","Even when you are engaged in another activity while traveling (such as foraging, navigating, or tracking), you remain alert to danger.","If you are traveling alone, you can move stealthily at a normal pace.","When you forage, you find twice as much food as you normally would.","While tracking other creatures, you also learn their exact number, their sizes, and how long ago they passed through the area."]', '["Difficult terrain doesn''t slow your group''s travel.","Your group can''t become lost except by magical means.","Even when you are engaged in another activity while traveling (such as foraging, navigating, or tracking), you remain alert to danger.","If you are traveling alone, you can move stealthily at a normal pace.","When you forage, you find twice as much food as you normally would.","While tracking other creatures, you also learn their exact number, their sizes, and how long ago they passed through the area."]', NULL, NULL, NULL, '{"type":"list","items":["Difficult terrain doesn''t slow your group''s travel.","Your group can''t become lost except by magical means.","Even when you are engaged in another activity while traveling (such as foraging, navigating, or tracking), you remain alert to danger.","If you are traveling alone, you can move stealthily at a normal pace.","When you forage, you find twice as much food as you normally would.","While tracking other creatures, you also learn their exact number, their sizes, and how long ago they passed through the area."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Natural Explorer' AND class_name = 'Ranger' AND level = 1), 3, 'You choose additional favored terrain types at 6th and 10th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fighting Style', 1, 89, 'Ranger', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fighting Style' AND class_name = 'Ranger' AND level = 2), 0, 'At 2nd level, you adopt a particular style of fighting as your specialty. Choose one of the following options. You can''t take a Fighting Style option more than once, even if you later get to choose again.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Fighting Style' AND class_name = 'Ranger' AND level = 2), 1, 'options', NULL, '[{"type":"refOptionalfeature","optionalfeature":"Archery"},{"type":"refOptionalfeature","optionalfeature":"Defense"},{"type":"refOptionalfeature","optionalfeature":"Dueling"},{"type":"refOptionalfeature","optionalfeature":"Two-Weapon Fighting"},{"type":"refOptionalfeature","optionalfeature":"Blind Fighting|TCE"},{"type":"refOptionalfeature","optionalfeature":"Druidic Warrior|TCE"},{"type":"refOptionalfeature","optionalfeature":"Thrown Weapon Fighting|TCE"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refOptionalfeature","optionalfeature":"Archery"},{"type":"refOptionalfeature","optionalfeature":"Defense"},{"type":"refOptionalfeature","optionalfeature":"Dueling"},{"type":"refOptionalfeature","optionalfeature":"Two-Weapon Fighting"},{"type":"refOptionalfeature","optionalfeature":"Blind Fighting|TCE"},{"type":"refOptionalfeature","optionalfeature":"Druidic Warrior|TCE"},{"type":"refOptionalfeature","optionalfeature":"Thrown Weapon Fighting|TCE"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spellcasting', 1, 89, 'Ranger', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Ranger' AND level = 2), 0, 'By the time you reach 2nd level, you have learned to use the magical essence of nature to cast spells, much as a druid does. See {@book chapter 10|PHB|10} for the general rules of spellcasting and {@book chapter 11|PHB|11} for the {@filter ranger spell list|spells|class=ranger}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Ranger' AND level = 2), 1, 'entries', 'Spell Slots', '["The Ranger table shows how many spell slots you have to cast your {@filter ranger spells|spells|class=ranger} of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","For example, if you know the 1st-level spell {@spell animal friendship} and have a 1st-level and a 2nd-level spell slot available, you can cast {@spell animal friendship} using either slot."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spell Slots","entries":["The Ranger table shows how many spell slots you have to cast your {@filter ranger spells|spells|class=ranger} of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","For example, if you know the 1st-level spell {@spell animal friendship} and have a 1st-level and a 2nd-level spell slot available, you can cast {@spell animal friendship} using either slot."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Ranger' AND level = 2), 2, 'entries', 'Spells Known of 1st Level and Higher', '["You know two 1st-level spells of your choice from the ranger spell list.","The Spells Known column of the Ranger table shows when you learn more ranger spells of your choice. Each of these spells must be of a level for which you have spell slots. For instance, when you reach 5th level in this class, you can learn one new spell of 1st or 2nd level.","Additionally, when you gain a level in this class, you can choose one of the ranger spells you know and replace it with another spell from the ranger spell list, which also must be of a level for which you have spell slots."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spells Known of 1st Level and Higher","entries":["You know two 1st-level spells of your choice from the ranger spell list.","The Spells Known column of the Ranger table shows when you learn more ranger spells of your choice. Each of these spells must be of a level for which you have spell slots. For instance, when you reach 5th level in this class, you can learn one new spell of 1st or 2nd level.","Additionally, when you gain a level in this class, you can choose one of the ranger spells you know and replace it with another spell from the ranger spell list, which also must be of a level for which you have spell slots."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Ranger' AND level = 2), 3, 'entries', 'Spellcasting Ability', '["Wisdom is your spellcasting ability for your ranger spells, since your magic draws on your attunement to nature. You use your Wisdom whenever a spell refers to your spellcasting ability. In addition, you use your Wisdom modifier when setting the saving throw DC for a ranger spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["wis"]},{"type":"abilityAttackMod","name":"Spell","attributes":["wis"]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Ability","entries":["Wisdom is your spellcasting ability for your ranger spells, since your magic draws on your attunement to nature. You use your Wisdom whenever a spell refers to your spellcasting ability. In addition, you use your Wisdom modifier when setting the saving throw DC for a ranger spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["wis"]},{"type":"abilityAttackMod","name":"Spell","attributes":["wis"]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spellcasting Focus', 2, 56, 'Ranger', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting Focus' AND class_name = 'Ranger' AND level = 2), 0, '{@i 2nd-level ranger {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting Focus' AND class_name = 'Ranger' AND level = 2), 1, 'You can use a {@item druidic focus|phb} as a spellcasting focus for your ranger spells. A druidic focus might be a sprig of mistletoe or holly, a wand or rod made of yew or another special wood, a staff drawn whole from a living tree, or an object incorporating feathers, fur, bones, and teeth from sacred animals.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Primal Awareness', 2, 56, 'Ranger', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Awareness' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level ranger {@variantrule optional class features|tce|optional feature}, which replaces the Primeval Awareness feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Awareness' AND class_name = 'Ranger' AND level = 3), 1, 'You can focus your awareness through the interconnections of nature: you learn additional spells when you reach certain levels in this class if you don''t already know them, as shown in the Primal Awareness Spells table. These spells don''t count against the number of ranger spells you know.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Primal Awareness' AND class_name = 'Ranger' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Primal Awareness Spells', '["Ranger Level","Spell"]', '[["3rd","{@spell speak with animals}"],["5th","{@spell beast sense}"],["9th","{@spell speak with plants}"],["13th","{@spell locate creature}"],["17th","{@spell commune with nature}"]]', '{"type":"table","caption":"Primal Awareness Spells","colLabels":["Ranger Level","Spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell speak with animals}"],["5th","{@spell beast sense}"],["9th","{@spell speak with plants}"],["13th","{@spell locate creature}"],["17th","{@spell commune with nature}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Awareness' AND class_name = 'Ranger' AND level = 3), 3, 'You can cast each of these spells once without expending a spell slot. Once you cast a spell in this way, you can''t do so again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Primeval Awareness', 1, 89, 'Ranger', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primeval Awareness' AND class_name = 'Ranger' AND level = 3), 0, 'Beginning at 3rd level, you can use your action and expend one ranger spell slot to focus your awareness on the region around you. For 1 minute per level of the spell slot you expend, you can sense whether the following types of creatures are present within 1 mile of you (or within up to 6 miles if you are in your favored terrain): aberrations, celestials, dragons, elementals, fey, fiends, and undead. This feature doesn''t reveal the creatures'' location or number.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ranger Archetype', 1, 89, 'Ranger', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger Archetype' AND class_name = 'Ranger' AND level = 3), 0, 'At 3rd level, you choose an archetype that you strive to emulate from the list of available archetypes. Your choice grants features at 3rd level, and again at 7th, 11th, and 15th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 89, 'Ranger', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 4), 0, 'When you reach 4th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 4), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Martial Versatility', 2, 56, 'Ranger', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Martial Versatility' AND class_name = 'Ranger' AND level = 4), 0, '{@i 4th-level ranger {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Martial Versatility' AND class_name = 'Ranger' AND level = 4), 1, 'Whenever you reach a level in this class that grants the Ability Score Improvement feature, you can replace a fighting style you know with another fighting style available to rangers. This replacement represents a shift of focus in your martial practice.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Extra Attack', 1, 89, 'Ranger', 'PHB', NULL, NULL, 5, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Extra Attack' AND class_name = 'Ranger' AND level = 5), 0, 'Beginning at 5th level, you can attack twice, instead of once, whenever you take the {@action Attack} action on your turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Deft Explorer Improvement', 2, 56, 'Ranger', 'PHB', NULL, NULL, 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Deft Explorer Improvement' AND class_name = 'Ranger' AND level = 6), 0, 'You gain an additional benefit when you reach 6th level in this class.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Deft Explorer Improvement' AND class_name = 'Ranger' AND level = 6), 1, 'entries', 'Roving', '["Your walking speed increases by 5, and you gain a climbing speed and a swimming speed equal to your walking speed."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Roving","entries":["Your walking speed increases by 5, and you gain a climbing speed and a swimming speed equal to your walking speed."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Favored Enemy and Natural Explorer improvements', 1, 89, 'Ranger', 'PHB', NULL, NULL, 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Enemy and Natural Explorer improvements' AND class_name = 'Ranger' AND level = 6), 0, 'At 6th level, you gain an additional favored terrain.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Enemy and Natural Explorer improvements' AND class_name = 'Ranger' AND level = 6), 1, 'At 6th level, you choose one additional favored enemy, as well as an associated language. Your choice should reflect the types of monsters you have encountered on your adventures.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ranger Archetype feature', 1, 89, 'Ranger', 'PHB', NULL, NULL, 7, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger Archetype feature' AND class_name = 'Ranger' AND level = 7), 0, 'At 7th level, you gain a feature granted to you by your Ranger Archetype.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 89, 'Ranger', 'PHB', NULL, NULL, 8, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 8), 0, 'When you reach 8th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 8), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Land''s Stride', 1, 89, 'Ranger', 'PHB', NULL, NULL, 8, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Land''s Stride' AND class_name = 'Ranger' AND level = 8), 0, 'Starting at 8th level, moving through nonmagical {@quickref difficult terrain||3} costs you no extra movement. You can also pass through nonmagical plants without being slowed by them and without taking damage from them if they have thorns, spines, or a similar hazard.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Land''s Stride' AND class_name = 'Ranger' AND level = 8), 1, 'In addition, you have advantage on saving throws against plants that are magically created or manipulated to impede movement, such as those created by the {@spell entangle} spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Deft Explorer Improvement', 2, 56, 'Ranger', 'PHB', NULL, NULL, 10, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Deft Explorer Improvement' AND class_name = 'Ranger' AND level = 10), 0, 'You gain an additional benefit when you reach 10th level in this class.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Deft Explorer Improvement' AND class_name = 'Ranger' AND level = 10), 1, 'entries', 'Tireless', '["As an action, you can give yourself a number of temporary hit points equal to {@dice 1d8} + your Wisdom modifier (minimum of 1 temporary hit point). You can use this action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.","In addition, whenever you finish a short rest, your {@condition exhaustion} level, if any, is decreased by 1."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Tireless","entries":["As an action, you can give yourself a number of temporary hit points equal to {@dice 1d8} + your Wisdom modifier (minimum of 1 temporary hit point). You can use this action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.","In addition, whenever you finish a short rest, your {@condition exhaustion} level, if any, is decreased by 1."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hide in Plain Sight', 1, 89, 'Ranger', 'PHB', NULL, NULL, 10, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hide in Plain Sight' AND class_name = 'Ranger' AND level = 10), 0, 'Starting at 10th level, you can spend 1 minute creating camouflage for yourself. You must have access to fresh mud, dirt, plants, soot, and other naturally occurring materials with which to create your camouflage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hide in Plain Sight' AND class_name = 'Ranger' AND level = 10), 1, 'Once you are camouflaged in this way, you can try to hide by pressing yourself up against a solid surface, such as a tree or wall, that is at least as tall and wide as you are. You gain a +10 bonus to Dexterity ({@skill Stealth}) checks as long as you remain there without moving or taking actions. Once you move or take an action or a reaction, you must camouflage yourself again to gain this benefit.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Natural Explorer improvement', 1, 89, 'Ranger', 'PHB', NULL, NULL, 10, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Natural Explorer improvement' AND class_name = 'Ranger' AND level = 10), 0, 'You gain an additional favored terrain.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Nature''s Veil', 2, 56, 'Ranger', 'PHB', NULL, NULL, 10, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature''s Veil' AND class_name = 'Ranger' AND level = 10), 0, '{@i 10th-level ranger {@variantrule optional class features|tce|optional feature}, which replaces the Hide in Plain Sight feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature''s Veil' AND class_name = 'Ranger' AND level = 10), 1, 'You draw on the powers of nature to hide yourself from view briefly. As a bonus action, you can magically become {@condition invisible}, along with any equipment you are wearing or carrying, until the start of your next turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Nature''s Veil' AND class_name = 'Ranger' AND level = 10), 2, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ranger Archetype feature', 1, 89, 'Ranger', 'PHB', NULL, NULL, 11, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger Archetype feature' AND class_name = 'Ranger' AND level = 11), 0, 'At 11th level, you gain a feature granted to you by your Ranger Archetype.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 89, 'Ranger', 'PHB', NULL, NULL, 12, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 12), 0, 'When you reach 12th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 12), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Favored Enemy improvement', 1, 89, 'Ranger', 'PHB', NULL, NULL, 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Favored Enemy improvement' AND class_name = 'Ranger' AND level = 14), 0, 'At 14th level, you choose one additional favored enemy, as well as an associated language. Your choice should reflect the types of monsters you have encountered on your adventures.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Vanish', 1, 89, 'Ranger', 'PHB', NULL, NULL, 14, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Vanish' AND class_name = 'Ranger' AND level = 14), 0, 'Starting at 14th level, you can use the {@action Hide} action as a bonus action on your turn. Also, you can''t be tracked by nonmagical means, unless you choose to leave a trail.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ranger Archetype feature', 1, 89, 'Ranger', 'PHB', NULL, NULL, 15, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger Archetype feature' AND class_name = 'Ranger' AND level = 15), 0, 'At 15th level, you gain a feature granted to you by your Ranger Archetype.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 89, 'Ranger', 'PHB', NULL, NULL, 16, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 16), 0, 'When you reach 16th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 16), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Feral Senses', 1, 89, 'Ranger', 'PHB', NULL, NULL, 18, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Feral Senses' AND class_name = 'Ranger' AND level = 18), 0, 'At 18th level, you gain preternatural senses that help you fight creatures you can''t see. When you attack a creature you can''t see, your inability to see it doesn''t impose disadvantage on your attack rolls against it. You are also aware of the location of any {@condition invisible} creature within 30 feet of you, provided that the creature isn''t hidden from you and you aren''t {@condition blinded} or {@condition deafened}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 89, 'Ranger', 'PHB', NULL, NULL, 19, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 19), 0, 'When you reach 19th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Ranger' AND level = 19), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Foe Slayer', 1, 89, 'Ranger', 'PHB', NULL, NULL, 20, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Foe Slayer' AND class_name = 'Ranger' AND level = 20), 0, 'At 20th level, you become an unparalleled hunter of your enemies. Once on each of your turns, you can add your Wisdom modifier to the attack roll or the damage roll of an attack you make against one of your favored enemies. You can choose to use this feature before or after the roll, but before any effects of the roll are applied.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Drakewarden', null, 15, 'Ranger', 'PHB', 'Drakewarden', 'FTD', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drakewarden' AND class_name = 'Ranger' AND level = 3), 0, 'Your connection to the natural world takes the form of a draconic spirit, which can manifest in physical form as a drake. As your powers grow, your drake grows as well, blossoming from a small four-legged companion to a majestic winged creature large and strong enough for you to ride. Along the way, you gain an increasing share of the awe-inspiring power of dragons.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drakewarden' AND class_name = 'Ranger' AND level = 3), 1, 'Consider the source of the draconic spirit you have bonded with. The Drakewarden Origin table offers examples.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Drakewarden' AND class_name = 'Ranger' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Drakewarden Origin', '["d6","Origin"]', '[["1","You studied a dragon''s scale or claw, or a trinket from a dragon''s hoard, creating your bond through that token''s lingering draconic magic."],["2","A secret order of rangers who collect and guard draconic lore taught you their ways."],["3","A dragon gave you a geode or gemstone to care for. To your surprise, the drake hatched from that stone."],["4","You ingested a few drops of dragon blood, forever infusing your nature magic with draconic power."],["5","An ancient Draconic inscription on a standing stone empowered you when you read it aloud."],["6","You had a vivid dream of a mysterious figure accompanied by seven yellow canaries, who warned you of impending doom. When you awoke, your drake was there, watching you."]]', '{"type":"table","caption":"Drakewarden Origin","colLabels":["d6","Origin"],"colStyles":["col-1 text-center","col-11"],"rows":[["1","You studied a dragon''s scale or claw, or a trinket from a dragon''s hoard, creating your bond through that token''s lingering draconic magic."],["2","A secret order of rangers who collect and guard draconic lore taught you their ways."],["3","A dragon gave you a geode or gemstone to care for. To your surprise, the drake hatched from that stone."],["4","You ingested a few drops of dragon blood, forever infusing your nature magic with draconic power."],["5","An ancient Draconic inscription on a standing stone empowered you when you read it aloud."],["6","You had a vivid dream of a mysterious figure accompanied by seven yellow canaries, who warned you of impending doom. When you awoke, your drake was there, watching you."]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Drakewarden' AND class_name = 'Ranger' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Draconic Gift|Ranger||Drakewarden|FTD|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Drakewarden' AND class_name = 'Ranger' AND level = 3), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Drake Companion|Ranger||Drakewarden|FTD|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Draconic Gift', null, 15, 'Ranger', 'PHB', 'Drakewarden', 'FTD', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Draconic Gift' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level Drakewarden feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Draconic Gift' AND class_name = 'Ranger' AND level = 3), 1, 'The bond you share with your drake creates a connection to dragonkind, granting you understanding and empowering your presence. You gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Draconic Gift' AND class_name = 'Ranger' AND level = 3), 2, 'list', NULL, '[{"type":"item","name":"Thaumaturgy","entry":"You learn the {@spell thaumaturgy} cantrip, which is a ranger spell for you."},{"type":"item","name":"Tongue of Dragons","entry":"You learn to speak, read, and write Draconic or one other language of your choice."}]', '[{"type":"item","name":"Thaumaturgy","entry":"You learn the {@spell thaumaturgy} cantrip, which is a ranger spell for you."},{"type":"item","name":"Tongue of Dragons","entry":"You learn to speak, read, and write Draconic or one other language of your choice."}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Thaumaturgy","entry":"You learn the {@spell thaumaturgy} cantrip, which is a ranger spell for you."},{"type":"item","name":"Tongue of Dragons","entry":"You learn to speak, read, and write Draconic or one other language of your choice."}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Drake Companion', null, 15, 'Ranger', 'PHB', 'Drakewarden', 'FTD', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake Companion' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level Drakewarden feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake Companion' AND class_name = 'Ranger' AND level = 3), 1, 'As an action, you can magically summon the drake that is bound to you. It appears in an unoccupied space of your choice within 30 feet of you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake Companion' AND class_name = 'Ranger' AND level = 3), 2, 'The drake is friendly to you and your companions, and it obeys your commands. See its game statistics in the accompanying {@creature Drake Companion|FTD} stat block, which uses your proficiency bonus (PB) in several places. Whenever you summon the drake, choose a damage type listed in its Draconic Essence trait. You can determine the cosmetic characteristics of the drake, such as its color, its scale texture, or any visible effect of its Draconic Essence; your choice has no effect on its game statistics.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake Companion' AND class_name = 'Ranger' AND level = 3), 3, 'In combat, the drake shares your initiative count, but it takes its turn immediately after yours. It can move and use its reaction on its own, but the only action it takes on its turn is the {@action Dodge} action, unless you take a bonus action on your turn to command it to take another action. That action can be one in its stat block or some other action. If you are {@condition incapacitated}, the drake can take any action of its choice, not just {@action Dodge}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake Companion' AND class_name = 'Ranger' AND level = 3), 4, 'The drake remains until it is reduced to 0 hit points, until you use this feature to summon the drake again, or until you die. Anything the drake was wearing or carrying is left behind when the drake vanishes.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake Companion' AND class_name = 'Ranger' AND level = 3), 5, 'Once you summon the drake, you can''t do so again until you finish a long rest, unless you expend a spell slot of 1st level or higher to summon it.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bond of Fang and Scale', null, 15, 'Ranger', 'PHB', 'Drakewarden', 'FTD', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bond of Fang and Scale' AND class_name = 'Ranger' AND level = 7), 0, '{@i 7th-level Drakewarden feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bond of Fang and Scale' AND class_name = 'Ranger' AND level = 7), 1, 'The bond you share with your drake intensifies, protecting you and stoking the drake''s fury. When you summon your drake, it grows wings on its back and gains a flying speed equal to its walking speed.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bond of Fang and Scale' AND class_name = 'Ranger' AND level = 7), 2, 'In addition, while your drake is summoned, you and the drake gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Bond of Fang and Scale' AND class_name = 'Ranger' AND level = 7), 3, 'list', NULL, '[{"type":"item","name":"Drake Mount","entries":["The drake grows to Medium size. Reflecting your special bond, you can use the drake as a mount if your size is Medium or smaller. While you are riding your drake, it can''t use the flying speed of this feature."]},{"type":"item","name":"Magic Fang","entries":["The drake''s Bite attack deals an extra {@damage 1d6} damage of the type chosen for the drake''s Draconic Essence."]},{"type":"item","name":"Resistance","entries":["You gain resistance to the damage type chosen for the drake''s Draconic Essence."]}]', '[{"type":"item","name":"Drake Mount","entries":["The drake grows to Medium size. Reflecting your special bond, you can use the drake as a mount if your size is Medium or smaller. While you are riding your drake, it can''t use the flying speed of this feature."]},{"type":"item","name":"Magic Fang","entries":["The drake''s Bite attack deals an extra {@damage 1d6} damage of the type chosen for the drake''s Draconic Essence."]},{"type":"item","name":"Resistance","entries":["You gain resistance to the damage type chosen for the drake''s Draconic Essence."]}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Drake Mount","entries":["The drake grows to Medium size. Reflecting your special bond, you can use the drake as a mount if your size is Medium or smaller. While you are riding your drake, it can''t use the flying speed of this feature."]},{"type":"item","name":"Magic Fang","entries":["The drake''s Bite attack deals an extra {@damage 1d6} damage of the type chosen for the drake''s Draconic Essence."]},{"type":"item","name":"Resistance","entries":["You gain resistance to the damage type chosen for the drake''s Draconic Essence."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Drake''s Breath', null, 15, 'Ranger', 'PHB', 'Drakewarden', 'FTD', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake''s Breath' AND class_name = 'Ranger' AND level = 11), 0, '{@i 11th-level Drakewarden feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake''s Breath' AND class_name = 'Ranger' AND level = 11), 1, 'As an action, you can exhale a 30-foot cone of damaging breath or cause your drake to exhale it. Choose acid, cold, fire, lightning, or poison damage (your choice doesn''t have to match your drake''s Draconic Essence). Each creature in the cone must make a Dexterity saving throw against your spell save DC, taking {@damage 8d6} damage on a failed save, or half as much damage on a successful one.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake''s Breath' AND class_name = 'Ranger' AND level = 11), 2, 'This damage increases to {@damage 10d6} when you reach 15th level in this class.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Drake''s Breath' AND class_name = 'Ranger' AND level = 11), 3, 'Once you use this feature, you can''t do so again until you finish a long rest, unless you expend a spell slot of 3rd level or higher to use it again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Perfected Bond', null, 15, 'Ranger', 'PHB', 'Drakewarden', 'FTD', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Perfected Bond' AND class_name = 'Ranger' AND level = 15), 0, '{@i 15th-level Drakewarden feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Perfected Bond' AND class_name = 'Ranger' AND level = 15), 1, 'Your bond to your drake reaches the pinnacle of its power. While your drake is summoned, you and the drake gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Perfected Bond' AND class_name = 'Ranger' AND level = 15), 2, 'list', NULL, '[{"type":"item","name":"Empowered Bite","entries":["The drake''s Bite attack deals an extra {@damage 1d6} damage of the type chosen for its Draconic Essence (for a total of {@damage 2d6} extra damage)."]},{"type":"item","name":"Large Drake","entries":["The drake grows to Large size. When you ride your drake, it is no longer prohibited from using the flying speed of Bond of Fang and Scale."]},{"type":"item","name":"Reflexive Resistance","entries":["When either you or the drake takes damage while you''re within 30 feet of each other, you can use your reaction to give yourself or the drake resistance to that instance of damage. You can use this reaction a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest."]}]', '[{"type":"item","name":"Empowered Bite","entries":["The drake''s Bite attack deals an extra {@damage 1d6} damage of the type chosen for its Draconic Essence (for a total of {@damage 2d6} extra damage)."]},{"type":"item","name":"Large Drake","entries":["The drake grows to Large size. When you ride your drake, it is no longer prohibited from using the flying speed of Bond of Fang and Scale."]},{"type":"item","name":"Reflexive Resistance","entries":["When either you or the drake takes damage while you''re within 30 feet of each other, you can use your reaction to give yourself or the drake resistance to that instance of damage. You can use this reaction a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest."]}]', NULL, NULL, NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Empowered Bite","entries":["The drake''s Bite attack deals an extra {@damage 1d6} damage of the type chosen for its Draconic Essence (for a total of {@damage 2d6} extra damage)."]},{"type":"item","name":"Large Drake","entries":["The drake grows to Large size. When you ride your drake, it is no longer prohibited from using the flying speed of Bond of Fang and Scale."]},{"type":"item","name":"Reflexive Resistance","entries":["When either you or the drake takes damage while you''re within 30 feet of each other, you can use your reaction to give yourself or the drake resistance to that instance of damage. You can use this reaction a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest."]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Beast Master', 1, 93, 'Ranger', 'PHB', 'Beast Master', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Beast Master' AND class_name = 'Ranger' AND level = 3), 0, 'The Beast Master archetype embodies a friendship between the civilized races and the beasts of the world. United in focus, beast and ranger work as one to fight the monstrous foes that threaten civilization and the wilderness alike. Emulating the Beast Master archetype means committing yourself to this ideal, working in partnership with an animal as its companion and friend.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Beast Master' AND class_name = 'Ranger' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Ranger''s Companion|Ranger||Beast Master||3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Beast Master' AND class_name = 'Ranger' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Primal Companion|Ranger||Beast Master||3|TCE"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Primal Companion', 2, 61, 'Ranger', 'PHB', 'Beast Master', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Companion' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level Beast Master variant feature, which replaces the Ranger''s Companion feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Companion' AND class_name = 'Ranger' AND level = 3), 1, 'You magically summon a primal beast, which draws strength from your bond with nature. The beast is friendly to you and your companions and obeys your commands. Choose its stat block—{@creature Beast of the Land|TCE}, {@creature Beast of the Sea|TCE}, or {@creature Beast of the Sky|TCE}—which uses your proficiency bonus (PB) in several places. You also determine the kind of animal the beast is, choosing a kind appropriate for the stat block. Whatever kind you choose, the beast bears primal markings, indicating its mystical origin.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Companion' AND class_name = 'Ranger' AND level = 3), 2, 'In combat, the beast acts during your turn. It can move and use its reaction on its own, but the only action it takes is the {@action Dodge} action, unless you take a bonus action on your turn to command it to take another action. That action can be one in its stat block or some other action. You can also sacrifice one of your attacks when you take the {@action Attack} action to command the beast to take the {@action Attack} action. If you are {@condition incapacitated}, the beast can take any action of its choice, not just {@action Dodge}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Companion' AND class_name = 'Ranger' AND level = 3), 3, 'If the beast has died within the last hour, you can use your action to touch it and expend a spell slot of 1st level or higher. The beast returns to life after 1 minute with all its hit points restored.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Primal Companion' AND class_name = 'Ranger' AND level = 3), 4, 'When you finish a long rest, you can summon a different primal beast. The new beast appears in an unoccupied space within 5 feet of you, and you choose its stat block and appearance. If you already have a beast from this feature, it vanishes when the new beast appears. The beast also vanishes if you die.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ranger''s Companion', 1, 93, 'Ranger', 'PHB', 'Beast Master', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger''s Companion' AND class_name = 'Ranger' AND level = 3), 0, 'You gain a beast companion that accompanies you on your adventures and is trained to fight alongside you. Choose a {@filter beast that is no larger than Medium and that has a challenge rating of 1/4 or lower|bestiary|challenge rating=[&0;&1/4]|type=beast|size=f;d;t;s;m|miscellaneous=!swarm}. Add your proficiency bonus to the beast''s AC, attack rolls, and damage rolls, as well as to any saving throws and skills it is proficient in. Its hit point maximum equals its normal maximum or four times your ranger level, whichever is higher. Like any creature, the beast can spend Hit Dice during a short rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger''s Companion' AND class_name = 'Ranger' AND level = 3), 1, 'The beast obeys your commands as best as it can. It takes its turn on your initiative. On your turn, you can verbally command the beast where to move (no action required by you). You can use your action to verbally command it to take the {@action Attack}, {@action Dash}, {@action Disengage}, or {@action Help} action. If you don''t issue a command, the beast takes the {@action Dodge} action. Once you have the Extra Attack feature, you can make one weapon attack yourself when you command the beast to take the {@action Attack} action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger''s Companion' AND class_name = 'Ranger' AND level = 3), 2, 'If you are {@condition incapacitated} or absent, your beast companion acts on its own, focusing on protecting you and itself. It never requires your command to use its reaction, such as when making an opportunity attack.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger''s Companion' AND class_name = 'Ranger' AND level = 3), 3, 'While traveling through your favored terrain with only the beast, you can move stealthily at a normal pace.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ranger''s Companion' AND class_name = 'Ranger' AND level = 3), 4, 'If the beast dies, you can obtain a new companion by spending 8 hours magically bonding with a beast that isn''t hostile to you and that meets the requirements.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Exceptional Training', 1, 93, 'Ranger', 'PHB', 'Beast Master', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Exceptional Training' AND class_name = 'Ranger' AND level = 7), 0, 'Beginning at 7th level, on any of your turns when your beast companion doesn''t attack, you can use a bonus action to command the beast to take the {@action Dash}, {@action Disengage}, or {@action Help} action on its turn. In addition, the beast''s attacks now count as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bestial Fury', 1, 93, 'Ranger', 'PHB', 'Beast Master', 'PHB', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bestial Fury' AND class_name = 'Ranger' AND level = 11), 0, 'Starting at 11th level, when you command your beast companion to take the {@action Attack} action, the beast can make two attacks, or it can take the Multiattack action if it has that action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Share Spells', 1, 93, 'Ranger', 'PHB', 'Beast Master', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Share Spells' AND class_name = 'Ranger' AND level = 15), 0, 'Beginning at 15th level, when you cast a spell targeting yourself, you can also affect your beast companion with the spell if the beast is within 30 feet of you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hunter', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hunter' AND class_name = 'Ranger' AND level = 3), 0, 'Emulating the Hunter archetype means accepting your place as a bulwark between civilization and the terrors of the wilderness. As you walk the Hunter''s path, you learn specialized techniques for fighting the threats you face, from rampaging ogres and hordes of orcs to towering giants and terrifying dragons.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Hunter' AND class_name = 'Ranger' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Hunter''s Prey|Ranger||Hunter||3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Horde Breaker', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Horde Breaker' AND class_name = 'Ranger' AND level = 3), 0, 'Once on each of your turns when you make a weapon attack, you can make another attack with the same weapon against a different creature that is within 5 feet of the original target and within range of your weapon.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hunter''s Prey', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hunter''s Prey' AND class_name = 'Ranger' AND level = 3), 0, 'At 3rd level, you gain one of the following features of your choice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Hunter''s Prey' AND class_name = 'Ranger' AND level = 3), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Colossus Slayer|Ranger||Hunter||3"},{"type":"refSubclassFeature","subclassFeature":"Giant Killer|Ranger||Hunter||3"},{"type":"refSubclassFeature","subclassFeature":"Horde Breaker|Ranger||Hunter||3"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refSubclassFeature","subclassFeature":"Colossus Slayer|Ranger||Hunter||3"},{"type":"refSubclassFeature","subclassFeature":"Giant Killer|Ranger||Hunter||3"},{"type":"refSubclassFeature","subclassFeature":"Horde Breaker|Ranger||Hunter||3"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Colossus Slayer', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Colossus Slayer' AND class_name = 'Ranger' AND level = 3), 0, 'Your tenacity can wear down the most potent foes. When you hit a creature with a weapon attack, the creature takes an extra {@damage 1d8} damage if it''s below its hit point maximum. You can deal this extra damage only once per turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Giant Killer', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 3, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Giant Killer' AND class_name = 'Ranger' AND level = 3), 0, 'When a Large or larger creature within 5 feet of you hits or misses you with an attack, you can use your reaction to attack that creature immediately after its attack, provided that you can see the creature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Defensive Tactics', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Defensive Tactics' AND class_name = 'Ranger' AND level = 7), 0, 'At 7th level, you gain one of the following features of your choice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Defensive Tactics' AND class_name = 'Ranger' AND level = 7), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Escape the Horde|Ranger||Hunter||7"},{"type":"refSubclassFeature","subclassFeature":"Multiattack Defense|Ranger||Hunter||7"},{"type":"refSubclassFeature","subclassFeature":"Steel Will|Ranger||Hunter||7"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refSubclassFeature","subclassFeature":"Escape the Horde|Ranger||Hunter||7"},{"type":"refSubclassFeature","subclassFeature":"Multiattack Defense|Ranger||Hunter||7"},{"type":"refSubclassFeature","subclassFeature":"Steel Will|Ranger||Hunter||7"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Escape the Horde', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Escape the Horde' AND class_name = 'Ranger' AND level = 7), 0, 'Opportunity attacks against you are made with disadvantage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Multiattack Defense', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Multiattack Defense' AND class_name = 'Ranger' AND level = 7), 0, 'When a creature hits you with an attack, you gain a +4 bonus to AC against all subsequent attacks made by that creature for the rest of the turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Steel Will', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steel Will' AND class_name = 'Ranger' AND level = 7), 0, 'You have advantage on saving throws against being {@condition frightened}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Multiattack', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Multiattack' AND class_name = 'Ranger' AND level = 11), 0, 'At 11th level, you gain one of the following features of your choice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Multiattack' AND class_name = 'Ranger' AND level = 11), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Volley|Ranger||Hunter||11"},{"type":"refSubclassFeature","subclassFeature":"Whirlwind Attack|Ranger||Hunter||11"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refSubclassFeature","subclassFeature":"Volley|Ranger||Hunter||11"},{"type":"refSubclassFeature","subclassFeature":"Whirlwind Attack|Ranger||Hunter||11"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Volley', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Volley' AND class_name = 'Ranger' AND level = 11), 0, 'You can use your action to make a ranged attack against any number of creatures within 10 feet of a point you can see within your weapon''s range. You must have ammunition for each target, as normal, and you make a separate attack roll for each target.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Whirlwind Attack', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Whirlwind Attack' AND class_name = 'Ranger' AND level = 11), 0, 'You can use your action to make a melee attack against any number of creatures within 5 feet of you, with a separate attack roll for each target.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Evasion', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Evasion' AND class_name = 'Ranger' AND level = 15), 0, 'You can nimbly dodge out of the way of certain area effects, such as a red dragon''s fiery breath or a {@spell lightning bolt} spell. When you are subjected to an effect that allows you to make a Dexterity saving throw to take only half damage, you instead take no damage if you succeed on the saving throw, and only half damage if you fail.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Stand Against the Tide', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Stand Against the Tide' AND class_name = 'Ranger' AND level = 15), 0, 'When a hostile creature misses you with a melee attack, you can use your reaction to force that creature to repeat the same attack against another creature (other than itself) of your choice.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Superior Hunter''s Defense', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Superior Hunter''s Defense' AND class_name = 'Ranger' AND level = 15), 0, 'At 15th level, you gain one of the following features of your choice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Superior Hunter''s Defense' AND class_name = 'Ranger' AND level = 15), 1, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Evasion|Ranger||Hunter||15"},{"type":"refSubclassFeature","subclassFeature":"Stand Against the Tide|Ranger||Hunter||15"},{"type":"refSubclassFeature","subclassFeature":"Uncanny Dodge|Ranger||Hunter||15"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refSubclassFeature","subclassFeature":"Evasion|Ranger||Hunter||15"},{"type":"refSubclassFeature","subclassFeature":"Stand Against the Tide|Ranger||Hunter||15"},{"type":"refSubclassFeature","subclassFeature":"Uncanny Dodge|Ranger||Hunter||15"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Uncanny Dodge', 1, 93, 'Ranger', 'PHB', 'Hunter', 'PHB', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Uncanny Dodge' AND class_name = 'Ranger' AND level = 15), 0, 'When an attacker that you can see hits you with an attack, you can use your reaction to halve the attack''s damage against you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fey Wanderer', 2, 58, 'Ranger', 'PHB', 'Fey Wanderer', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer' AND class_name = 'Ranger' AND level = 3), 0, 'A fey mystique surrounds you, thanks to the boon of an archfey, the shining fruit you ate from a talking tree, the magic spring you swam in, or some other auspicious event. However you acquired your fey magic, you are now a Fey Wanderer, a ranger who represents both the mortal and the fey realms. As you wander the multiverse, your joyful laughter brightens the hearts of the downtrodden, and your martial prowess strikes terror in your foes, for great is the mirth of the fey and dreadful is their fury.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer' AND class_name = 'Ranger' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Dreadful Strikes|Ranger||Fey Wanderer|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer' AND class_name = 'Ranger' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Fey Wanderer Magic|Ranger||Fey Wanderer|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer' AND class_name = 'Ranger' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Otherworldly Glamour|Ranger||Fey Wanderer|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dreadful Strikes', 2, 58, 'Ranger', 'PHB', 'Fey Wanderer', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dreadful Strikes' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level Fey Wanderer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dreadful Strikes' AND class_name = 'Ranger' AND level = 3), 1, 'You can augment your weapon strikes with mind-scarring magic, drawn from the gloomy hollows of the Feywild. When you hit a creature with a weapon, you can deal an extra {@damage 1d4} psychic damage to the target, which can take this extra damage only once per turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dreadful Strikes' AND class_name = 'Ranger' AND level = 3), 2, 'The extra damage increases to {@damage 1d6} when you reach 11th level in this class.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fey Wanderer Magic', 2, 58, 'Ranger', 'PHB', 'Fey Wanderer', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer Magic' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level Fey Wanderer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer Magic' AND class_name = 'Ranger' AND level = 3), 1, 'You learn an additional spell when you reach certain levels in this class, as shown in the Fey Wanderer Spells table. Each spell counts as a ranger spell for you, but it doesn''t count against the number of ranger spells you know.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer Magic' AND class_name = 'Ranger' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Fey Wanderer Spells', '["Ranger Level","Spells"]', '[["3rd","{@spell charm person}"],["5th","{@spell misty step}"],["9th","{@spell dispel magic}"],["13th","{@spell dimension door}"],["17th","{@spell mislead}"]]', '{"type":"table","caption":"Fey Wanderer Spells","colLabels":["Ranger Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell charm person}"],["5th","{@spell misty step}"],["9th","{@spell dispel magic}"],["13th","{@spell dimension door}"],["17th","{@spell mislead}"]]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer Magic' AND class_name = 'Ranger' AND level = 3), 3, 'You also possess a preternatural blessing from a fey ally or a place of fey power. Choose your blessing from the Feywild Gifts table or determine it randomly.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Fey Wanderer Magic' AND class_name = 'Ranger' AND level = 3), 4, 'table', NULL, NULL, NULL, 'Feywild Gifts', '["d6","Gift"]', '[["1","Illusory butterflies flutter around you while you take a short or long rest."],["2","Fresh, seasonal flowers sprout from your hair each dawn."],["3","You faintly smell of cinnamon, lavender, nutmeg, or another comforting herb or spice."],["4","Your shadow dances while no one is looking directly at it."],["5","Horns or antlers sprout from your head."],["6","Your skin and hair change color to match the season at each dawn."]]', '{"type":"table","caption":"Feywild Gifts","colLabels":["d6","Gift"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","Illusory butterflies flutter around you while you take a short or long rest."],["2","Fresh, seasonal flowers sprout from your hair each dawn."],["3","You faintly smell of cinnamon, lavender, nutmeg, or another comforting herb or spice."],["4","Your shadow dances while no one is looking directly at it."],["5","Horns or antlers sprout from your head."],["6","Your skin and hair change color to match the season at each dawn."]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Otherworldly Glamour', 2, 58, 'Ranger', 'PHB', 'Fey Wanderer', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Otherworldly Glamour' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level Fey Wanderer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Otherworldly Glamour' AND class_name = 'Ranger' AND level = 3), 1, 'Your fey qualities give you a supernatural charm. As a result, whenever you make a Charisma check, you gain a bonus to the check equal to your Wisdom modifier (minimum of +1).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Otherworldly Glamour' AND class_name = 'Ranger' AND level = 3), 2, 'In addition, you gain proficiency in one of the following skills of your choice: {@skill Deception}, {@skill Performance}, or {@skill Persuasion}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Beguiling Twist', 2, 58, 'Ranger', 'PHB', 'Fey Wanderer', 'TCE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Beguiling Twist' AND class_name = 'Ranger' AND level = 7), 0, '{@i 7th-level Fey Wanderer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Beguiling Twist' AND class_name = 'Ranger' AND level = 7), 1, 'The magic of the Feywild guards your mind. You have advantage on saving throws against being {@condition charmed} or {@condition frightened}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Beguiling Twist' AND class_name = 'Ranger' AND level = 7), 2, 'In addition, whenever you or a creature you can see within 120 feet of you succeeds on a saving throw against being {@condition charmed} or {@condition frightened}, you can use your reaction to force a different creature you can see within 120 feet of you to make a Wisdom saving throw against your spell save DC. If the save fails, the target is {@condition charmed} or {@condition frightened} by you (your choice) for 1 minute. The target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a successful save.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fey Reinforcements', 2, 58, 'Ranger', 'PHB', 'Fey Wanderer', 'TCE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Reinforcements' AND class_name = 'Ranger' AND level = 11), 0, '{@i 11th-level Fey Wanderer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Reinforcements' AND class_name = 'Ranger' AND level = 11), 1, 'The royal courts of the Feywild have blessed you with the assistance of fey beings: you know {@spell summon fey|tce}. It doesn''t count against the number of ranger spells you know, and you can cast it without a material component. You can also cast it once without a spell slot, and you regain the ability to do so when you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Reinforcements' AND class_name = 'Ranger' AND level = 11), 2, 'Whenever you start casting the spell, you can modify it so that it doesn''t require {@status concentration}. If you do so, the spell''s duration becomes 1 minute for that casting.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Misty Wanderer', 2, 58, 'Ranger', 'PHB', 'Fey Wanderer', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Misty Wanderer' AND class_name = 'Ranger' AND level = 15), 0, '{@i 15th-level Fey Wanderer feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Misty Wanderer' AND class_name = 'Ranger' AND level = 15), 1, 'You can slip in and out of the Feywild to move in a blink of an eye: you can cast {@spell misty step} without expending a spell slot. You can do so a number of times equal to your Wisdom modifier (minimum of once), and you regain all expended uses when you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Misty Wanderer' AND class_name = 'Ranger' AND level = 15), 2, 'In addition, whenever you cast {@i misty step}, you can bring along one willing creature you can see within 5 feet of you. That creature teleports to an unoccupied space of your choice within 5 feet of your destination space.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Swarmkeeper', 2, 59, 'Ranger', 'PHB', 'Swarmkeeper', 'TCE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Swarmkeeper' AND class_name = 'Ranger' AND level = 3), 0, 'Feeling a deep connection to the environment around them, some rangers reach out through their magical connection to the world and bond with a swarm of nature spirits. The swarm becomes a potent force in battle, as well as helpful company for the ranger. Some Swarmkeepers are outcasts or hermits, keeping to themselves and their attendant swarms rather than dealing with the discomfort of others. Other Swarmkeepers enjoy building vibrant communities that work for the mutual benefit of all those they consider part of their swarm.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Swarmkeeper' AND class_name = 'Ranger' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Gathered Swarm|Ranger||Swarmkeeper|TCE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Swarmkeeper' AND class_name = 'Ranger' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Swarmkeeper Magic|Ranger||Swarmkeeper|TCE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Gathered Swarm', 2, 59, 'Ranger', 'PHB', 'Swarmkeeper', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Gathered Swarm' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level Swarmkeeper feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Gathered Swarm' AND class_name = 'Ranger' AND level = 3), 1, 'A swarm of intangible nature spirits has bonded itself to you and can assist you in battle. While you''re alive, the swarm remains in your space, crawling on you or flying and skittering around you within your space. You determine its appearance, or you generate its appearance by rolling on the Swarm Appearance table.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Gathered Swarm' AND class_name = 'Ranger' AND level = 3), 2, 'table', NULL, NULL, NULL, 'Swarm Appearance', '["d4","Appearance"]', '[["1","Swarming insects"],["2","Miniature twig blights"],["3","Fluttering birds"],["4","Playful pixies"]]', '{"type":"table","caption":"Swarm Appearance","colLabels":["d4","Appearance"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","Swarming insects"],["2","Miniature twig blights"],["3","Fluttering birds"],["4","Playful pixies"]]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Gathered Swarm' AND class_name = 'Ranger' AND level = 3), 3, 'Once on each of your turns, you can cause the swarm to assist you in one of the following ways, immediately after you hit a creature with an attack:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Gathered Swarm' AND class_name = 'Ranger' AND level = 3), 4, 'list', NULL, '["The attack''s target takes {@damage 1d6} piercing damage from the swarm.","The attack''s target must succeed on a Strength saving throw against your spell save DC or be moved by the swarm up to 15 feet horizontally in a direction of your choice.","You are moved by the swarm 5 feet horizontally in a direction of your choice."]', '["The attack''s target takes {@damage 1d6} piercing damage from the swarm.","The attack''s target must succeed on a Strength saving throw against your spell save DC or be moved by the swarm up to 15 feet horizontally in a direction of your choice.","You are moved by the swarm 5 feet horizontally in a direction of your choice."]', NULL, NULL, NULL, '{"type":"list","items":["The attack''s target takes {@damage 1d6} piercing damage from the swarm.","The attack''s target must succeed on a Strength saving throw against your spell save DC or be moved by the swarm up to 15 feet horizontally in a direction of your choice.","You are moved by the swarm 5 feet horizontally in a direction of your choice."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Gathered Swarm' AND class_name = 'Ranger' AND level = 3), 5, 'inset', 'It''s Your Swarm', '["A Swarmkeeper''s swarm and spells are reflections of the character''s bond with nature spirits. Take the opportunity to describe the swarm and the ranger''s magic in play. For example, when your ranger casts gaseous form, they might appear to melt into the swarm, instead of a cloud of mist, or the {@spell arcane eye} spell could create an extension of your swarm that spies for you. Such descriptions don''t change the effects of spells, but they are an exciting opportunity to explore your character''s narrative through their class abilities. For more guidance on customizing spells, see the \"Personalizing Spells\" section in chapter 3.","Also, remember that the swarm''s appearance is yours to customize, and don''t feel confined to a single appearance. Perhaps the spirits'' look changes with the ranger''s mood or with the seasons. You decide!"]', NULL, NULL, NULL, NULL, '{"type":"inset","name":"It''s Your Swarm","entries":["A Swarmkeeper''s swarm and spells are reflections of the character''s bond with nature spirits. Take the opportunity to describe the swarm and the ranger''s magic in play. For example, when your ranger casts gaseous form, they might appear to melt into the swarm, instead of a cloud of mist, or the {@spell arcane eye} spell could create an extension of your swarm that spies for you. Such descriptions don''t change the effects of spells, but they are an exciting opportunity to explore your character''s narrative through their class abilities. For more guidance on customizing spells, see the \"Personalizing Spells\" section in chapter 3.","Also, remember that the swarm''s appearance is yours to customize, and don''t feel confined to a single appearance. Perhaps the spirits'' look changes with the ranger''s mood or with the seasons. You decide!"]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Swarmkeeper Magic', 2, 59, 'Ranger', 'PHB', 'Swarmkeeper', 'TCE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Swarmkeeper Magic' AND class_name = 'Ranger' AND level = 3), 0, '{@i 3rd-level Swarmkeeper feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Swarmkeeper Magic' AND class_name = 'Ranger' AND level = 3), 1, 'You learn the {@spell mage hand} cantrip if you don''t already know it. When you cast it, the hand takes the form of your swarming nature spirits.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Swarmkeeper Magic' AND class_name = 'Ranger' AND level = 3), 2, 'You also learn an additional spell of 1st level or higher when you reach certain levels in this class, as shown in the Swarmkeeper Spells table. Each spell counts as a ranger spell for you, but it doesn''t count against the number of ranger spells you know.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Swarmkeeper Magic' AND class_name = 'Ranger' AND level = 3), 3, 'table', NULL, NULL, NULL, 'Swarmkeeper Spells', '["Ranger Level","Spells"]', '[["3rd","{@spell faerie fire}"],["5th","{@spell web}"],["9th","{@spell gaseous form}"],["13th","{@spell arcane eye}"],["17th","{@spell insect plague}"]]', '{"type":"table","caption":"Swarmkeeper Spells","colLabels":["Ranger Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell faerie fire}"],["5th","{@spell web}"],["9th","{@spell gaseous form}"],["13th","{@spell arcane eye}"],["17th","{@spell insect plague}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Writhing Tide', 2, 59, 'Ranger', 'PHB', 'Swarmkeeper', 'TCE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Writhing Tide' AND class_name = 'Ranger' AND level = 7), 0, '{@i 7th-level Swarmkeeper feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Writhing Tide' AND class_name = 'Ranger' AND level = 7), 1, 'You can condense part of your swarm into a focused mass that lifts you up. As a bonus action, you gain a flying speed of 10 feet and can hover. This effect lasts for 1 minute or until you are {@condition incapacitated}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Writhing Tide' AND class_name = 'Ranger' AND level = 7), 2, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mighty Swarm', 2, 59, 'Ranger', 'PHB', 'Swarmkeeper', 'TCE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mighty Swarm' AND class_name = 'Ranger' AND level = 11), 0, '{@i 11th-level Swarmkeeper feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mighty Swarm' AND class_name = 'Ranger' AND level = 11), 1, 'Your Gathered Swarm grows mightier in the following ways:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Mighty Swarm' AND class_name = 'Ranger' AND level = 11), 2, 'list', NULL, '["The damage of Gathered Swarm increases to {@damage 1d8}.","If a creature fails its saving throw against being moved by Gathered Swarm, you can also cause the swarm to knock the creature {@condition prone}.","When you are moved by Gathered Swarm, it gives you {@quickref Cover||3||half cover} until the start of your next turn."]', '["The damage of Gathered Swarm increases to {@damage 1d8}.","If a creature fails its saving throw against being moved by Gathered Swarm, you can also cause the swarm to knock the creature {@condition prone}.","When you are moved by Gathered Swarm, it gives you {@quickref Cover||3||half cover} until the start of your next turn."]', NULL, NULL, NULL, '{"type":"list","items":["The damage of Gathered Swarm increases to {@damage 1d8}.","If a creature fails its saving throw against being moved by Gathered Swarm, you can also cause the swarm to knock the creature {@condition prone}.","When you are moved by Gathered Swarm, it gives you {@quickref Cover||3||half cover} until the start of your next turn."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Swarming Dispersal', 2, 59, 'Ranger', 'PHB', 'Swarmkeeper', 'TCE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Swarming Dispersal' AND class_name = 'Ranger' AND level = 15), 0, '{@i 15th-level Swarmkeeper feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Swarming Dispersal' AND class_name = 'Ranger' AND level = 15), 1, 'You can discorporate into your swarm, avoiding danger. When you take damage, you can use your reaction to give yourself resistance to that damage. You vanish into your swarm and then teleport to an unoccupied space that you can see within 30 feet of you, where you reappear with the swarm.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Swarming Dispersal' AND class_name = 'Ranger' AND level = 15), 2, 'You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Gloom Stalker', 3, 41, 'Ranger', 'PHB', 'Gloom Stalker', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Gloom Stalker' AND class_name = 'Ranger' AND level = 3), 0, 'Gloom Stalkers are at home in the darkest places: deep under the earth, in gloomy alleyways, in primeval forests, and wherever else the light dims. Most folk enter such places with trepidation, but a Gloom Stalker ventures boldly into the darkness, seeking to ambush threats before they can reach the broader world. Such rangers are often found in the Underdark, but they will go any place where evil lurks in the shadows.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Gloom Stalker' AND class_name = 'Ranger' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Gloom Stalker Magic|Ranger||Gloom Stalker|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Gloom Stalker' AND class_name = 'Ranger' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Dread Ambusher|Ranger||Gloom Stalker|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Gloom Stalker' AND class_name = 'Ranger' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Umbral Sight|Ranger||Gloom Stalker|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dread Ambusher', 3, 41, 'Ranger', 'PHB', 'Gloom Stalker', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dread Ambusher' AND class_name = 'Ranger' AND level = 3), 0, 'At 3rd level, you master the art of the ambush. You can give yourself a bonus to your initiative rolls equal to your Wisdom modifier.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dread Ambusher' AND class_name = 'Ranger' AND level = 3), 1, 'At the start of your first turn of each combat, your walking speed increases by 10 feet, which lasts until the end of that turn. If you take the {@action Attack} action on that turn, you can make one additional weapon attack as part of that action. If that attack hits, the target takes an extra {@damage 1d8} damage of the weapon''s damage type.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Gloom Stalker Magic', 3, 41, 'Ranger', 'PHB', 'Gloom Stalker', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Gloom Stalker Magic' AND class_name = 'Ranger' AND level = 3), 0, 'Starting at 3rd level, you learn an additional spell when you reach certain levels in this class, as shown in the Gloom Stalker Spells table. The spell counts as a ranger spell for you, but it doesn''t count against the number of ranger spells you know.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Gloom Stalker Magic' AND class_name = 'Ranger' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Gloom Stalker Spells', '["Ranger Level","Spells"]', '[["3rd","{@spell disguise self}"],["5th","{@spell rope trick}"],["9th","{@spell fear}"],["13th","{@spell greater invisibility}"],["17th","{@spell seeming}"]]', '{"type":"table","caption":"Gloom Stalker Spells","colLabels":["Ranger Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell disguise self}"],["5th","{@spell rope trick}"],["9th","{@spell fear}"],["13th","{@spell greater invisibility}"],["17th","{@spell seeming}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Umbral Sight', 3, 41, 'Ranger', 'PHB', 'Gloom Stalker', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Umbral Sight' AND class_name = 'Ranger' AND level = 3), 0, 'At 3rd level, you gain {@sense darkvision} out to a range of 60 feet. If you already have {@sense darkvision} from your race, its range increases by 30 feet.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Umbral Sight' AND class_name = 'Ranger' AND level = 3), 1, 'You are also adept at evading creatures that rely on {@sense darkvision}. While in darkness, you are {@condition invisible} to any creature that relies on {@sense darkvision} to see you in that darkness.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Iron Mind', 3, 41, 'Ranger', 'PHB', 'Gloom Stalker', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Iron Mind' AND class_name = 'Ranger' AND level = 7), 0, 'By 7th level, you have honed your ability to resist the mind-altering powers of your prey. You gain proficiency in Wisdom saving throws. If you already have this proficiency, you instead gain proficiency in Intelligence or Charisma saving throws (your choice).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Stalker''s Flurry', 3, 41, 'Ranger', 'PHB', 'Gloom Stalker', 'XGE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Stalker''s Flurry' AND class_name = 'Ranger' AND level = 11), 0, 'At 11th level, you learn to attack with such unexpected speed that you can turn a miss into another strike. Once on each of your turns when you miss with a weapon attack, you can make another weapon attack as part of the same action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Shadowy Dodge', 3, 41, 'Ranger', 'PHB', 'Gloom Stalker', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Shadowy Dodge' AND class_name = 'Ranger' AND level = 15), 0, 'Starting at 15th level, you can dodge in unforeseen ways, with wisps of supernatural shadow around you. Whenever a creature makes an attack roll against you and doesn''t have advantage on the roll, you can use your reaction to impose disadvantage on it. You must use this feature before you know the outcome of the attack roll.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Horizon Walker', 3, 42, 'Ranger', 'PHB', 'Horizon Walker', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Horizon Walker' AND class_name = 'Ranger' AND level = 3), 0, 'Horizon Walkers guard the world against threats that originate from other planes or that seek to ravage the mortal realm with otherworldly magic. They seek out planar portals and keep watch over them, venturing to the Inner Planes and the Outer Planes as needed to pursue their foes. These rangers are also friends to any forces in the multiverse—especially benevolent dragons, fey, and elementals—that work to preserve life and the order of the planes.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Horizon Walker' AND class_name = 'Ranger' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Horizon Walker Magic|Ranger||Horizon Walker|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Horizon Walker' AND class_name = 'Ranger' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Detect Portal|Ranger||Horizon Walker|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Horizon Walker' AND class_name = 'Ranger' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Planar Warrior|Ranger||Horizon Walker|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Detect Portal', 3, 42, 'Ranger', 'PHB', 'Horizon Walker', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Detect Portal' AND class_name = 'Ranger' AND level = 3), 0, 'At 3rd level, you gain the ability to magically sense the presence of a planar portal. As an action, you detect the distance and direction to the closest planar portal within 1 mile of you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Detect Portal' AND class_name = 'Ranger' AND level = 3), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Detect Portal' AND class_name = 'Ranger' AND level = 3), 2, 'See the "Planar Travel" section in chapter 2 of the Dungeon Master''s Guide for examples of planar portals.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Horizon Walker Magic', 3, 42, 'Ranger', 'PHB', 'Horizon Walker', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Horizon Walker Magic' AND class_name = 'Ranger' AND level = 3), 0, 'Starting at 3rd level, you learn an additional spell when you reach certain levels in this class, as shown in the Horizon Walker Spells table. The spell counts as a ranger spell for you, but it doesn''t count against the number of ranger spells you know.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Horizon Walker Magic' AND class_name = 'Ranger' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Horizon Walker Spells', '["Ranger Level","Spells"]', '[["3rd","{@spell protection from evil and good}"],["5th","{@spell misty step}"],["9th","{@spell haste}"],["13th","{@spell banishment}"],["17th","{@spell teleportation circle}"]]', '{"type":"table","caption":"Horizon Walker Spells","colLabels":["Ranger Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell protection from evil and good}"],["5th","{@spell misty step}"],["9th","{@spell haste}"],["13th","{@spell banishment}"],["17th","{@spell teleportation circle}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Planar Warrior', 3, 42, 'Ranger', 'PHB', 'Horizon Walker', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Planar Warrior' AND class_name = 'Ranger' AND level = 3), 0, 'At 3rd level, you learn to draw on the energy of the multiverse to augment your attacks.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Planar Warrior' AND class_name = 'Ranger' AND level = 3), 1, 'As a bonus action, choose one creature you can see within 30 feet of you. The next time you hit that creature on this turn with a weapon attack, all damage dealt by the attack becomes force damage, and the creature takes an extra {@damage 1d8} force damage from the attack. When you reach 11th level in this class, the extra damage increases to {@damage 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ethereal Step', 3, 42, 'Ranger', 'PHB', 'Horizon Walker', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ethereal Step' AND class_name = 'Ranger' AND level = 7), 0, 'At 7th level, you learn to step through the Ethereal Plane. As a bonus action, you can cast the {@spell etherealness} spell with this feature, without expending a spell slot, but the spell ends at the end of the current turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ethereal Step' AND class_name = 'Ranger' AND level = 7), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Distant Strike', 3, 42, 'Ranger', 'PHB', 'Horizon Walker', 'XGE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Distant Strike' AND class_name = 'Ranger' AND level = 11), 0, 'At 11th level, you gain the ability to pass between the planes in the blink of an eye. When you take the {@action Attack} action, you can teleport up to 10 feet before each attack to an unoccupied space you can see.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Distant Strike' AND class_name = 'Ranger' AND level = 11), 1, 'If you attack at least two different creatures with the action, you can make one additional attack with it against a third creature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spectral Defense', 3, 42, 'Ranger', 'PHB', 'Horizon Walker', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spectral Defense' AND class_name = 'Ranger' AND level = 15), 0, 'At 15th level, your ability to move between planes enables you to slip through the planar boundaries to lessen the harm done to you during battle. When you take damage from an attack, you can use your reaction to give yourself resistance to all of that attack''s damage on this turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Monster Slayer', 3, 43, 'Ranger', 'PHB', 'Monster Slayer', 'XGE', 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Monster Slayer' AND class_name = 'Ranger' AND level = 3), 0, 'You have dedicated yourself to hunting down creatures of the night and wielders of grim magic. A Monster Slayer seeks out vampires, dragons, evil fey, fiends, and other magical threats. Trained in supernatural techniques to overcome such monsters, slayers are experts at unearthing and defeating mighty, mystical foes.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Monster Slayer' AND class_name = 'Ranger' AND level = 3), 1, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Monster Slayer Magic|Ranger||Monster Slayer|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Monster Slayer' AND class_name = 'Ranger' AND level = 3), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Hunter''s Sense|Ranger||Monster Slayer|XGE|3"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Monster Slayer' AND class_name = 'Ranger' AND level = 3), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Slayer''s Prey|Ranger||Monster Slayer|XGE|3"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hunter''s Sense', 3, 43, 'Ranger', 'PHB', 'Monster Slayer', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hunter''s Sense' AND class_name = 'Ranger' AND level = 3), 0, 'At 3rd level, you gain the ability to peer at a creature and magically discern how best to hurt it. As an action, choose one creature you can see within 60 feet of you. You immediately learn whether the creature has any damage immunities, resistances, or vulnerabilities and what they are. If the creature is hidden from divination magic, you sense that it has no damage immunities, resistances, or vulnerabilities.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hunter''s Sense' AND class_name = 'Ranger' AND level = 3), 1, 'You can use this feature a number of times equal to your Wisdom modifier (minimum of once). You regain all expended uses of it when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Monster Slayer Magic', 3, 43, 'Ranger', 'PHB', 'Monster Slayer', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Monster Slayer Magic' AND class_name = 'Ranger' AND level = 3), 0, 'Starting at 3rd level, you learn an additional spell when you reach certain levels in this class, as shown in the Monster Slayer Spells table. The spell counts as a ranger spell for you, but it doesn''t count against the number of ranger spells you know.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Monster Slayer Magic' AND class_name = 'Ranger' AND level = 3), 1, 'table', NULL, NULL, NULL, 'Monster Slayer Spells', '["Ranger Level","Spells"]', '[["3rd","{@spell protection from evil and good}"],["5th","{@spell zone of truth}"],["9th","{@spell magic circle}"],["13th","{@spell banishment}"],["17th","{@spell hold monster}"]]', '{"type":"table","caption":"Monster Slayer Spells","colLabels":["Ranger Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["3rd","{@spell protection from evil and good}"],["5th","{@spell zone of truth}"],["9th","{@spell magic circle}"],["13th","{@spell banishment}"],["17th","{@spell hold monster}"]]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Slayer''s Prey', 3, 43, 'Ranger', 'PHB', 'Monster Slayer', 'XGE', 3, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Slayer''s Prey' AND class_name = 'Ranger' AND level = 3), 0, 'Starting at 3rd level, you can focus your ire on one foe, increasing the harm you inflict on it. As a bonus action, you designate one creature you can see within 60 feet of you as the target of this feature. The first time each turn that you hit that target with a weapon attack, it takes an extra {@damage 1d6} damage from the weapon.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Slayer''s Prey' AND class_name = 'Ranger' AND level = 3), 1, 'This benefit lasts until you finish a short or long rest. It ends early if you designate a different creature.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Supernatural Defense', 3, 43, 'Ranger', 'PHB', 'Monster Slayer', 'XGE', 7, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Supernatural Defense' AND class_name = 'Ranger' AND level = 7), 0, 'At 7th level, you gain extra resilience against your prey''s assaults on your mind and body. Whenever the target of your Slayer''s Prey forces you to make a saving throw and whenever you make an ability check to escape that target''s grapple, add {@dice 1d6} to your roll.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Magic-User''s Nemesis', 3, 43, 'Ranger', 'PHB', 'Monster Slayer', 'XGE', 11, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic-User''s Nemesis' AND class_name = 'Ranger' AND level = 11), 0, 'At 11th level, you gain the ability to thwart someone else''s magic. When you see a creature casting a spell or teleporting within 60 feet of you, you can use your reaction to try to magically foil it. The creature must succeed on a Wisdom saving throw against your spell save DC, or its spell or teleport fails and is wasted.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magic-User''s Nemesis' AND class_name = 'Ranger' AND level = 11), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Slayer''s Counter', 3, 43, 'Ranger', 'PHB', 'Monster Slayer', 'XGE', 15, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Slayer''s Counter' AND class_name = 'Ranger' AND level = 15), 0, 'At 15th level, you gain the ability to counterattack when your prey tries to sabotage you. If the target of your Slayer''s Prey forces you to make a saving throw, you can use your reaction to make one weapon attack against the quarry. You make this attack immediately before making the saving throw. If your attack hits, your save automatically succeeds, in addition to the attack''s normal effects.');
