INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Expertise', 1, 94, 'Rogue', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Expertise' AND class_name = 'Rogue' AND level = 1), 0, 'At 1st level, choose two of your skill proficiencies, or one of your skill proficiencies and your proficiency with {@item thieves'' tools|phb}. Your proficiency bonus is doubled for any ability check you make that uses either of the chosen proficiencies.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Expertise' AND class_name = 'Rogue' AND level = 1), 1, 'At 6th level, you can choose two more of your proficiencies (in skills or with {@item thieves'' tools|phb}) to gain this benefit.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sneak Attack', 1, 94, 'Rogue', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sneak Attack' AND class_name = 'Rogue' AND level = 1), 0, 'Beginning at 1st level, you know how to strike subtly and exploit a foe''s distraction. Once per turn, you can deal an extra {@damage 1d6} damage to one creature you hit with an attack if you have advantage on the attack roll. The attack must use a finesse or a ranged weapon.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sneak Attack' AND class_name = 'Rogue' AND level = 1), 1, 'You don''t need advantage on the attack roll if another enemy of the target is within 5 feet of it, that enemy isn''t {@condition incapacitated}, and you don''t have disadvantage on the attack roll.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sneak Attack' AND class_name = 'Rogue' AND level = 1), 2, 'The amount of the extra damage increases as you gain levels in this class, as shown in the Sneak Attack column of the Rogue table.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Thieves'' Cant', 1, 94, 'Rogue', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Thieves'' Cant' AND class_name = 'Rogue' AND level = 1), 0, 'During your rogue training you learned thieves'' cant, a secret mix of dialect, jargon, and code that allows you to hide messages in seemingly normal conversation. Only another creature that knows thieves'' cant understands such messages. It takes four times longer to convey such a message than it does to speak the same idea plainly.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Thieves'' Cant' AND class_name = 'Rogue' AND level = 1), 1, 'In addition, you understand a set of secret signs and symbols used to convey short, simple messages, such as whether an area is dangerous or the territory of a thieves'' guild, whether loot is nearby, or whether the people in an area are easy marks or will provide a safe house for thieves on the run.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Cunning Action', 1, 94, 'Rogue', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cunning Action' AND class_name = 'Rogue' AND level = 2), 0, 'Starting at 2nd level, your quick thinking and agility allow you to move and act quickly. You can take a bonus action on each of your turns in combat. This action can be used only to take the {@action Dash}, {@action Disengage}, or {@action Hide} action.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Roguish Archetype', 1, 94, 'Rogue', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Roguish Archetype' AND class_name = 'Rogue' AND level = 3), 0, 'At 3rd level, you choose an archetype that you emulate in the exercise of your rogue abilities from the list of available archetypes. Your archetype choice grants you features at 3rd level and then again at 9th, 13th, and 17th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Steady Aim', 2, 62, 'Rogue', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steady Aim' AND class_name = 'Rogue' AND level = 3), 0, '{@i 3rd-level rogue {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Steady Aim' AND class_name = 'Rogue' AND level = 3), 1, 'As a bonus action, you give yourself advantage on your next attack roll on the current turn. You can use this bonus action only if you haven''t moved during this turn, and after you use the bonus action, your speed is 0 until the end of the current turn.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 94, 'Rogue', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Rogue' AND level = 4), 0, 'When you reach 4th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Rogue' AND level = 4), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Uncanny Dodge', 1, 94, 'Rogue', 'PHB', NULL, NULL, 5, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Uncanny Dodge' AND class_name = 'Rogue' AND level = 5), 0, 'Starting at 5th level, when an attacker that you can see hits you with an attack, you can use your reaction to halve the attack''s damage against you.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Expertise', 1, 94, 'Rogue', 'PHB', NULL, NULL, 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Expertise' AND class_name = 'Rogue' AND level = 6), 0, 'At 6th level, you can choose two more of your proficiencies (in skills or with {@item thieves'' tools|phb}) to gain the benefit of Expertise.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Evasion', 1, 94, 'Rogue', 'PHB', NULL, NULL, 7, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Evasion' AND class_name = 'Rogue' AND level = 7), 0, 'Beginning at 7th level, you can nimbly dodge out of the way of certain area effects, such as a red dragon''s fiery breath or an {@spell ice storm} spell. When you are subjected to an effect that allows you to make a Dexterity saving throw to take only half damage, you instead take no damage if you succeed on the saving throw, and only half damage if you fail.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 94, 'Rogue', 'PHB', NULL, NULL, 8, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Rogue' AND level = 8), 0, 'When you reach 8th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Rogue' AND level = 8), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
