INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sorcerous Origin', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sorcerous Origin' AND class_name = 'Sorcerer' AND level = 1), 0, 'Choose a sorcerous origin, which describes the source of your innate magical power, from the list of available origins.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sorcerous Origin' AND class_name = 'Sorcerer' AND level = 1), 1, 'Your choice grants you features when you choose it at 1st level and again at 6th, 14th, and 18th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spellcasting', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Sorcerer' AND level = 1), 0, 'An event in your past, or in the life of a parent or ancestor, left an indelible mark on you, infusing you with arcane magic. This font of magic, whatever its origin, fuels your spells. See {@book chapter 10|PHB|10} for the general rules of spellcasting and {@book chapter 11|PHB|11} for the {@filter sorcerer spell list|spells|class=sorcerer}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Sorcerer' AND level = 1), 1, 'entries', 'Cantrips', '["At 1st level, you know four cantrips of your choice from the sorcerer spell list. You learn an additional sorcerer cantrip of your choice at 4th level and another at 10th level."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Cantrips","entries":["At 1st level, you know four cantrips of your choice from the sorcerer spell list. You learn an additional sorcerer cantrip of your choice at 4th level and another at 10th level."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Sorcerer' AND level = 1), 2, 'entries', 'Spell Slots', '["The Sorcerer table shows how many spell slots you have to cast your {@filter sorcerer spells|spells|class=sorcerer} of 1st level and higher. To cast one of these sorcerer spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","For example, if you know the 1st-level spell {@spell burning hands} and have a 1st-level and a 2nd-level spell slot available, you can cast {@spell burning hands} using either slot."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spell Slots","entries":["The Sorcerer table shows how many spell slots you have to cast your {@filter sorcerer spells|spells|class=sorcerer} of 1st level and higher. To cast one of these sorcerer spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","For example, if you know the 1st-level spell {@spell burning hands} and have a 1st-level and a 2nd-level spell slot available, you can cast {@spell burning hands} using either slot."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Sorcerer' AND level = 1), 3, 'entries', 'Spells Known of 1st Level and Higher', '["You know two 1st-level spells of your choice from the sorcerer spell list.","You learn an additional sorcerer spell of your choice at each level except 12th, 14th, 16th, 18th, 19th, and 20th. Each of these spells must be of a level for which you have spell slots. For instance, when you reach 3rd level in this class, you can learn one new spell of 1st or 2nd level.","Additionally, when you gain a level in this class, you can choose one of the sorcerer spells you know and replace it with another spell from the sorcerer spell list, which also must be of a level for which you have spell slots."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spells Known of 1st Level and Higher","entries":["You know two 1st-level spells of your choice from the sorcerer spell list.","You learn an additional sorcerer spell of your choice at each level except 12th, 14th, 16th, 18th, 19th, and 20th. Each of these spells must be of a level for which you have spell slots. For instance, when you reach 3rd level in this class, you can learn one new spell of 1st or 2nd level.","Additionally, when you gain a level in this class, you can choose one of the sorcerer spells you know and replace it with another spell from the sorcerer spell list, which also must be of a level for which you have spell slots."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Sorcerer' AND level = 1), 4, 'entries', 'Spellcasting Ability', '["Charisma is your spellcasting ability for your sorcerer spells, since the power of your magic relies on your ability to project your will into the world. You use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a sorcerer spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["cha"]},{"type":"abilityAttackMod","name":"Spell","attributes":["cha"]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Ability","entries":["Charisma is your spellcasting ability for your sorcerer spells, since the power of your magic relies on your ability to project your will into the world. You use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a sorcerer spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["cha"]},{"type":"abilityAttackMod","name":"Spell","attributes":["cha"]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Sorcerer' AND level = 1), 5, 'entries', 'Spellcasting Focus', '["You can use an {@item arcane focus|phb} as a spellcasting focus for your sorcerer spells."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Focus","entries":["You can use an {@item arcane focus|phb} as a spellcasting focus for your sorcerer spells."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Flexible Casting', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Flexible Casting' AND class_name = 'Sorcerer' AND level = 2), 0, 'You can use your sorcery points to gain additional spell slots, or sacrifice spell slots to gain additional sorcery points. You learn other ways to use your sorcery points as you reach higher levels.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Flexible Casting' AND class_name = 'Sorcerer' AND level = 2), 1, 'entries', 'Creating Spell Slots', '["You can transform unexpended sorcery points into one spell slot as a bonus action on your turn. The created spell slots vanish at the end of a long rest. The Creating Spell Slots table shows the cost of creating a spell slot of a given level. You can create spell slots no higher in level than 5th.",{"type":"table","caption":"Creating Spell Slots","colLabels":["Spell Slot Level","Sorcery Point Cost"],"colStyles":["col-6 text-center","col-6 text-center"],"rows":[["1st","2"],["2nd","3"],["3rd","5"],["4th","6"],["5th","7"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Creating Spell Slots","entries":["You can transform unexpended sorcery points into one spell slot as a bonus action on your turn. The created spell slots vanish at the end of a long rest. The Creating Spell Slots table shows the cost of creating a spell slot of a given level. You can create spell slots no higher in level than 5th.",{"type":"table","caption":"Creating Spell Slots","colLabels":["Spell Slot Level","Sorcery Point Cost"],"colStyles":["col-6 text-center","col-6 text-center"],"rows":[["1st","2"],["2nd","3"],["3rd","5"],["4th","6"],["5th","7"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Flexible Casting' AND class_name = 'Sorcerer' AND level = 2), 2, 'entries', 'Converting a Spell Slot to Sorcery Points', '["As a bonus action on your turn, you can expend one spell slot and gain a number of sorcery points equal to the slot''s level."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Converting a Spell Slot to Sorcery Points","entries":["As a bonus action on your turn, you can expend one spell slot and gain a number of sorcery points equal to the slot''s level."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Font of Magic', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Font of Magic' AND class_name = 'Sorcerer' AND level = 2), 0, 'At 2nd level, you tap into a deep wellspring of magic within yourself. This wellspring is represented by sorcery points, which allow you to create a variety of magical effects.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Font of Magic' AND class_name = 'Sorcerer' AND level = 2), 1, 'refClassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refClassFeature","classFeature":"Sorcery Points|Sorcerer||2"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Font of Magic' AND class_name = 'Sorcerer' AND level = 2), 2, 'refClassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refClassFeature","classFeature":"Flexible Casting|Sorcerer||2"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sorcery Points', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 2, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sorcery Points' AND class_name = 'Sorcerer' AND level = 2), 0, 'You have 2 sorcery points, and you gain one additional point every time you level up, to a maximum of 20 at level 20. You can never have more sorcery points than shown on the table for your level. You regain all spent sorcery points when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Metamagic', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Metamagic' AND class_name = 'Sorcerer' AND level = 3), 0, 'At 3rd level, you gain the ability to twist your spells to suit your needs. You gain two of the following Metamagic options of your choice. You gain another one at 10th and 17th level.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Metamagic' AND class_name = 'Sorcerer' AND level = 3), 1, 'You can use only one Metamagic option on a spell when you cast it, unless otherwise noted.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Metamagic' AND class_name = 'Sorcerer' AND level = 3), 2, 'options', NULL, '[{"type":"refOptionalfeature","optionalfeature":"Careful Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Distant Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Empowered Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Extended Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Heightened Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Quickened Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Subtle Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Twinned Spell","preserve":{"consumes":true}}]', NULL, NULL, NULL, NULL, '{"type":"options","count":2,"entries":[{"type":"refOptionalfeature","optionalfeature":"Careful Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Distant Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Empowered Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Extended Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Heightened Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Quickened Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Subtle Spell","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Twinned Spell","preserve":{"consumes":true}}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Metamagic Options', 2, 65, 'Sorcerer', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Metamagic Options' AND class_name = 'Sorcerer' AND level = 3), 0, '{@i 3rd-level sorcerer {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Metamagic Options' AND class_name = 'Sorcerer' AND level = 3), 1, 'When you choose Metamagic options, you have access to the following additional options.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Metamagic Options' AND class_name = 'Sorcerer' AND level = 3), 2, 'options', NULL, '[{"type":"refOptionalfeature","optionalfeature":"Seeking Spell|TCE","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Transmuted Spell|TCE","preserve":{"consumes":true}}]', NULL, NULL, NULL, NULL, '{"type":"options","entries":[{"type":"refOptionalfeature","optionalfeature":"Seeking Spell|TCE","preserve":{"consumes":true}},{"type":"refOptionalfeature","optionalfeature":"Transmuted Spell|TCE","preserve":{"consumes":true}}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Sorcerer' AND level = 4), 0, 'When you reach 4th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Sorcerer' AND level = 4), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sorcerous Versatility', 2, 65, 'Sorcerer', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sorcerous Versatility' AND class_name = 'Sorcerer' AND level = 4), 0, '{@i 4th-level sorcerer {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sorcerous Versatility' AND class_name = 'Sorcerer' AND level = 4), 1, 'Whenever you reach a level in this class that grants the Ability Score Improvement feature, you can do one of the following, representing the magic within you flowing in new ways:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Sorcerous Versatility' AND class_name = 'Sorcerer' AND level = 4), 2, 'list', NULL, '["Replace one of the options you chose for the Metamagic feature with a different {@filter Metamagic option|optionalfeatures|Feature Type=MM} available to you.","Replace one cantrip you learned from this class''s Spellcasting feature with another cantrip from the {@filter sorcerer spell list|spells|level=0|class=Sorcerer}."]', '["Replace one of the options you chose for the Metamagic feature with a different {@filter Metamagic option|optionalfeatures|Feature Type=MM} available to you.","Replace one cantrip you learned from this class''s Spellcasting feature with another cantrip from the {@filter sorcerer spell list|spells|level=0|class=Sorcerer}."]', NULL, NULL, NULL, '{"type":"list","items":["Replace one of the options you chose for the Metamagic feature with a different {@filter Metamagic option|optionalfeatures|Feature Type=MM} available to you.","Replace one cantrip you learned from this class''s Spellcasting feature with another cantrip from the {@filter sorcerer spell list|spells|level=0|class=Sorcerer}."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Magical Guidance', 2, 65, 'Sorcerer', 'PHB', NULL, NULL, 5, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magical Guidance' AND class_name = 'Sorcerer' AND level = 5), 0, '{@i 5th-level sorcerer {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Magical Guidance' AND class_name = 'Sorcerer' AND level = 5), 1, 'You can tap into your inner wellspring of magic to try to conjure success from failure. When you make an ability check that fails, you can spend 1 sorcery point to reroll the {@dice d20}, and you must use the new roll, potentially turning the failure into a success.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sorcerous Origin feature', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 6, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sorcerous Origin feature' AND class_name = 'Sorcerer' AND level = 6), 0, 'At 6th level, you gain a feature granted by your Sorcerous Origin.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 99, 'Sorcerer', 'PHB', NULL, NULL, 8, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Sorcerer' AND level = 8), 0, 'When you reach 8th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Sorcerer' AND level = 8), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
