INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Otherworldly Patron', 1, 105, 'Warlock', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Otherworldly Patron' AND class_name = 'Warlock' AND level = 1), 0, 'At 1st level, you have struck a bargain with an otherworldly being chosen from the list of available patrons. Your choice grants you features at 1st level and again at 6th, 10th, and 14th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Pact Magic', 1, 105, 'Warlock', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Pact Magic' AND class_name = 'Warlock' AND level = 1), 0, 'Your arcane research and the magic bestowed on you by your patron have given you facility with spells. See {@book chapter 10|PHB|10} for the general rules of spellcasting and {@book chapter 11|PHB|11} for the {@filter warlock spell list|spells|class=warlock}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Pact Magic' AND class_name = 'Warlock' AND level = 1), 1, 'entries', 'Cantrips', '["You know two cantrips of your choice from the warlock spell list. You learn additional warlock cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Warlock table."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Cantrips","entries":["You know two cantrips of your choice from the warlock spell list. You learn additional warlock cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Warlock table."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Pact Magic' AND class_name = 'Warlock' AND level = 1), 2, 'entries', 'Spell Slots', '["The Warlock table shows how many spell slots you have to cast your {@filter warlock spells|spells|class=warlock} of 1st through 5th level. The table also shows what the level of those slots is; all of your spell slots are the same level. To cast one of your warlock spells of 1st level or higher, you must expend a spell slot. You regain all expended spell slots when you finish a short or long rest.","For example, when you are 5th level, you have two 3rd-level spell slots. To cast the 1st-level spell {@spell witch bolt}, you must spend one of those slots, and you cast it as a 3rd-level spell."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spell Slots","entries":["The Warlock table shows how many spell slots you have to cast your {@filter warlock spells|spells|class=warlock} of 1st through 5th level. The table also shows what the level of those slots is; all of your spell slots are the same level. To cast one of your warlock spells of 1st level or higher, you must expend a spell slot. You regain all expended spell slots when you finish a short or long rest.","For example, when you are 5th level, you have two 3rd-level spell slots. To cast the 1st-level spell {@spell witch bolt}, you must spend one of those slots, and you cast it as a 3rd-level spell."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Pact Magic' AND class_name = 'Warlock' AND level = 1), 3, 'entries', 'Spells Known of 1st Level and Higher', '["At 1st level, you know two 1st-level spells of your choice from the warlock spell list.","The Spells Known column of the Warlock table shows when you learn more warlock spells of your choice of 1st level and higher. A spell you choose must be of a level no higher than what''s shown in the table''s Slot Level column for your level. When you reach 6th level, for example, you learn a new warlock spell, which can be 1st, 2nd, or 3rd level.","Additionally, when you gain a level in this class, you can choose one of the warlock spells you know and replace it with another spell from the warlock spell list, which also must be of a level for which you have spell slots."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spells Known of 1st Level and Higher","entries":["At 1st level, you know two 1st-level spells of your choice from the warlock spell list.","The Spells Known column of the Warlock table shows when you learn more warlock spells of your choice of 1st level and higher. A spell you choose must be of a level no higher than what''s shown in the table''s Slot Level column for your level. When you reach 6th level, for example, you learn a new warlock spell, which can be 1st, 2nd, or 3rd level.","Additionally, when you gain a level in this class, you can choose one of the warlock spells you know and replace it with another spell from the warlock spell list, which also must be of a level for which you have spell slots."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Pact Magic' AND class_name = 'Warlock' AND level = 1), 4, 'entries', 'Spellcasting Ability', '["Charisma is your spellcasting ability for your warlock spells, so you use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a warlock spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["cha"]},{"type":"abilityAttackMod","name":"Spell","attributes":["cha"]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Ability","entries":["Charisma is your spellcasting ability for your warlock spells, so you use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a warlock spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["cha"]},{"type":"abilityAttackMod","name":"Spell","attributes":["cha"]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Pact Magic' AND class_name = 'Warlock' AND level = 1), 5, 'entries', 'Spellcasting Focus', '["You can use an {@item arcane focus|phb} as a spellcasting focus for your warlock spells."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Focus","entries":["You can use an {@item arcane focus|phb} as a spellcasting focus for your warlock spells."]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eldritch Invocations', 1, 105, 'Warlock', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Invocations' AND class_name = 'Warlock' AND level = 2), 0, 'In your study of occult lore, you have unearthed eldritch invocations, fragments of forbidden knowledge that imbue you with an abiding magical ability.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Invocations' AND class_name = 'Warlock' AND level = 2), 1, 'At 2nd level, you gain two eldritch invocations of your choice. A list of the available options can be found on the {@filter Optional Features|optionalfeatures|Feature Type=EI} page. When you gain certain warlock levels, you gain additional invocations of your choice, as shown in the Invocations Known column of the Warlock table.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Invocations' AND class_name = 'Warlock' AND level = 2), 2, 'Additionally, when you gain a level in this class, you can choose one of the invocations you know and replace it with another invocation that you could learn at that level.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Invocations' AND class_name = 'Warlock' AND level = 2), 3, 'If an eldritch invocation has prerequisites, you must meet them to learn it. You can learn the invocation at the same time that you meet its prerequisites. A level prerequisite refers to your level in this class.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Pact Boon', 1, 105, 'Warlock', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Pact Boon' AND class_name = 'Warlock' AND level = 3), 0, 'At 3rd level, your otherworldly patron bestows a gift upon you for your loyal service. You gain one of the following features of your choice.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Pact Boon' AND class_name = 'Warlock' AND level = 3), 1, 'options', NULL, '[{"type":"refOptionalfeature","optionalfeature":"Pact of the Chain"},{"type":"refOptionalfeature","optionalfeature":"Pact of the Blade"},{"type":"refOptionalfeature","optionalfeature":"Pact of the Tome"},{"type":"refOptionalfeature","optionalfeature":"Pact of the Talisman|TCE"}]', NULL, NULL, NULL, NULL, '{"type":"options","count":1,"entries":[{"type":"refOptionalfeature","optionalfeature":"Pact of the Chain"},{"type":"refOptionalfeature","optionalfeature":"Pact of the Blade"},{"type":"refOptionalfeature","optionalfeature":"Pact of the Tome"},{"type":"refOptionalfeature","optionalfeature":"Pact of the Talisman|TCE"}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 105, 'Warlock', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Warlock' AND level = 4), 0, 'When you reach 4th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Warlock' AND level = 4), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eldritch Versatility', 2, 70, 'Warlock', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Versatility' AND class_name = 'Warlock' AND level = 4), 0, '{@i 4th-level warlock {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Versatility' AND class_name = 'Warlock' AND level = 4), 1, 'Whenever you reach a level in this class that grants the Ability Score Improvement feature, you can do one of the following, representing a change of focus in your occult studies:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Versatility' AND class_name = 'Warlock' AND level = 4), 2, 'list', NULL, '["Replace one cantrip you learned from this class''s Pact Magic feature with another cantrip from the {@filter warlock spell list|spells|level=0|class=Warlock}.","Replace the option you chose for the Pact Boon feature with one of that feature''s other options.","If you''re 12th level or higher, replace one spell from your Mystic Arcanum feature with another warlock spell of the same level."]', '["Replace one cantrip you learned from this class''s Pact Magic feature with another cantrip from the {@filter warlock spell list|spells|level=0|class=Warlock}.","Replace the option you chose for the Pact Boon feature with one of that feature''s other options.","If you''re 12th level or higher, replace one spell from your Mystic Arcanum feature with another warlock spell of the same level."]', NULL, NULL, NULL, '{"type":"list","items":["Replace one cantrip you learned from this class''s Pact Magic feature with another cantrip from the {@filter warlock spell list|spells|level=0|class=Warlock}.","Replace the option you chose for the Pact Boon feature with one of that feature''s other options.","If you''re 12th level or higher, replace one spell from your Mystic Arcanum feature with another warlock spell of the same level."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Versatility' AND class_name = 'Warlock' AND level = 4), 3, 'If this change makes you ineligible for any of your Eldritch Invocations, you must also replace them now, choosing invocations for which you qualify.');
