INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mystic Arcanum (8th level)' AND class_name = 'Warlock' AND level = 15), 0, 'At 15th level, your patron bestows upon you a magical secret called an arcanum. Choose one {@filter 8th-level spell from the warlock spell list|spells|level=8|class=Warlock} as this arcanum.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mystic Arcanum (8th level)' AND class_name = 'Warlock' AND level = 15), 1, 'You can cast your arcanum spell once without expending a spell slot. You must finish a long rest before you can do so again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 105, 'Warlock', 'PHB', NULL, NULL, 16, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Warlock' AND level = 16), 0, 'When you reach 16th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Warlock' AND level = 16), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Mystic Arcanum (9th level)', 1, 105, 'Warlock', 'PHB', NULL, NULL, 17, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mystic Arcanum (9th level)' AND class_name = 'Warlock' AND level = 17), 0, 'At 17th level, your patron bestows upon you a magical secret called an arcanum. Choose one {@filter 9th-level spell from the warlock spell list|spells|level=9|class=Warlock} as this arcanum.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Mystic Arcanum (9th level)' AND class_name = 'Warlock' AND level = 17), 1, 'You can cast your arcanum spell once without expending a spell slot. You must finish a long rest before you can do so again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 105, 'Warlock', 'PHB', NULL, NULL, 19, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Warlock' AND level = 19), 0, 'When you reach 19th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Warlock' AND level = 19), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Eldritch Master', 1, 105, 'Warlock', 'PHB', NULL, NULL, 20, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Eldritch Master' AND class_name = 'Warlock' AND level = 20), 0, 'At 20th level, you can draw on your inner reserve of mystical power while entreating your patron to regain expended spell slots. You can spend 1 minute entreating your patron for aid to regain all your expended spell slots from your Pact Magic feature. Once you regain spell slots with this feature, you must finish a long rest before you can do so again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Archfey', 1, 108, 'Warlock', 'PHB', 'Archfey', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Archfey' AND class_name = 'Warlock' AND level = 1), 0, 'Your patron is a lord or lady of the fey, a creature of legend who holds secrets that were forgotten before the mortal races were born. This being''s motivations are often inscrutable, and sometimes whimsical, and might involve a striving for greater magical power or the settling of age-old grudges. Beings of this sort include the Prince of Frost; the Queen of Air and Darkness, ruler of the Gloaming Court; Titania of the Summer Court; her consort Oberon, the Green Lord; Hyrsam, the Prince of Fools; and ancient hags.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Archfey' AND class_name = 'Warlock' AND level = 1), 1, 'entries', 'Expanded Spell List', '["The Archfey lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Archfey Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell faerie fire}, {@spell sleep}"],["2nd","{@spell calm emotions}, {@spell phantasmal force}"],["3rd","{@spell blink}, {@spell plant growth}"],["4th","{@spell dominate beast}, {@spell greater invisibility}"],["5th","{@spell dominate person}, {@spell seeming}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Expanded Spell List","entries":["The Archfey lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Archfey Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell faerie fire}, {@spell sleep}"],["2nd","{@spell calm emotions}, {@spell phantasmal force}"],["3rd","{@spell blink}, {@spell plant growth}"],["4th","{@spell dominate beast}, {@spell greater invisibility}"],["5th","{@spell dominate person}, {@spell seeming}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Archfey' AND class_name = 'Warlock' AND level = 1), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Fey Presence|Warlock||Archfey||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fey Presence', 1, 108, 'Warlock', 'PHB', 'Archfey', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Presence' AND class_name = 'Warlock' AND level = 1), 0, 'Starting at 1st level, your patron bestows upon you the ability to project the beguiling and fearsome presence of the fey. As an action, you can cause each creature in a 10-foot cube originating from you to make a Wisdom saving throw against your warlock spell save DC. The creatures that fail their saving throws are all {@condition charmed} or {@condition frightened} by you (your choice) until the end of your next turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fey Presence' AND class_name = 'Warlock' AND level = 1), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Misty Escape', 1, 108, 'Warlock', 'PHB', 'Archfey', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Misty Escape' AND class_name = 'Warlock' AND level = 6), 0, 'Starting at 6th level, you can vanish in a puff of mist in response to harm. When you take damage, you can use your reaction to turn {@condition invisible} and teleport up to 60 feet to an unoccupied space you can see. You remain {@condition invisible} until the start of your next turn or until you attack or cast a spell.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Misty Escape' AND class_name = 'Warlock' AND level = 6), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Beguiling Defenses', 1, 108, 'Warlock', 'PHB', 'Archfey', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Beguiling Defenses' AND class_name = 'Warlock' AND level = 10), 0, 'Beginning at 10th level, your patron teaches you how to turn the mind-affecting magic of your enemies against them. You are immune to being {@condition charmed}, and when another creature attempts to charm you, you can use your reaction to attempt to turn the charm back on that creature. The creature must succeed on a Wisdom saving throw against your warlock spell save DC or be {@condition charmed} by you for 1 minute or until the creature takes any damage.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dark Delirium', 1, 108, 'Warlock', 'PHB', 'Archfey', 'PHB', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dark Delirium' AND class_name = 'Warlock' AND level = 14), 0, 'Starting at 14th level, you can plunge a creature into an illusory realm. As an action, choose a creature that you can see within 60 feet of you. It must make a Wisdom saving throw against your warlock spell save DC. On a failed save, it is {@condition charmed} or {@condition frightened} by you (your choice) for 1 minute or until your {@status concentration} is broken (as if you are {@status concentration||concentrating} on a spell). This effect ends early if the creature takes any damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dark Delirium' AND class_name = 'Warlock' AND level = 14), 1, 'Until this illusion ends, the creature thinks it is lost in a misty realm, the appearance of which you choose. The creature can see and hear only itself, you, and the illusion.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dark Delirium' AND class_name = 'Warlock' AND level = 14), 2, 'You must finish a short or long rest before you can use this feature again.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Fiend', 1, 109, 'Warlock', 'PHB', 'Fiend', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Fiend' AND class_name = 'Warlock' AND level = 1), 0, 'You have made a pact with a fiend from the lower planes of existence, a being whose aims are evil, even if you strive against those aims. Such beings desire the corruption or destruction of all things, ultimately including you. Fiends powerful enough to forge a pact include demon lords such as Demogorgon, Orcus, Fraz-Urb''luu, and Baphomet; archdevils such as Asmodeus, Dispater, Mephistopheles, and Belial; pit fiends and balors that are especially mighty; and ultroloths and other lords of the yugoloths.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Fiend' AND class_name = 'Warlock' AND level = 1), 1, 'entries', 'Expanded Spell List', '["The Fiend lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Fiend Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell burning hands}, {@spell command}"],["2nd","{@spell blindness/deafness}, {@spell scorching ray}"],["3rd","{@spell fireball}, {@spell stinking cloud}"],["4th","{@spell fire shield}, {@spell wall of fire}"],["5th","{@spell flame strike}, {@spell hallow}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Expanded Spell List","entries":["The Fiend lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Fiend Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell burning hands}, {@spell command}"],["2nd","{@spell blindness/deafness}, {@spell scorching ray}"],["3rd","{@spell fireball}, {@spell stinking cloud}"],["4th","{@spell fire shield}, {@spell wall of fire}"],["5th","{@spell flame strike}, {@spell hallow}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Fiend' AND class_name = 'Warlock' AND level = 1), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Dark One''s Blessing|Warlock||Fiend||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dark One''s Blessing', 1, 109, 'Warlock', 'PHB', 'Fiend', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dark One''s Blessing' AND class_name = 'Warlock' AND level = 1), 0, 'Starting at 1st level, when you reduce a hostile creature to 0 hit points, you gain temporary hit points equal to your Charisma modifier + your warlock level (minimum of 1).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Dark One''s Own Luck', 1, 109, 'Warlock', 'PHB', 'Fiend', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dark One''s Own Luck' AND class_name = 'Warlock' AND level = 6), 0, 'Starting at 6th level, you can call on your patron to alter fate in your favor. When you make an ability check or a saving throw, you can use this feature to add a {@dice d10} to your roll. You can do so after seeing the initial roll but before any of the roll''s effects occur.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Dark One''s Own Luck' AND class_name = 'Warlock' AND level = 6), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fiendish Resilience', 1, 109, 'Warlock', 'PHB', 'Fiend', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fiendish Resilience' AND class_name = 'Warlock' AND level = 10), 0, 'Starting at 10th level, you can choose one damage type when you finish a short or long rest. You gain resistance to that damage type until you choose a different one with this feature. Damage from magical weapons or silver weapons ignores this resistance.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hurl Through Hell', 1, 109, 'Warlock', 'PHB', 'Fiend', 'PHB', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hurl Through Hell' AND class_name = 'Warlock' AND level = 14), 0, 'Starting at 14th level, when you hit a creature with an attack, you can use this feature to instantly transport the target through the lower planes. The creature disappears and hurtles through a nightmare landscape.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hurl Through Hell' AND class_name = 'Warlock' AND level = 14), 1, 'At the end of your next turn, the target returns to the space it previously occupied, or the nearest unoccupied space. If the target is not a fiend, it takes {@damage 10d10} psychic damage as it reels from its horrific experience.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hurl Through Hell' AND class_name = 'Warlock' AND level = 14), 2, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Great Old One', 1, 109, 'Warlock', 'PHB', 'Great Old One', 'PHB', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Great Old One' AND class_name = 'Warlock' AND level = 1), 0, 'Your patron is a mysterious entity whose nature is utterly foreign to the fabric of reality. It might come from the Far Realm, the space beyond reality, or it could be one of the elder gods known only in legends. Its motives are incomprehensible to mortals, and its knowledge so immense and ancient that even the greatest libraries pale in comparison to the vast secrets it holds. The Great Old One might be unaware of your existence or entirely indifferent to you, but the secrets you have learned allow you to draw your magic from it. Entities of this type include Ghaunadar, called That Which Lurks; Tharizdun, the Chained God; Dendar, the Night Serpent; Zargon, the Returner; Great Cthulhu; and other unfathomable beings.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Great Old One' AND class_name = 'Warlock' AND level = 1), 1, 'entries', 'Expanded Spell List', '["The Great Old One lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Great Old One Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell dissonant whispers}, {@spell Tasha''s hideous laughter}"],["2nd","{@spell detect thoughts}, {@spell phantasmal force}"],["3rd","{@spell clairvoyance}, {@spell sending}"],["4th","{@spell dominate beast}, {@spell Evard''s black tentacles}"],["5th","{@spell dominate person}, {@spell telekinesis}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Expanded Spell List","entries":["The Great Old One lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Great Old One Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell dissonant whispers}, {@spell Tasha''s hideous laughter}"],["2nd","{@spell detect thoughts}, {@spell phantasmal force}"],["3rd","{@spell clairvoyance}, {@spell sending}"],["4th","{@spell dominate beast}, {@spell Evard''s black tentacles}"],["5th","{@spell dominate person}, {@spell telekinesis}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Great Old One' AND class_name = 'Warlock' AND level = 1), 2, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Awakened Mind|Warlock||Great Old One||1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Awakened Mind', 1, 109, 'Warlock', 'PHB', 'Great Old One', 'PHB', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Awakened Mind' AND class_name = 'Warlock' AND level = 1), 0, 'Starting at 1st level, your alien knowledge gives you the ability to touch the minds of other creatures. You can telepathically speak to any creature you can see within 30 feet of you. You don''t need to share a language with the creature for it to understand your telepathic utterances, but the creature must be able to understand at least one language.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Entropic Ward', 1, 109, 'Warlock', 'PHB', 'Great Old One', 'PHB', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Entropic Ward' AND class_name = 'Warlock' AND level = 6), 0, 'At 6th level, you learn to magically ward yourself against attack and to turn an enemy''s failed strike into good luck for yourself. When a creature makes an attack roll against you, you can use your reaction to impose disadvantage on that roll. If the attack misses you, your next attack roll against the creature has advantage if you make it before the end of your next turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Entropic Ward' AND class_name = 'Warlock' AND level = 6), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Thought Shield', 1, 109, 'Warlock', 'PHB', 'Great Old One', 'PHB', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Thought Shield' AND class_name = 'Warlock' AND level = 10), 0, 'Starting at 10th level, your thoughts can''t be read by telepathy or other means unless you allow it. You also have resistance to psychic damage, and whenever a creature deals psychic damage to you, that creature takes the same amount of damage that you do.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Create Thrall', 1, 109, 'Warlock', 'PHB', 'Great Old One', 'PHB', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Create Thrall' AND class_name = 'Warlock' AND level = 14), 0, 'At 14th level, you gain the ability to infect a humanoid''s mind with the alien magic of your patron. You can use your action to touch an {@condition incapacitated} humanoid. That creature is then {@condition charmed} by you until a {@spell remove curse} spell is cast on it, the {@condition charmed} condition is removed from it, or you use this feature again.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Create Thrall' AND class_name = 'Warlock' AND level = 14), 1, 'You can communicate telepathically with the {@condition charmed} creature as long as the two of you are on the same plane of existence.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Undying', null, 139, 'Warlock', 'PHB', 'Undying', 'SCAG', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Undying' AND class_name = 'Warlock' AND level = 1), 0, 'Death holds no sway over your patron, who has unlocked the secrets of everlasting life, although such a prize—like all power—comes at a price. Once mortal, the Undying has seen mortal lifetimes pass like the seasons, like the flicker of endless days and nights. It has the secrets of the ages to share, secrets of life and death. Beings of this sort include Vecna, Lord of the Hand and the Eye; the dread Iuz; the lich-queen Vol; the Undying Court of Aerenal; Vlaakith, lich-queen of the githyanki; and the deathless wizard Fistandantilus.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Undying' AND class_name = 'Warlock' AND level = 1), 1, 'In the Realms, Undying patrons include Larloch the Shadow King, legendary master of Warlock''s Crypt, and Gilgeam, the God-King of Unther.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Undying' AND class_name = 'Warlock' AND level = 1), 2, 'entries', 'Expanded Spell List', '["The Undying lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Undying Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell false life}, {@spell ray of sickness}"],["2nd","{@spell blindness/deafness}, {@spell silence}"],["3rd","{@spell feign death}, {@spell speak with dead}"],["4th","{@spell aura of life}, {@spell death ward}"],["5th","{@spell contagion}, {@spell legend lore}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Expanded Spell List","entries":["The Undying lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Undying Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell false life}, {@spell ray of sickness}"],["2nd","{@spell blindness/deafness}, {@spell silence}"],["3rd","{@spell feign death}, {@spell speak with dead}"],["4th","{@spell aura of life}, {@spell death ward}"],["5th","{@spell contagion}, {@spell legend lore}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Undying' AND class_name = 'Warlock' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Among the Dead|Warlock||Undying|SCAG|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Among the Dead', null, 139, 'Warlock', 'PHB', 'Undying', 'SCAG', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Among the Dead' AND class_name = 'Warlock' AND level = 1), 0, 'Starting at 1st level, you learn the {@spell spare the dying} cantrip, which counts as a warlock cantrip for you. You also have advantage on saving throws against any disease.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Among the Dead' AND class_name = 'Warlock' AND level = 1), 1, 'Additionally, undead have difficulty harming you. If an undead targets you directly with an attack or a harmful spell, that creature must make a Wisdom saving throw against your spell save DC (an undead needn''t make the save when it includes you in an area effect, such as the explosion of {@spell fireball}). On a failed save, the creature must choose a new target or forfeit targeting someone instead of you, potentially wasting the attack or spell. On a successful save, the creature is immune to this effect for 24 hours. An undead is also immune to this effect for 24 hours if you target it with an attack or a harmful spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Defy Death', null, 139, 'Warlock', 'PHB', 'Undying', 'SCAG', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Defy Death' AND class_name = 'Warlock' AND level = 6), 0, 'Starting at 6th level, you can give yourself vitality when you cheat death or when you help someone else cheat it. You can regain hit points equal to {@dice 1d8} + your Constitution modifier (minimum of 1 hit point) when you succeed on a death saving throw or when you stabilize a creature with {@spell spare the dying}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Defy Death' AND class_name = 'Warlock' AND level = 6), 1, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Undying Nature', null, 139, 'Warlock', 'PHB', 'Undying', 'SCAG', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Undying Nature' AND class_name = 'Warlock' AND level = 10), 0, 'Beginning at 10th level, you can hold your breath indefinitely, and you don''t require food, water, or sleep, although you still require rest to reduce {@condition exhaustion} and still benefit from finishing short and long rests.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Undying Nature' AND class_name = 'Warlock' AND level = 10), 1, 'In addition, you age at a slower rate. For every 10 years that pass, your body ages only 1 year, and you are immune to being magically aged.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Indestructible Life', null, 139, 'Warlock', 'PHB', 'Undying', 'SCAG', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Indestructible Life' AND class_name = 'Warlock' AND level = 14), 0, 'When you reach 14th level, you partake some of the true secrets of the Undying. On your turn, you can use a bonus action to regain hit points equal to {@dice 1d8} + your warlock level. Additionally, if you put a severed body part of yours back in place when you use this feature, the part reattaches.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Indestructible Life' AND class_name = 'Warlock' AND level = 14), 1, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Fathomless', 2, 72, 'Warlock', 'PHB', 'Fathomless', 'TCE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Fathomless' AND class_name = 'Warlock' AND level = 1), 0, 'You have plunged into a pact with the deeps. An entity of the ocean, the Elemental Plane of Water, or another otherworldly sea now allows you to draw on its thalassic power. Is it merely using you to learn about terrestrial realms, or does it want you to open cosmic floodgates and drown the world?');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Fathomless' AND class_name = 'Warlock' AND level = 1), 1, 'Perhaps you were born into a generational cult that venerates the Fathomless and its spawn. Or you might have been shipwrecked and on the brink of drowning when your patron''s grasp offered you a chance at life. Whatever the reason for your pact, the sea and its unknown depths call to you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Fathomless' AND class_name = 'Warlock' AND level = 1), 2, 'Entities of the deep that might empower a warlock include krakens, ancient water elementals, godlike hallucinations dreamed into being by kuo-toa, merfolk demigods, and sea hag covens.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Fathomless' AND class_name = 'Warlock' AND level = 1), 3, 'entries', 'Expanded Spell List', '["{@i 1st-level Fathomless feature}","The Fathomless lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Fathomless Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell create or destroy water}, {@spell thunderwave}"],["2nd","{@spell gust of wind}, {@spell silence}"],["3rd","{@spell lightning bolt}, {@spell sleet storm}"],["4th","{@spell control water}, {@spell summon elemental|tce} (water only)"],["5th","{@spell Bigby''s hand} (appears as a tentacle), {@spell cone of cold}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Expanded Spell List","entries":["{@i 1st-level Fathomless feature}","The Fathomless lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Fathomless Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell create or destroy water}, {@spell thunderwave}"],["2nd","{@spell gust of wind}, {@spell silence}"],["3rd","{@spell lightning bolt}, {@spell sleet storm}"],["4th","{@spell control water}, {@spell summon elemental|tce} (water only)"],["5th","{@spell Bigby''s hand} (appears as a tentacle), {@spell cone of cold}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Fathomless' AND class_name = 'Warlock' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Tentacle of the Deeps|Warlock||Fathomless|TCE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Fathomless' AND class_name = 'Warlock' AND level = 1), 5, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Gift of the Sea|Warlock||Fathomless|TCE|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Gift of the Sea', 2, 72, 'Warlock', 'PHB', 'Fathomless', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Gift of the Sea' AND class_name = 'Warlock' AND level = 1), 0, '{@i 1st-level Fathomless feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Gift of the Sea' AND class_name = 'Warlock' AND level = 1), 1, 'You gain a swimming speed of 40 feet, and you can breathe underwater.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Tentacle of the Deeps', 2, 72, 'Warlock', 'PHB', 'Fathomless', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tentacle of the Deeps' AND class_name = 'Warlock' AND level = 1), 0, '{@i 1st-level Fathomless feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tentacle of the Deeps' AND class_name = 'Warlock' AND level = 1), 1, 'You can magically summon a spectral tentacle that strikes at your foes. As a bonus action, you create a 10-foot-long tentacle at a point you can see within 60 feet of you. The tentacle lasts for 1 minute or until you use this feature to create another tentacle.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tentacle of the Deeps' AND class_name = 'Warlock' AND level = 1), 2, 'When you create the tentacle, you can make a melee spell attack against one creature within 10 feet of it. On a hit, the target takes {@damage 1d8} cold damage, and its speed is reduced by 10 feet until the start of your next turn. When you reach 10th level in this class, the damage increases to {@damage 2d8}.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tentacle of the Deeps' AND class_name = 'Warlock' AND level = 1), 3, 'As a bonus action on your turn, you can move the tentacle up to 30 feet and repeat the attack.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Tentacle of the Deeps' AND class_name = 'Warlock' AND level = 1), 4, 'You can summon the tentacle a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Guardian Coil', 2, 72, 'Warlock', 'PHB', 'Fathomless', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Guardian Coil' AND class_name = 'Warlock' AND level = 6), 0, '{@i 6th-level Fathomless feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Guardian Coil' AND class_name = 'Warlock' AND level = 6), 1, 'Your Tentacle of the Deeps can defend you and others, interposing itself between them and harm. When you or a creature you can see takes damage while within 10 feet of the tentacle, you can use your reaction to choose one of those creatures and reduce the damage to that creature by {@dice 1d8}. When you reach 10th level in this class, the damage reduced by the tentacle increases to {@dice 2d8}.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Oceanic Soul', 2, 72, 'Warlock', 'PHB', 'Fathomless', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oceanic Soul' AND class_name = 'Warlock' AND level = 6), 0, '{@i 6th-level Fathomless feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Oceanic Soul' AND class_name = 'Warlock' AND level = 6), 1, 'You are now even more at home in the depths. You gain resistance to cold damage. In addition, when you are fully submerged, any creature that is also fully submerged can understand your speech, and you can understand theirs.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Grasping Tentacles', 2, 72, 'Warlock', 'PHB', 'Fathomless', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Grasping Tentacles' AND class_name = 'Warlock' AND level = 10), 0, '{@i 10th-level Fathomless feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Grasping Tentacles' AND class_name = 'Warlock' AND level = 10), 1, 'You learn the spell {@spell Evard''s black tentacles}. It counts as a warlock spell for you, but it doesn''t count against the number of spells you know. You can also cast it once without a spell slot, and you regain the ability to do so when you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Grasping Tentacles' AND class_name = 'Warlock' AND level = 10), 2, 'Whenever you cast this spell, your patron''s magic bolsters you, granting you a number of temporary hit points equal to your warlock level. Moreover, damage can''t break your {@status concentration} on this spell.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Fathomless Plunge', 2, 72, 'Warlock', 'PHB', 'Fathomless', 'TCE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fathomless Plunge' AND class_name = 'Warlock' AND level = 14), 0, '{@i 14th-level Fathomless feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fathomless Plunge' AND class_name = 'Warlock' AND level = 14), 1, 'You can magically open temporary conduits to watery destinations. As an action, you can teleport yourself and up to five other willing creatures that you can see within 30 feet of you. Amid a whirl of tentacles, you all vanish and then reappear up to 1 mile away in a body of water you''ve seen (pond size or larger) or within 30 feet of it, each of you appearing in an unoccupied space within 30 feet of the others.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Fathomless Plunge' AND class_name = 'Warlock' AND level = 14), 2, 'Once you use this feature, you can''t use it again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bottled Respite', 2, 73, 'Warlock', 'PHB', 'Genie', 'TCE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bottled Respite' AND class_name = 'Warlock' AND level = 1), 0, 'As an action, you can magically vanish and enter your vessel, which remains in the space you left. The interior of the vessel is an extradimensional space in the shape of a 20-foot-radius cylinder, 20 feet high, and resembles your vessel. The interior is appointed with cushions and low tables and is a comfortable temperature. While inside, you can hear the area around your vessel as if you were in its space. You can remain inside the vessel up to a number of hours equal to twice your proficiency bonus. You exit the vessel early if you use a bonus action to leave, if you die, or if the vessel is destroyed. When you exit the vessel, you appear in the unoccupied space closest to it. Any objects left in the vessel remain there until carried out, and if the vessel is destroyed, every object stored there harmlessly appears in the unoccupied spaces closest to the vessel''s former space. Once you enter the vessel, you can''t enter again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Genie''s Wrath', 2, 73, 'Warlock', 'PHB', 'Genie', 'TCE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Genie''s Wrath' AND class_name = 'Warlock' AND level = 1), 0, 'Once during each of your turns when you hit with an attack roll, you can deal extra damage to the target equal to your proficiency bonus. The type of this damage is determined by your patron: bludgeoning (dao), thunder (djinni), fire (efreeti), or cold (marid).');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Genie', 2, 73, 'Warlock', 'PHB', 'Genie', 'TCE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Genie' AND class_name = 'Warlock' AND level = 1), 0, 'You have made a pact with one of the rarest kinds of genie, a noble genie. Such entities rule vast fiefs on the Elemental Planes and have great influence over lesser genies and elemental creatures. Noble genies are varied in their motivations, but most are arrogant and wield power that rivals that of lesser deities. They delight in turning the table on mortals, who often bind genies into servitude, and readily enter into pacts that expand their reach.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Genie' AND class_name = 'Warlock' AND level = 1), 1, 'You choose your patron''s kind or determine it randomly, using the Genie Kind table.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Genie' AND class_name = 'Warlock' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Genie Kind', '["d4","Kind","Element"]', '[[1,"{@creature Dao}","Earth"],[2,"{@creature Djinni}","Air"],[3,"{@creature Efreeti}","Fire"],[4,"{@creature Marid}","Water"]]', '{"type":"table","caption":"Genie Kind","colLabels":["d4","Kind","Element"],"colStyles":["col-2 text-center","col-5","col-5"],"rows":[[1,"{@creature Dao}","Earth"],[2,"{@creature Djinni}","Air"],[3,"{@creature Efreeti}","Fire"],[4,"{@creature Marid}","Water"]]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Genie' AND class_name = 'Warlock' AND level = 1), 3, 'entries', 'Expanded Spell List', '["{@i 1st-level Genie feature}","The Genie lets you choose from an expanded list of spells when you learn a warlock spell. The Genie Expanded Spells table shows the genie spells that are added to the warlock spell list for you, along with the spells associated in the table with your patron''s kind: dao, djinni, efreeti, or marid.",{"type":"table","caption":"Genie Expanded Spells","colLabels":["Spell Level","Genie Spells","Dao Spells","Djinni Spells","Efreeti Spells","Marid Spells"],"colStyles":["col-1 text-center","col-2-2","col-2-2","col-2-2","col-2-2","col-2-2"],"rows":[["1st","{@spell detect evil and good}","{@spell sanctuary}","{@spell thunderwave}","{@spell burning hands}","{@spell fog cloud}"],["2nd","{@spell phantasmal force}","{@spell spike growth}","{@spell gust of wind}","{@spell scorching ray}","{@spell blur}"],["3rd","{@spell create food and water}","{@spell meld into stone}","{@spell wind wall}","{@spell fireball}","{@spell sleet storm}"],["4th","{@spell phantasmal killer}","{@spell stone shape}","{@spell greater invisibility}","{@spell fire shield}","{@spell control water}"],["5th","{@spell creation}","{@spell wall of stone}","{@spell seeming}","{@spell flame strike}","{@spell cone of cold}"],["9th","{@spell wish}","—","—","—","—"]]}]', NULL, NULL, NULL, NULL, '{"name":"Expanded Spell List","type":"entries","entries":["{@i 1st-level Genie feature}","The Genie lets you choose from an expanded list of spells when you learn a warlock spell. The Genie Expanded Spells table shows the genie spells that are added to the warlock spell list for you, along with the spells associated in the table with your patron''s kind: dao, djinni, efreeti, or marid.",{"type":"table","caption":"Genie Expanded Spells","colLabels":["Spell Level","Genie Spells","Dao Spells","Djinni Spells","Efreeti Spells","Marid Spells"],"colStyles":["col-1 text-center","col-2-2","col-2-2","col-2-2","col-2-2","col-2-2"],"rows":[["1st","{@spell detect evil and good}","{@spell sanctuary}","{@spell thunderwave}","{@spell burning hands}","{@spell fog cloud}"],["2nd","{@spell phantasmal force}","{@spell spike growth}","{@spell gust of wind}","{@spell scorching ray}","{@spell blur}"],["3rd","{@spell create food and water}","{@spell meld into stone}","{@spell wind wall}","{@spell fireball}","{@spell sleet storm}"],["4th","{@spell phantasmal killer}","{@spell stone shape}","{@spell greater invisibility}","{@spell fire shield}","{@spell control water}"],["5th","{@spell creation}","{@spell wall of stone}","{@spell seeming}","{@spell flame strike}","{@spell cone of cold}"],["9th","{@spell wish}","—","—","—","—"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Genie' AND class_name = 'Warlock' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Genie''s Vessel|Warlock||Genie|TCE|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Genie''s Vessel', 2, 73, 'Warlock', 'PHB', 'Genie', 'TCE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Genie''s Vessel' AND class_name = 'Warlock' AND level = 1), 0, '{@i 1st-level Genie feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Genie''s Vessel' AND class_name = 'Warlock' AND level = 1), 1, 'Your patron gifts you a magical vessel that grants you a measure of the genie''s power. The vessel is a Tiny object, and you can use it as a spellcasting focus for your warlock spells. You decide what the object is, or you can determine what it is randomly by rolling on the Genie''s Vessel table.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Genie''s Vessel' AND class_name = 'Warlock' AND level = 1), 2, 'table', NULL, NULL, NULL, 'Genie''s Vessel', '["d6","Vessel"]', '[["1","Oil lamp"],["2","Urn"],["3","Ring with a compartment"],["4","Stoppered bottle"],["5","Hollow statuette"],["6","Ornate lantern"]]', '{"type":"table","caption":"Genie''s Vessel","colLabels":["d6","Vessel"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","Oil lamp"],["2","Urn"],["3","Ring with a compartment"],["4","Stoppered bottle"],["5","Hollow statuette"],["6","Ornate lantern"]]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Genie''s Vessel' AND class_name = 'Warlock' AND level = 1), 3, 'While you are touching the vessel, you can use it in the following ways:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Genie''s Vessel' AND class_name = 'Warlock' AND level = 1), 4, 'options', NULL, '[{"type":"refSubclassFeature","subclassFeature":"Bottled Respite|Warlock|PHB|Genie|TCE|1"},{"type":"refSubclassFeature","subclassFeature":"Genie''s Wrath|Warlock|PHB|Genie|TCE|1"}]', NULL, NULL, NULL, NULL, '{"type":"options","style":"list-hang-notitle","entries":[{"type":"refSubclassFeature","subclassFeature":"Bottled Respite|Warlock|PHB|Genie|TCE|1"},{"type":"refSubclassFeature","subclassFeature":"Genie''s Wrath|Warlock|PHB|Genie|TCE|1"}]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Genie''s Vessel' AND class_name = 'Warlock' AND level = 1), 5, 'The vessel''s AC equals your spell save DC. Its hit points equal your warlock level plus your proficiency bonus, and it is immune to poison and psychic damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Genie''s Vessel' AND class_name = 'Warlock' AND level = 1), 6, 'If the vessel is destroyed or you lose it, you can perform a 1-hour ceremony to receive a replacement from your patron. This ceremony can be performed during a short or long rest, and the previous vessel is destroyed if it still exists. The vessel vanishes in a flare of elemental power when you die.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Elemental Gift', 2, 73, 'Warlock', 'PHB', 'Genie', 'TCE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Gift' AND class_name = 'Warlock' AND level = 6), 0, '{@i 6th-level Genie feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Gift' AND class_name = 'Warlock' AND level = 6), 1, 'You begin to take on characteristics of your patron''s kind. You now have resistance to a damage type determined by your patron''s kind: bludgeoning (dao), thunder (djinni), fire (efreeti), or cold (marid).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Elemental Gift' AND class_name = 'Warlock' AND level = 6), 2, 'In addition, as a bonus action, you can give yourself a flying speed of 30 feet that lasts for 10 minutes, during which you can hover. You can use this bonus action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Sanctuary Vessel', 2, 73, 'Warlock', 'PHB', 'Genie', 'TCE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sanctuary Vessel' AND class_name = 'Warlock' AND level = 10), 0, '{@i 10th-level Genie feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sanctuary Vessel' AND class_name = 'Warlock' AND level = 10), 1, 'When you enter your Genie''s Vessel via the Bottled Respite feature, you can now choose up to five willing creatures that you can see within 30 feet of you, and the chosen creatures are drawn into the vessel with you.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sanctuary Vessel' AND class_name = 'Warlock' AND level = 10), 2, 'As a bonus action, you can eject any number of creatures from the vessel, and everyone is ejected if you leave or die or if the vessel is destroyed.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Sanctuary Vessel' AND class_name = 'Warlock' AND level = 10), 3, 'In addition, anyone (including you) who remains within the vessel for at least 10 minutes gains the benefit of finishing a short rest, and anyone can add your proficiency bonus to the number of hit points they regain if they spend any Hit Dice as part of a short rest there.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Limited Wish', 2, 73, 'Warlock', 'PHB', 'Genie', 'TCE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Limited Wish' AND class_name = 'Warlock' AND level = 14), 0, '{@i 14th-level Genie feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Limited Wish' AND class_name = 'Warlock' AND level = 14), 1, 'You entreat your patron to grant you a small wish. As an action, you can speak your desire to your Genie''s Vessel, requesting the effect of one {@filter spell that is 6th level or lower and has a casting time of 1 action|spells|cast time=action|level=0;1;2;3;4;5;6}. The spell can be from any class''s spell list, and you don''t need to meet the requirements in that spell, including costly components; the spell simply takes effect as part of this action.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Limited Wish' AND class_name = 'Warlock' AND level = 14), 2, 'Once you use this feature, you can''t use it again until you finish {@dice 1d4} long rests.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Undead', null, 30, 'Warlock', 'PHB', 'Undead', 'VRGR', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Undead' AND class_name = 'Warlock' AND level = 1), 0, 'You''ve made a pact with a deathless being, a creature that defies the cycle and life and death, forsaking its mortal shell so it might eternally pursue its unfathomable ambitions. For such beings, time and morality are fleeting things, the concerns of those for whom grains of sand still rush through life''s hourglass. Having once been mortal themselves, these ancient undead know firsthand the paths of ambition and the routes past the doors of death. They eagerly share this profane knowledge, along with other secrets, with those who work their will among the living.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Undead' AND class_name = 'Warlock' AND level = 1), 1, 'Beings of this type include the demilich Acererak, the vampire tyrant Kas the Bloody-Handed, the githyanki lich-queen Vlaakith, the dracolich Dragotha, the undead pharaoh Ankhtepot, and the elusive Darklord, Azalin Rex.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Undead' AND class_name = 'Warlock' AND level = 1), 2, 'entries', 'Expanded Spell List', '["{@i 1st-level Undead feature}","The Undead lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Undead Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell bane}, {@spell false life}"],["2nd","{@spell blindness/deafness}, {@spell phantasmal force}"],["3rd","{@spell phantom steed}, {@spell speak with dead}"],["4th","{@spell death ward}, {@spell greater invisibility}"],["5th","{@spell antilife shell}, {@spell cloudkill}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Expanded Spell List","entries":["{@i 1st-level Undead feature}","The Undead lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Undead Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell bane}, {@spell false life}"],["2nd","{@spell blindness/deafness}, {@spell phantasmal force}"],["3rd","{@spell phantom steed}, {@spell speak with dead}"],["4th","{@spell death ward}, {@spell greater invisibility}"],["5th","{@spell antilife shell}, {@spell cloudkill}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Undead' AND class_name = 'Warlock' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Form of Dread|Warlock||Undead|VRGR|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Form of Dread', null, 30, 'Warlock', 'PHB', 'Undead', 'VRGR', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Form of Dread' AND class_name = 'Warlock' AND level = 1), 0, '{@i 1st-level Undead feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Form of Dread' AND class_name = 'Warlock' AND level = 1), 1, 'You manifest an aspect of your patron''s dreadful power. As a bonus action, you transform for 1 minute. You gain the following benefits while transformed:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Form of Dread' AND class_name = 'Warlock' AND level = 1), 2, 'list', NULL, '["You gain temporary hit points equal to {@dice 1d10} + your warlock level.","Once during each of your turns, when you hit a creature with an attack roll, you can force it to make a Wisdom saving throw, and if the saving throw fails, the target is {@condition frightened} of you until the end of your next turn.","You are immune to the {@condition frightened} condition."]', '["You gain temporary hit points equal to {@dice 1d10} + your warlock level.","Once during each of your turns, when you hit a creature with an attack roll, you can force it to make a Wisdom saving throw, and if the saving throw fails, the target is {@condition frightened} of you until the end of your next turn.","You are immune to the {@condition frightened} condition."]', NULL, NULL, NULL, '{"type":"list","items":["You gain temporary hit points equal to {@dice 1d10} + your warlock level.","Once during each of your turns, when you hit a creature with an attack roll, you can force it to make a Wisdom saving throw, and if the saving throw fails, the target is {@condition frightened} of you until the end of your next turn.","You are immune to the {@condition frightened} condition."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Form of Dread' AND class_name = 'Warlock' AND level = 1), 3, 'You can transform a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Form of Dread' AND class_name = 'Warlock' AND level = 1), 4, 'The appearance of your Form of Dread reflects some aspect of your patron. For example, your form could be a shroud of shadows forming the crown and robes of your lich patron, or your body might glow with glyphs from ancient funerary rites and be surrounded by desert winds, suggesting your mummy patron.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Grave Touched', null, 30, 'Warlock', 'PHB', 'Undead', 'VRGR', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Grave Touched' AND class_name = 'Warlock' AND level = 6), 0, '{@i 6th-level Undead feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Grave Touched' AND class_name = 'Warlock' AND level = 6), 1, 'Your patron''s powers have a profound effect on your body and magic. You don''t need to eat, drink, or breathe.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Grave Touched' AND class_name = 'Warlock' AND level = 6), 2, 'In addition, once during each of your turns, when you hit a creature with an attack roll and roll damage against the creature, you can replace the damage type with necrotic damage. While you are using your Form of Dread, you can roll one additional damage die when determining the necrotic damage the target takes.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Necrotic Husk', null, 30, 'Warlock', 'PHB', 'Undead', 'VRGR', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Necrotic Husk' AND class_name = 'Warlock' AND level = 10), 0, '{@i 10th-level Undead feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Necrotic Husk' AND class_name = 'Warlock' AND level = 10), 1, 'Your connection to undeath and necrotic energy now saturates your body. You have resistance to necrotic damage. If you are transformed using your Form of Dread, you instead become immune to necrotic damage.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Necrotic Husk' AND class_name = 'Warlock' AND level = 10), 2, 'In addition, when you would be reduced to 0 hit points, you can use your reaction to drop to 1 hit point instead and cause your body to erupt with deathly energy. Each creature of your choice that is within 30 feet of you takes necrotic damage equal to {@damage 2d10} + your warlock level. You then gain 1 level of {@condition exhaustion}. Once you use this reaction, you can''t do so again until you finish {@dice 1d4} long rests.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spirit Projection', null, 30, 'Warlock', 'PHB', 'Undead', 'VRGR', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Projection' AND class_name = 'Warlock' AND level = 14), 0, '{@i 14th-level Undead feature}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Projection' AND class_name = 'Warlock' AND level = 14), 1, 'Your spirit can become untethered from your physical form. As an action, you can project your spirit from your body. The body you leave behind is {@condition unconscious} and in a state of suspended animation.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Projection' AND class_name = 'Warlock' AND level = 14), 2, 'Your spirit resembles your mortal form in almost every way, replicating your game statistics but not your possessions. Any damage or other effects that apply to your spirit or physical body affects the other. Your spirit can remain outside your body for up to 1 hour or until your {@status concentration} is broken (as if {@status concentration||concentrating} on a spell). When your projection ends, your spirit returns to your body or your body magically teleports to your spirit''s space (your choice).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Projection' AND class_name = 'Warlock' AND level = 14), 3, 'While projecting your spirit, you gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spirit Projection' AND class_name = 'Warlock' AND level = 14), 4, 'list', NULL, '["Your spirit and body gain resistance to bludgeoning, piercing, and slashing damage.","When you cast a spell of the conjuration or necromancy school, the spell doesn''t require verbal or somatic components or material components that lack a gold cost.","You have a flying speed equal to your walking speed and can hover. You can move through creatures and objects as if they were {@quickref difficult terrain||3}, but you take {@damage 1d10} force damage if you end your turn inside a creature or an object.","While you are using your Form of Dread, once during each of your turns when you deal necrotic damage to a creature, you regain hit points equal to half the amount of necrotic damage dealt."]', '["Your spirit and body gain resistance to bludgeoning, piercing, and slashing damage.","When you cast a spell of the conjuration or necromancy school, the spell doesn''t require verbal or somatic components or material components that lack a gold cost.","You have a flying speed equal to your walking speed and can hover. You can move through creatures and objects as if they were {@quickref difficult terrain||3}, but you take {@damage 1d10} force damage if you end your turn inside a creature or an object.","While you are using your Form of Dread, once during each of your turns when you deal necrotic damage to a creature, you regain hit points equal to half the amount of necrotic damage dealt."]', NULL, NULL, NULL, '{"type":"list","items":["Your spirit and body gain resistance to bludgeoning, piercing, and slashing damage.","When you cast a spell of the conjuration or necromancy school, the spell doesn''t require verbal or somatic components or material components that lack a gold cost.","You have a flying speed equal to your walking speed and can hover. You can move through creatures and objects as if they were {@quickref difficult terrain||3}, but you take {@damage 1d10} force damage if you end your turn inside a creature or an object.","While you are using your Form of Dread, once during each of your turns when you deal necrotic damage to a creature, you regain hit points equal to half the amount of necrotic damage dealt."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spirit Projection' AND class_name = 'Warlock' AND level = 14), 5, 'Once you use this feature, you can''t do so again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Celestial', 3, 54, 'Warlock', 'PHB', 'Celestial', 'XGE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Celestial' AND class_name = 'Warlock' AND level = 1), 0, 'Your patron is a powerful being of the Upper Planes. You have bound yourself to an ancient empyrean, solar, ki-rin, unicorn, or other entity that resides in the planes of everlasting bliss. Your pact with that being allows you to experience the barest touch of the holy light that illuminates the multiverse.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Celestial' AND class_name = 'Warlock' AND level = 1), 1, 'Being connected to such power can cause changes in your behavior and beliefs. You might find yourself driven to annihilate the undead, to defeat fiends, and to protect the innocent. At times, your heart might also be filled with a longing for the celestial realm of your patron, and a desire to wander that paradise for the rest of your days. But you know that your mission is among mortals for now, and that your pact binds you to bring light to the dark places of the world.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Celestial' AND class_name = 'Warlock' AND level = 1), 2, 'entries', 'Expanded Spell List', '["The Celestial lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Celestial Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell cure wounds}, {@spell guiding bolt}"],["2nd","{@spell flaming sphere}, {@spell lesser restoration}"],["3rd","{@spell daylight}, {@spell revivify}"],["4th","{@spell guardian of faith}, {@spell wall of fire}"],["5th","{@spell flame strike}, {@spell greater restoration}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Expanded Spell List","entries":["The Celestial lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Celestial Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell cure wounds}, {@spell guiding bolt}"],["2nd","{@spell flaming sphere}, {@spell lesser restoration}"],["3rd","{@spell daylight}, {@spell revivify}"],["4th","{@spell guardian of faith}, {@spell wall of fire}"],["5th","{@spell flame strike}, {@spell greater restoration}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Celestial' AND class_name = 'Warlock' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Bonus Cantrips|Warlock||Celestial|XGE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Celestial' AND class_name = 'Warlock' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Healing Light|Warlock||Celestial|XGE|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Bonus Cantrips', 3, 54, 'Warlock', 'PHB', 'Celestial', 'XGE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Bonus Cantrips' AND class_name = 'Warlock' AND level = 1), 0, 'At 1st level, you learn the {@spell sacred flame} and {@spell light} cantrips. They count as warlock cantrips for you, but they don''t count against your number of cantrips known.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Healing Light', 3, 54, 'Warlock', 'PHB', 'Celestial', 'XGE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Healing Light' AND class_name = 'Warlock' AND level = 1), 0, 'At 1st level, you gain the ability to channel celestial energy to heal wounds. You have a pool of {@dice d6}s that you spend to fuel this healing. The number of dice in the pool equals 1 + your warlock level.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Healing Light' AND class_name = 'Warlock' AND level = 1), 1, 'As a bonus action, you can heal one creature you can see within 60 feet of you, spending dice from the pool. The maximum number of dice you can spend at once equals your Charisma modifier (minimum of one die). Roll the dice you spend, add them together, and restore a number of hit points equal to the total.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Healing Light' AND class_name = 'Warlock' AND level = 1), 2, 'Your pool regains all expended dice when you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Radiant Soul', 3, 54, 'Warlock', 'PHB', 'Celestial', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Radiant Soul' AND class_name = 'Warlock' AND level = 6), 0, 'Starting at 6th level, your link to the Celestial allows you to serve as a conduit for radiant energy. You have resistance to radiant damage, and when you cast a spell that deals radiant or fire damage, you can add your Charisma modifier to one radiant or fire damage roll of that spell against one of its targets.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Celestial Resilience', 3, 54, 'Warlock', 'PHB', 'Celestial', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Celestial Resilience' AND class_name = 'Warlock' AND level = 10), 0, 'Starting at 10th level, you gain temporary hit points whenever you finish a short or long rest. These temporary hit points equal your warlock level + your Charisma modifier. Additionally, choose up to five creatures you can see at the end of the rest. Those creatures each gain temporary hit points equal to half your warlock level + your Charisma modifier.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Searing Vengeance', 3, 54, 'Warlock', 'PHB', 'Celestial', 'XGE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Searing Vengeance' AND class_name = 'Warlock' AND level = 14), 0, 'Starting at 14th level, the radiant energy you channel allows you to resist death. When you have to make a death saving throw at the start of your turn, you can instead spring back to your feet with a burst of radiant energy. You regain hit points equal to half your hit point maximum, and then you stand up if you so choose. Each creature of your choice that is within 30 feet of you takes radiant damage equal to {@dice 2d8} + your Charisma modifier, and it is {@condition blinded} until the end of the current turn.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Searing Vengeance' AND class_name = 'Warlock' AND level = 14), 1, 'Once you use this feature, you can''t use it again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('The Hexblade', 3, 55, 'Warlock', 'PHB', 'Hexblade', 'XGE', 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Hexblade' AND class_name = 'Warlock' AND level = 1), 0, 'You have made your pact with a mysterious entity from the Shadowfell—a force that manifests in sentient magic weapons carved from the stuff of shadow. The mighty sword Blackrazor is the most notable of these weapons, which have been spread across the multiverse over the ages. The shadowy force behind these weapons can offer power to warlocks who form pacts with it. Many Hexblade warlocks create weapons that emulate those formed in the Shadowfell. Others forgo such arms, content to weave the dark magic of that plane into their spellcasting.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'The Hexblade' AND class_name = 'Warlock' AND level = 1), 1, 'Because the Raven Queen is known to have forged the first of these weapons, many sages speculate that she and the force are one and that the weapons, along with Hexblade warlocks, are tools she uses to manipulate events on the Material Plane to her inscrutable ends.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Hexblade' AND class_name = 'Warlock' AND level = 1), 2, 'entries', 'Expanded Spell List', '["The Hexblade lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Hexblade Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell shield}, {@spell wrathful smite}"],["2nd","{@spell blur}, {@spell branding smite}"],["3rd","{@spell blink}, {@spell elemental weapon}"],["4th","{@spell phantasmal killer}, {@spell staggering smite}"],["5th","{@spell banishing smite}, {@spell cone of cold}"]]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Expanded Spell List","entries":["The Hexblade lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.",{"type":"table","caption":"Hexblade Expanded Spells","colLabels":["Spell Level","Spells"],"colStyles":["col-2 text-center","col-10"],"rows":[["1st","{@spell shield}, {@spell wrathful smite}"],["2nd","{@spell blur}, {@spell branding smite}"],["3rd","{@spell blink}, {@spell elemental weapon}"],["4th","{@spell phantasmal killer}, {@spell staggering smite}"],["5th","{@spell banishing smite}, {@spell cone of cold}"]]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Hexblade' AND class_name = 'Warlock' AND level = 1), 3, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Hexblade''s Curse|Warlock||Hexblade|XGE|1"}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'The Hexblade' AND class_name = 'Warlock' AND level = 1), 4, 'refSubclassFeature', NULL, NULL, NULL, NULL, NULL, NULL, '{"type":"refSubclassFeature","subclassFeature":"Hex Warrior|Warlock||Hexblade|XGE|1"}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hex Warrior', 3, 55, 'Warlock', 'PHB', 'Hexblade', 'XGE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hex Warrior' AND class_name = 'Warlock' AND level = 1), 0, 'At 1st level, you acquire the training necessary to effectively arm yourself for battle. You gain proficiency with medium armor, shields, and martial weapons.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hex Warrior' AND class_name = 'Warlock' AND level = 1), 1, 'The influence of your patron also allows you to mystically channel your will through a particular weapon. Whenever you finish a long rest, you can touch one weapon that you are proficient with and that lacks the two-handed property. When you attack with that weapon, you can use your Charisma modifier, instead of Strength or Dexterity, for the attack and damage rolls. This benefit lasts until you finish a long rest. If you later gain the Pact of the Blade feature, this benefit extends to every pact weapon you conjure with that feature, no matter the weapon''s type.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Hexblade''s Curse', 3, 55, 'Warlock', 'PHB', 'Hexblade', 'XGE', 1, 1, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hexblade''s Curse' AND class_name = 'Warlock' AND level = 1), 0, 'Starting at 1st level, you gain the ability to place a baleful curse on someone. As a bonus action, choose one creature you can see within 30 feet of you. The target is cursed for 1 minute. The curse ends early if the target dies, you die, or you are {@condition incapacitated}. Until the curse ends, you gain the following benefits:');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Hexblade''s Curse' AND class_name = 'Warlock' AND level = 1), 1, 'list', NULL, '["You gain a bonus to damage rolls against the cursed target. The bonus equals your proficiency bonus.","Any attack roll you make against the cursed target is a critical hit on a roll of 19 or 20 on the {@dice d20}.","If the cursed target dies, you regain hit points equal to your warlock level + your Charisma modifier (minimum of 1 hit point)."]', '["You gain a bonus to damage rolls against the cursed target. The bonus equals your proficiency bonus.","Any attack roll you make against the cursed target is a critical hit on a roll of 19 or 20 on the {@dice d20}.","If the cursed target dies, you regain hit points equal to your warlock level + your Charisma modifier (minimum of 1 hit point)."]', NULL, NULL, NULL, '{"type":"list","items":["You gain a bonus to damage rolls against the cursed target. The bonus equals your proficiency bonus.","Any attack roll you make against the cursed target is a critical hit on a roll of 19 or 20 on the {@dice d20}.","If the cursed target dies, you regain hit points equal to your warlock level + your Charisma modifier (minimum of 1 hit point)."]}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Hexblade''s Curse' AND class_name = 'Warlock' AND level = 1), 2, 'You can''t use this feature again until you finish a short or long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Accursed Specter', 3, 55, 'Warlock', 'PHB', 'Hexblade', 'XGE', 6, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Accursed Specter' AND class_name = 'Warlock' AND level = 6), 0, 'Starting at 6th level, you can curse the soul of a person you slay, temporarily binding it to your service. When you slay a humanoid, you can cause its spirit to rise from its corpse as a {@creature specter}, the statistics for which are in the Monster Manual. When the specter appears, it gains temporary hit points equal to half your warlock level. Roll initiative for the specter, which has its own turns. It obeys your verbal commands, and it gains a special bonus to its attack rolls equal to your Charisma modifier (minimum of +0).');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Accursed Specter' AND class_name = 'Warlock' AND level = 6), 1, 'The specter remains in your service until the end of your next long rest, at which point it vanishes to the afterlife.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Accursed Specter' AND class_name = 'Warlock' AND level = 6), 2, 'Once you bind a specter with this feature, you can''t use the feature again until you finish a long rest.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Armor of Hexes', 3, 55, 'Warlock', 'PHB', 'Hexblade', 'XGE', 10, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Armor of Hexes' AND class_name = 'Warlock' AND level = 10), 0, 'At 10th level, your hex grows more powerful. If the target cursed by your Hexblade''s Curse hits you with an attack roll, you can use your reaction to roll a {@dice d6}. On a 4 or higher, the attack instead misses you, regardless of its roll.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Master of Hexes', 3, 55, 'Warlock', 'PHB', 'Hexblade', 'XGE', 14, 2, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Master of Hexes' AND class_name = 'Warlock' AND level = 14), 0, 'Starting at 14th level, you can spread your Hexblade''s Curse from a slain creature to another creature. When the creature cursed by your Hexblade''s Curse dies, you can apply the curse to a different creature you can see within 30 feet of you, provided you aren''t {@condition incapacitated}. When you apply the curse in this way, you don''t regain hit points from the death of the previously cursed creature.');
