INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Recovery', 1, 112, 'Wizard', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Recovery' AND class_name = 'Wizard' AND level = 1), 0, 'You have learned to regain some of your magical energy by studying your spellbook. Once per day when you finish a short rest, you can choose expended spell slots to recover. The spell slots can have a combined level that is equal to or less than half your wizard level (rounded up), and none of the slots can be 6th level or higher.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Recovery' AND class_name = 'Wizard' AND level = 1), 1, 'For example, if you''re a 4th-level wizard, you can recover up to two levels worth of spell slots. You can recover either a 2nd-level spell slot or two 1st-level spell slots.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Spellcasting', 1, 112, 'Wizard', 'PHB', NULL, NULL, 1, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 0, 'As a student of arcane magic, you have a spellbook containing spells that show the first glimmerings of your true power. See {@book chapter 10|PHB|10} for the general rules of spellcasting and {@book chapter 11|PHB|11} for the {@filter wizard spell list|spells|class=wizard}.');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 1, 'entries', 'Cantrips', '["At 1st level, you know three cantrips of your choice from the wizard spell list. You learn additional wizard cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Wizard table."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Cantrips","entries":["At 1st level, you know three cantrips of your choice from the wizard spell list. You learn additional wizard cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Wizard table."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 2, 'entries', 'Spellbook', '["At 1st level, you have a spellbook containing six 1st-level {@filter wizard spells|spells|class=wizard} of your choice. Your spellbook is the repository of the wizard spells you know, except your cantrips, which are fixed in your mind."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellbook","entries":["At 1st level, you have a spellbook containing six 1st-level {@filter wizard spells|spells|class=wizard} of your choice. Your spellbook is the repository of the wizard spells you know, except your cantrips, which are fixed in your mind."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 3, 'entries', 'Preparing and Casting Spells', '["The Wizard table shows how many spell slots you have to cast your wizard spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","You prepare the list of wizard spells that are available for you to cast. To do so, choose a number of wizard spells from your spellbook equal to your Intelligence modifier + your wizard level (minimum of one spell). The spells must be of a level for which you have spell slots.","For example, if you''re a 3rd-level wizard, you have four 1st-level and two 2nd-level spell slots. With an Intelligence of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination, chosen from your spellbook. If you prepare the 1st-level spell {@spell magic missile}, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.","You can change your list of prepared spells when you finish a long rest. Preparing a new list of wizard spells requires time spent studying your spellbook and memorizing the incantations and gestures you must make to cast the spell: at least 1 minute per spell level for each spell on your list."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Preparing and Casting Spells","entries":["The Wizard table shows how many spell slots you have to cast your wizard spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","You prepare the list of wizard spells that are available for you to cast. To do so, choose a number of wizard spells from your spellbook equal to your Intelligence modifier + your wizard level (minimum of one spell). The spells must be of a level for which you have spell slots.","For example, if you''re a 3rd-level wizard, you have four 1st-level and two 2nd-level spell slots. With an Intelligence of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination, chosen from your spellbook. If you prepare the 1st-level spell {@spell magic missile}, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.","You can change your list of prepared spells when you finish a long rest. Preparing a new list of wizard spells requires time spent studying your spellbook and memorizing the incantations and gestures you must make to cast the spell: at least 1 minute per spell level for each spell on your list."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 4, 'entries', 'Spellcasting Ability', '["Intelligence is your spellcasting ability for your wizard spells, since you learn your wizard spells through dedicated study and memorization. You use your Intelligence whenever a spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for a wizard spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["int"]},{"type":"abilityAttackMod","name":"Spell","attributes":["int"]}]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Ability","entries":["Intelligence is your spellcasting ability for your wizard spells, since you learn your wizard spells through dedicated study and memorization. You use your Intelligence whenever a spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for a wizard spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["int"]},{"type":"abilityAttackMod","name":"Spell","attributes":["int"]}]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 5, 'entries', 'Ritual Casting', '["You can cast a wizard spell as a ritual if that spell has the ritual tag and you have the spell in your spellbook. You don''t need to have the spell prepared."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Ritual Casting","entries":["You can cast a wizard spell as a ritual if that spell has the ritual tag and you have the spell in your spellbook. You don''t need to have the spell prepared."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 6, 'entries', 'Spellcasting Focus', '["You can use an {@item arcane focus|phb} as a spellcasting focus for your wizard spells."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Spellcasting Focus","entries":["You can use an {@item arcane focus|phb} as a spellcasting focus for your wizard spells."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 7, 'entries', 'Learning Spells of 1st Level and Higher', '["Each time you gain a wizard level, you can add two wizard spells of your choice to your spellbook. Each of these spells must be of a level for which you have spell slots, as shown on the Wizard table. On your adventures, you might find other spells that you can add to your spellbook (see \"Your Spellbook\")."]', NULL, NULL, NULL, NULL, '{"type":"entries","name":"Learning Spells of 1st Level and Higher","entries":["Each time you gain a wizard level, you can add two wizard spells of your choice to your spellbook. Each of these spells must be of a level for which you have spell slots, as shown on the Wizard table. On your adventures, you might find other spells that you can add to your spellbook (see \"Your Spellbook\")."]}');
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES ((SELECT id FROM features WHERE name = 'Spellcasting' AND class_name = 'Wizard' AND level = 1), 8, 'inset', 'Your Spellbook', '["The spells that you add to your spellbook as you gain levels reflect the arcane research you conduct on your own, as well as intellectual breakthroughs you have had about the nature of the multiverse. You might find other spells during your adventures. You could discover a spell recorded on a scroll in an evil wizard''s chest, for example, or in a dusty tome in an ancient library.","A spellbook doesn''t contain cantrips.",{"type":"entries","entries":[{"type":"entries","name":"Copying a Spell into the Book","entries":["When you find a wizard spell of 1st level or higher, you can add it to your spellbook if it is of a spell level you can prepare and if you can spare the time to decipher and copy it.","Copying a spell into your spellbook involves reproducing the basic form of the spell, then deciphering the unique system of notation used by the wizard who wrote it. You must practice the spell until you understand the sounds or gestures required, then transcribe it into your spellbook using your own notation.","For each level of the spell, the process takes 2 hours and costs 50 gp. The cost represents material components you expend as you experiment with the spell to master it, as well as the fine inks you need to record it. Once you have spent this time and money, you can prepare the spell just like your other spells.",{"type":"entries","name":"Copying from a Spell Scroll","source":"DMG","page":139,"entries":["A wizard spell on a spell scroll can be copied just as spells in spellbooks can be copied. When you copy a spell from a spell scroll, you must succeed on an Intelligence ({@skill Arcana}) check with a DC equal to 10 + the spell''s level. If the check succeeds, the spell is successfully copied. Whether the check succeeds or fails, the spell scroll is destroyed."]}]}]},{"type":"entries","entries":[{"type":"entries","name":"Replacing the Book","entries":["You can copy a spell from your own spellbook into another book—for example, if you want to make a backup copy of your spellbook. This is just like copying a new spell into your spellbook, but faster and easier, since you understand your own notation and already know how to cast the spell. You need spend only 1 hour and 10 gp for each level of the copied spell.","If you lose your spellbook, you can use the same procedure to transcribe the spells that you have prepared into a new spellbook. Filling out the remainder of your spellbook requires you to find new spells to do so, as normal. For this reason, many wizards keep backup spellbooks in a safe place."]}]},{"type":"entries","entries":[{"type":"entries","name":"The Book''s Appearance","entries":["Your spellbook is a unique compilation of spells, with its own decorative flourishes and margin notes. It might be a plain, functional leather volume that you received as a gift from your master, a finely bound gilt-edged tome you found in an ancient library, or even a loose collection of notes scrounged together after you lost your previous spellbook in a mishap."]}]}]', NULL, NULL, NULL, NULL, '{"type":"inset","name":"Your Spellbook","entries":["The spells that you add to your spellbook as you gain levels reflect the arcane research you conduct on your own, as well as intellectual breakthroughs you have had about the nature of the multiverse. You might find other spells during your adventures. You could discover a spell recorded on a scroll in an evil wizard''s chest, for example, or in a dusty tome in an ancient library.","A spellbook doesn''t contain cantrips.",{"type":"entries","entries":[{"type":"entries","name":"Copying a Spell into the Book","entries":["When you find a wizard spell of 1st level or higher, you can add it to your spellbook if it is of a spell level you can prepare and if you can spare the time to decipher and copy it.","Copying a spell into your spellbook involves reproducing the basic form of the spell, then deciphering the unique system of notation used by the wizard who wrote it. You must practice the spell until you understand the sounds or gestures required, then transcribe it into your spellbook using your own notation.","For each level of the spell, the process takes 2 hours and costs 50 gp. The cost represents material components you expend as you experiment with the spell to master it, as well as the fine inks you need to record it. Once you have spent this time and money, you can prepare the spell just like your other spells.",{"type":"entries","name":"Copying from a Spell Scroll","source":"DMG","page":139,"entries":["A wizard spell on a spell scroll can be copied just as spells in spellbooks can be copied. When you copy a spell from a spell scroll, you must succeed on an Intelligence ({@skill Arcana}) check with a DC equal to 10 + the spell''s level. If the check succeeds, the spell is successfully copied. Whether the check succeeds or fails, the spell scroll is destroyed."]}]}]},{"type":"entries","entries":[{"type":"entries","name":"Replacing the Book","entries":["You can copy a spell from your own spellbook into another book—for example, if you want to make a backup copy of your spellbook. This is just like copying a new spell into your spellbook, but faster and easier, since you understand your own notation and already know how to cast the spell. You need spend only 1 hour and 10 gp for each level of the copied spell.","If you lose your spellbook, you can use the same procedure to transcribe the spells that you have prepared into a new spellbook. Filling out the remainder of your spellbook requires you to find new spells to do so, as normal. For this reason, many wizards keep backup spellbooks in a safe place."]}]},{"type":"entries","entries":[{"type":"entries","name":"The Book''s Appearance","entries":["Your spellbook is a unique compilation of spells, with its own decorative flourishes and margin notes. It might be a plain, functional leather volume that you received as a gift from your master, a finely bound gilt-edged tome you found in an ancient library, or even a loose collection of notes scrounged together after you lost your previous spellbook in a mishap."]}]}]}');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Arcane Tradition', 1, 112, 'Wizard', 'PHB', NULL, NULL, 2, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Arcane Tradition' AND class_name = 'Wizard' AND level = 2), 0, 'When you reach 2nd level, you choose an arcane tradition from the list of available traditions, shaping your practice of magic. Your choice grants you features at 2nd level and again at 6th, 10th, and 14th level.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Cantrip Formulas', 2, 75, 'Wizard', 'PHB', NULL, NULL, 3, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cantrip Formulas' AND class_name = 'Wizard' AND level = 3), 0, '{@i 3rd-level wizard {@variantrule optional class features|tce|optional feature}}');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Cantrip Formulas' AND class_name = 'Wizard' AND level = 3), 1, 'You have scribed a set of arcane formulas in your spellbook that you can use to formulate a cantrip in your mind. Whenever you finish a long rest and consult those formulas in your spellbook, you can replace one wizard cantrip you know with another cantrip from the wizard spell list.');
INSERT INTO features (name, source_id, page, class_name, class_source, subclass_short_name, subclass_source, level, header, has_fluff, has_fluff_images) VALUES ('Ability Score Improvement', 1, 112, 'Wizard', 'PHB', NULL, NULL, 4, null, false, false) ON CONFLICT (name) DO NOTHING;
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Wizard' AND level = 4), 0, 'When you reach 4th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.');
INSERT INTO feature_entries (feature_id, entry_order, content) VALUES ((SELECT id FROM features WHERE name = 'Ability Score Improvement' AND class_name = 'Wizard' AND level = 4), 1, 'If your DM allows the use of feats, you may instead take a {@5etools feat|feats.html}.');
