    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 0, 'list', NULL, NULL, '["A blinded creature can''t see and automatically fails any ability check that requires sight.","Attack rolls against the creature have advantage, and the creature''s attack rolls have disadvantage."]'::jsonb, NULL, NULL::jsonb, NULL::jsonb, '{"type":"list","items":["A blinded creature can''t see and automatically fails any ability check that requires sight.","Attack rolls against the creature have advantage, and the creature''s attack rolls have disadvantage."]}'::jsonb);
