DO $$
DECLARE
    condition_id_var INTEGER;
BEGIN
    INSERT INTO conditions (name, source_id, page, srd, basic_rules, has_fluff_images) VALUES ('Exhaustion', (SELECT id FROM sources WHERE name ILIKE 'PHB' LIMIT 1), 291, true, true, false) RETURNING id INTO condition_id_var;
    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 0, NULL, NULL, 'Some special abilities and environmental hazards, such as starvation and the long-term effects of freezing or scorching temperatures, can lead to a special condition called exhaustion. Exhaustion is measured in six levels. An effect can give a creature one or more levels of exhaustion, as specified in the effect''s description.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Some special abilities and environmental hazards, such as starvation and the long-term effects of freezing or scorching temperatures, can lead to a special condition called exhaustion. Exhaustion is measured in six levels. An effect can give a creature one or more levels of exhaustion, as specified in the effect''s description."'::jsonb);
    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 1, 'table', NULL, NULL, NULL::jsonb, NULL, '["Level","Effect"]'::jsonb, '[["1","Disadvantage on ability checks"],["2","Speed halved"],["3","Disadvantage on attack rolls and saving throws"],["4","Hit point maximum halved"],["5","Speed reduced to 0"],["6","Death"]]'::jsonb, '{"type":"table","colLabels":["Level","Effect"],"colStyles":["col-1 text-center","col-11"],"rows":[["1","Disadvantage on ability checks"],["2","Speed halved"],["3","Disadvantage on attack rolls and saving throws"],["4","Hit point maximum halved"],["5","Speed reduced to 0"],["6","Death"]]}'::jsonb);
    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 2, NULL, NULL, 'If an already exhausted creature suffers another effect that causes exhaustion, its current level of exhaustion increases by the amount specified in the effect''s description.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"If an already exhausted creature suffers another effect that causes exhaustion, its current level of exhaustion increases by the amount specified in the effect''s description."'::jsonb);
    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 3, NULL, NULL, 'A creature suffers the effect of its current level of exhaustion as well as all lower levels. For example, a creature suffering level 2 exhaustion has its speed halved and has disadvantage on ability checks.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A creature suffers the effect of its current level of exhaustion as well as all lower levels. For example, a creature suffering level 2 exhaustion has its speed halved and has disadvantage on ability checks."'::jsonb);
    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 4, NULL, NULL, 'An effect that removes exhaustion reduces its level as specified in the effect''s description, with all exhaustion effects ending if a creature''s exhaustion level is reduced below 1.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"An effect that removes exhaustion reduces its level as specified in the effect''s description, with all exhaustion effects ending if a creature''s exhaustion level is reduced below 1."'::jsonb);
    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 5, NULL, NULL, 'Finishing a long rest reduces a creature''s exhaustion level by 1, provided that the creature has also ingested some food and drink. Also, being raised from the dead reduces a creature''s exhaustion level by 1.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Finishing a long rest reduces a creature''s exhaustion level by 1, provided that the creature has also ingested some food and drink. Also, being raised from the dead reduces a creature''s exhaustion level by 1."'::jsonb);
    INSERT INTO condition_other_sources (condition_id, source_name, page) VALUES (condition_id_var, 'RMR', 62);
END $$;

