    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 0, 'list', NULL, NULL, '["A grappled creature''s speed becomes 0, and it can''t benefit from any bonus to its speed.","The condition ends if the grappler is {@condition incapacitated}.","The condition also ends if an effect removes the grappled creature from the reach of the grappler or grappling effect, such as when a creature is hurled away by the {@spell thunderwave} spell."]'::jsonb, NULL, NULL::jsonb, NULL::jsonb, '{"type":"list","items":["A grappled creature''s speed becomes 0, and it can''t benefit from any bonus to its speed.","The condition ends if the grappler is {@condition incapacitated}.","The condition also ends if an effect removes the grappled creature from the reach of the grappler or grappling effect, such as when a creature is hurled away by the {@spell thunderwave} spell."]}'::jsonb);
