    INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (condition_id_var, 0, 'list', NULL, NULL, '["A petrified creature is transformed, along with any nonmagical object it is wearing or carrying, into a solid inanimate substance (usually stone). Its weight increases by a factor of ten, and it ceases aging.","The creature is {@condition incapacitated}, can''t move or speak, and is unaware of its surroundings.","Attack rolls against the creature have advantage.","The creature automatically fails Strength and Dexterity saving throws.","The creature has resistance to all damage.","The creature is immune to poison and disease, although a poison or disease already in its system is suspended, not neutralized."]'::jsonb, NULL, NULL::jsonb, NULL::jsonb, '{"type":"list","items":["A petrified creature is transformed, along with any nonmagical object it is wearing or carrying, into a solid inanimate substance (usually stone). Its weight increases by a factor of ten, and it ceases aging.","The creature is {@condition incapacitated}, can''t move or speak, and is unaware of its surroundings.","Attack rolls against the creature have advantage.","The creature automatically fails Strength and Dexterity saving throws.","The creature has resistance to all damage.","The creature is immune to poison and disease, although a poison or disease already in its system is suspended, not neutralized."]}'::jsonb);
