-- Diseases Inserts
DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Arcane Blight', (SELECT id FROM sources WHERE name ILIKE 'IDRotF' LIMIT 1), 233) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'Any humanoid that spends 12 hours in the necropolis must succeed on a {@dc 15} Constitution saving throw or contract an arcane blight. This magical disease transforms the humanoid into a {@creature nothic}, but only after the victim experiences hallucinations and feelings of isolation and paranoia. Other symptoms include clammy skin, hair loss, and myopia (nearsightedness).', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Any humanoid that spends 12 hours in the necropolis must succeed on a {@dc 15} Constitution saving throw or contract an arcane blight. This magical disease transforms the humanoid into a {@creature nothic}, but only after the victim experiences hallucinations and feelings of isolation and paranoia. Other symptoms include clammy skin, hair loss, and myopia (nearsightedness)."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'A player character infected with the arcane blight gains the following flaw: "I don''t trust anyone." This flaw, which supersedes any conflicting flaw, is fed by delusions that are difficult for the character to distinguish from reality. Common delusions include the belief that that allies are conspiring to steal the victim''s riches or otherwise turn against the victim.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A player character infected with the arcane blight gains the following flaw: \"I don''t trust anyone.\" This flaw, which supersedes any conflicting flaw, is fed by delusions that are difficult for the character to distinguish from reality. Common delusions include the belief that that allies are conspiring to steal the victim''s riches or otherwise turn against the victim."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 2, NULL, NULL, 'Whenever it finishes a long rest, an infected humanoid must repeat the saving throw. On a successful save, the DC for future saves against the arcane blight drops by {@dice 1d6}. If the saving throw DC drops to 0, the creature overcomes the arcane blight and becomes immune to the effect of further exposure. A creature that fails three of these saving throws transforms into a {@creature nothic} under the DM''s control. Only a {@spell wish} spell or divine intervention can undo this transformation.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Whenever it finishes a long rest, an infected humanoid must repeat the saving throw. On a successful save, the DC for future saves against the arcane blight drops by {@dice 1d6}. If the saving throw DC drops to 0, the creature overcomes the arcane blight and becomes immune to the effect of further exposure. A creature that fails three of these saving throws transforms into a {@creature nothic} under the DM''s control. Only a {@spell wish} spell or divine intervention can undo this transformation."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 3, NULL, NULL, 'A {@spell greater restoration} spell or similar magic ends the infection on the target, removing the flaw and all other symptoms, but this magic doesn''t protect the target against further exposure.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A {@spell greater restoration} spell or similar magic ends the infection on the target, removing the flaw and all other symptoms, but this magic doesn''t protect the target against further exposure."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

