    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'Pain grips the creature''s mind, and its eyes turn milky white. The creature has disadvantage on Wisdom checks and Wisdom saving throws and is {@condition blinded}.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Pain grips the creature''s mind, and its eyes turn milky white. The creature has disadvantage on Wisdom checks and Wisdom saving throws and is {@condition blinded}."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, '{@note This disease can be inflicted with the {@spell contagion} spell.}', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"{@note This disease can be inflicted with the {@spell contagion} spell.}"'::jsonb);
