    INSERT INTO diseases (name, source_id, page) VALUES ('Filth Fever', (SELECT id FROM sources WHERE name ILIKE 'PHB' LIMIT 1), 227) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'A raging fever sweeps through the creature''s body. The creature has disadvantage on Strength checks, Strength saving throws, and attack rolls that use Strength.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A raging fever sweeps through the creature''s body. The creature has disadvantage on Strength checks, Strength saving throws, and attack rolls that use Strength."'::jsonb);
