    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'The creature''s flesh decays. The creature has disadvantage on Charisma checks and vulnerability to all damage.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"The creature''s flesh decays. The creature has disadvantage on Charisma checks and vulnerability to all damage."'::jsonb);
