DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Frigid Woe', (SELECT id FROM sources WHERE name ILIKE 'EGW' LIMIT 1), 125) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'Frigid woe is a special disease developed by Aeor''s mages that cannot be cured by conventional treatment or magic. The only way a creature infected with the disease can be cured is by finding and drinking the manufactured antidote, a milky liquid stored in gold vials found in Eiselcross''s ruins. This disease was created to slow down the forces of the gods and get around the healing power of their clerics and angels.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Frigid woe is a special disease developed by Aeor''s mages that cannot be cured by conventional treatment or magic. The only way a creature infected with the disease can be cured is by finding and drinking the manufactured antidote, a milky liquid stored in gold vials found in Eiselcross''s ruins. This disease was created to slow down the forces of the gods and get around the healing power of their clerics and angels."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'The disease is transmitted by breathing in blue spores that Aeor''s mages created long ago. When a creature comes into contact with these spores, it must succeed on a {@dc 11} Constitution saving throw or become infected with frigid woe. It takes {@dice 1d4} days for the symptoms to manifest in an infected creature. These symptoms include fatigue, chills, and visible blue veins that appear on the creature''s body. The infected creature''s speed is reduced by 5 feet as long as it remains infected. Every 10 days after symptoms appear, an infected creature must succeed on a {@dc 11} Constitution saving throw, or its speed is reduced by another 5 feet. If a creature''s speed is reduced to 0 as a result of this disease, the creature dies and its body turns into a statue made of ice.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"The disease is transmitted by breathing in blue spores that Aeor''s mages created long ago. When a creature comes into contact with these spores, it must succeed on a {@dc 11} Constitution saving throw or become infected with frigid woe. It takes {@dice 1d4} days for the symptoms to manifest in an infected creature. These symptoms include fatigue, chills, and visible blue veins that appear on the creature''s body. The infected creature''s speed is reduced by 5 feet as long as it remains infected. Every 10 days after symptoms appear, an infected creature must succeed on a {@dc 11} Constitution saving throw, or its speed is reduced by another 5 feet. If a creature''s speed is reduced to 0 as a result of this disease, the creature dies and its body turns into a statue made of ice."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 2, NULL, NULL, 'A creature can drink the antidote as an action, ending all symptoms and effects of the disease instantly.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A creature can drink the antidote as an action, ending all symptoms and effects of the disease instantly."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Ghoul Gut', (SELECT id FROM sources WHERE name ILIKE 'OoW' LIMIT 1), 111) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'Water polluted with ghoul parts carries ghoul gut. A creature drinking this tainted water must succeed on a {@dc 12} Constitution saving throw or contract the disease. Symptoms strike when a victim starts a long rest or next becomes excited, as during combat. A victim suffers severe cramps, and their bowels make sounds like two ghouls fighting. When the symptoms first strike, the victim takes 5 ({@damage 2d4}) necrotic damage. In addition, a diseased victim regains no hit points or Hit Dice from nonmagical sources, including resting or spending Hit Dice. A victim who finishes a long rest can attempt a {@dc 12} Constitution saving throw. On a successful save, the victim shakes off the disease. Failure deals the character another 5 ({@damage 2d4}) necrotic damage.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Water polluted with ghoul parts carries ghoul gut. A creature drinking this tainted water must succeed on a {@dc 12} Constitution saving throw or contract the disease. Symptoms strike when a victim starts a long rest or next becomes excited, as during combat. A victim suffers severe cramps, and their bowels make sounds like two ghouls fighting. When the symptoms first strike, the victim takes 5 ({@damage 2d4}) necrotic damage. In addition, a diseased victim regains no hit points or Hit Dice from nonmagical sources, including resting or spending Hit Dice. A victim who finishes a long rest can attempt a {@dc 12} Constitution saving throw. On a successful save, the victim shakes off the disease. Failure deals the character another 5 ({@damage 2d4}) necrotic damage."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'A character who succeeds on a {@dc 15} Wisdom ({@skill Medicine}) check can also remove the disease from another creature. Doing so requires the character making the check to care for the sick individual during a long rest. The caregiver gains no benefit from that rest.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A character who succeeds on a {@dc 15} Wisdom ({@skill Medicine}) check can also remove the disease from another creature. Doing so requires the character making the check to care for the sick individual during a long rest. The caregiver gains no benefit from that rest."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Grackle-Lung', (SELECT id FROM sources WHERE name ILIKE 'OotA' LIMIT 1), 54) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'The constant smog in Gracklstugh causes grackle-lung in living, breathing creatures, resulting in persistent, wracking coughs and the spewing of thick, black phlegm. Whenever a living, breathing creature finishes a long rest in Gracklstugh, it must make a {@dc 11} Constitution saving throw. On each failed save, the creature gains one level of {@condition exhaustion} as its airways become increasingly clogged. A creature that reaches level 6 {@condition exhaustion} dies, as normal.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"The constant smog in Gracklstugh causes grackle-lung in living, breathing creatures, resulting in persistent, wracking coughs and the spewing of thick, black phlegm. Whenever a living, breathing creature finishes a long rest in Gracklstugh, it must make a {@dc 11} Constitution saving throw. On each failed save, the creature gains one level of {@condition exhaustion} as its airways become increasingly clogged. A creature that reaches level 6 {@condition exhaustion} dies, as normal."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'A creature with one or more levels of {@condition exhaustion} brought on by grackle-lung must succeed on a Constitution check to take the {@action Dash} action. If the check fails, the action cannot be attempted. If the creature attempts to cast a spell with a verbal component, it must succeed on a Constitution check or be unable to complete the spell, causing the spell to fail with no effect. The DC for each check is 10 + the creature''s current {@condition exhaustion} level.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A creature with one or more levels of {@condition exhaustion} brought on by grackle-lung must succeed on a Constitution check to take the {@action Dash} action. If the check fails, the action cannot be attempted. If the creature attempts to cast a spell with a verbal component, it must succeed on a Constitution check or be unable to complete the spell, causing the spell to fail with no effect. The DC for each check is 10 + the creature''s current {@condition exhaustion} level."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 2, NULL, NULL, 'If a creature''s {@condition exhaustion} level drops below 1, it no longer suffers the effects of grackle-lung and becomes immune to it for the next week. Duergar and derro are inured to grackle-lung, making Constitution checks against it with advantage. Any spell or effect that cures disease also cures grackle-lung, effectively removing all levels of {@condition exhaustion} brought on by the affliction.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"If a creature''s {@condition exhaustion} level drops below 1, it no longer suffers the effects of grackle-lung and becomes immune to it for the next week. Duergar and derro are inured to grackle-lung, making Constitution checks against it with advantage. Any spell or effect that cures disease also cures grackle-lung, effectively removing all levels of {@condition exhaustion} brought on by the affliction."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Mindfire', (SELECT id FROM sources WHERE name ILIKE 'PHB' LIMIT 1), 227) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'The creature''s mind becomes feverish. The creature has disadvantage on Intelligence checks and Intelligence saving throws, and the creature behaves as if under the effects of the {@spell confusion} spell during combat.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"The creature''s mind becomes feverish. The creature has disadvantage on Intelligence checks and Intelligence saving throws, and the creature behaves as if under the effects of the {@spell confusion} spell during combat."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, '{@note This disease can be inflicted with the {@spell contagion} spell.}', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"{@note This disease can be inflicted with the {@spell contagion} spell.}"'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Redface', (SELECT id FROM sources WHERE name ILIKE 'GoS' LIMIT 1), 168) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'The effects of redface are identical to those of {@disease sight rot} (see "{@book Diseases|DMG|8|Diseases}" in chapter 8 of the {@book Dungeon Master''s Guide|DMG}), but it''s caused by pollutants in the air rather than contaminated drinking water, making it difficult to avoid.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"The effects of redface are identical to those of {@disease sight rot} (see \"{@book Diseases|DMG|8|Diseases}\" in chapter 8 of the {@book Dungeon Master''s Guide|DMG}), but it''s caused by pollutants in the air rather than contaminated drinking water, making it difficult to avoid."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Saprophytic Plague', (SELECT id FROM sources WHERE name ILIKE 'CM' LIMIT 1), 253) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'Xanthoria''s research into lichdom and her creation of a living phylactery led to the emergence of the plague spreading across the Sword Coast. Any beast or humanoid that comes within 10 feet of a creature infected by the saprophytic plague must succeed on a {@dc 20} Constitution saving throw or become infected as well. On a successful save, a creature is immune to the plague for 24 hours, and any creature that is immune to disease succeeds on the save automatically.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Xanthoria''s research into lichdom and her creation of a living phylactery led to the emergence of the plague spreading across the Sword Coast. Any beast or humanoid that comes within 10 feet of a creature infected by the saprophytic plague must succeed on a {@dc 20} Constitution saving throw or become infected as well. On a successful save, a creature is immune to the plague for 24 hours, and any creature that is immune to disease succeeds on the save automatically."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'After a failed save, a creature experiences the first symptoms—body aches, nausea, slurred speech, and uncontrollable drooling—{@dice 1d4} hours later and gains 1 level of {@condition exhaustion}. Every 24 hours thereafter, the creature automatically gains another level of {@condition exhaustion}. Any creature killed by the saprophytic plague transforms into a lifeless magenta ooze.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"After a failed save, a creature experiences the first symptoms—body aches, nausea, slurred speech, and uncontrollable drooling—{@dice 1d4} hours later and gains 1 level of {@condition exhaustion}. Every 24 hours thereafter, the creature automatically gains another level of {@condition exhaustion}. Any creature killed by the saprophytic plague transforms into a lifeless magenta ooze."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 2, NULL, NULL, 'A creature infected by the saprophytic plague for at least 24 hours behaves like a zombie and seems barely aware of its surroundings, as fungal growths sprout from its head, body, and limbs. It feels an overriding urge to be around groups of uninfected creatures so that it can spread the contagion. This is the time when infected humanoids begin speaking the word "Xanthoria" over and over, with no understanding of what the word means. This behavior is the result of a mycelial network of spores that forms a tenuous connection between Xanthoria and humanoids that become infected by the plague.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A creature infected by the saprophytic plague for at least 24 hours behaves like a zombie and seems barely aware of its surroundings, as fungal growths sprout from its head, body, and limbs. It feels an overriding urge to be around groups of uninfected creatures so that it can spread the contagion. This is the time when infected humanoids begin speaking the word \"Xanthoria\" over and over, with no understanding of what the word means. This behavior is the result of a mycelial network of spores that forms a tenuous connection between Xanthoria and humanoids that become infected by the plague."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 3, NULL, NULL, 'Foodstuffs that are exposed to the plague''s spores decay or go sour within {@dice 2d12} hours, leaving behind a lurid magenta mass of oozing fungi. Eating infected food requires a saving throw as above.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Foodstuffs that are exposed to the plague''s spores decay or go sour within {@dice 2d12} hours, leaving behind a lurid magenta mass of oozing fungi. Eating infected food requires a saving throw as above."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 4, NULL, NULL, 'Any magic that cures a disease can rid a creature of the saprophytic plague. Casting {@spell purify food and drink} destroys the infection in foodstuffs.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Any magic that cures a disease can rid a creature of the saprophytic plague. Casting {@spell purify food and drink} destroys the infection in foodstuffs."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Seizure', (SELECT id FROM sources WHERE name ILIKE 'PHB' LIMIT 1), 227) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'The creature is overcome with shaking. The creature has disadvantage on Dexterity checks, Dexterity saving throws, and attack rolls that use Dexterity.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"The creature is overcome with shaking. The creature has disadvantage on Dexterity checks, Dexterity saving throws, and attack rolls that use Dexterity."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, '{@note This disease can be inflicted with the {@spell contagion} spell.}', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"{@note This disease can be inflicted with the {@spell contagion} spell.}"'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Sewer Plague', (SELECT id FROM sources WHERE name ILIKE 'DMG' LIMIT 1), 257) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'Sewer plague is a generic term for a broad category of illnesses that incubate in sewers, refuse heaps, and stagnant swamps, and which are sometimes transmitted by creatures that dwell in those areas, such as rats and otyughs.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Sewer plague is a generic term for a broad category of illnesses that incubate in sewers, refuse heaps, and stagnant swamps, and which are sometimes transmitted by creatures that dwell in those areas, such as rats and otyughs."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'When a humanoid creature is bitten by a creature that carries the disease, or when it comes into contact with filth or offal contaminated by the disease, the creature must succeed on a {@dc 11} Constitution saving throw or become infected.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"When a humanoid creature is bitten by a creature that carries the disease, or when it comes into contact with filth or offal contaminated by the disease, the creature must succeed on a {@dc 11} Constitution saving throw or become infected."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 2, NULL, NULL, 'It takes {@dice 1d4} days for sewer plague''s symptoms to manifest in an infected creature. Symptoms include fatigue and cramps. The infected creature suffers one level of {@condition exhaustion}, and it regains only half the normal number of hit points from spending Hit Dice and no hit points from finishing a long rest.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"It takes {@dice 1d4} days for sewer plague''s symptoms to manifest in an infected creature. Symptoms include fatigue and cramps. The infected creature suffers one level of {@condition exhaustion}, and it regains only half the normal number of hit points from spending Hit Dice and no hit points from finishing a long rest."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 3, NULL, NULL, 'At the end of each long rest, an infected creature must make a {@dc 11} Constitution saving throw. On a failed save, the character gains one level of {@condition exhaustion}.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"At the end of each long rest, an infected creature must make a {@dc 11} Constitution saving throw. On a failed save, the character gains one level of {@condition exhaustion}."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 4, NULL, NULL, 'On a successful save, the character''s {@condition exhaustion} level decreases by one level. If a successful saving throw reduces the infected creature''s level of {@condition exhaustion} below 1, the creature recovers from the disease.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"On a successful save, the character''s {@condition exhaustion} level decreases by one level. If a successful saving throw reduces the infected creature''s level of {@condition exhaustion} below 1, the creature recovers from the disease."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Shivering Sickness', (SELECT id FROM sources WHERE name ILIKE 'ToA' LIMIT 1), 40) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'Insects native to the jungles and marshes of Chult carry this disease, shivering sickness. The easiest protection against it is a coating of insect-repelling salve on all exposed skin (for the cost of insect repellent, see {@adventure "Buying a Special Item"|ToA|1|Buying a Special Item}.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Insects native to the jungles and marshes of Chult carry this disease, shivering sickness. The easiest protection against it is a coating of insect-repelling salve on all exposed skin (for the cost of insect repellent, see {@adventure \"Buying a Special Item\"|ToA|1|Buying a Special Item}."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'A giant or humanoid that takes damage from {@creature Swarm of Insects||insect swarms} or from {@creature giant centipede||giant centipedes}, {@creature giant scorpion||giant scorpions}, or {@creature giant wasp||giant wasps} is exposed to the disease at the end of the encounter. Those who haven''t applied insect repellent since their previous long rest are exposed to the disease when they finish a long rest.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A giant or humanoid that takes damage from {@creature Swarm of Insects||insect swarms} or from {@creature giant centipede||giant centipedes}, {@creature giant scorpion||giant scorpions}, or {@creature giant wasp||giant wasps} is exposed to the disease at the end of the encounter. Those who haven''t applied insect repellent since their previous long rest are exposed to the disease when they finish a long rest."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 2, NULL, NULL, 'A giant or humanoid exposed to the disease must succeed on a {@dc 11} Constitution saving throw or become infected. A creature with natural armor has advantage on the saving throw. It takes {@dice 2d6} hours for symptoms to manifest in an infected creature. Symptoms include blurred vision, disorientation, and a sudden drop in body temperature that causes uncontrollable shivering and chattering of the teeth.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A giant or humanoid exposed to the disease must succeed on a {@dc 11} Constitution saving throw or become infected. A creature with natural armor has advantage on the saving throw. It takes {@dice 2d6} hours for symptoms to manifest in an infected creature. Symptoms include blurred vision, disorientation, and a sudden drop in body temperature that causes uncontrollable shivering and chattering of the teeth."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 3, NULL, NULL, 'Once symptoms begin, the infected creature regains only half the normal number of hit points from spending Hit Dice and no hit points from a long rest. The infected creature also has disadvantage on ability checks and attack rolls. At the end of a long rest, an infected creature repeats the saving throw, shaking off the disease on a successful save.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Once symptoms begin, the infected creature regains only half the normal number of hit points from spending Hit Dice and no hit points from a long rest. The infected creature also has disadvantage on ability checks and attack rolls. At the end of a long rest, an infected creature repeats the saving throw, shaking off the disease on a successful save."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Sight Rot', (SELECT id FROM sources WHERE name ILIKE 'DMG' LIMIT 1), 257) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'This painful infection causes bleeding from the eyes and eventually blinds the victim.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"This painful infection causes bleeding from the eyes and eventually blinds the victim."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'A beast or humanoid that drinks water tainted by sight rot must succeed on a {@dc 15} Constitution saving throw or become infected. One day after infection, the creature''s vision starts to become blurry. The creature takes a -1 penalty to attack rolls and ability checks that rely on sight. At the end of each long rest after the symptoms appear, the penalty worsens by 1. When it reaches -5, the victim is {@condition blinded} until its sight is restored by magic such as {@spell lesser restoration} or {@spell heal}.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A beast or humanoid that drinks water tainted by sight rot must succeed on a {@dc 15} Constitution saving throw or become infected. One day after infection, the creature''s vision starts to become blurry. The creature takes a -1 penalty to attack rolls and ability checks that rely on sight. At the end of each long rest after the symptoms appear, the penalty worsens by 1. When it reaches -5, the victim is {@condition blinded} until its sight is restored by magic such as {@spell lesser restoration} or {@spell heal}."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 2, NULL, NULL, 'Sight rot can be cured using a rare flower called Eyebright, which grows in some swamps. Given an hour, a character who has proficiency with an {@item herbalism kit|phb} can turn the flower into one dose of ointment. Applied to the eyes before a long rest, one dose of it prevents the disease from worsening after that rest. After three doses, the ointment cures the disease entirely.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Sight rot can be cured using a rare flower called Eyebright, which grows in some swamps. Given an hour, a character who has proficiency with an {@item herbalism kit|phb} can turn the flower into one dose of ointment. Applied to the eyes before a long rest, one dose of it prevents the disease from worsening after that rest. After three doses, the ointment cures the disease entirely."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
