BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Slimy Doom', (SELECT id FROM sources WHERE name ILIKE 'PHB' LIMIT 1), 227) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'The creature begins to bleed uncontrollably. The creature has disadvantage on Constitution checks and Constitution saving throws. In addition, whenever the creature takes damage, it is {@condition stunned} until the end of its next turn.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"The creature begins to bleed uncontrollably. The creature has disadvantage on Constitution checks and Constitution saving throws. In addition, whenever the creature takes damage, it is {@condition stunned} until the end of its next turn."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, '{@note This disease can be inflicted with the {@spell contagion} spell.}', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"{@note This disease can be inflicted with the {@spell contagion} spell.}"'::jsonb);
